arasuperobject.biblemap.put("b2_26_5", new arasuperobject(2,26,5,"خمسين عروة تصنع في الشقة الواحدة وخمسين عروة تصنع في طرف الشقة الذي في الموصّل الثاني. تكون العرى بعضها مقابل لبعض"));
arasuperobject.biblemap.put("b2_26_6", new arasuperobject(2,26,6,"وتصنع خمسين شظاظا من ذهب. وتصل الشقتين بعضهما ببعض بالاشظة. فيصير المسكن واحدا"));
arasuperobject.biblemap.put("b2_26_7", new arasuperobject(2,26,7,"وتصنع شققا من شعر معزى خيمة على المسكن. احدى عشرة شقة تصنعها"));
arasuperobject.biblemap.put("b2_26_8", new arasuperobject(2,26,8,"طول الشقة الواحدة ثلاثون ذراعا وعرض الشقة الواحدة اربع اذرع. قياسا واحدا للاحدى عشرة شقة"));
arasuperobject.biblemap.put("b2_26_9", new arasuperobject(2,26,9,"وتصل خمسا من الشقق وحدها وستّا من الشقق وحدها. وتثني الشقة السادسة في وجه الخيمة"));
arasuperobject.biblemap.put("b2_26_10", new arasuperobject(2,26,10,"وتصنع خمسين عروة على حاشية الشقة الواحدة الطرفية من الموصّل الواحد وخمسين عروة على حاشية الشقة من الموصّل الثاني"));
arasuperobject.biblemap.put("b2_26_11", new arasuperobject(2,26,11,"وتصنع خمسين شظاظا من نحاس. وتدخل الاشظّة في العرى وتصل الخيمة فتصير واحدة"));
arasuperobject.biblemap.put("b2_26_12", new arasuperobject(2,26,12,"واما المدلّى الفاضل من شقق الخيمة نصف الشقة الموصلة الفاضل فيدلّى على مؤخر المسكن"));
arasuperobject.biblemap.put("b2_26_13", new arasuperobject(2,26,13,"والذراع من هنا والذراع من هناك من الفاضل في طول شقق الخيمة تكونان مدلاتين على جانبي المسكن من هنا ومن هناك لتغطيته"));
arasuperobject.biblemap.put("b2_26_14", new arasuperobject(2,26,14,"وتصنع غطاء للخيمة من جلود كباش محمرّة. وغطاء من جلود تخس من فوق"));
arasuperobject.biblemap.put("b2_26_15", new arasuperobject(2,26,15,"وتصنع الالواح للمسكن من خشب السنط قائمة"));
arasuperobject.biblemap.put("b2_26_16", new arasuperobject(2,26,16,"طول اللوح عشر اذرع وعرض اللوح الواحد ذراع ونصف"));
arasuperobject.biblemap.put("b2_26_17", new arasuperobject(2,26,17,"وللّوح الواحد رجلان مقرونة احداهما بالاخرى. هكذا تصنع لجميع الواح المسكن"));
