arasuperobject.biblemap.put("b2_26_19", new arasuperobject(2,26,19,"وتصنع اربعين قاعدة من فضة تحت العشرين لوحا. تحت اللوح الواحد قاعدتان لرجليه وتحت اللوح الواحد قاعدتان لرجليه"));
arasuperobject.biblemap.put("b2_26_20", new arasuperobject(2,26,20,"ولجانب المسكن الثاني الى جهة الشمال عشرين لوحا"));
arasuperobject.biblemap.put("b2_26_21", new arasuperobject(2,26,21,"واربعين قاعدة لها من فضة. تحت اللوح الواحد قاعدتان وتحت اللوح الواحد قاعدتان"));
arasuperobject.biblemap.put("b2_26_22", new arasuperobject(2,26,22,"ولمؤخر المسكن نحو الغرب تصنع ستة الواح"));
arasuperobject.biblemap.put("b2_26_23", new arasuperobject(2,26,23,"وتصنع لوحين لزاويتي المسكن في المؤخر"));
arasuperobject.biblemap.put("b2_26_24", new arasuperobject(2,26,24,"ويكونان مزدوجين من اسفل. وعلى سواء يكونان مزدوجين الى راسه الى الحلقة الواحدة. هكذا يكون لكليهما. يكونان للزاويتين"));
arasuperobject.biblemap.put("b2_26_25", new arasuperobject(2,26,25,"فتكون ثمانية الواح وقواعدها من فضة ست عشرة قاعدة. تحت اللوح الواحد قاعدتان وتحت اللوح الواحد قاعدتان"));
arasuperobject.biblemap.put("b2_26_26", new arasuperobject(2,26,26,"وتصنع عوارض من خشب السنط. خمسا لالواح جانب المسكن الواحد"));
arasuperobject.biblemap.put("b2_26_27", new arasuperobject(2,26,27,"وخمس عوارض لالواح جانب المسكن الثاني. وخمس عوارض لالواح جانب المسكن في المؤخر نحو الغرب"));
arasuperobject.biblemap.put("b2_26_28", new arasuperobject(2,26,28,"والعارضة الوسطى في وسط الالواح تنفذ من الطرف الى الطرف"));
arasuperobject.biblemap.put("b2_26_29", new arasuperobject(2,26,29,"وتغشّي الالواح بذهب. وتصنع حلقاتها من ذهب بيوتا للعوارض. وتغشّي العوارض بذهب"));
arasuperobject.biblemap.put("b2_26_30", new arasuperobject(2,26,30,"وتقيم المسكن كرسمه الذي أظهر لك في الجبل"));
arasuperobject.biblemap.put("b2_26_31", new arasuperobject(2,26,31,"وتصنع حجابا من اسمانجوني وارجوان وقرمز وبوص مبروم. صنعة حائك حاذق يصنعه بكروبيم"));
arasuperobject.biblemap.put("b2_26_32", new arasuperobject(2,26,32,"وتجعله على اربعة اعمدة من سنط مغشّاة بذهب. رززها من ذهب. على اربع قواعد من فضة"));
arasuperobject.biblemap.put("b2_26_33", new arasuperobject(2,26,33,"وتجعل الحجاب تحت الاشظّة. وتدخل الى هناك داخل الحجاب تابوت الشهادة. فيفصل لكم الحجاب بين القدس وقدس الاقداس"));
arasuperobject.biblemap.put("b2_26_34", new arasuperobject(2,26,34,"وتجعل الغطاء على تابوت الشهادة في قدس الاقداس"));
arasuperobject.biblemap.put("b2_26_35", new arasuperobject(2,26,35,"وتضع المائدة خارج الحجاب والمنارة مقابل المائدة على جانب المسكن نحو التيمن. وتجعل المائدة على جانب الشمال"));
arasuperobject.biblemap.put("b2_26_36", new arasuperobject(2,26,36,"وتصنع سجفا لمدخل الخيمة من اسمانجوني وارجوان وقرمز وبوص مبروم صنعة الطرّاز"));
arasuperobject.biblemap.put("b2_26_37", new arasuperobject(2,26,37,"وتصنع للسجف خمسة اعمدة من سنط وتغشّيها بذهب. رززها من ذهب. وتسبك لها خمس قواعد من نحاس"));
arasuperobject.biblemap.put("b2_27_1", new arasuperobject(2,27,1,"وتصنع المذبح من خشب السنط. طوله خمس اذرع وعرضه خمس اذرع. مربعا يكون المذبح. وارتفاعه ثلاث اذرع"));
arasuperobject.biblemap.put("b2_27_2", new arasuperobject(2,27,2,"وتصنع قرونه على زواياه الاربع. منه تكون قرونه. وتغشّيه بنحاس"));
arasuperobject.biblemap.put("b2_27_3", new arasuperobject(2,27,3,"وتصنع قدوره لرفع رماده ورفوشه ومراكنه ومناشله ومجامره. جميع آنيته تصنعها من نحاس"));
arasuperobject.biblemap.put("b2_27_4", new arasuperobject(2,27,4,"وتصنع له شبّاكة صنعة الشبكة من نحاس. وتصنع على الشبكة اربع حلقات من نحاس على اربعة اطرافه"));
arasuperobject.biblemap.put("b2_27_5", new arasuperobject(2,27,5,"وتجعلها تحت حاجب المذبح من اسفل. وتكون الشبكة الى نصف المذبح"));
arasuperobject.biblemap.put("b2_27_6", new arasuperobject(2,27,6,"وتصنع عصوين للمذبح عصوين من خشب السنط وتغشّيهما بنحاس"));
arasuperobject.biblemap.put("b2_27_7", new arasuperobject(2,27,7,"وتدخل عصواه في الحلقات. فتكون العصوان على جانبي المذبح حينما يحمل"));
arasuperobject.biblemap.put("b2_27_8", new arasuperobject(2,27,8,"مجوّفا تصنعه من الواح. كما أظهر لك في الجبل هكذا يصنعونه"));
arasuperobject.biblemap.put("b2_27_9", new arasuperobject(2,27,9,"وتصنع دار المسكن. الى جهة الجنوب نحو التيمن للدار استار من بوص مبروم. مئة ذراع طولا الى الجهة الواحدة"));
arasuperobject.biblemap.put("b2_27_10", new arasuperobject(2,27,10,"واعمدتها عشرون وقواعدها عشرون من نحاس. رزز الاعمدة وقضبانها من فضة"));
arasuperobject.biblemap.put("b2_27_11", new arasuperobject(2,27,11,"وكذلك الى جهة الشمال في الطول استار مئة ذراع طولا. واعمدتها عشرون وقواعدها عشرون من نحاس. رزز الاعمدة وقضبانها من فضة"));
arasuperobject.biblemap.put("b2_27_12", new arasuperobject(2,27,12,"وفي عرض الدار الى جهة الغرب استار خمسون ذراعا. اعمدتها عشرة وقواعدها عشر"));
arasuperobject.biblemap.put("b2_27_13", new arasuperobject(2,27,13,"وعرض الدار الى جهة الشرق نحو الشروق خمسون ذراعا"));
arasuperobject.biblemap.put("b2_27_14", new arasuperobject(2,27,14,"وخمس عشرة ذراعا من الاستار للجانب الواحد. اعمدتها ثلاثة وقواعدها ثلاث"));
arasuperobject.biblemap.put("b2_27_15", new arasuperobject(2,27,15,"وللجانب الثاني خمس عشرة ذراعا من الاستار. اعمدتها ثلاثة وقواعدها ثلاث"));
arasuperobject.biblemap.put("b2_27_16", new arasuperobject(2,27,16,"ولباب الدار سجف عشرون ذراعا من اسمانجوني وارجوان وقرمز وبوص مبروم صنعة الطراز. اعمدته اربعة وقواعدها اربع"));
arasuperobject.biblemap.put("b2_27_17", new arasuperobject(2,27,17,"لكل اعمدة الدار حواليها قضبان من فضة. رززها من فضة وقواعدها من نحاس"));
arasuperobject.biblemap.put("b2_27_18", new arasuperobject(2,27,18,"طول الدار مئة ذراع وعرضها خمسون فخمسون وارتفاعها خمس اذرع من بوص مبروم. وقواعدها من نحاس"));
arasuperobject.biblemap.put("b2_27_19", new arasuperobject(2,27,19,"جميع اواني المسكن في كل خدمته وجميع اوتاده وجميع اوتاد الدار من نحاس"));
arasuperobject.biblemap.put("b2_27_20", new arasuperobject(2,27,20,"وانت تامر بني اسرائيل ان يقدموا اليك زيت زيتون مرضوض نقيا للضوء لاصعاد السرج دائما"));
arasuperobject.biblemap.put("b2_27_21", new arasuperobject(2,27,21,"في خيمة الاجتماع خارج الحجاب الذي امام الشهادة يرتّبها هرون وبنوه من المساء الى الصباح امام الرب. فريضة دهرية في اجيالهم من بني اسرائيل"));
arasuperobject.biblemap.put("b2_28_1", new arasuperobject(2,28,1,"وقرّب اليك هرون اخاك وبنيه معه من بين بني اسرائيل ليكهن لي. هرون ناداب وابيهو العازار وايثامار بني هرون"));
arasuperobject.biblemap.put("b2_28_2", new arasuperobject(2,28,2,"واصنع ثيابا مقدسة لهرون اخيك للمجد والبهاء"));
arasuperobject.biblemap.put("b2_28_3", new arasuperobject(2,28,3,"وتكلم جميع حكماء القلوب الذين ملأتهم روح حكمة ان يصنعوا ثياب هرون لتقديسه ليكهن لي"));
arasuperobject.biblemap.put("b2_28_4", new arasuperobject(2,28,4,"وهذه هي الثياب التي يصنعونها صدرة ورداء وجبّة وقميص مخرّم وعمامة ومنطقة. فيصنعون ثيابا مقدسة لهرون اخيك ولبنيه ليكهن لي"));
arasuperobject.biblemap.put("b2_28_5", new arasuperobject(2,28,5,"وهم ياخذون الذهب والاسمانجوني والارجوان والقرمز والبوص"));
arasuperobject.biblemap.put("b2_28_6", new arasuperobject(2,28,6,"فيصنعون الرداء من ذهب واسمانجوني وارجوان وقرمز وبوص مبروم صنعة حائك حاذق"));
arasuperobject.biblemap.put("b2_28_7", new arasuperobject(2,28,7,"يكون له كتفان موصولان في طرفيه ليتّصل"));
arasuperobject.biblemap.put("b2_28_8", new arasuperobject(2,28,8,"وزنار شدّه الذي عليه يكون منه كصنعته. من ذهب واسمانجوني وقرمز وبوص مبروم"));
arasuperobject.biblemap.put("b2_28_9", new arasuperobject(2,28,9,"وتاخذ حجري جزع وتنقش عليهما اسماء بني اسرائيل"));
arasuperobject.biblemap.put("b2_28_10", new arasuperobject(2,28,10,"ستة من اسمائهم على الحجر الواحد واسماء الستة الباقين على الحجر الثاني حسب مواليدهم"));
arasuperobject.biblemap.put("b2_28_11", new arasuperobject(2,28,11,"صنعة نقّاش الحجارة نقش الخاتم تنقش الحجرين على حسب اسماء بني اسرائيل. محاطين بطوقين من ذهب تصنعهما"));
arasuperobject.biblemap.put("b2_28_12", new arasuperobject(2,28,12,"وتضع الحجرين على كتفي الرداء حجري تذكار لبني اسرائيل. فيحمل هرون اسماءهم امام الرب على كتفيه للتذكار"));
arasuperobject.biblemap.put("b2_28_13", new arasuperobject(2,28,13,"وتصنع طوقين من ذهب"));
arasuperobject.biblemap.put("b2_28_14", new arasuperobject(2,28,14,"وسلسلتين من ذهب نقي. مجدولتين تصنعهما صنعة الضفر. وتجعل سلسلتي الضفائر في الطوقين"));
arasuperobject.biblemap.put("b2_28_15", new arasuperobject(2,28,15,"وتصنع صدرة قضاء. صنعة حائك حاذق كصنعة الرداء تصنعها. من ذهب واسمانجوني وارجوان وقرمز وبوص مبروم تصنعها"));
arasuperobject.biblemap.put("b2_28_16", new arasuperobject(2,28,16,"تكون مربعة مثنية طولها شبر وعرضها شبر"));
arasuperobject.biblemap.put("b2_28_17", new arasuperobject(2,28,17,"وترصع فيها ترصيع حجر اربعة صفوف حجارة. صف عقيق احمر وياقوت اصفر وزمرّد. الصف الاول"));
arasuperobject.biblemap.put("b2_28_18", new arasuperobject(2,28,18,"والصف الثاني بهرمان وياقوت ازرق وعقيق ابيض"));
arasuperobject.biblemap.put("b2_28_19", new arasuperobject(2,28,19,"والصف الثالث عين الهر ويشم وجمشت"));
arasuperobject.biblemap.put("b2_28_20", new arasuperobject(2,28,20,"والصف الرابع زبرجد وجزع ويشب. تكون مطوّقة بذهب في ترصيعها"));
arasuperobject.biblemap.put("b2_28_21", new arasuperobject(2,28,21,"وتكون الحجارة على اسماء بني اسرائيل اثني عشر على اسمائهم. كنقش الخاتم كل واحد على اسمه تكون للاثني عشر سبطا"));
arasuperobject.biblemap.put("b2_28_22", new arasuperobject(2,28,22,"وتصنع على الصدرة سلاسل مجدولة صنعة الضفر من ذهب نقي"));
arasuperobject.biblemap.put("b2_28_23", new arasuperobject(2,28,23,"وتصنع على الصدرة حلقتين من ذهب. وتجعل الحلقتين على طرفي الصدرة"));
arasuperobject.biblemap.put("b2_28_24", new arasuperobject(2,28,24,"وتجعل ضفيرتي الذهب في الحلقتين على طرفي الصدرة"));
arasuperobject.biblemap.put("b2_28_25", new arasuperobject(2,28,25,"وتجعل طرفي الضفيرتين الآخرين في الطوقين. وتجعلها على كتفي الرداء الى قدامه"));
arasuperobject.biblemap.put("b2_28_26", new arasuperobject(2,28,26,"وتصنع حلقتين من ذهب وتضعهما على طرفي الصدرة على حاشيتها التي الى جهة الرداء من داخل"));
arasuperobject.biblemap.put("b2_28_27", new arasuperobject(2,28,27,"وتصنع حلقتين من ذهب. وتجعلها على كتفي الرداء من اسفل من قدامه عند وصله من فوق زنار الرداء"));
arasuperobject.biblemap.put("b2_28_28", new arasuperobject(2,28,28,"ويربطون الصدرة بحلقتيها الى حلقتي الرداء بخيط من اسمانجوني لتكون على زنار الرداء. ولا تنزع الصدرة عن الرداء"));
arasuperobject.biblemap.put("b2_28_29", new arasuperobject(2,28,29,"فيحمل هرون اسماء بني اسرائيل في صدرة القضاء على قلبه عند دخوله الى القدس للتذكار امام الرب دائما"));
arasuperobject.biblemap.put("b2_28_30", new arasuperobject(2,28,30,"وتجعل في صدرة القضاء الأوريم والتمّيم لتكون على قلب هرون عند دخوله امام الرب. فيحمل هرون قضاء بني اسرائيل على قلبه امام الرب دائما"));
arasuperobject.biblemap.put("b2_28_31", new arasuperobject(2,28,31,"وتصنع جبة الرداء كلها من اسمانجوني"));
arasuperobject.biblemap.put("b2_28_32", new arasuperobject(2,28,32,"وتكون فتحة راسها في وسطها. ويكون لفتحتها حاشية حواليها صنعة الحائك. كفتحة الدرع يكون لها. لا تشق"));
arasuperobject.biblemap.put("b2_28_33", new arasuperobject(2,28,33,"وتصنع على اذيالها رمانات من اسمانجوني وارجوان وقرمز. على اذيالها حواليها. وجلاجل من ذهب بينها حواليها"));
arasuperobject.biblemap.put("b2_28_34", new arasuperobject(2,28,34,"جلجل ذهب ورمانة جلجل ذهب ورمانة على اذيال الجبة حواليها"));
arasuperobject.biblemap.put("b2_28_35", new arasuperobject(2,28,35,"فتكون على هرون للخدمة ليسمع صوتها عند دخوله الى القدس امام الرب وعند خروجه لئلا يموت"));
arasuperobject.biblemap.put("b2_28_36", new arasuperobject(2,28,36,"وتصنع صفيحة من ذهب نقي. وتنقش عليها نقش خاتم قدس للرب"));
arasuperobject.biblemap.put("b2_28_37", new arasuperobject(2,28,37,"وتضعها على خيط اسمانجوني لتكون على العمامة. الى قدام العمامة تكون"));
arasuperobject.biblemap.put("b2_28_38", new arasuperobject(2,28,38,"فتكون على جبهة هرون. فيحمل هرون اثم الاقداس التي يقدسها بنو اسرائيل جميع عطايا اقداسهم وتكون على جبهته دائما للرضى عنهم امام الرب"));
arasuperobject.biblemap.put("b2_28_39", new arasuperobject(2,28,39,"وتخرم القميص من بوص. وتصنع العمامة من بوص. والمنطقة تصنعها صنعة الطراز"));
arasuperobject.biblemap.put("b2_28_40", new arasuperobject(2,28,40,"ولبني هرون تصنع اقمصة وتصنع لهم مناطق وتصنع لهم قلانس للمجد والبهاء"));
arasuperobject.biblemap.put("b2_28_41", new arasuperobject(2,28,41,"وتلبس هرون اخاك اياها وبنيه معه وتمسحهم وتملأ اياديهم وتقدسهم ليكهنوا لي"));
arasuperobject.biblemap.put("b2_28_42", new arasuperobject(2,28,42,"وتصنع لهم سراويل من كتان لستر العورة. من الحقوين الى الفخذين تكون"));
arasuperobject.biblemap.put("b2_28_43", new arasuperobject(2,28,43,"فتكون على هرون وبنيه عند دخولهم الى خيمة الاجتماع او عند اقترابهم الى المذبح للخدمة في القدس. لئلا يحملوا اثما ويموتوا. فريضة ابدية له ولنسله من بعده"));
arasuperobject.biblemap.put("b2_29_1", new arasuperobject(2,29,1,"وهذا ما تصنعه لهم لتقديسهم ليكهنوا لي. خذ ثورا واحدا ابن بقر وكبشين صحيحين"));
arasuperobject.biblemap.put("b2_29_2", new arasuperobject(2,29,2,"وخبز فطير واقراص فطير ملتوتة بزيت ورقاق فطير مدهونة بزيت. من دقيق حنطة تصنعها"));
arasuperobject.biblemap.put("b2_29_3", new arasuperobject(2,29,3,"وتجعلها في سلة واحدة وتقدمها في السلة مع الثور والكبشين"));
arasuperobject.biblemap.put("b2_29_4", new arasuperobject(2,29,4,"وتقدم هرون وبنيه الى باب خيمة الاجتماع وتغسلهم بماء"));
arasuperobject.biblemap.put("b2_29_5", new arasuperobject(2,29,5,"وتاخذ الثياب وتلبس هرون القميص وجبة الرداء والرداء والصدرة وتشده بزنّار الرداء"));
arasuperobject.biblemap.put("b2_29_6", new arasuperobject(2,29,6,"وتضع العمامة على راسه. وتجعل الاكليل المقدس على العمامة"));
arasuperobject.biblemap.put("b2_29_7", new arasuperobject(2,29,7,"وتاخذ دهن المسحة وتسكبه على راسه وتمسحه"));
arasuperobject.biblemap.put("b2_29_8", new arasuperobject(2,29,8,"وتقدم بنيه وتلبسهم اقمصة"));
arasuperobject.biblemap.put("b2_29_9", new arasuperobject(2,29,9,"وتنطّقهم بمناطق هرون وبنيه. وتشدّ لهم قلانص. فيكون لهم كهنوت فريضة ابدية. وتملأ يد هرون وايدي بنيه"));
arasuperobject.biblemap.put("b2_29_10", new arasuperobject(2,29,10,"وتقدم الثور الى قدام خيمة الاجتماع. فيضع هرون وبنوه ايديهم على راس الثور"));
arasuperobject.biblemap.put("b2_29_11", new arasuperobject(2,29,11,"فتذبح الثور امام الرب عند باب خيمة الاجتماع"));
arasuperobject.biblemap.put("b2_29_12", new arasuperobject(2,29,12,"وتأخذ من دم الثور وتجعله على قرون المذبح باصبعك. وسائر الدم تصبّه الى اسفل المذبح"));
arasuperobject.biblemap.put("b2_29_13", new arasuperobject(2,29,13,"وتأخذ كل الشحم الذي يغشّي الجوف وزيادة الكبد والكليتين والشحم الذي عليهما. وتوقدها على المذبح"));
arasuperobject.biblemap.put("b2_29_14", new arasuperobject(2,29,14,"واما لحم الثور وجلده وفرثه فتحرقها بنار خارج المحلّة. هو ذبيحة خطية"));
arasuperobject.biblemap.put("b2_29_15", new arasuperobject(2,29,15,"وتاخذ الكبش الواحد. فيضع هرون وبنوه ايديهم على راس الكبش"));
arasuperobject.biblemap.put("b2_29_16", new arasuperobject(2,29,16,"فتذبح الكبش وتاخذ دمه وترشّه على المذبح من كل ناحية"));
arasuperobject.biblemap.put("b2_29_17", new arasuperobject(2,29,17,"وتقطع الكبش الى قطعه. وتغسل جوفه واكارعه وتجعلها على قطعه وعلى راسه"));
arasuperobject.biblemap.put("b2_29_18", new arasuperobject(2,29,18,"وتوقد كل الكبش على المذبح. هو محرقة للرب. رائحة سرور. وقود هو للرب"));
arasuperobject.biblemap.put("b2_29_19", new arasuperobject(2,29,19,"وتأخذ الكبش الثاني. فيضع هرون وبنوه ايديهم على راس الكبش"));
arasuperobject.biblemap.put("b2_29_20", new arasuperobject(2,29,20,"فتذبح الكبش وتاخذ من دمه وتجعل على شحمة اذن هرون وعلى شحم آذان بنيه اليمنى وعلى اباهم ايديهم اليمنى وعلى اباهم ارجلهم اليمنى. وترش الدم على المذبح من كل ناحية"));
arasuperobject.biblemap.put("b2_29_21", new arasuperobject(2,29,21,"وتاخذ من الدم الذي على المذبح ومن دهن المسحة وتنضح على هرون وثيابه وعلى بنيه وثياب بنيه معه. فيتقدس هو وثيابه وبنوه وثياب بنيه معه"));
arasuperobject.biblemap.put("b2_29_22", new arasuperobject(2,29,22,"ثم تأخذ من الكبش الشحم والالية والشحم الذي يغشي الجوف وزيادة الكبد والكليتين والشحم الذي عليهما والساق اليمنى. فانه كبش ملء"));
arasuperobject.biblemap.put("b2_29_23", new arasuperobject(2,29,23,"ورغيفا واحدا من الخبز وقرصا واحدا من الخبز بزيت ورقاقة واحدة من سلة الفطير التي امام الرب"));
arasuperobject.biblemap.put("b2_29_24", new arasuperobject(2,29,24,"وتضع الجميع في يدي هرون وفي ايدي بنيه وتردّدها ترديدا امام الرب"));
arasuperobject.biblemap.put("b2_29_25", new arasuperobject(2,29,25,"ثم تأخذها من ايديهم وتوقدها على المذبح فوق المحرقة رائحة سرور امام الرب. وقود هو للرب"));
arasuperobject.biblemap.put("b2_29_26", new arasuperobject(2,29,26,"ثم تأخذ القصّ من كبش الملء الذي لهرون وتردّده ترديدا امام الرب. فيكون لك نصيبا"));
arasuperobject.biblemap.put("b2_29_27", new arasuperobject(2,29,27,"وتقدس قصّ الترديد وساق الرفيعة الذي ردّد والذي رفع من كبش الملء مما لهرون ولبنيه"));
arasuperobject.biblemap.put("b2_29_28", new arasuperobject(2,29,28,"فيكونان لهرون وبنيه فريضة ابدية من بني اسرائيل لانهما رفيعة. ويكونان رفيعة من بني اسرائيل من ذبائح سلامتهم رفيعتهم للرب"));
arasuperobject.biblemap.put("b2_29_29", new arasuperobject(2,29,29,"والثياب المقدسة التي لهرون تكون لبنيه بعده ليمسحوا فيها ولتملأ فيها ايديهم"));
arasuperobject.biblemap.put("b2_29_30", new arasuperobject(2,29,30,"سبعة ايام يلبسها الكاهن الذي هو عوض عنه من بنيه. الذي يدخل خيمة الاجتماع ليخدم في القدس"));
arasuperobject.biblemap.put("b2_29_31", new arasuperobject(2,29,31,"واما كبش الملء فتاخذه وتطبخ لحمه في مكان مقدس"));
arasuperobject.biblemap.put("b2_29_32", new arasuperobject(2,29,32,"فيأكل هرون وبنوه لحم الكبش والخبز الذي في السلة عند باب خيمة الاجتماع"));
arasuperobject.biblemap.put("b2_29_33", new arasuperobject(2,29,33,"يأكلها الذين كفّر بها عنهم لملء ايديهم لتقديسهم. واما الاجنبي فلا ياكل لانها مقدسة"));
arasuperobject.biblemap.put("b2_29_34", new arasuperobject(2,29,34,"وان بقي شيء من لحم الملء او من الخبز الى الصباح تحرق الباقي بالنار. لا يؤكل لانه مقدس"));
arasuperobject.biblemap.put("b2_29_35", new arasuperobject(2,29,35,"وتصنع لهرون وبنيه هكذا بحسب كل ما امرتك. سبعة ايام تملأ ايديهم"));
arasuperobject.biblemap.put("b2_29_36", new arasuperobject(2,29,36,"وتقدم ثور خطية كل يوم لاجل الكفّارة. وتطهر المذبح بتكفيرك عليه. وتمسحه لتقديسه"));
arasuperobject.biblemap.put("b2_29_37", new arasuperobject(2,29,37,"سبعة ايام تكفّر على المذبح وتقدسه. فيكون المذبح قدس اقداس. كل ما مسّ المذبح يكون مقدسا"));
arasuperobject.biblemap.put("b2_29_38", new arasuperobject(2,29,38,"وهذا ما تقدمه على المذبح. خروفان حوليان كل يوم دائما"));
arasuperobject.biblemap.put("b2_29_39", new arasuperobject(2,29,39,"الخروف الواحد تقدمه صباحا. والخروف الثاني تقدمه في العشية"));
arasuperobject.biblemap.put("b2_29_40", new arasuperobject(2,29,40,"وعشر من دقيق ملتوت بربع الهين من زيت الرض وسكيب ربع الهين من الخمر للخروف الواحد"));
arasuperobject.biblemap.put("b2_29_41", new arasuperobject(2,29,41,"والخروف الثاني تقدمه في العشية. مثل تقدمة الصباح وسكيبه تصنع له. رائحة سرور وقود للرب"));
arasuperobject.biblemap.put("b2_29_42", new arasuperobject(2,29,42,"محرقة دائمة في اجيالكم عند باب خيمة الاجتماع امام الرب. حيث اجتمع بكم لاكلمك هناك"));
arasuperobject.biblemap.put("b2_29_43", new arasuperobject(2,29,43,"واجتمع هناك ببني اسرائيل فيقدّس بمجدي"));
arasuperobject.biblemap.put("b2_29_44", new arasuperobject(2,29,44,"واقدّس خيمة الاجتماع والمذبح. وهرون وبنوه اقدّسهم لكي يكهنوا لي"));
arasuperobject.biblemap.put("b2_29_45", new arasuperobject(2,29,45,"واسكن في وسط بني اسرائيل واكون لهم الها"));
arasuperobject.biblemap.put("b2_29_46", new arasuperobject(2,29,46,"فيعلمون اني انا الرب الههم الذي اخرجهم من ارض مصر لاسكن في وسطهم. انا الرب الههم"));
arasuperobject.biblemap.put("b2_30_1", new arasuperobject(2,30,1,"وتصنع مذبحا لايقاد البخور. من خشب السنط تصنعه"));
arasuperobject.biblemap.put("b2_30_2", new arasuperobject(2,30,2,"طوله ذراع وعرضه ذراع. مربعا يكون. وارتفاعه ذراعان. منه تكون قرونه"));
arasuperobject.biblemap.put("b2_30_3", new arasuperobject(2,30,3,"وتغشيه بذهب نقي سطحه وحيطانه حواليه وقرونه. وتصنع له اكليلا من ذهب حواليه"));
arasuperobject.biblemap.put("b2_30_4", new arasuperobject(2,30,4,"وتصنع له حلقتين من ذهب تحت اكليله على جانبيه. على الجانبين تصنعهما. لتكونا بيتين لعصوين لحمله بهما"));
arasuperobject.biblemap.put("b2_30_5", new arasuperobject(2,30,5,"وتصنع العصوين من خشب السنط وتغشيهما بذهب"));
arasuperobject.biblemap.put("b2_30_6", new arasuperobject(2,30,6,"وتجعله قدام الحجاب الذي امام تابوت الشهادة. قدام الغطاء الذي على الشهادة حيث اجتمع بك"));
arasuperobject.biblemap.put("b2_30_7", new arasuperobject(2,30,7,"فيوقد عليه هرون بخورا عطرا كل صباح. حين يصلح السرج يوقده"));
arasuperobject.biblemap.put("b2_30_8", new arasuperobject(2,30,8,"وحين يصعد هرون السرج في العشية يوقده. بخورا دائما امام الرب في اجيالكم"));
arasuperobject.biblemap.put("b2_30_9", new arasuperobject(2,30,9,"لا تصعدوا عليه بخورا غريبا ولا محرقة او تقدمة. ولا تسكبوا عليه سكيبا"));
arasuperobject.biblemap.put("b2_30_10", new arasuperobject(2,30,10,"ويصنع هرون كفّارة على قرونه مرة في السنة. من دم ذبيحة الخطية التي للكفّارة مرة في السنة يصنع كفّارة عليه في اجيالكم. قدس اقداس هو للرب"));
arasuperobject.biblemap.put("b2_30_11", new arasuperobject(2,30,11,"وكلم الرب موسى قائلا"));
arasuperobject.biblemap.put("b2_30_12", new arasuperobject(2,30,12,"اذا اخذت كمية بني اسرائيل بحسب المعدودين منهم يعطون كل واحد فدية نفسه للرب عندما تعدّهم. لئلا يصير فيهم وبأ عندما تعدّهم"));
arasuperobject.biblemap.put("b2_30_13", new arasuperobject(2,30,13,"هذا ما يعطيه كل من اجتاز الى المعدودين نصف الشاقل بشاقل القدس. الشاقل هو عشرون جيرة. نصف الشاقل تقدمة للرب"));
arasuperobject.biblemap.put("b2_30_14", new arasuperobject(2,30,14,"كل من اجتاز الى المعدودين من ابن عشرين سنة فصاعدا يعطي تقدمة للرب"));
arasuperobject.biblemap.put("b2_30_15", new arasuperobject(2,30,15,"الغني لا يكثر والفقير لا يقلل عن نصف الشاقل حين تعطون تقدمة الرب للتكفير عن نفوسكم"));
arasuperobject.biblemap.put("b2_30_16", new arasuperobject(2,30,16,"وتأخذ فضة الكفّارة من بني اسرائيل وتجعلها لخدمة خيمة الاجتماع. فتكون لبني اسرائيل تذكارا امام الرب للتكفير عن نفوسكم"));
arasuperobject.biblemap.put("b2_30_17", new arasuperobject(2,30,17,"وكلم الرب موسى قائلا"));
arasuperobject.biblemap.put("b2_30_18", new arasuperobject(2,30,18,"وتصنع مرحضة من نحاس وقاعدتها من نحاس للاغتسال. وتجعلها بين خيمة الاجتماع والمذبح وتجعل فيها ماء"));
arasuperobject.biblemap.put("b2_30_19", new arasuperobject(2,30,19,"فيغسل هرون وبنوه ايديهم وارجلهم منها"));
arasuperobject.biblemap.put("b2_30_20", new arasuperobject(2,30,20,"عند دخولهم الى خيمة الاجتماع يغسلون بماء لئلا يموتوا. او عند اقترابهم الى المذبح للخدمة ليوقدوا وقودا للرب"));
arasuperobject.biblemap.put("b2_30_21", new arasuperobject(2,30,21,"يغسلون ايدهم وارجلهم لئلا يموتوا. ويكون لهم فريضة ابدية له ولنسله في اجيالهم"));
arasuperobject.biblemap.put("b2_30_22", new arasuperobject(2,30,22,"وكلم الرب موسى قائلا"));
arasuperobject.biblemap.put("b2_30_23", new arasuperobject(2,30,23,"وانت تأخذ لك افخر الاطياب. مرّا قاطرا خمس مئة شاقل وقرفة عطرة نصف ذلك مئتين وخمسين وقصب الذريرة مئتين وخمسين"));
arasuperobject.biblemap.put("b2_30_24", new arasuperobject(2,30,24,"وسليخة خمس مئة بشاقل القدس. ومن زيت الزيتون هينا"));
arasuperobject.biblemap.put("b2_30_25", new arasuperobject(2,30,25,"وتصنعه دهنا مقدسا للمسحة. عطر عطارة صنعة العطّار. دهنا مقدسا للمسحة يكون"));
arasuperobject.biblemap.put("b2_30_26", new arasuperobject(2,30,26,"وتمسح به خيمة الاجتماع وتابوت الشهادة"));
arasuperobject.biblemap.put("b2_30_27", new arasuperobject(2,30,27,"والمائدة وكل آنيتها والمنارة وآنيتها ومذبح البخور"));
arasuperobject.biblemap.put("b2_30_28", new arasuperobject(2,30,28,"ومذبح المحرقة وكل آنيته والمرحضة وقاعدتها"));
arasuperobject.biblemap.put("b2_30_29", new arasuperobject(2,30,29,"وتقدّسها فتكون قدس اقداس. كل ما مسّها يكون مقدسا"));
arasuperobject.biblemap.put("b2_30_30", new arasuperobject(2,30,30,"وتمسح هرون وبنيه وتقدسهم ليكهنوا لي"));
arasuperobject.biblemap.put("b2_30_31", new arasuperobject(2,30,31,"وتكلم بني اسرائيل قائلا. يكون هذا لي دهنا مقدّسا للمسحة في اجيالكم"));
arasuperobject.biblemap.put("b2_30_32", new arasuperobject(2,30,32,"على جسد انسان لا يسكب. وعلى مقاديره لا تصنعوا مثله. مقدّس هو ويكون مقدّسا عندكم"));
arasuperobject.biblemap.put("b2_30_33", new arasuperobject(2,30,33,"كل من ركّب مثله ومن جعل منه على اجنبي يقطع من شعبه"));
arasuperobject.biblemap.put("b2_30_34", new arasuperobject(2,30,34,"وقال الرب لموسى خذ لك اعطارا. ميعة واظفارا وقنّة عطرة ولبانا نقيا. تكون اجزاء متساوية"));
arasuperobject.biblemap.put("b2_30_35", new arasuperobject(2,30,35,"فتصنعها بخورا عطرا صنعة العطّار مملحا نقيا مقدّسا"));
arasuperobject.biblemap.put("b2_30_36", new arasuperobject(2,30,36,"وتسحق منه ناعما وتجعل منه قدام الشهادة في خيمة الاجتماع حيث اجتمع بك. قدس اقداس يكون عندكم"));
arasuperobject.biblemap.put("b2_30_37", new arasuperobject(2,30,37,"والبخور الذي تصنعه على مقاديره لا تصنعوا لانفسكم. يكون عندك مقدّسا للرب"));
arasuperobject.biblemap.put("b2_30_38", new arasuperobject(2,30,38,"كل من صنع مثله ليشمّه يقطع من شعبه"));
arasuperobject.biblemap.put("b2_31_1", new arasuperobject(2,31,1,"وكلم الرب موسى قائلا"));
arasuperobject.biblemap.put("b2_31_2", new arasuperobject(2,31,2,"انظر. قد دعوت بصلئيل بن أوري بن حور من سبط يهوذا باسمه"));
arasuperobject.biblemap.put("b2_31_3", new arasuperobject(2,31,3,"وملأته من روح الله بالحكمة والفهم والمعرفة وكل صنعة"));
arasuperobject.biblemap.put("b2_31_4", new arasuperobject(2,31,4,"لاختراع مخترعات ليعمل في الذهب والفضة والنحاس"));
arasuperobject.biblemap.put("b2_31_5", new arasuperobject(2,31,5,"ونقش حجارة للترصيع ونجارة الخشب. ليعمل في كل صنعة"));
arasuperobject.biblemap.put("b2_31_6", new arasuperobject(2,31,6,"وها انا قد جعلت معه أهوليآب بن اخيساماك من سبط دان. وفي قلب كل حكيم القلب جعلت حكمة ليصنعوا كل ما امرتك"));
arasuperobject.biblemap.put("b2_31_7", new arasuperobject(2,31,7,"خيمة الاجتماع وتابوت الشهادة والغطاء الذي عليه وكل آنية الخيمة"));
arasuperobject.biblemap.put("b2_31_8", new arasuperobject(2,31,8,"والمائدة وآنيتها والمنارة الطاهرة وكل آنيتها ومذبح البخور"));
arasuperobject.biblemap.put("b2_31_9", new arasuperobject(2,31,9,"ومذبح المحرقة وكل آنيته والمرحضة وقاعدتها"));
arasuperobject.biblemap.put("b2_31_10", new arasuperobject(2,31,10,"والثياب المنسوجة والثياب المقدسة لهرون الكاهن وثياب بنيه للكهانة"));
arasuperobject.biblemap.put("b2_31_11", new arasuperobject(2,31,11,"ودهن المسحة والبخور العطر للقدس. حسب كل ما امرتك به يصنعون"));
arasuperobject.biblemap.put("b2_31_12", new arasuperobject(2,31,12,"وكلم الرب موسى قائلا"));
arasuperobject.biblemap.put("b2_31_13", new arasuperobject(2,31,13,"وانت تكلم بني اسرائيل قائلا سبوتي تحفظونها. لانه علامة بيني وبينكم في اجيالكم لتعلموا اني انا الرب الذي يقدّسكم"));
arasuperobject.biblemap.put("b2_31_14", new arasuperobject(2,31,14,"فتحفظون السبت لانه مقدّس لكم. من دنّسه يقتل قتلا. ان كل من صنع فيه عملا تقطع تلك النفس من بين شعبها"));
arasuperobject.biblemap.put("b2_31_15", new arasuperobject(2,31,15,"ستة ايام يصنع عمل. واما اليوم السابع ففيه سبت عطلة مقدّس للرب. كل من صنع عملا في يوم السبت يقتل قتلا"));
arasuperobject.biblemap.put("b2_31_16", new arasuperobject(2,31,16,"فيحفظ بنو اسرائيل السبت ليصنعوا السبت في اجيالهم عهدا ابديا"));
arasuperobject.biblemap.put("b2_31_17", new arasuperobject(2,31,17,"هو بيني وبين بني اسرائيل علامة الى الابد. لانه في ستة ايام صنع الرب السماء والارض وفي اليوم السابع استراح وتنفّس"));
arasuperobject.biblemap.put("b2_31_18", new arasuperobject(2,31,18,"ثم اعطى موسى عند فراغه من الكلام معه في جبل سيناء لوحي الشهادة لوحي حجر مكتوبين باصبع الله"));
arasuperobject.biblemap.put("b2_32_1", new arasuperobject(2,32,1,"ولما رأى الشعب ان موسى ابطأ في النزول من الجبل اجتمع الشعب على هرون. وقالوا له قم اصنع لنا آلهة تسير امامنا. لان هذا موسى الرجل الذي اصعدنا من ارض مصر لا نعلم ماذا اصابه"));
arasuperobject.biblemap.put("b2_32_2", new arasuperobject(2,32,2,"فقال لهم هرون انزعوا اقراط الذهب التي في آذان نسائكم وبنيكم وبناتكم وأتوني بها"));
arasuperobject.biblemap.put("b2_32_3", new arasuperobject(2,32,3,"فنزع كل الشعب اقراط الذهب التي في آذانهم وأتوا بها الى هرون"));
arasuperobject.biblemap.put("b2_32_4", new arasuperobject(2,32,4,"فاخذ ذلك من ايديهم وصوّره بالازميل وصنعه عجلا مسبوكا. فقالوا هذه آلهتك يا اسرائيل التي اصعدتك من ارض مصر"));
arasuperobject.biblemap.put("b2_32_5", new arasuperobject(2,32,5,"فلما نظر هرون بنى مذبحا امامه. ونادى هرون وقال غدا عيد للرب"));
arasuperobject.biblemap.put("b2_32_6", new arasuperobject(2,32,6,"فبكروا في الغد واصعدوا محرقات وقدموا ذبائح سلامة. وجلس الشعب للاكل والشرب ثم قاموا للّعب"));
arasuperobject.biblemap.put("b2_32_7", new arasuperobject(2,32,7,"فقال الرب لموسى اذهب انزل. لانه قد فسد شعبك الذي اصعدته من ارض مصر"));
arasuperobject.biblemap.put("b2_32_8", new arasuperobject(2,32,8,"زاغوا سريعا عن الطريق الذي اوصيتهم به. صنعوا لهم عجلا مسبوكا وسجدوا له وذبحوا له وقالوا هذه آلهتك يا اسرائيل التي اصعدتك من ارض مصر"));
arasuperobject.biblemap.put("b2_32_9", new arasuperobject(2,32,9,"وقال الرب لموسى رأيت هذا الشعب واذا هو شعب صلب الرقبة"));
arasuperobject.biblemap.put("b2_32_10", new arasuperobject(2,32,10,"فالآن اتركني ليحمى غضبي عليهم وافنيهم. فاصيّرك شعبا عظيما"));
arasuperobject.biblemap.put("b2_32_11", new arasuperobject(2,32,11,"فتضرع موسى امام الرب الهه. وقال لماذا يا رب يحمى غضبك على شعبك الذي اخرجته من ارض مصر بقوّة عظيمة ويد شديدة"));
arasuperobject.biblemap.put("b2_32_12", new arasuperobject(2,32,12,"لماذا يتكلم المصريون قائلين اخرجهم بخبث ليقتلهم في الجبال ويفنيهم عن وجه الارض. ارجع عن حمو غضبك واندم على الشر بشعبك"));
arasuperobject.biblemap.put("b2_32_13", new arasuperobject(2,32,13,"اذكر ابراهيم واسحق واسرائيل عبيدك الذين حلفت لهم بنفسك وقلت لهم اكثر نسلكم كنجوم السماء واعطي نسلكم كل هذه الارض الذي تكلمت عنها فيملكونها الى الابد"));
arasuperobject.biblemap.put("b2_32_14", new arasuperobject(2,32,14,"فندم الرب على الشر الذي قال انه يفعله بشعبه"));
arasuperobject.biblemap.put("b2_32_15", new arasuperobject(2,32,15,"فانصرف موسى ونزل من الجبل ولوحا الشهادة في يده. لوحان مكتوبان على جانبيهما. من هنا ومن هنا كانا مكتوبين"));
arasuperobject.biblemap.put("b2_32_16", new arasuperobject(2,32,16,"واللوحان هما صنعة الله والكتابة كتابة الله منقوشة على اللوحين"));
arasuperobject.biblemap.put("b2_32_17", new arasuperobject(2,32,17,"وسمع يشوع صوت الشعب في هتافه. فقال لموسى صوت قتال في المحلّة"));
arasuperobject.biblemap.put("b2_32_18", new arasuperobject(2,32,18,"فقال ليس صوت صياح النصرة ولا صوت صياح الكسرة. بل صوت غناء انا سامع"));
arasuperobject.biblemap.put("b2_32_19", new arasuperobject(2,32,19,"وكان عندما اقترب الى المحلّة انه ابصر العجل والرقص. فحمي غضب موسى وطرح اللوحين من يديه وكسرهما في اسفل الجبل"));
arasuperobject.biblemap.put("b2_32_20", new arasuperobject(2,32,20,"ثم اخذ العجل الذي صنعوا واحرقه بالنار وطحنه حتى صار ناعما وذراه على وجه الماء وسقى بني اسرائيل"));
arasuperobject.biblemap.put("b2_32_21", new arasuperobject(2,32,21,"وقال موسى لهرون ماذا صنع بك هذا الشعب حتى جلبت عليه خطية عظيمة"));
arasuperobject.biblemap.put("b2_32_22", new arasuperobject(2,32,22,"فقال هرون لا يحم غضب سيدي. انت تعرف الشعب انه في شر"));
arasuperobject.biblemap.put("b2_32_23", new arasuperobject(2,32,23,"فقالوا لي اصنع لنا آلهة تسير امامنا. لان هذا موسى الرجل الذي اصعدنا من ارض مصر لا نعلم ماذا اصابه"));
arasuperobject.biblemap.put("b2_32_24", new arasuperobject(2,32,24,"فقلت لهم من له ذهب فلينزعه ويعطيني. فطرحته في النار فخرج هذا العجل"));
arasuperobject.biblemap.put("b2_32_25", new arasuperobject(2,32,25,"ولما رأى موسى الشعب انه معرّى. لان هرون قد عرّاه للهزء بين مقاوميه"));
arasuperobject.biblemap.put("b2_32_26", new arasuperobject(2,32,26,"وقف موسى في باب المحلّة. وقال من للرب فإليّ. فاجتمع اليه جميع بني لاوي"));
arasuperobject.biblemap.put("b2_32_27", new arasuperobject(2,32,27,"فقال لهم هكذا قال الرب اله اسرائيل ضعوا كل واحد سيفه على فخذه ومرّوا وارجعوا من باب الى باب في المحلّة واقتلوا كل واحد اخاه وكل واحد صاحبه وكل واحد قريبه"));
arasuperobject.biblemap.put("b2_32_28", new arasuperobject(2,32,28,"ففعل بنو لاوي بحسب قول موسى. ووقع من الشعب في ذلك اليوم نحو ثلاثة آلاف رجل"));
arasuperobject.biblemap.put("b2_32_29", new arasuperobject(2,32,29,"وقال موسى املأوا ايديكم اليوم للرب حتى كل واحد بابنه وباخيه. فيعطيكم اليوم بركة"));
arasuperobject.biblemap.put("b2_32_30", new arasuperobject(2,32,30,"وكان في الغد ان موسى قال للشعب انتم قد اخطأتم خطية عظيمة. فاصعد الآن الى الرب لعلي اكفّر خطيتكم"));
arasuperobject.biblemap.put("b2_32_31", new arasuperobject(2,32,31,"فرجع موسى الى الرب. وقال آه قد اخطأ هذا الشعب خطية عظيمة وصنعوا لانفسهم آلهة من ذهب"));
arasuperobject.biblemap.put("b2_32_32", new arasuperobject(2,32,32,"والآن ان غفرت خطيتهم. والا فامحني من كتابك الذي كتبت"));
arasuperobject.biblemap.put("b2_32_33", new arasuperobject(2,32,33,"فقال الرب لموسى من اخطأ اليّ امحوه من كتابي"));
arasuperobject.biblemap.put("b2_32_34", new arasuperobject(2,32,34,"والآن اذهب اهد الشعب الى حيث كلمتك. هوذا ملاكي يسير امامك. ولكن في يوم افتقادي افتقد فيهم خطيتهم"));
arasuperobject.biblemap.put("b2_32_35", new arasuperobject(2,32,35,"فضرب الرب الشعب. لانهم صنعوا العجل الذي صنعه هرون"));
arasuperobject.biblemap.put("b2_33_1", new arasuperobject(2,33,1,"وقال الرب لموسى اذهب اصعد من هنا انت والشعب الذي اصعدته من ارض مصر الى الارض التي حلفت لابراهيم واسحق ويعقوب قائلا لنسلك اعطيها"));
arasuperobject.biblemap.put("b2_33_2", new arasuperobject(2,33,2,"وانا ارسل امامك ملاكا واطرد الكنعانيين والاموريين والحثّيين والفرزّيين والحوّيين واليبوسيين"));
arasuperobject.biblemap.put("b2_33_3", new arasuperobject(2,33,3,"الى ارض تفيض لبنا وعسلا. فاني لا اصعد في وسطك لانك شعب صلب الرقبة. لئلا افنيك في الطريق"));
arasuperobject.biblemap.put("b2_33_4", new arasuperobject(2,33,4,"فلما سمع الشعب هذا الكلام السوء ناحوا ولم يضع احد زينته عليه"));
arasuperobject.biblemap.put("b2_33_5", new arasuperobject(2,33,5,"وكان الرب قد قال لموسى قل لبني اسرائيل انتم شعب صلب الرقبة. ان صعدت لحظة واحدة في وسطكم افنيتكم. ولكن الآن اخلع زينتك عنك فاعلم ماذا اصنع بك"));
arasuperobject.biblemap.put("b2_33_6", new arasuperobject(2,33,6,"فنزع بنو اسرائيل زينتهم من جبل حوريب"));
arasuperobject.biblemap.put("b2_33_7", new arasuperobject(2,33,7,"واخذ موسى الخيمة ونصبها له خارج المحلّة بعيدا عن المحلّة ودعاها خيمة الاجتماع. فكان كل من يطلب الرب يخرج الى خيمة الاجتماع التي خارج المحلة"));
arasuperobject.biblemap.put("b2_33_8", new arasuperobject(2,33,8,"وكان جميع الشعب اذا خرج موسى الى الخيمة يقومون ويقفون كل واحد في باب خيمته وينظرون وراء موسى حتى يدخل الخيمة"));
arasuperobject.biblemap.put("b2_33_9", new arasuperobject(2,33,9,"وكان عمود السحاب اذا دخل موسى الخيمة ينزل ويقف عند باب الخيمة. ويتكلم الرب مع موسى"));
arasuperobject.biblemap.put("b2_33_10", new arasuperobject(2,33,10,"فيرى جميع الشعب عمود السحاب واقفا عند باب الخيمة. ويقوم كل الشعب ويسجدون كل واحد في باب خيمته ً"));
arasuperobject.biblemap.put("b2_33_11", new arasuperobject(2,33,11,"ويكلم الرب موسى وجها لوجه كما يكلم الرجل صاحبه. واذا رجع موسى الى المحلّة كان خادمه يشوع بن نون الغلام لا يبرح من داخل الخيمة"));
arasuperobject.biblemap.put("b2_33_12", new arasuperobject(2,33,12,"وقال موسى للرب انظر. انت قائل لي أصعد هذا الشعب. وانت لم تعرّفني من ترسل معي. وانت قد قلت عرفتك باسمك. ووجدت ايضا نعمة في عينيّ"));
arasuperobject.biblemap.put("b2_33_13", new arasuperobject(2,33,13,"فالآن ان كنت قد وجدت نعمة في عينيك فعلّمني طريقك حتى اعرفك لكي اجد نعمة في عينيك. وانظر ان هذه الامة شعبك"));
arasuperobject.biblemap.put("b2_33_14", new arasuperobject(2,33,14,"فقال وجهي يسير فأريحك"));
arasuperobject.biblemap.put("b2_33_15", new arasuperobject(2,33,15,"فقال له ان لم يسر وجهك فلا تصعدنا من ههنا"));
arasuperobject.biblemap.put("b2_33_16", new arasuperobject(2,33,16,"فانه بماذا يعلم اني وجدت نعمة في عينيك انا وشعبك. أليس بمسيرك معنا. فنمتاز انا وشعبك عن جميع الشعوب الذين على وجه الارض"));
arasuperobject.biblemap.put("b2_33_17", new arasuperobject(2,33,17,"فقال الرب لموسى هذا الامر ايضا الذي تكلمت عنه افعله. لانك وجدت نعمة في عيني وعرفتك باسمك"));
arasuperobject.biblemap.put("b2_33_18", new arasuperobject(2,33,18,"فقال أرني مجدك"));
arasuperobject.biblemap.put("b2_33_19", new arasuperobject(2,33,19,"فقال أجيز كل جودتي قدامك. وانادي باسم الرب قدامك. واتراءف على من اتراءف وارحم من ارحم"));
arasuperobject.biblemap.put("b2_33_20", new arasuperobject(2,33,20,"وقال لا تقدر ان ترى وجهي. لان الانسان لا يراني ويعيش"));
arasuperobject.biblemap.put("b2_33_21", new arasuperobject(2,33,21,"وقال الرب هوذا عندي مكان. فتقف على الصخرة"));
arasuperobject.biblemap.put("b2_33_22", new arasuperobject(2,33,22,"ويكون متى اجتاز مجدي اني اضعك في نقرة من الصخرة واسترك بيدي حتى اجتاز"));
arasuperobject.biblemap.put("b2_33_23", new arasuperobject(2,33,23,"ثم ارفع يدي فتنظر ورائي. واما وجهي فلا يرى"));
arasuperobject.biblemap.put("b2_34_1", new arasuperobject(2,34,1,"ثم قال الرب لموسى انحت لك لوحين من حجر مثل الاولين. فأكتب انا على اللوحين الكلمات التي كانت على اللوحين الاولين الذين كسرتهما"));
arasuperobject.biblemap.put("b2_34_2", new arasuperobject(2,34,2,"وكن مستعدا للصباح. واصعد في الصباح الى جبل سيناء وقف عندي هناك على راس الجبل"));
arasuperobject.biblemap.put("b2_34_3", new arasuperobject(2,34,3,"ولا يصعد احد معك وايضا لا ير احد في كل الجبل. الغنم ايضا والبقر لا ترع الى جهة ذلك الجبل"));
arasuperobject.biblemap.put("b2_34_4", new arasuperobject(2,34,4,"فنحت لوحين من حجر كالاولين. وبكر موسى في الصباح وصعد الى جبل سيناء كما امره الرب. واخذ في يده لوحي الحجر"));
arasuperobject.biblemap.put("b2_34_5", new arasuperobject(2,34,5,"فنزل الرب في السحاب. فوقف عنده هناك ونادى باسم الرب"));
arasuperobject.biblemap.put("b2_34_6", new arasuperobject(2,34,6,"فاجتاز الرب قدامه ونادى الرب الرب اله رحيم ورؤوف بطيء الغضب وكثير الاحسان والوفاء"));
arasuperobject.biblemap.put("b2_34_7", new arasuperobject(2,34,7,"حافظ الاحسان الى الوف. غافر الاثم والمعصية والخطية. ولكنه لن يبرئ ابراء مفتقد اثم الآباء في الابناء وفي ابناء الابناء في الجيل الثالث والرابع"));
arasuperobject.biblemap.put("b2_34_8", new arasuperobject(2,34,8,"فاسرع موسى وخرّ الى الارض وسجد"));
arasuperobject.biblemap.put("b2_34_9", new arasuperobject(2,34,9,"وقال ان وجدت نعمة في عينيك ايها السيد فليسر السيد في وسطنا. فانه شعب صلب الرقبة. واغفر اثمنا وخطيتنا واتخذنا ملكا"));
arasuperobject.biblemap.put("b2_34_10", new arasuperobject(2,34,10,"فقال ها انا قاطع عهدا قدام جميع شعبك افعل عجائب لم تخلق في كل الارض وفي جميع الامم. فيرى جميع الشعب الذي انت في وسطه فعل الرب. ان الذي انا فاعله معك رهيب"));
arasuperobject.biblemap.put("b2_34_11", new arasuperobject(2,34,11,"احفظ ما انا موصيك اليوم. ها انا طارد من قدامك الاموريين والكنعانيين والحثّيين والفرزّيين والحوّيين واليبوسيين"));
arasuperobject.biblemap.put("b2_34_12", new arasuperobject(2,34,12,"احترز من ان تقطع عهدا مع سكان الارض التي انت آت اليها لئلا يصيروا فخا في وسطك"));
arasuperobject.biblemap.put("b2_34_13", new arasuperobject(2,34,13,"بل تهدمون مذابحهم وتكسّرون انصابهم وتقطعون سواريهم"));
arasuperobject.biblemap.put("b2_34_14", new arasuperobject(2,34,14,"فانك لا تسجد لاله آخر لان الرب اسمه غيور. اله غيور هو"));
arasuperobject.biblemap.put("b2_34_15", new arasuperobject(2,34,15,"احترز من ان تقطع عهدا مع سكان الارض. فيزنون وراء آلهتهم ويذبحون لآلهتهم فتدعى وتأكل من ذبيحتهم"));
arasuperobject.biblemap.put("b2_34_16", new arasuperobject(2,34,16,"وتأخذ من بناتهم لبنيك. فتزني بناتهم وراء آلهتهنّ ويجعلن بنيك يزنون وراء آلهتهنّ"));
arasuperobject.biblemap.put("b2_34_17", new arasuperobject(2,34,17,"لا تصنع لنفسك آلهة مسبوكة"));
arasuperobject.biblemap.put("b2_34_18", new arasuperobject(2,34,18,"تحفظ عيد الفطير. سبعة ايام تاكل فطيرا كما امرتك في وقت شهر ابيب. لانك في شهر ابيب خرجت من مصر"));
arasuperobject.biblemap.put("b2_34_19", new arasuperobject(2,34,19,"لي كل فاتح رحم. وكل ما يولد ذكرا من مواشيك بكرا من ثور وشاة"));
arasuperobject.biblemap.put("b2_34_20", new arasuperobject(2,34,20,"واما بكر الحمار فتفديه بشاة. وان لم تفده تكسر عنقه. كل بكر من بنيك تفديه. ولا يظهروا امامي فارغين"));
arasuperobject.biblemap.put("b2_34_21", new arasuperobject(2,34,21,"ستة ايام تعمل. واما اليوم السابع فتستريح فيه. في الفلاحة وفي الحصاد تستريح"));
arasuperobject.biblemap.put("b2_34_22", new arasuperobject(2,34,22,"وتصنع لنفسك عيد الاسابيع ابكار حصاد الحنطة. وعيد الجمع في آخر السنة"));
arasuperobject.biblemap.put("b2_34_23", new arasuperobject(2,34,23,"ثلاث مرات في السنة يظهر جميع ذكورك امام السيد الرب اله اسرائيل"));
arasuperobject.biblemap.put("b2_34_24", new arasuperobject(2,34,24,"فاني اطرد الامم من قدامك واوسع تخومك. ولا يشتهي احد ارضك حين تصعد لتظهر امام الرب الهك ثلاث مرات في السنة"));
arasuperobject.biblemap.put("b2_34_25", new arasuperobject(2,34,25,"لا تذبح على خمير دم ذبيحتي. ولا تبت الى الغد ذبيحة عيد الفصح"));
arasuperobject.biblemap.put("b2_34_26", new arasuperobject(2,34,26,"اول ابكار ارضك تحضره الى بيت الرب الهك. لا تطبخ جديا بلبن امه"));
arasuperobject.biblemap.put("b2_34_27", new arasuperobject(2,34,27,"وقال الرب لموسى اكتب لنفسك هذه الكلمات. لانني بحسب هذه الكلمات قطعت عهدا معك ومع اسرائيل"));
arasuperobject.biblemap.put("b2_34_28", new arasuperobject(2,34,28,"وكان هناك عند الرب اربعين نهارا واربعين ليلة لم ياكل خبزا ولم يشرب ماء. فكتب على اللوحين كلمات العهد الكلمات العشر"));
arasuperobject.biblemap.put("b2_34_29", new arasuperobject(2,34,29,"وكان لما نزل موسى من جبل سيناء ولوحا الشهادة في يد موسى عند نزوله من الجبل ان موسى لم يعلم ان جلد وجهه صار يلمع في كلامه معه"));
arasuperobject.biblemap.put("b2_34_30", new arasuperobject(2,34,30,"فنظر هرون وجميع بني اسرائيل موسى واذا جلد وجهه يلمع. فخافوا ان يقتربوا اليه"));
arasuperobject.biblemap.put("b2_34_31", new arasuperobject(2,34,31,"فدعاهم موسى. فرجع اليه هرون وجميع الرؤساء في الجماعة. فكلمهم موسى"));
arasuperobject.biblemap.put("b2_34_32", new arasuperobject(2,34,32,"وبعد ذلك اقترب جميع بني اسرائيل. فاوصاهم بكل ما تكلم به الرب معه في جبل سيناء"));
arasuperobject.biblemap.put("b2_34_33", new arasuperobject(2,34,33,"ولما فرغ موسى من الكلام معهم جعل على وجهه برقعا"));
arasuperobject.biblemap.put("b2_34_34", new arasuperobject(2,34,34,"وكان موسى عند دخوله امام الرب ليتكلم معه ينزع البرقع حتى يخرج. ثم يخرج ويكلم بني اسرائيل بما يوصى"));
arasuperobject.biblemap.put("b2_34_35", new arasuperobject(2,34,35,"فاذا رأى بنو اسرائيل وجه موسى ان جلده يلمع كان موسى يرد البرقع على وجهه حتى يدخل ليتكلم معه"));
arasuperobject.biblemap.put("b2_35_1", new arasuperobject(2,35,1,"وجمع موسى كل جماعة بني اسرائيل وقال لهم هذه الكلمات التي امر الرب ان تصنع"));
arasuperobject.biblemap.put("b2_35_2", new arasuperobject(2,35,2,"ستة ايام يعمل عمل. واما اليوم السابع ففيه يكون لكم سبت عطلة مقدّس للرب. كل من يعمل فيه عملا يقتل"));
arasuperobject.biblemap.put("b2_35_3", new arasuperobject(2,35,3,"لا تشعلوا نارا في جميع مساكنكم يوم السبت"));
arasuperobject.biblemap.put("b2_35_4", new arasuperobject(2,35,4,"وكلم موسى كل جماعة بني اسرائيل قائلا هذا هو الشيء الذي امر به الرب قائلا"));
