arasuperobject.biblemap.put("b22_8_1", new arasuperobject(22,8,1,"ليتك كاخ لي الراضع ثديي امي فاجدك في الخارج واقبلك ولا يخزونني"));
arasuperobject.biblemap.put("b22_8_2", new arasuperobject(22,8,2,"واقودك وادخل بك بيت امي وهي تعلمني فاسقيك من الخمر الممزوجة من سلاف رماني"));
arasuperobject.biblemap.put("b22_8_3", new arasuperobject(22,8,3,"شماله تحت راسي ويمينه تعانقني"));
arasuperobject.biblemap.put("b22_8_4", new arasuperobject(22,8,4,"احلفكن يا بنات اورشليم ألا تيقظن ولا تنبهن الحبيب حتى يشاء"));
arasuperobject.biblemap.put("b22_8_5", new arasuperobject(22,8,5,"من هذه الطالعة من البرية مستندة على حبيبها تحت شجرة التفاح شوقتك هناك خطبت لك امك هناك خطبت لك والدتك"));
arasuperobject.biblemap.put("b22_8_6", new arasuperobject(22,8,6,"اجعلني كخاتم على قلبك كخاتم على ساعدك. لان المحبة قوية كالموت. الغيرة قاسية كالهاوة. لهيبها لهيب نار لظى الرب"));
arasuperobject.biblemap.put("b22_8_7", new arasuperobject(22,8,7,"مياه كثيرة لا تستطيع ان تطفئ المحبة والسيول لا تغمرها. ان اعطى الانسان كل ثروة بيته بدل المحبة تحتقر احتقارا"));
arasuperobject.biblemap.put("b22_8_8", new arasuperobject(22,8,8,"لنا اخت صغيرة ليس لها ثديان. فماذا نصنع لاختنا في يوم تخطب"));
arasuperobject.biblemap.put("b22_8_9", new arasuperobject(22,8,9,"ان تكن سورا فنبني عليها برج فضة. وان تكن بابا فنحصرها بألواح ارز"));
arasuperobject.biblemap.put("b22_8_10", new arasuperobject(22,8,10,"انا سور وثدياي كبرجين. حينئذ كنت في عينيه كواجدة سلامة"));
arasuperobject.biblemap.put("b22_8_11", new arasuperobject(22,8,11,"كان لسليمان كرم في بعل هامون. دفع الكرم الى نواطير كل واحد يؤدي عن ثمره الفا من الفضة"));
arasuperobject.biblemap.put("b22_8_12", new arasuperobject(22,8,12,"كرمي الذي لي هو امامي. الالف لك يا سليمان ومئتان لنواطير الثمر"));
arasuperobject.biblemap.put("b22_8_13", new arasuperobject(22,8,13,"ايتها الجالسة في الجنات الاصحاب يسمعون صوتك فأسمعيني"));
arasuperobject.biblemap.put("b22_8_14", new arasuperobject(22,8,14,"اهرب يا حبيبي وكن كالظبي او كغفر الايائل على جبال الاطياب"));

