arasuperobject.biblemap.put("b23_1_4", new arasuperobject(23,1,4,"ويل للامّة الخاطئة الشعب الثقيل الاثم نسل فاعلي الشر اولاد مفسدين. تركوا الرب استهانوا بقدوس اسرائيل ارتدوا الى وراء"));
arasuperobject.biblemap.put("b23_1_5", new arasuperobject(23,1,5,"على م تضربون بعد. تزدادون زيغانا. كل الراس مريض وكل القلب سقيم"));
arasuperobject.biblemap.put("b23_1_6", new arasuperobject(23,1,6,"من اسفل القدم الى الراس ليس فيه صحة بل جرح واحباط وضربة طرية لم تعصر ولم تعصب ولم تلين بالزيت"));
arasuperobject.biblemap.put("b23_1_7", new arasuperobject(23,1,7,"بلادكم خربة مدنكم محرقة بالنار. ارضكم تاكلها غرباء قدامكم وهي خربة كانقلاب الغرباء"));
arasuperobject.biblemap.put("b23_1_8", new arasuperobject(23,1,8,"فبقيت ابنة صهيون كمظلة في كرم كخيمة في مقثأة كمدينة محاصرة"));
arasuperobject.biblemap.put("b23_1_9", new arasuperobject(23,1,9,"لولا ان رب الجنود ابقى لنا بقية صغيرة لصرنا مثل سدوم وشابهنا عمورة"));
arasuperobject.biblemap.put("b23_1_10", new arasuperobject(23,1,10,"اسمعوا كلام الرب يا قضاة سدوم. اصغوا الى شريعة الهنا يا شعب عمورة"));
arasuperobject.biblemap.put("b23_1_11", new arasuperobject(23,1,11,"لماذا لي كثرة ذبائحكم يقول الرب. اتخمت من محرقات كباش وشحم مسمنات. وبدم عجول وخرفان وتيوس ما اسر"));
arasuperobject.biblemap.put("b23_1_12", new arasuperobject(23,1,12,"حينما تأتون لتظهروا امامي من طلب هذا من ايديكم ان تدوسوا دوري"));
arasuperobject.biblemap.put("b23_1_13", new arasuperobject(23,1,13,"لا تعودوا تأتون بتقدمة باطلة. البخور هو مكرهة لي. راس الشهر والسبت ونداء المحفل. لست اطيق الاثم والاعتكاف"));
arasuperobject.biblemap.put("b23_1_14", new arasuperobject(23,1,14,"رؤوس شهوركم واعيادكم بغضتها نفسي. صارت علي ثقلا. مللت حملها"));
