arasuperobject.biblemap.put("b23_2_1", new arasuperobject(23,2,1,"الامور التي رآها اشعياء بن آموص من جهة يهوذا واورشليم"));
arasuperobject.biblemap.put("b23_2_2", new arasuperobject(23,2,2,"ويكون في آخر الايام ان جبل بيت الرب يكون ثابتا في راس الجبال ويرتفع فوق التلال وتجري اليه كل الامم"));
arasuperobject.biblemap.put("b23_2_3", new arasuperobject(23,2,3,"وتسير شعوب كثيرة ويقولون هلم نصعد الى جبل الرب الى بيت اله يعقوب فيعلّمنا من طرقه ونسلك في سبله لانه من صهيون تخرج الشريعة ومن اورشليم كلمة الرب"));
arasuperobject.biblemap.put("b23_2_4", new arasuperobject(23,2,4,"فيقضي بين الامم وينصف لشعوب كثيرين فيطبعون سيوفهم سككا ورماحهم مناجل. لا ترفع امة على امة سيفا ولا يتعلمون الحرب فيما بعد"));
