arasuperobject.biblemap.put("b23_3_4", new arasuperobject(23,3,4,"واجعل صبيانا رؤساء لهم واطفالا تتسلط عليهم"));
arasuperobject.biblemap.put("b23_3_5", new arasuperobject(23,3,5,"ويظلم الشعب بعضهم بعضا والرجل صاحبه. يتمرد الصبي على الشيخ والدنيء على الشريف"));
arasuperobject.biblemap.put("b23_3_6", new arasuperobject(23,3,6,"اذا امسك انسان باخيه في بيت ابيه قائلا لك ثوب فتكون لنا رئيسا وهذا الخراب تحت يدك"));
arasuperobject.biblemap.put("b23_3_7", new arasuperobject(23,3,7,"يرفع صوته في ذلك اليوم قائلا لا اكون عاصبا وفي بيتي لا خبز ولا ثوب. لا تجعلوني رئيس الشعب"));
arasuperobject.biblemap.put("b23_3_8", new arasuperobject(23,3,8,"لان اورشليم عثرت ويهوذا سقطت لان لسانهما وافعالهما ضد الرب لاغاظة عيني مجده"));
arasuperobject.biblemap.put("b23_3_9", new arasuperobject(23,3,9,"نظر وجوههم يشهد عليهم وهم يخبرون بخطيتهم كسدوم. لا يخفونها. ويل لنفوسهم لانهم يصنعون لانفسهم شرا"));
arasuperobject.biblemap.put("b23_3_10", new arasuperobject(23,3,10,"قولوا للصدّيق خير. لانهم يأكلون ثمر افعالهم"));
