arasuperobject.biblemap.put("b23_3_13", new arasuperobject(23,3,13,"قد انتصب الرب للمخاصمة وهو قائم لدينونة الشعوب"));
arasuperobject.biblemap.put("b23_3_14", new arasuperobject(23,3,14,"الرب يدخل في المحاكمة مع شيوخ شعبه ورؤسائهم. وانتم قد اكلتم الكرم. سلب البائس في بيوتكم"));
arasuperobject.biblemap.put("b23_3_15", new arasuperobject(23,3,15,"مالكم تسحقون شعبي وتطحنون وجوه البائسين يقول السيد رب الجنود"));
arasuperobject.biblemap.put("b23_3_16", new arasuperobject(23,3,16,"وقال الرب من اجل ان بنات صهيون يتشامخن ويمشين ممدودات الاعناق وغامزات بعيونهنّ وخاطرات في مشيهنّ ويخشخشن بارجلهنّ"));
