arasuperobject.biblemap.put("b23_3_18", new arasuperobject(23,3,18,"ينزع السيد في ذلك اليوم زينة الخلاخيل والضفائر والاهلة"));
arasuperobject.biblemap.put("b23_3_19", new arasuperobject(23,3,19,"والحلق والاساور والبراقع"));
arasuperobject.biblemap.put("b23_3_20", new arasuperobject(23,3,20,"والعصائب والسلاسل والمناطق وحناجر الشمامات والاحراز"));
arasuperobject.biblemap.put("b23_3_21", new arasuperobject(23,3,21,"والخواتم وخزائم الانف"));
arasuperobject.biblemap.put("b23_3_22", new arasuperobject(23,3,22,"والثياب المزخرفة والعطف والاردية والاكياس"));
