arasuperobject.biblemap.put("b24_1_16", new arasuperobject(24,1,16,"واقيم دعواي على كل شرهم لانهم تركوني وبخروا لآلهة اخرى وسجدوا لاعمال ايديهم"));
arasuperobject.biblemap.put("b24_1_17", new arasuperobject(24,1,17,"اما انت فنطّق حقويك وقم وكلمهم بكل ما آمرك به. لا ترتع من وجوههم لئلا اريعك امامهم"));
arasuperobject.biblemap.put("b24_1_18", new arasuperobject(24,1,18,"هانذا قد جعلتك اليوم مدينة حصينة وعمود حديد واسوار نحاس على كل الارض. لملوك يهوذا ولرؤسائها ولكهنتها ولشعب الارض"));
arasuperobject.biblemap.put("b24_1_19", new arasuperobject(24,1,19,"فيحاربونك ولا يقدرون عليك لاني انا معك يقول الرب لانقذك"));
