arasuperobject.biblemap.put("b24_2_12", new arasuperobject(24,2,12,"ابهتي ايتها السموات من هذا واقشعري وتحيري جدا يقول الرب"));
arasuperobject.biblemap.put("b24_2_13", new arasuperobject(24,2,13,"لان شعبي عمل شرين. تركوني انا ينبوع المياه الحية لينقروا لانفسهم ابآرا ابآرا مشققة لا تضبط ماء"));
arasuperobject.biblemap.put("b24_2_14", new arasuperobject(24,2,14,"أعبد اسرائيل او مولود البيت هو. لماذا صار غنيمة"));
