arasuperobject.biblemap.put("b24_2_25", new arasuperobject(24,2,25,"احفظي رجلك من الحفا وحلقك من الظمإ. فقلت باطل. لا. لاني قد احببت الغرباء وورائهم اذهب"));
arasuperobject.biblemap.put("b24_2_26", new arasuperobject(24,2,26,"كخزي السارق اذا وجد هكذا خزي بيت اسرائيل هم وملوكهم ورؤساؤهم وكهنتهم وانبياؤهم"));
arasuperobject.biblemap.put("b24_2_27", new arasuperobject(24,2,27,"قائلين للعود انت ابي وللحجر انت ولدتني. لانهم حوّلوا نحوي القفا لا الوجه وفي وقت بليتهم يقولون قم وخلصنا"));
arasuperobject.biblemap.put("b24_2_28", new arasuperobject(24,2,28,"فاين آلهتك التي صنعت لنفسك. فليقوموا ان كانوا يخلّصونك في وقت بليتك. لانه على عدد مدنك صارت آلهتك يا يهوذا"));
