arasuperobject.biblemap.put("b24_2_32", new arasuperobject(24,2,32,"هل تنسى عذراء زينتها او عروس مناطقها. اما شعبي فقد نسيني اياما بلا عدد"));
arasuperobject.biblemap.put("b24_2_33", new arasuperobject(24,2,33,"لماذا تحسّنين طريقك لتطلبي المحبة. لذلك علّمت الشريرات ايضا طرقك"));
