arasuperobject.biblemap.put("b24_3_9", new arasuperobject(24,3,9,"وكان من هوان زناها انها نجست الارض وزنت مع الحجر ومع الشجر"));
arasuperobject.biblemap.put("b24_3_10", new arasuperobject(24,3,10,"وفي كل هذا ايضا لم ترجع اليّ اختها الخائنة يهوذا بكل قلبها بل بالكذب يقول الرب"));
arasuperobject.biblemap.put("b24_3_11", new arasuperobject(24,3,11,"فقال الرب لي قد بررت نفسها العاصية اسرائيل اكثر من الخائنة يهوذا"));
arasuperobject.biblemap.put("b24_3_12", new arasuperobject(24,3,12,"اذهب وناد بهذه الكلمات نحو الشمال وقل ارجعي ايتها العاصية اسرائيل يقول الرب. لا اوقع غضبي بكم لاني رؤوف يقول الرب. لا احقد الى الابد"));
arasuperobject.biblemap.put("b24_3_13", new arasuperobject(24,3,13,"اعرفي فقط اثمك انك الى الرب الهك اذنبت وفرّقت طرقك للغرباء تحت كل شجرة خضراء ولصوتي لم تسمعوا يقول الرب"));
arasuperobject.biblemap.put("b24_3_14", new arasuperobject(24,3,14,"ارجعوا ايها البنون العصاة يقول الرب لاني سدت عليكم فآخذكم واحد من المدينة واثنين من العشيرة وآتي بكم الى صهيون"));
arasuperobject.biblemap.put("b24_3_15", new arasuperobject(24,3,15,"واعطيكم رعاة حسب قلبي فيرعونكم بالمعرفة والفهم"));
arasuperobject.biblemap.put("b24_3_16", new arasuperobject(24,3,16,"ويكون اذ تكثرون وتثمرون في الارض في تلك الايام يقول الرب انهم لا يقولون بعد تابوت عهد الرب ولا يخطر على بال ولا يذكرونه ولا يتعهدونه ولا يصنع بعد"));
arasuperobject.biblemap.put("b24_3_17", new arasuperobject(24,3,17,"في ذلك الزمان يسمّون اورشليم كرسي الرب ويجتمع اليها كل الامم الى اسم الرب الى اورشليم ولا يذهبون بعد وراء عناد قلبهم الشرير"));
arasuperobject.biblemap.put("b24_3_18", new arasuperobject(24,3,18,"في تلك الايام يذهب بيت يهوذا مع بيت اسرائيل ويأتيان معا من ارض الشمال الى الارض التي ملّكت آباءكم اياها"));
arasuperobject.biblemap.put("b24_3_19", new arasuperobject(24,3,19,"وانا قلت كيف اضعك بين البنين واعطيك ارضا شهية ميراث مجد امجاد الامم. وقلت تدعينني يا ابي ومن ورائي لا ترجعين"));
arasuperobject.biblemap.put("b24_3_20", new arasuperobject(24,3,20,"حقا انه كما تخون المرأة قرينها هكذا خنتموني يا بيت اسرائيل يقول الرب"));
arasuperobject.biblemap.put("b24_3_21", new arasuperobject(24,3,21,"سمع صوت على الهضاب بكاء تضرعات بني اسرائيل. لانهم عوّجوا طريقهم. نسوا الرب الههم"));
arasuperobject.biblemap.put("b24_3_22", new arasuperobject(24,3,22,"ارجعوا ايها البنون العصاة فاشفي عصيانكم. ها قد أتينا اليك لانك انت الرب الهنا"));
arasuperobject.biblemap.put("b24_3_23", new arasuperobject(24,3,23,"حقا باطلة هي الآكام ثروة الجبال. حقا بالرب الهنا خلاص اسرائيل"));
arasuperobject.biblemap.put("b24_3_24", new arasuperobject(24,3,24,"وقد اكل الخزي تعب آبائنا منذ صبانا غنمهم وبقرهم بنيهم وبناتهم"));
arasuperobject.biblemap.put("b24_3_25", new arasuperobject(24,3,25,"نضطجع في خزينا ويغطينا خجلنا لاننا الى الرب الهنا اخطأنا نحن وآباؤنا منذ صبانا الى هذا اليوم ولم نسمع لصوت الرب الهنا"));
arasuperobject.biblemap.put("b24_4_1", new arasuperobject(24,4,1,"ان رجعت يا اسرائيل يقول الرب ان رجعت اليّ وان نزعت مكرهاتك من امامي فلا تتيه"));
arasuperobject.biblemap.put("b24_4_2", new arasuperobject(24,4,2,"وان حلفت حيّ هو الرب بالحق والعدل والبر فتتبرّك الشعوب به وبه يفتخرون"));
arasuperobject.biblemap.put("b24_4_3", new arasuperobject(24,4,3,"لانه هكذا قال الرب لرجال يهوذا ولاورشليم احرثوا لانفسكم حرثا ولا تزرعوا في الاشواك"));
arasuperobject.biblemap.put("b24_4_4", new arasuperobject(24,4,4,"اختتنوا للرب وانزعوا غرل قلوبكم يا رجال يهوذا وسكان اورشليم لئلا يخرج كنار غيظي فيحرق وليس من يطفئ بسبب شر اعمالكم"));
arasuperobject.biblemap.put("b24_4_5", new arasuperobject(24,4,5,"اخبروا في يهوذا وسمّعوا في اورشليم وقولوا اضربوا بالبوق في الارض. نادوا بصوت عال وقولوا اجتمعوا فلندخل المدن الحصينة"));
arasuperobject.biblemap.put("b24_4_6", new arasuperobject(24,4,6,"ارفعوا الراية نحو صهيون. احتموا. لا تقفوا. لاني آتي بشر من الشمال وكسر عظيم"));
arasuperobject.biblemap.put("b24_4_7", new arasuperobject(24,4,7,"قد صعد الاسد من غابته وزحف مهلك الامم. خرج من مكانه ليجعل ارضك خرابا. تخرب مدنك فلا ساكن"));
arasuperobject.biblemap.put("b24_4_8", new arasuperobject(24,4,8,"من اجل ذلك تنطقوا بمسوح الطموا وولولوا لانه لم يرتد حمو غضب الرب عنا"));
arasuperobject.biblemap.put("b24_4_9", new arasuperobject(24,4,9,"ويكون في ذلك اليوم يقول الرب ان قلب الملك يعدم وقلوب الرؤساء وتتحير الكهنة وتتعجب الانبياء"));
arasuperobject.biblemap.put("b24_4_10", new arasuperobject(24,4,10,"فقلت آه يا سيد الرب حقا انك خداعا خادعت هذا الشعب واورشليم قائلا يكون لكم سلام وقد بلغ السيف النفس"));
arasuperobject.biblemap.put("b24_4_11", new arasuperobject(24,4,11,"في ذلك الزمان يقال لهذا الشعب ولاورشليم ريح لافحة من الهضاب في البرية نحو بنت شعبي لا للتذرية ولا للتنقية"));
arasuperobject.biblemap.put("b24_4_12", new arasuperobject(24,4,12,"ريح اشد تأتي لي من هذه. الآن انا ايضا احاكمهم"));
arasuperobject.biblemap.put("b24_4_13", new arasuperobject(24,4,13,"هوذا كسحاب يصعد وكزوبعة مركباته. اسرع من النسور خيله. ويل لنا لاننا قد أخربنا"));
arasuperobject.biblemap.put("b24_4_14", new arasuperobject(24,4,14,"اغسلي من الشر قلبك يا اورشليم لكي تخلّصي. الى متى تبيت في وسطك افكارك الباطلة"));
arasuperobject.biblemap.put("b24_4_15", new arasuperobject(24,4,15,"لان صوتا يخبر من دان ويسمع ببلية من جبل افرايم"));
arasuperobject.biblemap.put("b24_4_16", new arasuperobject(24,4,16,"اذكروا للامم. انظروا. أسمعوا على اورشليم. المحاصرون آتون من ارض بعيدة فيطلقون على مدن يهوذا صوتهم"));
arasuperobject.biblemap.put("b24_4_17", new arasuperobject(24,4,17,"كحارسي حقل صاروا عليها حواليها لانها تمردت عليّ يقول الرب"));
arasuperobject.biblemap.put("b24_4_18", new arasuperobject(24,4,18,"طريقك واعمالك صنعت هذه لك. هذا شرّك. فانه مرّ فانه قد بلغ قلبك"));
arasuperobject.biblemap.put("b24_4_19", new arasuperobject(24,4,19,"احشائي. احشائي. توجعني جدران قلبي. يئن فيّ قلبي. لا استطيع السكوت. لانك سمعت يا نفسي صوت البوق وهتاف الحرب"));
arasuperobject.biblemap.put("b24_4_20", new arasuperobject(24,4,20,"بكسر على كسر نودي لانه قد خربت كل الارض. بغتة خربت خيامي وشققي في لحظة"));
arasuperobject.biblemap.put("b24_4_21", new arasuperobject(24,4,21,"حتى متى ارى الراية واسمع صوت البوق"));
arasuperobject.biblemap.put("b24_4_22", new arasuperobject(24,4,22,"لان شعبي احمق. اياي لم يعرفوا. هم بنون جاهلون وهم غير فاهمين. هم حكماء في عمل الشر ولعمل الصالح ما يفهمون"));
arasuperobject.biblemap.put("b24_4_23", new arasuperobject(24,4,23,"نظرت الى الارض واذا هي خربة وخالية والى السموات فلا نور لها"));
arasuperobject.biblemap.put("b24_4_24", new arasuperobject(24,4,24,"نظرت الى الجبال واذا هي ترتجف وكل الاكام تقلقلت"));
arasuperobject.biblemap.put("b24_4_25", new arasuperobject(24,4,25,"نظرت واذا لا انسان وكل طيور السماء هربت"));
arasuperobject.biblemap.put("b24_4_26", new arasuperobject(24,4,26,"نظرت واذا البستان برية وكل مدنها نقضت من وجه الرب من وجه حمو غضبه"));
arasuperobject.biblemap.put("b24_4_27", new arasuperobject(24,4,27,"لانه هكذا قال الرب. خرابا تكون كل الارض ولكنني لا افنيها"));
arasuperobject.biblemap.put("b24_4_28", new arasuperobject(24,4,28,"من اجل ذلك تنوح الارض وتظلم السموات من فوق من اجل اني قد تكلمت قصدت ولا اندم ولا ارجع عنه"));
arasuperobject.biblemap.put("b24_4_29", new arasuperobject(24,4,29,"من صوت الفارس ورامي القوس كل المدينة هاربة. دخلوا الغابات وصعدوا على الصخور. كل المدن متروكة ولا انسان ساكن فيها"));
arasuperobject.biblemap.put("b24_4_30", new arasuperobject(24,4,30,"وانت ايتها الخربة ماذا تعملين. اذا لبست قرمزا اذا تزينت بزينة من ذهب اذا كحلت بالاثمد عينيك فباطلا تحسّنين ذاتك فقد رذلك العاشقون. يطلبون نفسك"));
arasuperobject.biblemap.put("b24_4_31", new arasuperobject(24,4,31,"لاني سمعت صوتا كماخضة ضيقا مثل ضيق بكرية. صوت ابنة صهيون تزفر. تبسط يديها قائلة ويل لي لان نفسي قد أغمي عليها بسبب القاتلين"));
arasuperobject.biblemap.put("b24_5_1", new arasuperobject(24,5,1,"طوفوا في شوارع اورشليم وانظروا واعرفوا وفتشوا في ساحاتها هل تجدون انسانا او يوجد عامل بالعدل طالب الحق فاصفح عنها"));
arasuperobject.biblemap.put("b24_5_2", new arasuperobject(24,5,2,"وان قالوا حيّ هو الرب فانهم يحلفون بالكذب"));
arasuperobject.biblemap.put("b24_5_3", new arasuperobject(24,5,3,"يا رب أليست عيناك على الحق. ضربتهم فلم يتوجعوا. افنيتهم وابوا قبول التأديب. صلّبوا وجوههم اكثر من الصخر. ابوا الرجوع"));
arasuperobject.biblemap.put("b24_5_4", new arasuperobject(24,5,4,"اما انا فقلت انما هم مساكين. قد جهلوا لانهم لم يعرفوا طريق الرب قضاء الههم"));
arasuperobject.biblemap.put("b24_5_5", new arasuperobject(24,5,5,"أنطلق الى العظماء واكلمهم لانهم عرفوا طريق الرب قضاء الههم. اما هم فقد كسروا النير جميعا وقطعوا الربط"));
arasuperobject.biblemap.put("b24_5_6", new arasuperobject(24,5,6,"من اجل ذلك يضربهم الاسد من الوعر. ذئب المساء يهلكهم. يكمن النمر حول مدنهم. كل من خرج منها يفترس. لان ذنوبهم كثرت. تعاظمت معاصيهم"));
arasuperobject.biblemap.put("b24_5_7", new arasuperobject(24,5,7,"كيف اصفح لك عن هذه. بنوك تركوني وحلفوا بما ليست آلهة. ولما اشبعتهم زنوا وفي بيت زانية تزاحموا"));
arasuperobject.biblemap.put("b24_5_8", new arasuperobject(24,5,8,"صاروا حصنا معلوفة سائبة. صهلوا كل واحد على امرأة صاحبه"));
arasuperobject.biblemap.put("b24_5_9", new arasuperobject(24,5,9,"أما اعاقب على هذا يقول الرب او ما تنتقم نفسي من امة كهذه"));
arasuperobject.biblemap.put("b24_5_10", new arasuperobject(24,5,10,"اصعدوا على اسوارها واخربوا ولكن لا تفنوها. انزعوا افنانها لانها ليست للرب"));
arasuperobject.biblemap.put("b24_5_11", new arasuperobject(24,5,11,"لانه خيانة خانني بيت اسرائيل وبيت يهوذا يقول الرب"));
arasuperobject.biblemap.put("b24_5_12", new arasuperobject(24,5,12,"جحدوا الرب وقالوا ليس هو ولا يأتي علينا شر ولا نرى سيفا ولا جوعا"));
arasuperobject.biblemap.put("b24_5_13", new arasuperobject(24,5,13,"والانبياء يصيرون ريحا والكلمة ليست فيهم. هكذا يصنع بهم"));
arasuperobject.biblemap.put("b24_5_14", new arasuperobject(24,5,14,"لذلك هكذا قال الرب اله الجنود. من اجل انكم تتكلمون بهذه الكلمة هانذا جاعل كلامي في فمك نارا وهذا الشعب حطبا فتاكلهم"));
arasuperobject.biblemap.put("b24_5_15", new arasuperobject(24,5,15,"هانذا اجلب عليكم امة من بعد يا بيت اسرائيل يقول الرب. امة قوية امة منذ القديم امة لا تعرف لسانها ولا تفهم ما تتكلم به"));
arasuperobject.biblemap.put("b24_5_16", new arasuperobject(24,5,16,"جعبتهم كقبر مفتوح. كلهم جبابرة"));
arasuperobject.biblemap.put("b24_5_17", new arasuperobject(24,5,17,"فيأكلون حصادك وخبزك الذي يأكله بنوك وبناتك. يأكلون غنمك وبقرك. يأكلون جفنتك وتينتك. يهلكون بالسيف مدنك الحصينة التي انت متكل عليها"));
arasuperobject.biblemap.put("b24_5_18", new arasuperobject(24,5,18,"وايضا في تلك الايام يقول الرب لا افنيكم"));
arasuperobject.biblemap.put("b24_5_19", new arasuperobject(24,5,19,"ويكون حين تقولون لماذا صنع الرب الهنا بنا كل هذه. تقول لهم كما انكم تركتموني وعبدتم آلهة غريبة في ارضكم هكذا تعبدون الغرباء في ارض ليست لكم"));
arasuperobject.biblemap.put("b24_5_20", new arasuperobject(24,5,20,"اخبروا بهذا في بيت يعقوب وأسمعوا به في يهوذا قائلين"));
arasuperobject.biblemap.put("b24_5_21", new arasuperobject(24,5,21,"اسمع هذا ايها الشعب الجاهل والعديم الفهم الذين لهم اعين ولا يبصرون. لهم آذان ولا يسمعون"));
arasuperobject.biblemap.put("b24_5_22", new arasuperobject(24,5,22,"أإياي لا تخشون يقول الرب او لا ترتعدون من وجهي انا الذي وضعت الرمل تخوما للبحر فريضة ابدية لا يتعداها فتتلاطم ولا تستطيع وتعج امواجه ولا تتجاوزها"));
arasuperobject.biblemap.put("b24_5_23", new arasuperobject(24,5,23,"وصار لهذا الشعب قلب عاص ومتمرد. عصوا ومضوا"));
arasuperobject.biblemap.put("b24_5_24", new arasuperobject(24,5,24,"ولم يقولوا بقلوبهم لنخف الرب الهنا الذي يعطي المطر المبكر والمتاخر في وقته. يحفظ لنا اسابيع الحصاد المفروضة"));
arasuperobject.biblemap.put("b24_5_25", new arasuperobject(24,5,25,"آثامكم عكست هذه وخطاياكم منعت الخير عنكم"));
arasuperobject.biblemap.put("b24_5_26", new arasuperobject(24,5,26,"لانه وجد في شعبي اشرار يرصدون كمنحن من القانصين ينصبون اشراكا يمسكون الناس"));
arasuperobject.biblemap.put("b24_5_27", new arasuperobject(24,5,27,"مثل قفص ملآن طيورا هكذا بيوتهم ملآنة مكرا. من اجل ذلك عظموا واستغنوا"));
arasuperobject.biblemap.put("b24_5_28", new arasuperobject(24,5,28,"سمنوا لمعوا. ايضا تجاوزوا في امور الشر. لم يقضوا في الدعوى دعوى اليتيم. وقد نجحوا. وبحق المساكين لم يقضوا"));
arasuperobject.biblemap.put("b24_5_29", new arasuperobject(24,5,29,"أفلاجل هذه لا اعاقب يقول الرب او لا تنتقم نفسي من امة كهذه"));
arasuperobject.biblemap.put("b24_5_30", new arasuperobject(24,5,30,"صار في الارض دهش وقشعريرة"));
arasuperobject.biblemap.put("b24_5_31", new arasuperobject(24,5,31,"الانبياء يتنبأون بالكذب والكهنة تحكم على ايديهم وشعبي هكذا احب. وماذا تعملون في آخرتها"));
arasuperobject.biblemap.put("b24_6_1", new arasuperobject(24,6,1,"اهربوا يا بني بنيامين من وسط اورشليم واضربوا بالبوق في تقوع وعلى بيت هكاريم ارفعوا علم نار لان الشر اشرف من الشمال وكسر عظيم"));
arasuperobject.biblemap.put("b24_6_2", new arasuperobject(24,6,2,"الجميلة اللطيفة ابنة صهيون أهلكها"));
arasuperobject.biblemap.put("b24_6_3", new arasuperobject(24,6,3,"اليها تأتي الرعاة وقطعانهم ينصبون عندها خياما حواليها يرعون كل واحد في مكانه"));
arasuperobject.biblemap.put("b24_6_4", new arasuperobject(24,6,4,"قدسوا عليها حربا. قوموا فنصعد في الظهيرة. ويل لنا لان النهار مال لان ظلال المساء امتدّت"));
arasuperobject.biblemap.put("b24_6_5", new arasuperobject(24,6,5,"قوموا فنصعد في الليل ونهدم قصورها"));
arasuperobject.biblemap.put("b24_6_6", new arasuperobject(24,6,6,"لانه هكذا قال رب الجنود. اقطعوا اشجارا اقيموا حول اورشليم مترسة. هي المدينة المعاقبة. كلها ظلم في وسطها"));
arasuperobject.biblemap.put("b24_6_7", new arasuperobject(24,6,7,"كما تنبع العين مياهها هكذا تنبع هي شرها. ظلم وخطف يسمع فيها. امامي دائما مرض وضرب"));
arasuperobject.biblemap.put("b24_6_8", new arasuperobject(24,6,8,"تأدبي يا اورشليم لئلا تجفوك نفسي لئلا اجعلك خرابا ارضا غير مسكونة"));
arasuperobject.biblemap.put("b24_6_9", new arasuperobject(24,6,9,"هكذا قال رب الجنود. تعليلا يعللون كجفنة بقية اسرائيل. رد يدك كقاطف الى السلال"));
arasuperobject.biblemap.put("b24_6_10", new arasuperobject(24,6,10,"من اكلمهم وأنذرهم فيسمعوا. ها ان اذنهم غلفاء فلا يقدرون ان يصغوا. ها ان كلمة الرب صارت لهم عارا. لا يسرون بها"));
arasuperobject.biblemap.put("b24_6_11", new arasuperobject(24,6,11,"فامتلأت من غيظ الرب. مللت الطاقة. اسكبه على الاطفال في الخارج وعلى مجلس الشبان معا لان الرجل والمرأة يؤخذان كلاهما والشيخ مع الممتلئ اياما"));
arasuperobject.biblemap.put("b24_6_12", new arasuperobject(24,6,12,"وتتحول بيوتهم الى آخرين الحقول والنساء معا لاني امد يدي على سكان الارض يقول الرب"));
arasuperobject.biblemap.put("b24_6_13", new arasuperobject(24,6,13,"لانهم من صغيرهم الى كبيرهم كل واحد مولع بالربح ومن النبي الى الكاهن كل واحد يعمل بالكذب"));
arasuperobject.biblemap.put("b24_6_14", new arasuperobject(24,6,14,"ويشفون كسر بنت شعبي على عثم قائلين سلام سلام ولا سلام"));
arasuperobject.biblemap.put("b24_6_15", new arasuperobject(24,6,15,"هل خزوا لانهم عملوا رجسا. بل لم يخزوا خزيا ولم يعرفوا الخجل. لذلك يسقطون بين الساقطين. في وقت معاقبتهم يعثرون قال الرب"));
arasuperobject.biblemap.put("b24_6_16", new arasuperobject(24,6,16,"هكذا قال الرب. قفوا على الطريق وانظروا واسألوا عن السبل القديمة اين هو الطريق الصالح وسيروا فيه فتجدوا راحة لنفوسكم. ولكنهم قالوا لا نسير فيه"));
arasuperobject.biblemap.put("b24_6_17", new arasuperobject(24,6,17,"وأقمت عليكم رقباء قائلين اصغوا لصوت البوق. فقالوا لا نصغى"));
arasuperobject.biblemap.put("b24_6_18", new arasuperobject(24,6,18,"لذلك اسمعوا يا ايها الشعوب واعرفي ايتها الجماعة ما هو بينهم"));
arasuperobject.biblemap.put("b24_6_19", new arasuperobject(24,6,19,"اسمعي ايتها الارض هانذا جالب شرا على هذا الشعب ثمر افكارهم لانهم لم يصغوا لكلامي وشريعتي رفضوها"));
arasuperobject.biblemap.put("b24_6_20", new arasuperobject(24,6,20,"لماذا يأتي لي اللبان من شبا وقصب الذريرة من ارض بعيدة. محرقاتكم غير مقبولة وذبائحكم لا تلذ لي"));
arasuperobject.biblemap.put("b24_6_21", new arasuperobject(24,6,21,"لذلك هكذا قال الرب هانذا جاعل لهذا الشعب معثرات فيعثر بها الآباء والابناء معا. الجار وصاحبه يبيدان"));
arasuperobject.biblemap.put("b24_6_22", new arasuperobject(24,6,22,"هكذا قال الرب. هوذا شعب قادم من ارض الشمال وامة عظيمة تقوم من اقاصي الارض"));
arasuperobject.biblemap.put("b24_6_23", new arasuperobject(24,6,23,"تمسك القوس والرمح. هي قاسية لا ترحم. صوتها كالبحر يعج وعلى خيل تركب مصطفة كانسان لمحاربتك يا ابنة صهيون"));
arasuperobject.biblemap.put("b24_6_24", new arasuperobject(24,6,24,"سمعنا خبرها. ارتخت ايدينا. امسكنا ضيق ووجع كالماخض"));
arasuperobject.biblemap.put("b24_6_25", new arasuperobject(24,6,25,"لا تخرجوا الى الحقل وفي الطريق لا تمشوا لان سيف العدو خوف من كل جهة"));
arasuperobject.biblemap.put("b24_6_26", new arasuperobject(24,6,26,"يا ابنة شعبي تنطقي بمسح وتمرغي في الرماد. نوح وحيد اصنعي لنفسك مناحة مرة لان المخرب ياتي علينا بغتة"));
arasuperobject.biblemap.put("b24_6_27", new arasuperobject(24,6,27,"قد جعلتك برجا في شعبي حصنا لتعرف وتمتحن طريقهم"));
arasuperobject.biblemap.put("b24_6_28", new arasuperobject(24,6,28,"كلهم عصاة متمردون ساعون في الوشاية. هم نحاس وحديد. كلهم مفسدون"));
arasuperobject.biblemap.put("b24_6_29", new arasuperobject(24,6,29,"احترق المنفاخ من النار فني الرصاص. باطلا صاغ الصائغ والاشرار لا يفرزون"));
arasuperobject.biblemap.put("b24_6_30", new arasuperobject(24,6,30,"فضة مرفوضة يدعون. لان الرب قد رفضهم"));
arasuperobject.biblemap.put("b24_7_1", new arasuperobject(24,7,1,"الكلمة التي صارت الى ارميا من قبل الرب قائلا"));
arasuperobject.biblemap.put("b24_7_2", new arasuperobject(24,7,2,"قف في باب بيت الرب وناد هناك بهذه الكلمة وقل. اسمعوا كلمة الرب يا جميع يهوذا الداخلين في هذه الابواب لتسجدوا للرب"));
arasuperobject.biblemap.put("b24_7_3", new arasuperobject(24,7,3,"هكذا قال رب الجنود اله اسرائيل. اصلحوا طرقكم واعمالكم فاسكنكم في هذا الموضع"));
arasuperobject.biblemap.put("b24_7_4", new arasuperobject(24,7,4,"لا تتكلوا على كلام الكذب قائلين هيكل الرب هيكل الرب هيكل الرب هو"));
arasuperobject.biblemap.put("b24_7_5", new arasuperobject(24,7,5,"لانكم ان اصلحتم اصلاحا طرقكم واعمالكم ان اجريتم عدلا بين الانسان وصاحبه"));
arasuperobject.biblemap.put("b24_7_6", new arasuperobject(24,7,6,"ان لم تظلموا الغريب واليتيم والارملة ولم تسفكوا دما زكيا في هذا الموضع ولم تسيروا وراء آلهة اخرى لاذائكم"));
arasuperobject.biblemap.put("b24_7_7", new arasuperobject(24,7,7,"فاني اسكنكم في هذا الموضع في الارض التي اعطيت لآبائكم من الازل والى الابد"));
arasuperobject.biblemap.put("b24_7_8", new arasuperobject(24,7,8,"ها انكم متكلون على كلام الكذب الذي لا ينفع"));
arasuperobject.biblemap.put("b24_7_9", new arasuperobject(24,7,9,"أتسرقون وتقتلون وتزنون وتحلفون كذبا وتبخرون للبعل وتسيرون وراء آلهة اخرى لم تعرفوها"));
arasuperobject.biblemap.put("b24_7_10", new arasuperobject(24,7,10,"ثم تأتون وتقفون امامي في هذا البيت الذي دعي باسمي عليه وتقولون قد انقذنا. حتى تعملوا كل هذه الرجاسات"));
arasuperobject.biblemap.put("b24_7_11", new arasuperobject(24,7,11,"هل صار هذا البيت الذي دعي باسمي عليه مغارة لصوص في اعينكم. هانذا ايضا قد رأيت يقول الرب"));
arasuperobject.biblemap.put("b24_7_12", new arasuperobject(24,7,12,"لكن اذهبوا الى موضعي الذي في شيلو الذي أسكنت فيه اسمي اولا وانظروا ما صنعت به من اجل شر شعبي اسرائيل"));
arasuperobject.biblemap.put("b24_7_13", new arasuperobject(24,7,13,"والآن من اجل عملكم هذه الاعمال يقول الرب وقد كلمتكم مبكرا ومكلما فلم تسمعوا ودعوتكم فلم تجيبوا"));
arasuperobject.biblemap.put("b24_7_14", new arasuperobject(24,7,14,"اصنع بالبيت الذي دعي باسمي عليه الذي انتم متكلون عليه وبالموضع الذي اعطيتكم وآبائكم اياه كما صنعت بشيلو"));
arasuperobject.biblemap.put("b24_7_15", new arasuperobject(24,7,15,"واطرحكم من امامي كما طرحت كل اخوتكم كل نسل افرايم"));
arasuperobject.biblemap.put("b24_7_16", new arasuperobject(24,7,16,"وانت فلا تصل لاجل هذا الشعب ولا ترفع لاجلهم دعاء ولا صلاة ولا تلحّ عليّ لاني لا اسمعك"));
arasuperobject.biblemap.put("b24_7_17", new arasuperobject(24,7,17,"أما ترى ماذا يعملون في مدن يهوذا وفي شوارع اورشليم"));
arasuperobject.biblemap.put("b24_7_18", new arasuperobject(24,7,18,"الابناء يلتقطون حطبا والآباء يوقدون النار والنساء يعجنّ العجين ليصنعن كعكا لملكة السموات ولسكب سكائب لآلهة اخرى لكي يغيظوني"));
arasuperobject.biblemap.put("b24_7_19", new arasuperobject(24,7,19,"أفاياي يغيظون يقول الرب. أليس انفسهم لاجل خزي وجوههم"));
arasuperobject.biblemap.put("b24_7_20", new arasuperobject(24,7,20,"لذلك هكذا قال السيد الرب. ها غضبي وغيظي ينسكبان على هذا الموضع على الناس وعلى البهائم وعلى شجر الحقل وعلى ثمر الارض فيتقدان ولا ينطفئان"));
arasuperobject.biblemap.put("b24_7_21", new arasuperobject(24,7,21,"هكذا قال رب الجنود اله اسرائيل. ضموا محرقاتكم الى ذبائحكم وكلوا لحما"));
arasuperobject.biblemap.put("b24_7_22", new arasuperobject(24,7,22,"لاني لم اكلم آباءكم ولا اوصيتهم يوم اخرجتهم من ارض مصر من جهة محرقة وذبيحة"));
arasuperobject.biblemap.put("b24_7_23", new arasuperobject(24,7,23,"بل انما اوصيتهم بهذا الامر قائلا اسمعوا صوتي فاكون لكم الها وانتم تكونون لي شعبا وسيروا في كل الطريق الذي اوصيكم به ليحسن اليكم"));
arasuperobject.biblemap.put("b24_7_24", new arasuperobject(24,7,24,"فلم يسمعوا ولم يميلوا اذنهم بل ساروا في مشورات وعناد قلبهم الشرير واعطوا القفا لا الوجه"));
arasuperobject.biblemap.put("b24_7_25", new arasuperobject(24,7,25,"فمن اليوم الذي خرج فيه آباؤكم من ارض مصر الى هذا اليوم ارسلت اليكم كل عبيدي الانبياء مبكرا كل يوم ومرسلا"));
arasuperobject.biblemap.put("b24_7_26", new arasuperobject(24,7,26,"فلم يسمعوا لي ولم يميلوا اذنهم بل صلّبوا رقابهم. اساءوا اكثر من آبائهم"));
arasuperobject.biblemap.put("b24_7_27", new arasuperobject(24,7,27,"فتكلمهم بكل هذه الكلمات ولا يسمعون لك وتدعوهم ولا يجيبونك"));
arasuperobject.biblemap.put("b24_7_28", new arasuperobject(24,7,28,"فتقول لهم هذه هي الامة التي لم تسمع لصوت الرب الهها ولم تقبل تأديبا. باد الحق وقطع عن افواههم"));
arasuperobject.biblemap.put("b24_7_29", new arasuperobject(24,7,29,"جزي شعرك واطرحيه وارفعي على الهضاب مرثاة لان الرب قد رفض ورذل جيل رجزه"));
arasuperobject.biblemap.put("b24_7_30", new arasuperobject(24,7,30,"لان بني يهوذا قد عملوا الشر في عيني يقول الرب. وضعوا مكرهاتهم في البيت الذي دعي باسمي لينجسوه"));
arasuperobject.biblemap.put("b24_7_31", new arasuperobject(24,7,31,"وبنو مرتفعات توفة التي في وادي ابن هنوم ليحرقوا بنيهم وبناتهم بالنار الذي لم آمر به ولا صعد على قلبي"));
arasuperobject.biblemap.put("b24_7_32", new arasuperobject(24,7,32,"لذلك ها هي ايام تأتي يقول الرب ولا يسمى بعد توفة ولا وادي ابن هنوم بل وادي القتل ويدفنون في توفة حتى لا يكون موضع"));
arasuperobject.biblemap.put("b24_7_33", new arasuperobject(24,7,33,"وتصير جثث هذا الشعب أكلا لطيور السماء ولوحوش الارض ولا مزعج"));
arasuperobject.biblemap.put("b24_7_34", new arasuperobject(24,7,34,"وابطّل من مدن يهوذا ومن شوارع اورشليم صوت الطرب وصوت الفرح صوت العريس وصوت العروس لان الارض تصير خرابا"));
arasuperobject.biblemap.put("b24_8_1", new arasuperobject(24,8,1,"في ذلك الزمان يقول الرب يخرجون عظام ملوك يهوذا وعظام رؤسائه وعظام الكهنة وعظام الانبياء وعظام سكان اورشليم من قبورهم"));
arasuperobject.biblemap.put("b24_8_2", new arasuperobject(24,8,2,"ويبسطونها للشمس وللقمر ولكل جنود السموات التي احبوها والتي عبدوها والتي ساروا وراءها والتي استشاروها والتي سجدوا لها. لا تجمع ولا تدفن بل تكون دمنة على وجه الارض"));
arasuperobject.biblemap.put("b24_8_3", new arasuperobject(24,8,3,"ويختار الموت على الحياة عند كل البقية الباقية من هذه العشيرة الشريرة الباقية في كل الاماكن التي طردتهم اليها يقول رب الجنود"));
arasuperobject.biblemap.put("b24_8_4", new arasuperobject(24,8,4,"وتقول لهم هكذا قال الرب. هل يسقطون ولا يقومون او يرتد احد ولا يرجع"));
arasuperobject.biblemap.put("b24_8_5", new arasuperobject(24,8,5,"فلماذا ارتد هذا الشعب في اورشليم ارتدادا دائما. تمسكوا بالمكر. ابوا ان يرجعوا"));
arasuperobject.biblemap.put("b24_8_6", new arasuperobject(24,8,6,"صغيت وسمعت. بغير المستقيم يتكلمون. ليس احد يتوب عن شره قائلا ماذا عملت. كل واحد رجع الى مسراه كفرس ثائر في الحرب"));
arasuperobject.biblemap.put("b24_8_7", new arasuperobject(24,8,7,"بل اللقلق في السموات يعرف ميعاده واليمامة والسنونة المزقزقة حفظتا وقت مجيئهما. اما شعبي فلم يعرف قضاء الرب"));
arasuperobject.biblemap.put("b24_8_8", new arasuperobject(24,8,8,"كيف تقولون نحن حكماء وشريعة الرب معنا. حقا انه الى الكذب حوّلها قلم الكتبة الكاذب"));
arasuperobject.biblemap.put("b24_8_9", new arasuperobject(24,8,9,"خزي الحكماء ارتاعوا وأخذوا. ها قد رفضوا كلمة الرب فاية حكمة لهم"));
arasuperobject.biblemap.put("b24_8_10", new arasuperobject(24,8,10,"لذلك اعطي نساءهم لآخرين وحقولهم لمالكين لانهم من الصغير الى الكبير كل واحد مولع بالربح من النبي الى الكاهن كل واحد يعمل بالكذب"));
arasuperobject.biblemap.put("b24_8_11", new arasuperobject(24,8,11,"ويشفون كسر بنت شعبي على عثم قائلين سلام سلام ولا سلام"));
arasuperobject.biblemap.put("b24_8_12", new arasuperobject(24,8,12,"هل خزوا لانهم عملوا رجسا. بل لم يخزوا خزيا ولم يعرفوا الخجل. لذلك يسقطون بين الساقطين في وقت معاقبتهم يعثرون قال الرب"));
arasuperobject.biblemap.put("b24_8_13", new arasuperobject(24,8,13,"نزعا انزعهم يقول الرب. لا عنب في الجفنة ولا تين في التينة والورق ذبل واعطيهم ما يزول عنهم"));
arasuperobject.biblemap.put("b24_8_14", new arasuperobject(24,8,14,"لماذا نحن جلوس. اجتمعوا فلندخل الى المدن الحصينة ونصمت هناك. لان الرب الهنا قد اصمتنا واسقانا ماء العلقم لاننا قد اخطانا الى الرب"));
arasuperobject.biblemap.put("b24_8_15", new arasuperobject(24,8,15,"انتظرنا السلام ولم يكن خير. وزمان الشفاء واذا رعب"));
arasuperobject.biblemap.put("b24_8_16", new arasuperobject(24,8,16,"من دان سمعت حمحمة خيله. عند صوت صهيل جياده ارتجفت كل الارض. فأتوا واكلوا الارض وملأها المدينة والساكنين فيها"));
arasuperobject.biblemap.put("b24_8_17", new arasuperobject(24,8,17,"لاني هانذا مرسل عليكم حيّات افاعي لا ترقى فتلدغكم يقول الرب"));
arasuperobject.biblemap.put("b24_8_18", new arasuperobject(24,8,18,"من مفرج عني الحزن. قلبي فيّ سقيم"));
arasuperobject.biblemap.put("b24_8_19", new arasuperobject(24,8,19,"هوذا صوت استغاثة بنت شعبي من ارض بعيدة. ألعل الرب ليس في صهيون او ملكها ليس فيها. لماذا اغاظوني بمنحوتاتهم باباطيل غريبة"));
arasuperobject.biblemap.put("b24_8_20", new arasuperobject(24,8,20,"مضى الحصاد انتهى الصيف ونحن لم نخلص"));
arasuperobject.biblemap.put("b24_8_21", new arasuperobject(24,8,21,"من اجل سحق بنت شعبي انسحقت. حزنت اخذتني دهشة"));
arasuperobject.biblemap.put("b24_8_22", new arasuperobject(24,8,22,"أليس بلسان في جلعاد أم ليس هناك طبيب. فلماذا لم تعصب بنت شعبي"));
arasuperobject.biblemap.put("b24_9_1", new arasuperobject(24,9,1,"يا ليت راسي ماء وعينيّ ينبوع دموع فابكي نهارا وليلا قتلى بنت شعبي"));
arasuperobject.biblemap.put("b24_9_2", new arasuperobject(24,9,2,"يا ليت لي في البرية مبيت مسافرين فاترك شعبي وانطلق من عندهم لانهم جميعا زناة جماعة خائنين"));
arasuperobject.biblemap.put("b24_9_3", new arasuperobject(24,9,3,"يمدون ألسنتهم كقسيهم للكذب. لا للحق قووا في الارض. لانهم خرجوا من شر الى شر واياي لم يعرفوا يقول الرب"));
arasuperobject.biblemap.put("b24_9_4", new arasuperobject(24,9,4,"احترزوا كل واحد من صاحبه وعلى كل اخ لا تتكلوا لان كل اخ يعقب عقبا وكل صاحب يسعى في الوشاية"));
arasuperobject.biblemap.put("b24_9_5", new arasuperobject(24,9,5,"ويختل الانسان صاحبه ولا يتكلمون بالحق. علّموا السنتهم التكلم بالكذب وتعبوا في الافتراء"));
arasuperobject.biblemap.put("b24_9_6", new arasuperobject(24,9,6,"مسكنك في وسط المكر. بالمكر ابوا ان يعرفوني يقول الرب"));
arasuperobject.biblemap.put("b24_9_7", new arasuperobject(24,9,7,"لذلك هكذا قال رب الجنود هانذا انقيهم وامتحنهم. لاني ماذا اعمل من اجل بنت شعبي"));
arasuperobject.biblemap.put("b24_9_8", new arasuperobject(24,9,8,"لسانهم سهم قتال يتكلم بالغش. بفمه يكلم صاحبه بسلام وفي قلبه يضع له كمينا"));
arasuperobject.biblemap.put("b24_9_9", new arasuperobject(24,9,9,"أفما اعاقبهم على هذه يقول الرب ام لا تنتقم نفسي من امة كهذه"));
arasuperobject.biblemap.put("b24_9_10", new arasuperobject(24,9,10,"على الجبال ارفع بكاء ومرثاة على مراعي البرية ندبا لانها احترقت فلا انسان عابر ولا يسمع صوت الماشية. من طير السموات الى البهائم هربت مضت"));
arasuperobject.biblemap.put("b24_9_11", new arasuperobject(24,9,11,"واجعل اورشليم رجما ومأوى بنات آوى ومدن يهوذا اجعلها خرابا بلا ساكن"));
arasuperobject.biblemap.put("b24_9_12", new arasuperobject(24,9,12,"من هو الانسان الحكيم الذي يفهم هذه والذي كلمه فم الرب فيخبر بها. لماذا بادت الارض واحترقت كبرية بلا عابر"));
arasuperobject.biblemap.put("b24_9_13", new arasuperobject(24,9,13,"فقال الرب على تركهم شريعتي التي جعلتها امامهم ولم يسمعوا لصوتي ولم يسلكوا بها"));
arasuperobject.biblemap.put("b24_9_14", new arasuperobject(24,9,14,"بل سلكوا وراء عناد قلوبهم ووراء البعليم التي علمهم اياها آباؤهم"));
arasuperobject.biblemap.put("b24_9_15", new arasuperobject(24,9,15,"لذلك هكذا قال رب الجنود اله اسرائيل. هانذا اطعم هذا الشعب افسنتينا واسقيهم ماء العلقم"));
arasuperobject.biblemap.put("b24_9_16", new arasuperobject(24,9,16,"وابددهم في امم لم يعرفوها هم ولا آباؤهم واطلق وراءهم السيف حتى افنيهم"));
arasuperobject.biblemap.put("b24_9_17", new arasuperobject(24,9,17,"هكذا قال رب الجنود. تأملوا وادعوا النادبات فيأتين وارسلوا الى الحكيمات فيقبلن"));
arasuperobject.biblemap.put("b24_9_18", new arasuperobject(24,9,18,"ويسرعن ويرفعن علينا مرثاة فتذرف اعيننا دموعا وتفيض اجفاننا ماء"));
arasuperobject.biblemap.put("b24_9_19", new arasuperobject(24,9,19,"لان صوت رثاية سمع من صهيون كيف أهلكنا. خزينا جدا لاننا تركنا الارض لانهم هدموا مساكننا"));
arasuperobject.biblemap.put("b24_9_20", new arasuperobject(24,9,20,"بل اسمعن ايتها النساء كلمة الرب ولتقبل آذانكنّ كلمة فمه وعلّمن بناتكنّ الرثاية والمرأة صاحبتها الندب"));
arasuperobject.biblemap.put("b24_9_21", new arasuperobject(24,9,21,"لان الموت طلع الى كوانا دخل قصورنا ليقطع الاطفال من خارج والشبان من الساحات"));
arasuperobject.biblemap.put("b24_9_22", new arasuperobject(24,9,22,"تكلم. هكذا يقول الرب. وتسقط جثة الانسان كدمنة على وجه الحقل وكقبضة وراء الحاصد وليس من يجمع"));
arasuperobject.biblemap.put("b24_9_23", new arasuperobject(24,9,23,"هكذا قال الرب. لا يفتخرنّ الحكيم بحكمته ولا يفتخر الجبار بجبروته ولا يفتخر الغني بغناه"));
arasuperobject.biblemap.put("b24_9_24", new arasuperobject(24,9,24,"بل بهذا ليفتخرنّ المفتخر بانه يفهم ويعرفني اني انا الرب الصانع رحمة وقضاء وعدلا في الارض لاني بهذه اسرّ يقول الرب"));
arasuperobject.biblemap.put("b24_9_25", new arasuperobject(24,9,25,"ها ايام تأتي يقول الرب واعاقب كل مختون واغلف"));
arasuperobject.biblemap.put("b24_9_26", new arasuperobject(24,9,26,"مصر ويهوذا وادوم وبني عمون وموآب وكل مقصوصي الشعر مستديرا الساكنين في البرية لان كل الامم غلف وكل بيت اسرائيل غلف القلوب"));
arasuperobject.biblemap.put("b24_10_1", new arasuperobject(24,10,1,"اسمعوا الكلمة التي تكلم بها الرب عليكم يا بيت اسرائيل"));
arasuperobject.biblemap.put("b24_10_2", new arasuperobject(24,10,2,"هكذا قال الرب. لا تتعلموا طريق الامم ومن آيات السموات لا ترتعبوا. لان الامم ترتعب منها"));
arasuperobject.biblemap.put("b24_10_3", new arasuperobject(24,10,3,"لان فرائض الامم باطلة. لانها شجرة يقطعونها من الوعر. صنعة يدي نجار بالقدوم"));
arasuperobject.biblemap.put("b24_10_4", new arasuperobject(24,10,4,"بالفضة والذهب يزينونها وبالمسامير والمطارق يشددونها فلا تتحرك"));
arasuperobject.biblemap.put("b24_10_5", new arasuperobject(24,10,5,"هي كاللّعين في مقثأة فلا تتكلم. تحمل حملا لانها لا تمشي. لا تخافوها لانها لا تضر ولا فيها ان تصنع خيرا"));
arasuperobject.biblemap.put("b24_10_6", new arasuperobject(24,10,6,"لا مثل لك يا رب عظيم انت وعظيم اسمك في الجبروت"));
arasuperobject.biblemap.put("b24_10_7", new arasuperobject(24,10,7,"من لا يخافك يا ملك الشعوب لانه بك يليق. لانه في جميع حكماء الشعوب وفي كل ممالكهم ليس مثلك"));
arasuperobject.biblemap.put("b24_10_8", new arasuperobject(24,10,8,"بلدوا وحمقوا معا. ادب اباطيل هو الخشب"));
arasuperobject.biblemap.put("b24_10_9", new arasuperobject(24,10,9,"فضة مطرقة تجلب من ترشيش وذهب من اوفاز صنعة صانع ويدي صائغ. اسمانجوني وارجوان لباسها. كلها صنعة حكماء"));
arasuperobject.biblemap.put("b24_10_10", new arasuperobject(24,10,10,"اما الرب الاله فحق. هو اله حيّ وملك ابدي. من سخطه ترتعد الارض ولا تطيق الامم غضبه"));
arasuperobject.biblemap.put("b24_10_11", new arasuperobject(24,10,11,"هكذا تقولون لهم. الآلهة التي لم تصنع السموات والارض تبيد من الارض ومن تحت هذه السموات"));
arasuperobject.biblemap.put("b24_10_12", new arasuperobject(24,10,12,"صانع الارض بقوته مؤسس المسكونة بحكمته وبفهمه بسط السموات"));
arasuperobject.biblemap.put("b24_10_13", new arasuperobject(24,10,13,"اذا اعطى قولا تكون كثرة مياه في السموات ويصعد السحاب من اقاصي الارض. صنع بروقا للمطر واخرج الريح من خزائنه"));
arasuperobject.biblemap.put("b24_10_14", new arasuperobject(24,10,14,"بلد كل انسان من معرفته. خزي كل صائغ من التمثال. لان مسبوكه كذب ولا روح فيه"));
arasuperobject.biblemap.put("b24_10_15", new arasuperobject(24,10,15,"هي باطلة صنعة الاضاليل. في وقت عقابها تبيد"));
arasuperobject.biblemap.put("b24_10_16", new arasuperobject(24,10,16,"ليس كهذه نصيب يعقوب. لانه مصور الجميع واسرائيل قضيب ميراثه. رب الجنود اسمه"));
arasuperobject.biblemap.put("b24_10_17", new arasuperobject(24,10,17,"اجمعي من الارض حزمك ايتها الساكنة في الحصار"));
arasuperobject.biblemap.put("b24_10_18", new arasuperobject(24,10,18,"لانه هكذا قال الرب. هانذا رام من مقلاع سكان الارض هذه المرة واضيق عليهم لكي يشعروا"));
arasuperobject.biblemap.put("b24_10_19", new arasuperobject(24,10,19,"ويل لي من اجل سحقي. ضربتي عديمة الشفاء. فقلت انما هذه مصيبة فاحتملها"));
arasuperobject.biblemap.put("b24_10_20", new arasuperobject(24,10,20,"خيمتي خربت وكل اطنابي قطعت. بنيّ خرجوا عني وليسوا. ليس من يبسط بعد خيمتي ويقيم شققي"));
arasuperobject.biblemap.put("b24_10_21", new arasuperobject(24,10,21,"لان الرعاة بلدوا والرب لم يطلبوا. من اجل ذلك لم ينجحوا وكل رعيتهم تبددت"));
arasuperobject.biblemap.put("b24_10_22", new arasuperobject(24,10,22,"هوذا صوت خبر جاء واضطراب عظيم من ارض الشمال لجعل مدن يهوذا خرابا مأوى بنات آوى"));
arasuperobject.biblemap.put("b24_10_23", new arasuperobject(24,10,23,"عرفت يا رب انه ليس للانسان طريقه. ليس لانسان يمشي ان يهدي خطواته"));
arasuperobject.biblemap.put("b24_10_24", new arasuperobject(24,10,24,"ادبني يا رب ولكن بالحق لا بغضبك لئلا تفنيني"));
arasuperobject.biblemap.put("b24_10_25", new arasuperobject(24,10,25,"اسكب غضبك على الامم التي لم تعرفك وعلى العشائر التي لم تدع باسمك. لانهم اكلوا يعقوب. اكلوه وافنوه واخربوا مسكنه"));
arasuperobject.biblemap.put("b24_11_1", new arasuperobject(24,11,1,"الكلام الذي صار الى ارميا النبي من قبل الرب قائلا"));
arasuperobject.biblemap.put("b24_11_2", new arasuperobject(24,11,2,"اسمعوا كلام هذا العهد وكلموا رجال يهوذا وسكان اورشليم"));
arasuperobject.biblemap.put("b24_11_3", new arasuperobject(24,11,3,"فتقول لهم هكذا قال الرب اله اسرائيل. ملعون الانسان الذي لا يسمع كلام هذا العهد"));
arasuperobject.biblemap.put("b24_11_4", new arasuperobject(24,11,4,"الذي أمرت به آباءكم يوم اخرجتهم من ارض مصر من كور الحديد قائلا اسمعوا صوتي واعملوا به حسب كل ما آمركم به فتكونوا لي شعبا وانا اكون لكم الها"));
arasuperobject.biblemap.put("b24_11_5", new arasuperobject(24,11,5,"لاقيم الحلف الذي حلفت لآبائكم ان اعطيهم ارضا تفيض لبنا وعسلا كهذا اليوم. فاجبت وقلت آمين يا رب"));
arasuperobject.biblemap.put("b24_11_6", new arasuperobject(24,11,6,"فقال الرب لي. ناد بكل هذا الكلام في مدن يهوذا وفي شوارع اورشليم قائلا. اسمعوا كلام هذا العهد واعملوا به"));
arasuperobject.biblemap.put("b24_11_7", new arasuperobject(24,11,7,"لاني اشهدت على آبائكم اشهادا يوم اصعدتهم من ارض مصر الى هذا اليوم مبكرا ومشهدا قائلا اسمعوا صوتي"));
arasuperobject.biblemap.put("b24_11_8", new arasuperobject(24,11,8,"فلم يسمعوا ولم يميلوا اذنهم بل سلكوا كل واحد في عناد قلبه الشرير. فجلبت عليهم كل كلام هذا العهد الذي أمرتهم ان يصنعوه ولم يصنعوه"));
arasuperobject.biblemap.put("b24_11_9", new arasuperobject(24,11,9,"وقال الرب لي. توجد فتنة بين رجال يهوذا وسكان اورشليم"));
arasuperobject.biblemap.put("b24_11_10", new arasuperobject(24,11,10,"قد رجعوا الى آثام آبائهم الاولين الذين ابوا ان يسمعوا كلامي وقد ذهبوا وراء آلهة اخرى ليعبدوها. قد نقض بيت اسرائيل وبيت يهوذا عهدي الذي قطعته مع آبائهم"));
arasuperobject.biblemap.put("b24_11_11", new arasuperobject(24,11,11,"لذلك هكذا قال الرب. هانذا جالب عليهم شرا لا يستطيعون ان يخرجوا منه ويصرخون اليّ فلا اسمع لهم"));
arasuperobject.biblemap.put("b24_11_12", new arasuperobject(24,11,12,"فينطلق مدن يهوذا وسكان اورشليم ويصرخون الى الآلهة التي يبخرون لها فلن تخلصهم في وقت بليتهم"));
arasuperobject.biblemap.put("b24_11_13", new arasuperobject(24,11,13,"لانه بعدد مدنك صارت آلهتك يا يهوذا وبعدد شوارع اورشليم وضعتم مذابح للخزي مذابح للتبخير للبعل"));
arasuperobject.biblemap.put("b24_11_14", new arasuperobject(24,11,14,"وانت فلا تصل لاجل هذا الشعب ولا ترفع لاجلهم دعاء ولا صلاة لاني لا اسمع في وقت صراخهم اليّ من قبل بليتهم"));
arasuperobject.biblemap.put("b24_11_15", new arasuperobject(24,11,15,"ما لحبيبتي في بيتي. قد عملت فظائع كثيرة واللحم المقدس قد عبر عنك. اذا صنعت الشر حينئذ تبتهجين"));
arasuperobject.biblemap.put("b24_11_16", new arasuperobject(24,11,16,"زيتونة خضراء ذات ثمر جميل الصورة دعا الرب اسمك. بصوت ضجة عظيمة اوقد نارا عليها فانكسرت اغصانها"));
arasuperobject.biblemap.put("b24_11_17", new arasuperobject(24,11,17,"ورب الجنود غارسك قد تكلم عليك شرا من اجل شر بيت اسرائيل وبيت يهوذا الذي صنعوه ضد انفسهم ليغيظوني بتبخيرهم للبعل"));
arasuperobject.biblemap.put("b24_11_18", new arasuperobject(24,11,18,"والرب عرّفني فعرفت. حينئذ اريتني افعالهم"));
arasuperobject.biblemap.put("b24_11_19", new arasuperobject(24,11,19,"وانا كخروف داجن يساق الى الذبح ولم اعلم انهم فكروا عليّ افكارا قائلين لنهلك الشجرة بثمرها ونقطعه من ارض الاحياء فلا يذكر بعد اسمه"));
arasuperobject.biblemap.put("b24_11_20", new arasuperobject(24,11,20,"فيا رب الجنود القاضي العدل فاحص الكلى والقلب دعني ارى انتقامك منهم لاني لك كشفت دعواي"));
arasuperobject.biblemap.put("b24_11_21", new arasuperobject(24,11,21,"لذلك هكذا قال الرب عن اهل عناثوث الذين يطلبون نفسك قائلين لا تتنبأ باسم الرب فلا تموت بيدنا"));
arasuperobject.biblemap.put("b24_11_22", new arasuperobject(24,11,22,"لذلك هكذا قال رب الجنود. هانذا اعاقبهم. يموت الشبان بالسيف ويموت بنوهم وبناتهم بالجوع"));
arasuperobject.biblemap.put("b24_11_23", new arasuperobject(24,11,23,"ولا تكون لهم بقية لاني اجلب شرا على اهل عناثوث سنة عقابهم"));
arasuperobject.biblemap.put("b24_12_1", new arasuperobject(24,12,1,"أبر انت يا رب من ان اخاصمك. لكن اكلمك من جهة احكامك. لماذا تنجح طريق الاشرار. اطمأن كل الغادرين غدرا"));
arasuperobject.biblemap.put("b24_12_2", new arasuperobject(24,12,2,"غرستهم فاصّلوا نموا واثمروا ثمرا. انت قريب في فمهم وبعيد من كلاهم"));
arasuperobject.biblemap.put("b24_12_3", new arasuperobject(24,12,3,"وانت يا رب عرفتني رأيتني واختبرت قلبي من جهتك. افرزهم كغنم للذبح وخصصهم ليوم القتل"));
arasuperobject.biblemap.put("b24_12_4", new arasuperobject(24,12,4,"حتى متى تنوح الارض وييبس عشب كل الحقل. من شر الساكنين فيها فنيت البهائم والطيور لانهم قالوا لا يرى آخرتنا"));
arasuperobject.biblemap.put("b24_12_5", new arasuperobject(24,12,5,"ان جريت مع المشاة فاتعبوك فكيف تباري الخيل. وان كنت منبطحا في ارض السلام فكيف تعمل في كبرياء الاردن"));
arasuperobject.biblemap.put("b24_12_6", new arasuperobject(24,12,6,"لان اخوتك انفسهم وبيت ابيك قد غادروك هم ايضا. هم ايضا نادوا وراءك بصوت عال. لا تأتمنهم اذا كلموك بالخير"));
arasuperobject.biblemap.put("b24_12_7", new arasuperobject(24,12,7,"قد تركت بيتي رفضت ميراثي دفعت حبيبة نفسي ليد اعدائها"));
arasuperobject.biblemap.put("b24_12_8", new arasuperobject(24,12,8,"صار لي ميراثي كاسد في الوعر. نطق علي بصوته. من اجل ذلك ابغضته"));
arasuperobject.biblemap.put("b24_12_9", new arasuperobject(24,12,9,"جارحة ضبع ميراثي لي. الجوارح حواليه عليه. هلم اجمعوا كل حيوان الحقل. ايتوا بها للاكل"));
arasuperobject.biblemap.put("b24_12_10", new arasuperobject(24,12,10,"رعاة كثيرون افسدوا كرمي داسوا نصيبي جعلوا نصيبي المشتهى برية خربة"));
arasuperobject.biblemap.put("b24_12_11", new arasuperobject(24,12,11,"جعلوه خرابا ينوح عليّ وهو خرب. خربت كل الارض لانه لا احد يضع في قلبه"));
arasuperobject.biblemap.put("b24_12_12", new arasuperobject(24,12,12,"على جميع الروابي في البرية اتى الناهبون لان سيفا للرب يأكل من اقصى الارض الى اقصى الارض. ليس سلام لاحد من البشر"));
arasuperobject.biblemap.put("b24_12_13", new arasuperobject(24,12,13,"زرعوا حنطة وحصدوا شوكا. اعيوا ولم ينتفعوا بل خزوا من غلاتكم من حمو غضب الرب"));
arasuperobject.biblemap.put("b24_12_14", new arasuperobject(24,12,14,"هكذا قال الرب على جميع جيراني الاشرار الذين يلمسون الميراث الذي اورثته لشعبي اسرائيل هانذا اقتلعهم عن ارضهم واقتلع بيت يهوذا من وسطهم"));
arasuperobject.biblemap.put("b24_12_15", new arasuperobject(24,12,15,"ويكون بعد اقتلاعي اياهم اني ارجع فارحمهم واردهم كل واحد الى ميراثه وكل واحد الى ارضه"));
arasuperobject.biblemap.put("b24_12_16", new arasuperobject(24,12,16,"ويكون اذا تعلموا علما طرق شعبي ان يحلفوا باسمي حيّ هو الرب كما علّموا شعبي ان يحلفوا ببعل انهم يبنون في وسط شعبي"));
arasuperobject.biblemap.put("b24_12_17", new arasuperobject(24,12,17,"وان لم يسمعوا فاني اقتلع تلك الامة اقتلاعا وابيدها يقول الرب"));
arasuperobject.biblemap.put("b24_13_1", new arasuperobject(24,13,1,"هكذا قال الرب لي اذهب واشتر لنفسك منطقة من كتان وضعها على حقويك ولا تدخلها في الماء"));
arasuperobject.biblemap.put("b24_13_2", new arasuperobject(24,13,2,"فاشتريت المنطقة كقول الرب ووضعتها على حقوي"));
arasuperobject.biblemap.put("b24_13_3", new arasuperobject(24,13,3,"فصار كلام الرب اليّ ثانية قائلا"));
arasuperobject.biblemap.put("b24_13_4", new arasuperobject(24,13,4,"خذ المنطقة التي اشتريتها التي هي على حقويك وقم انطلق الى الفرات واطمرها هناك في شق صخر"));
arasuperobject.biblemap.put("b24_13_5", new arasuperobject(24,13,5,"فانطلقت وطمرتها عند الفرات. كما امرني الرب"));
arasuperobject.biblemap.put("b24_13_6", new arasuperobject(24,13,6,"وكان بعد ايام كثيرة ان الرب قال لي قم انطلق الى الفرات وخذ من هناك المنطقة التي امرتك ان تطمرها هناك"));
arasuperobject.biblemap.put("b24_13_7", new arasuperobject(24,13,7,"فانطلقت الى الفرات وحفرت واخذت المنطقة من الموضع الذي طمرتها فيه. واذا بالمنطقة قد فسدت لا تصلح لشيء"));
arasuperobject.biblemap.put("b24_13_8", new arasuperobject(24,13,8,"فصار كلام الرب اليّ قائلا"));
arasuperobject.biblemap.put("b24_13_9", new arasuperobject(24,13,9,"هكذا قال الرب. هكذا افسد كبرياء يهوذا وكبرياء اورشليم العظيمة"));
arasuperobject.biblemap.put("b24_13_10", new arasuperobject(24,13,10,"هذا الشعب الشرير الذي يأبى ان يسمع كلامي الذي يسلك في عناد قلبه ويسير وراء آلهة اخرى ليعبدها ويسجد لها يصير كهذه المنطقة التي لا تصلح لشيء"));
arasuperobject.biblemap.put("b24_13_11", new arasuperobject(24,13,11,"لانه كما تلتصق المنطقة بحقوي الانسان هكذا الصقت بنفسي كل بيت اسرائيل وكل بيت يهوذا يقول الرب ليكونوا لي شعبا واسما وفخرا ومجدا ولكنهم لم يسمعوا"));
arasuperobject.biblemap.put("b24_13_12", new arasuperobject(24,13,12,"فتقول لهم هذه الكلمة. هكذا قال الرب اله اسرائيل. كل زق يمتلئ خمرا. فيقولون لك أما نعرف معرفة ان كل زق يمتلئ خمرا"));
arasuperobject.biblemap.put("b24_13_13", new arasuperobject(24,13,13,"فتقول لهم. هكذا قال الرب. هانذا املأ كل سكان هذه الارض والملوك الجالسين لداود على كرسيه والكهنة والانبياء وكل سكان اورشليم سكرا"));
arasuperobject.biblemap.put("b24_13_14", new arasuperobject(24,13,14,"واحطمهم الواحد على اخيه الآباء والابناء معا يقول الرب. لا اشفق ولا اترأف ولا ارحم من اهلاكهم"));
arasuperobject.biblemap.put("b24_13_15", new arasuperobject(24,13,15,"اسمعوا واصغوا. لا تتعظموا لان الرب تكلم"));
arasuperobject.biblemap.put("b24_13_16", new arasuperobject(24,13,16,"اعطوا الرب الهكم مجدا قبل ان يجعل ظلاما وقبلما تعثر ارجلكم على جبال العتمة فتنتظرون نورا فيجعله ظل موت ويجعله ظلاما دامسا"));
arasuperobject.biblemap.put("b24_13_17", new arasuperobject(24,13,17,"وان لم تسمعوا ذلك فان نفسي تبكي في اماكن مستترة من اجل الكبرياء وتبكي عيني بكاء وتذرف الدموع لانه قد سبي قطيع الرب"));
arasuperobject.biblemap.put("b24_13_18", new arasuperobject(24,13,18,"قل للملك وللملكة اتضعا واجلسا لانه قد هبط عن راسيكما تاج مجدكما"));
arasuperobject.biblemap.put("b24_13_19", new arasuperobject(24,13,19,"اغلقت مدن الجنوب وليس من يفتح. سبيت يهوذا كلها سبيت بالتمام"));
arasuperobject.biblemap.put("b24_13_20", new arasuperobject(24,13,20,"ارفعوا اعينكم وانظروا المقبلين من الشمال. اين القطيع الذي أعطي لك غنم مجدك"));
arasuperobject.biblemap.put("b24_13_21", new arasuperobject(24,13,21,"ماذا تقولين حين يعاقبك وقد علّمتهم على نفسك قواد للرياسة. أما تاخذك الاوجاع كامرأة ماخض"));
arasuperobject.biblemap.put("b24_13_22", new arasuperobject(24,13,22,"وان قلت في قلبك لماذا اصابتني هذه. لاجل عظمة اثمك هتك ذيلاك وانكشف عنفا عقباك"));
arasuperobject.biblemap.put("b24_13_23", new arasuperobject(24,13,23,"هل يغير الكوشي جلده او النمر رقطه. فانتم ايضا تقدرون ان تصنعوا خيرا ايها المتعلمون الشر"));
arasuperobject.biblemap.put("b24_13_24", new arasuperobject(24,13,24,"فابددهم كقش يعبر مع ريح البرية"));
arasuperobject.biblemap.put("b24_13_25", new arasuperobject(24,13,25,"هذه قرعتك النصيب المكيل لك من عندي يقول الرب لانك نسيتني واتكلت على الكذب"));
arasuperobject.biblemap.put("b24_13_26", new arasuperobject(24,13,26,"فانا ايضا ارفع ذيليك على وجهك فيرى خزيك"));
arasuperobject.biblemap.put("b24_13_27", new arasuperobject(24,13,27,"فسقك وصهيلك ورذالة زناك على الآكام في الحقل قد رأيت مكرهاتك. ويل لك يا اورشليم لا تطهرين. حتى متى بعد"));
arasuperobject.biblemap.put("b24_14_1", new arasuperobject(24,14,1,"كلمة الرب التي صارت الى ارميا من جهة القحط"));
arasuperobject.biblemap.put("b24_14_2", new arasuperobject(24,14,2,"ناحت يهوذا وابوابها ذبلت حزنت الى الارض وصعد عويل اورشليم"));
arasuperobject.biblemap.put("b24_14_3", new arasuperobject(24,14,3,"واشرافهم ارسلوا اصاغرهم للماء. أتوا الى الاجباب فلم يجدوا ماء رجعوا بآنيتهم فارغة. خزوا وخجلوا وغطوا رؤوسهم"));
arasuperobject.biblemap.put("b24_14_4", new arasuperobject(24,14,4,"من اجل ان الارض قد تشققت لانه لم يكن مطر على الارض خزي الفلاحون. غطوا رؤوسهم"));
arasuperobject.biblemap.put("b24_14_5", new arasuperobject(24,14,5,"حتى ان الايلة ايضا في الحقل ولدت وتركت لانه لم يكن كلأ"));
arasuperobject.biblemap.put("b24_14_6", new arasuperobject(24,14,6,"الفراء وقفت على الهضاب تستنشق الريح مثل بنات آوى. كلت عيونها لانه ليس عشب"));
arasuperobject.biblemap.put("b24_14_7", new arasuperobject(24,14,7,"وان تكن آثامنا تشهد علينا يا رب فاعمل لاجل اسمك. لان معاصينا كثرت. اليك اخطأنا"));
arasuperobject.biblemap.put("b24_14_8", new arasuperobject(24,14,8,"يا رجاء اسرائيل مخلصه في زمان الضيق لماذا تكون كغريب في الارض وكمسافر يميل ليبيت"));
arasuperobject.biblemap.put("b24_14_9", new arasuperobject(24,14,9,"لماذا تكون كانسان قد تحير كجبار لا يستطيع ان يخلّص. وانت في وسطنا يا رب وقد دعينا باسمك. لا تتركنا"));
arasuperobject.biblemap.put("b24_14_10", new arasuperobject(24,14,10,"هكذا قال الرب لهذا الشعب. هكذا احبوا ان يجولوا. لم يمنعوا ارجلهم فالرب لم يقبلهم. الآن يذكر اثمهم ويعاقب خطاياهم"));
arasuperobject.biblemap.put("b24_14_11", new arasuperobject(24,14,11,"وقال الرب لي لا تصل لاجل هذا الشعب للخير"));
arasuperobject.biblemap.put("b24_14_12", new arasuperobject(24,14,12,"حين يصومون لا اسمع صراخهم وحين يصعدون محرقة وتقدمة لا اقبلهم بل بالسيف والجوع والوبإ انا افنيهم"));
arasuperobject.biblemap.put("b24_14_13", new arasuperobject(24,14,13,"فقلت آه ايها السيد الرب. هوذا الانبياء يقولون لهم لا ترون سيفا ولا يكون لكم جوع بل سلاما ثابتا اعطيكم في هذا الموضع"));
arasuperobject.biblemap.put("b24_14_14", new arasuperobject(24,14,14,"فقال الرب لي. بالكذب يتنبأ الانبياء باسمي. لم ارسلهم ولا امرتهم ولا كلمتهم. برؤيا كاذبة وعرافة وباطل ومكر قلوبهم هم يتنبأون لكم"));
arasuperobject.biblemap.put("b24_14_15", new arasuperobject(24,14,15,"لذلك هكذا قال الرب عن الانبياء الذين يتنبأون باسمي وانا لم ارسلهم وهم يقولون لا يكون سيف ولا جوع في هذه الارض بالسيف والجوع يفنى اولئك الانبياء"));
arasuperobject.biblemap.put("b24_14_16", new arasuperobject(24,14,16,"والشعب الذي يتنبأون له يكون مطروحا في شوارع اورشليم من جرى الجوع والسيف وليس من يدفنهم هم ونساؤهم وبنوهم وبناتهم واسكب عليهم شرهم"));
arasuperobject.biblemap.put("b24_14_17", new arasuperobject(24,14,17,"وتقول لهم هذه الكلمة. لتذرف عيناي دموعا ليلا ونهارا ولا تكفا لان العذراء بنت شعبي سحقت سحقا عظيما بضربة موجعة جدا"));
arasuperobject.biblemap.put("b24_14_18", new arasuperobject(24,14,18,"اذا خرجت الى الحقل فاذا القتلى بالسيف واذا دخلت المدينة فاذا المرضى بالجوع لان النبي والكاهن كليهما يطوفان في الارض ولا يعرفان شيئا"));
arasuperobject.biblemap.put("b24_14_19", new arasuperobject(24,14,19,"هل رفضت يهوذا رفضا او كرهت نفسك صهيون. لماذا ضربتنا ولا شفاء لنا. انتظرنا السلام فلم يكن خير وزمان الشفاء فاذا رعب"));
arasuperobject.biblemap.put("b24_14_20", new arasuperobject(24,14,20,"قد عرفنا يا رب شرنا اثم آبائنا لاننا قد اخطأنا اليك"));
arasuperobject.biblemap.put("b24_14_21", new arasuperobject(24,14,21,"لا ترفض لاجل اسمك. لا تهن كرسي مجدك. اذكر. لا تنقض عهدك معنا"));
arasuperobject.biblemap.put("b24_14_22", new arasuperobject(24,14,22,"هل يوجد في اباطيل الامم من يمطر او هل تعطي السموات وابلا. أما انت هو الرب الهنا فنرجوك لانك انت صنعت كل هذه"));
arasuperobject.biblemap.put("b24_15_1", new arasuperobject(24,15,1,"ثم قال الرب لي وان وقف موسى وصموئيل امامي لا تكون نفسي نحو هذا الشعب. اطرحهم من امامي فيخرجوا"));
arasuperobject.biblemap.put("b24_15_2", new arasuperobject(24,15,2,"ويكون اذا قالوا لك الى اين نخرج انك تقول لهم. هكذا قال الرب الذين للموت فالى الموت والذين للسيف فالى السيف والذين للجوع فالى الجوع والذين للسبي فالى السبي"));
arasuperobject.biblemap.put("b24_15_3", new arasuperobject(24,15,3,"وأوكل عليهم اربعة انواع يقول الرب. السيف للقتل والكلاب للسحب وطيور السماء ووحوش الارض للأكل والاهلاك"));
arasuperobject.biblemap.put("b24_15_4", new arasuperobject(24,15,4,"وادفعهم للقلق في كل ممالك الارض من اجل منسّى بن حزقيا ملك يهوذا من اجل ما صنع في اورشليم"));
arasuperobject.biblemap.put("b24_15_5", new arasuperobject(24,15,5,"فمن يشفق عليك يا اورشليم ومن يعزيك ومن يميل ليسأل عن سلامتك"));
arasuperobject.biblemap.put("b24_15_6", new arasuperobject(24,15,6,"انت تركتني يقول الرب. الى الوراء سرت فامدّ يدي عليك واهلكك. مللت من الندامة"));
arasuperobject.biblemap.put("b24_15_7", new arasuperobject(24,15,7,"واذريهم بمذراة في ابواب الارض. اثكل وابيد شعبي. لم يرجعوا عن طرقهم"));
arasuperobject.biblemap.put("b24_15_8", new arasuperobject(24,15,8,"كثرت لي اراملهم اكثر من رمل البحار. جلبت عليهم على ام الشبان ناهبا في الظهيرة. اوقعت عليها بغتة رعدة ورعبات"));
arasuperobject.biblemap.put("b24_15_9", new arasuperobject(24,15,9,"ذبلت والدة السبعة اسلمت نفسها. غربت شمسها اذ بعد نهار. خزيت وخجلت. اما بقيتهم فللسيف ادفعها امام اعدائهم يقول الرب"));
arasuperobject.biblemap.put("b24_15_10", new arasuperobject(24,15,10,"ويل لي يا امي لانك ولدتني انسان خصام وانسان نزاع لكل الارض. لم اقرض ولا اقرضوني وكل واحد يلعنني"));
arasuperobject.biblemap.put("b24_15_11", new arasuperobject(24,15,11,"قال الرب اني احلّك للخير. اني اجعل العدو يتضرع اليك في وقت الشر وفي وقت الضيق"));
arasuperobject.biblemap.put("b24_15_12", new arasuperobject(24,15,12,"هل يكسر الحديد الحديد الذي من الشمال والنحاس"));
arasuperobject.biblemap.put("b24_15_13", new arasuperobject(24,15,13,"ثروتك وخزائنك ادفعها للنهب لا بثمن بل بكل خطاياك وفي كل تخومك"));
arasuperobject.biblemap.put("b24_15_14", new arasuperobject(24,15,14,"واعبرك مع اعدائك في ارض لم تعرفها لان نارا قد أشعلت بغضبي توقد عليكم"));
arasuperobject.biblemap.put("b24_15_15", new arasuperobject(24,15,15,"انت يا رب عرفت. اذكرني وتعهدني وانتقم لي من مضطهديّ. بطول اناتك لا تأخذني. اعرف احتمالي العار لاجلك"));
arasuperobject.biblemap.put("b24_15_16", new arasuperobject(24,15,16,"وجد كلامك فأكلته فكان كلامك لي للفرح ولبهجة قلبي لاني دعيت باسمك يا رب اله الجنود"));
arasuperobject.biblemap.put("b24_15_17", new arasuperobject(24,15,17,"لم اجلس في محفل المازحين مبتهجا. من اجل يدك جلست وحدي لانك قد ملأتني غضبا"));
arasuperobject.biblemap.put("b24_15_18", new arasuperobject(24,15,18,"لماذا كان وجعي دائما وجرحي عديم الشفاء يأبى ان يشفى. أتكون لي مثل كاذب مثل مياه غير دائمة"));
arasuperobject.biblemap.put("b24_15_19", new arasuperobject(24,15,19,"لذلك هكذا قال الرب. ان رجعت ارجعك فتقف امامي واذا اخرجت الثمين من المرذول فمثل فمي تكون. هم يرجعون اليك وانت لا ترجع اليهم"));
arasuperobject.biblemap.put("b24_15_20", new arasuperobject(24,15,20,"واجعلك لهذا الشعب سور نحاس حصينا فيحاربونك ولا يقدرون عليك لاني معك لاخلّصك وانقذك يقول الرب"));
arasuperobject.biblemap.put("b24_15_21", new arasuperobject(24,15,21,"فانقذك من يد الاشرار وافديك من كف العتاة"));
arasuperobject.biblemap.put("b24_16_1", new arasuperobject(24,16,1,"ثم صار اليّ كلام الرب قائلا"));
arasuperobject.biblemap.put("b24_16_2", new arasuperobject(24,16,2,"لا تتخذ لنفسك امرأة ولا يكن لك بنون ولا بنات في هذا الموضع"));
arasuperobject.biblemap.put("b24_16_3", new arasuperobject(24,16,3,"لانه هكذا قال الرب عن البنين وعن البنات المولودين في هذا الموضع وعن امهاتهم اللواتي ولدنهم وعن آبائهم الذين ولدوهم في هذه الارض"));
arasuperobject.biblemap.put("b24_16_4", new arasuperobject(24,16,4,"ميتات امراض يموتون. لا يندبون ولا يدفنون بل يكونون دمنة على وجه الارض وبالسيف والجوع يفنون وتكون جثثهم أكلا لطيور السماء ولوحوش الارض"));
arasuperobject.biblemap.put("b24_16_5", new arasuperobject(24,16,5,"لانه هكذا قال الرب. لا تدخل بيت النوح ولا تمض للندب ولا تعزهم لاني نزعت سلامي من هذا الشعب يقول الرب الاحسان والمراحم"));
arasuperobject.biblemap.put("b24_16_6", new arasuperobject(24,16,6,"فيموت الكبار والصغار في هذه الارض. لا يدفنون ولا يندبونهم ولا يخمشون انفسهم ولا يجعلون قرعة من اجلهم"));
arasuperobject.biblemap.put("b24_16_7", new arasuperobject(24,16,7,"ولا يكسرون خبزا في المناحة ليعزوهم عن ميت ولا يسقونهم كاس التعزية عن اب او ام"));
arasuperobject.biblemap.put("b24_16_8", new arasuperobject(24,16,8,"ولا تدخل بيت الوليمة لتجلس معهم للأكل والشرب"));
arasuperobject.biblemap.put("b24_16_9", new arasuperobject(24,16,9,"لانه هكذا قال رب الجنود اله اسرائيل. هانذا مبطل من هذا الموضع امام اعينكم وفي ايامكم صوت الطرب وصوت الفرح صوت العريس وصوت العروس"));
arasuperobject.biblemap.put("b24_16_10", new arasuperobject(24,16,10,"ويكون حين تخبر هذا الشعب بكل هذه الامور انهم يقولون لك لماذا تكلم الرب علينا بكل هذا الشر العظيم فما هو ذنبنا وما هي خطيتنا التي اخطاناها الى الرب الهنا"));
arasuperobject.biblemap.put("b24_16_11", new arasuperobject(24,16,11,"فتقول لهم من اجل ان آباءكم قد تركوني يقول الرب وذهبوا وراء آلهة اخرى وعبدوها وسجدوا لها واياي تركوا وشريعتي لم يحفظوها"));
arasuperobject.biblemap.put("b24_16_12", new arasuperobject(24,16,12,"وانتم اسأتم في عملكم اكثر من آبائكم وها انتم ذاهبون كل واحد وراء عناد قلبه الشرير حتى لا تسمعوا لي"));
arasuperobject.biblemap.put("b24_16_13", new arasuperobject(24,16,13,"فاطردكم من هذه الارض الى ارض لم تعرفوها انتم ولا آباؤكم فتعبدون هناك آلهة اخرى نهارا وليلا حيث لا اعطيكم نعمة"));
arasuperobject.biblemap.put("b24_16_14", new arasuperobject(24,16,14,"لذلك ها ايام تأتي يقول الرب ولا يقال بعد حيّ هو الرب الذي اصعد بني اسرائيل من ارض مصر"));
arasuperobject.biblemap.put("b24_16_15", new arasuperobject(24,16,15,"بل حيّ هو الرب الذي اصعد بني اسرائيل من ارض الشمال ومن جميع الاراضي التي طردهم اليها. فارجعهم الى ارضهم التي اعطيت آباءهم اياها"));
arasuperobject.biblemap.put("b24_16_16", new arasuperobject(24,16,16,"هانذا ارسل الى جزافين كثيرين يقول الرب فيصطادونهم ثم بعد ذلك ارسل الى كثيرين من القانصين فيقتنصونهم عن كل جبل وعن كل اكمة ومن شقوق الصخور"));
arasuperobject.biblemap.put("b24_16_17", new arasuperobject(24,16,17,"لان عينيّ على كل طرقهم. لم تستتر عن وجهي ولم يختف اثمهم من امام عينيّ"));
arasuperobject.biblemap.put("b24_16_18", new arasuperobject(24,16,18,"واعاقب اولا اثمهم وخطيتهم ضعفين لانهم دنسوا ارضي وبجثث مكرهاتهم ورجاساتهم قد ملأوا ميراثي"));
arasuperobject.biblemap.put("b24_16_19", new arasuperobject(24,16,19,"يا رب عزي وحصني وملجإي في يوم الضيق اليك تأتي الامم من اطراف الارض ويقولون انما ورث آباؤنا كذبا واباطيل وما لا منفعة فيه"));
arasuperobject.biblemap.put("b24_16_20", new arasuperobject(24,16,20,"هل يصنع الانسان لنفسه آلهة وهي ليست آلهة"));
arasuperobject.biblemap.put("b24_16_21", new arasuperobject(24,16,21,"لذلك هانذا اعرّفهم هذه المرة اعرّفهم يدي وجبروتي فيعرفون ان اسمي يهوه"));
arasuperobject.biblemap.put("b24_17_1", new arasuperobject(24,17,1,"خطية يهوذا مكتوبة بقلم من حديد براس من الماس منقوشة على لوح قلبهم وعلى قرون مذابحكم"));
arasuperobject.biblemap.put("b24_17_2", new arasuperobject(24,17,2,"كذكر بنيهم مذابحهم وسواريهم عند اشجار خضر على آكام مرتفعة"));
arasuperobject.biblemap.put("b24_17_3", new arasuperobject(24,17,3,"يا جبلي في الحقل اجعل ثروتك كل خزائنك للنهب ومرتفعاتك للخطية في كل تخومك"));
arasuperobject.biblemap.put("b24_17_4", new arasuperobject(24,17,4,"وتتبرأ وبنفسك عن ميراثك الذي اعطيتك اياه واجعلك تخدم اعداءك في ارض لم تعرفها لانكم قد اضرمتم نارا بغضبي تتقد الى الابد"));
arasuperobject.biblemap.put("b24_17_5", new arasuperobject(24,17,5,"هكذا قال الرب. ملعون الرجل الذي يتكل على الانسان ويجعل البشر ذراعه وعن الرب يحيد قلبه"));
arasuperobject.biblemap.put("b24_17_6", new arasuperobject(24,17,6,"ويكون مثل العرعر في البادية ولا يرى اذا جاء الخير بل يسكن الحرّة في البرية ارضا سبخة وغير مسكونة"));
arasuperobject.biblemap.put("b24_17_7", new arasuperobject(24,17,7,"مبارك الرجل الذي يتكل على الرب وكان الرب متكله"));
arasuperobject.biblemap.put("b24_17_8", new arasuperobject(24,17,8,"فانه يكون كشجرة مغروسة على مياه وعلى نهر تمد اصولها ولا ترى اذا جاء الحرّ ويكون ورقها اخضر وفي سنة القحط لا تخاف ولا تكف عن الاثمار"));
arasuperobject.biblemap.put("b24_17_9", new arasuperobject(24,17,9,"القلب اخدع من كل شيء وهو نجيس من يعرفه"));
arasuperobject.biblemap.put("b24_17_10", new arasuperobject(24,17,10,"انا الرب فاحص القلب مختبر الكلى لاعطي كل واحد حسب طرقه حسب ثمر اعماله"));
arasuperobject.biblemap.put("b24_17_11", new arasuperobject(24,17,11,"حجلة تحضن ما لم تبض محصل الغنى بغير حق. في نصف ايامه يتركه وفي آخرته يكون احمق"));
arasuperobject.biblemap.put("b24_17_12", new arasuperobject(24,17,12,"كرسي مجد مرتفع من الابتداء هو موضع مقدسنا"));
arasuperobject.biblemap.put("b24_17_13", new arasuperobject(24,17,13,"ايها الرب رجاء اسرائيل كل الذين يتركونك يخزون. الحائدون عني في التراب يكتبون لانهم تركوا الرب ينبوع المياه الحية"));
arasuperobject.biblemap.put("b24_17_14", new arasuperobject(24,17,14,"اشفني يا رب فاشفى. خلصني فاخلص لانك انت تسبيحتي"));
arasuperobject.biblemap.put("b24_17_15", new arasuperobject(24,17,15,"ها هم يقولون لي اين هي كلمة الرب. لتأت"));
arasuperobject.biblemap.put("b24_17_16", new arasuperobject(24,17,16,"أما انا فلم اعتزل عن ان اكون راعيا وراءك ولا اشتهيت يوم البلية. انت عرفت. ما خرج من شفتيّ كان مقابل وجهك"));
arasuperobject.biblemap.put("b24_17_17", new arasuperobject(24,17,17,"لا تكن لي رعبا. انت ملجإي في يوم الشر"));
arasuperobject.biblemap.put("b24_17_18", new arasuperobject(24,17,18,"ليخز طارديّ ولا اخز انا. ليرتعبوا هم ولا ارتعب انا. اجلب عليهم يوم الشر واسحقهم سحقا مضاعفا"));
arasuperobject.biblemap.put("b24_17_19", new arasuperobject(24,17,19,"هكذا قال الرب لي. اذهب وقف في باب بني الشعب الذي يدخل منه ملوك يهوذا ويخرجون منه وفي كل ابواب اورشليم"));
arasuperobject.biblemap.put("b24_17_20", new arasuperobject(24,17,20,"وقل لهم. اسمعوا كلمة الرب يا ملوك يهوذا وكل يهوذا وكل سكان اورشليم الداخلين من هذه الابواب"));
arasuperobject.biblemap.put("b24_17_21", new arasuperobject(24,17,21,"هكذا قال الرب. تحفظوا بانفسكم ولا تحملوا حملا يوم السبت ولا تدخلوه في ابواب اورشليم"));
arasuperobject.biblemap.put("b24_17_22", new arasuperobject(24,17,22,"ولا تخرجوا حملا من بيوتكم يوم السبت ولا تعملوا شغلا ما بل قدسوا يوم السبت كما امرت آباءكم"));
arasuperobject.biblemap.put("b24_17_23", new arasuperobject(24,17,23,"فلم يسمعوا ولم يميلوا اذنهم بل قسّوا اعناقهم لئلا يسمعوا ولئلا يقبلوا تأديبا"));
arasuperobject.biblemap.put("b24_17_24", new arasuperobject(24,17,24,"ويكون اذا سمعتم لي سمعا يقول الرب ولم تدخلوا حملا في ابواب هذه المدينة يوم السبت بل قدستم يوم السبت ولم تعملوا فيه شغلا ما"));
arasuperobject.biblemap.put("b24_17_25", new arasuperobject(24,17,25,"انه يدخل في ابواب هذه المدينة ملوك ورؤساء جالسون على كرسي داود راكبون في مركبات وعلى خيل هم ورؤساؤهم رجال يهوذا وسكان اورشليم وتسكن هذه المدينة الى الابد"));
arasuperobject.biblemap.put("b24_17_26", new arasuperobject(24,17,26,"ويأتون من مدن يهوذا ومن حوالي اورشليم ومن ارض بنيامين ومن السهل ومن الجبال ومن الجنوب يأتون بمحرقات وذبائح وتقدمات ولبان ويدخلون بذبائح شكر الى بيت الرب"));
arasuperobject.biblemap.put("b24_17_27", new arasuperobject(24,17,27,"ولكن ان لم تسمعوا لي لتقدسوا يوم السبت لكي لا تحملوا حملا ولا تدخلوه في ابواب اورشليم يوم السبت فاني اشعل نارا في ابوابها فتأكل قصور اورشليم ولا تنطفئ"));
arasuperobject.biblemap.put("b24_18_1", new arasuperobject(24,18,1,"الكلام الذي صار الى ارميا من قبل الرب قائلا"));
arasuperobject.biblemap.put("b24_18_2", new arasuperobject(24,18,2,"قم انزل الى بيت الفخاري وهناك اسمعك كلامي"));
arasuperobject.biblemap.put("b24_18_3", new arasuperobject(24,18,3,"فنزلت الى بيت الفخاري واذا هو يصنع عملا على الدولاب"));
arasuperobject.biblemap.put("b24_18_4", new arasuperobject(24,18,4,"ففسد الوعاء الذي كان يصنعه من الطين بيد الفخاري فعاد وعمله وعاء آخر كما حسن في عيني الفخاري ان يصنعه"));
arasuperobject.biblemap.put("b24_18_5", new arasuperobject(24,18,5,"فصار اليّ كلام الرب قائلا"));
arasuperobject.biblemap.put("b24_18_6", new arasuperobject(24,18,6,"أما استطيع ان اصنع بكم كهذا الفخاري يا بيت اسرائيل يقول الرب. هوذا كالطين بيد الفخاري انتم هكذا بيدي يا بيت اسرائيل"));
arasuperobject.biblemap.put("b24_18_7", new arasuperobject(24,18,7,"تارة اتكلم على امة وعلى مملكة بالقلع والهدم والاهلاك"));
arasuperobject.biblemap.put("b24_18_8", new arasuperobject(24,18,8,"فترجع تلك الامة التي تكلمت عليها عن شرها فاندم عن الشر الذي قصدت ان اصنعه بها"));
arasuperobject.biblemap.put("b24_18_9", new arasuperobject(24,18,9,"وتارة اتكلم على امة وعلى مملكة بالبناء والغرس"));
arasuperobject.biblemap.put("b24_18_10", new arasuperobject(24,18,10,"فتفعل الشر في عينيّ فلا تسمع لصوتي فاندم عن الخير الذي قلت اني احسن اليها به"));
arasuperobject.biblemap.put("b24_18_11", new arasuperobject(24,18,11,"فالآن كلم رجال يهوذا وسكان اورشليم قائلا. هكذا قال الرب. هانذا مصدر عليكم شرا وقاصد عليكم قصدا. فارجعوا كل واحد عن طريقه الرديء واصلحوا طرقكم واعمالكم"));
arasuperobject.biblemap.put("b24_18_12", new arasuperobject(24,18,12,"فقالوا باطل. لاننا نسعى وراء افكارنا وكل واحد يعمل حسب عناد قلبه الرديء"));
arasuperobject.biblemap.put("b24_18_13", new arasuperobject(24,18,13,"لذلك هكذا قال الرب. اسألوا بين الامم من سمع كهذه. ما يقشعر منه جدا عملت عذراء اسرائيل"));
arasuperobject.biblemap.put("b24_18_14", new arasuperobject(24,18,14,"هل يخلو صخر حقلي من ثلج لبنان. او هل تنشف المياه المنفجرة الباردة الجارية"));
arasuperobject.biblemap.put("b24_18_15", new arasuperobject(24,18,15,"لان شعبي قد نسيني. بخروا للباطل وقد اعثروهم في طرقهم في السبل القديمة ليسلكوا في شعب في طريق غير مسهل"));
arasuperobject.biblemap.put("b24_18_16", new arasuperobject(24,18,16,"لتجعل ارضهم خرابا وصفيرا ابديا. كل مار فيها يدهش وينغض راسه"));
arasuperobject.biblemap.put("b24_18_17", new arasuperobject(24,18,17,"كريح شرقية ابددهم امام العدو. اريهم القفا لا الوجه في يوم مصيبتهم"));
arasuperobject.biblemap.put("b24_18_18", new arasuperobject(24,18,18,"فقالوا هلم فنفكر على ارميا افكارا لان الشريعة لا تبيد عن الكاهن ولا المشورة عن الحكيم ولا الكلمة عن النبي. هلم فنضربه باللسان ولكل كلامه لا نصغ"));
arasuperobject.biblemap.put("b24_18_19", new arasuperobject(24,18,19,"اصغ لي يا رب واسمع صوت اخصامي"));
arasuperobject.biblemap.put("b24_18_20", new arasuperobject(24,18,20,"هل يجازى عن خير بشر. لانهم حفروا حفرة لنفسي. اذكر وقوفي امامك لأتكلم عنهم بالخير لاردّ غضبك عنهم"));
arasuperobject.biblemap.put("b24_18_21", new arasuperobject(24,18,21,"لذلك سلّم بنيهم للجوع وادفعهم ليد السيف فتصير نساؤهم ثكالى وارامل وتصير رجالهم قتلى الموت وشبانهم مضروبي السيف في الحرب"));
arasuperobject.biblemap.put("b24_18_22", new arasuperobject(24,18,22,"ليسمع صياح من بيوتهم اذ تجلب عليهم جيشا بغتة. لانهم حفروا حفرة ليمسكوني وطمروا فخاخا لرجليّ"));
arasuperobject.biblemap.put("b24_18_23", new arasuperobject(24,18,23,"وانت يا رب عرفت كل مشورتهم عليّ للموت. لا تصفح عن اثمهم ولا تمح خطيتهم من امامك بل ليكونوا متعثرين امامك. في وقت غضبك عاملهم"));
arasuperobject.biblemap.put("b24_19_1", new arasuperobject(24,19,1,"هكذا قال الرب. اذهب واشتر ابريق فخاري من خزف وخذ من شيوخ الشعب ومن شيوخ الكهنة"));
arasuperobject.biblemap.put("b24_19_2", new arasuperobject(24,19,2,"واخرج الى وادي ابن هنوم الذي عند مدخل باب الفخّار وناد هناك بالكلمات التي اكلمك بها"));
arasuperobject.biblemap.put("b24_19_3", new arasuperobject(24,19,3,"وقل اسمعوا كلمة الرب يا ملوك يهوذا وسكان اورشليم. هكذا قال رب الجنود اله اسرائيل. هانذا جالب على هذا الموضع شرا كل من سمع به تطن اذناه"));
arasuperobject.biblemap.put("b24_19_4", new arasuperobject(24,19,4,"من اجل انهم تركوني وانكروا هذا الموضع وبخروا فيه لآلهة اخرى لم يعرفوها هم ولا آباؤهم ولا ملوك يهوذا وملأوا هذا الموضع من دم الازكياء"));
arasuperobject.biblemap.put("b24_19_5", new arasuperobject(24,19,5,"وبنوا مرتفعات للبعل ليحرقوا اولادهم بالنار محرقات للبعل الذي لم اوص ولا تكلمت به ولا صعد على قلبي"));
arasuperobject.biblemap.put("b24_19_6", new arasuperobject(24,19,6,"لذلك ها ايام تأتي يقول الرب ولا يدعى بعد هذا الموضع توفة ولا وادي ابن هنوم بل وادي القتل"));
arasuperobject.biblemap.put("b24_19_7", new arasuperobject(24,19,7,"وانقض مشورة يهوذا واورشليم في هذا الموضع واجعلهم يسقطون بالسيف امام اعدائهم وبيد طالبي نفوسهم واجعل جثثهم اكلا لطيور السماء ولوحوش الارض"));
arasuperobject.biblemap.put("b24_19_8", new arasuperobject(24,19,8,"واجعل هذه المدينة للدهش والصفير. كل عابر بها يدهش ويصفر من اجل كل ضرباتها"));
arasuperobject.biblemap.put("b24_19_9", new arasuperobject(24,19,9,"واطعمهم لحم بنيهم ولحم بناتهم فياكلون كل واحد لحم صاحبه في الحصار والضيق الذي يضايقهم به اعداؤهم وطالبو نفوسهم"));
arasuperobject.biblemap.put("b24_19_10", new arasuperobject(24,19,10,"ثم تكسر الابريق امام اعين القوم الذين يسيرون معك"));
arasuperobject.biblemap.put("b24_19_11", new arasuperobject(24,19,11,"وتقول لهم. هكذا قال رب الجنود. هكذا اكسر هذا الشعب وهذه المدينة كما يكسر وعاء الفخاري بحيث لا يمكن جبره بعد وفي توفة يدفنون حتى لا يكون موضع للدفن"));
arasuperobject.biblemap.put("b24_19_12", new arasuperobject(24,19,12,"هكذا اصنع لهذا الموضع يقول الرب ولسكانه واجعل هذه المدينة مثل توفه"));
arasuperobject.biblemap.put("b24_19_13", new arasuperobject(24,19,13,"وتكون بيوت اورشليم وبيوت ملوك يهوذا كموضع توفه نجسة كل البيوت التي بخروا على سطوحها لكل جند السماء وسكبوا سكائب لآلهة اخرى"));
arasuperobject.biblemap.put("b24_19_14", new arasuperobject(24,19,14,"ثم جاء ارميا من توفه التي ارسله الرب اليها ليتنبأ ووقف في دار بيت الرب وقال لكل الشعب"));
arasuperobject.biblemap.put("b24_19_15", new arasuperobject(24,19,15,"هكذا قال رب الجنود اله اسرائيل. هانذا جالب على هذه المدينة وعلى كل قراها كل الشر الذي تكلمت به عليها لانهم صلّبوا رقابهم فلم يسمعوا لكلامي"));
arasuperobject.biblemap.put("b24_20_1", new arasuperobject(24,20,1,"وسمع فشحور بن امّير الكاهن. وهو ناظر اول في بيت الرب. ارميا يتنبأ بهذه الكلمات"));
arasuperobject.biblemap.put("b24_20_2", new arasuperobject(24,20,2,"فضرب فشحور ارميا النبي وجعله في المقطرة التي في باب بنيامين الاعلى الذي عند بيت الرب"));
arasuperobject.biblemap.put("b24_20_3", new arasuperobject(24,20,3,"وكان في الغد ان فشحور اخرج ارميا من المقطرة. فقال له ارميا لم يدع الرب اسمك فشحور بل مجور مسّا بيب"));
arasuperobject.biblemap.put("b24_20_4", new arasuperobject(24,20,4,"لانه هكذا قال الرب. هانذا اجعلك خوفا لنفسك ولكل محبيك فيسقطون بسيف اعدائهم وعيناك تنظران وادفع كل يهوذا ليد ملك بابل فيسبيهم الى بابل ويضربهم بالسيف"));
arasuperobject.biblemap.put("b24_20_5", new arasuperobject(24,20,5,"وادفع كل ثروة هذه المدينة وكل تعبها وكل مثمناتها وكل خزائن ملوك يهوذا ادفعها ليد اعدائهم فيغنمونها وياخذونها ويحضرونها الى بابل"));
arasuperobject.biblemap.put("b24_20_6", new arasuperobject(24,20,6,"وانت يا فشحور وكل سكان بيتك تذهبون في السبي وتأتي الى بابل وهناك تموت وهناك تدفن انت وكل محبيك الذين تنبأت لهم بالكذب"));
arasuperobject.biblemap.put("b24_20_7", new arasuperobject(24,20,7,"قد اقنعتني يا رب فاقتنعت وألححت عليّ فغلبت. صرت للضحك كل النهار كل واحد استهزأ بي"));
arasuperobject.biblemap.put("b24_20_8", new arasuperobject(24,20,8,"لاني كلما تكلمت صرخت. ناديت ظلم واغتصاب. لان كلمة الرب صارت لي للعار وللسخرة كل النهار"));
arasuperobject.biblemap.put("b24_20_9", new arasuperobject(24,20,9,"فقلت لا اذكره ولا انطق بعد باسمه. فكان في قلبي كنار محرقة محصورة في عظامي فمللت من الامساك ولم استطع"));
arasuperobject.biblemap.put("b24_20_10", new arasuperobject(24,20,10,"لاني سمعت مذمة من كثيرين. خوف من كل جانب. يقولون اشتكوا فنشتكي عليه. كل اصحابي يراقبون ظلعي قائلين لعله يطغى فنقدر عليه وننتقم منه"));
arasuperobject.biblemap.put("b24_20_11", new arasuperobject(24,20,11,"ولكن الرب معي كجبار قدير. من اجل ذلك يعثر مضطهديّ ولا يقدرون. خزوا جدا لانهم لم ينجحوا خزيا ابديا لا ينسى"));
arasuperobject.biblemap.put("b24_20_12", new arasuperobject(24,20,12,"فيا رب الجنود مختبر الصدّيق ناظر الكلى والقلب دعني ارى نقمتك منهم لاني لك كشفت دعواي"));
arasuperobject.biblemap.put("b24_20_13", new arasuperobject(24,20,13,"رنموا للرب سبحوا الرب لانه قد انقذ نفس المسكين من يد الاشرار"));
arasuperobject.biblemap.put("b24_20_14", new arasuperobject(24,20,14,"ملعون اليوم الذي ولدت فيه. اليوم الذي ولدتني فيه امي لا يكن مباركا"));
arasuperobject.biblemap.put("b24_20_15", new arasuperobject(24,20,15,"ملعون الانسان الذي بشّر ابي قائلا قد ولد لك ابن مفرحا اياه فرحا"));
arasuperobject.biblemap.put("b24_20_16", new arasuperobject(24,20,16,"وليكن ذلك الانسان كالمدن التي قلبها الرب ولم يندم فيسمع صياحا في الصباح وجلبة في وقت الظهيرة"));
arasuperobject.biblemap.put("b24_20_17", new arasuperobject(24,20,17,"لانه لم يقتلني من الرحم فكانت لي امي قبري ورحمها حبلى الى الابد"));
arasuperobject.biblemap.put("b24_20_18", new arasuperobject(24,20,18,"لماذا خرجت من الرحم لارى تعبا وحزنا فتفنى بالخزي ايامي"));
arasuperobject.biblemap.put("b24_21_1", new arasuperobject(24,21,1,"الكلام الذي صار الى ارميا من قبل الرب حين ارسل اليه الملك صدقيا فشحور بن ملكيا وصفنيا بن معسيا الكاهن قائلا"));
arasuperobject.biblemap.put("b24_21_2", new arasuperobject(24,21,2,"اسأل الرب من اجلنا لان نبوخذراصر ملك بابل يحاربنا. لعل الرب يصنع معنا حسب كل عجائبه فيصعد عنا"));
arasuperobject.biblemap.put("b24_21_3", new arasuperobject(24,21,3,"فقال لهما ارميا. هكذا تقولان لصدقيا"));
arasuperobject.biblemap.put("b24_21_4", new arasuperobject(24,21,4,"هكذا قال الرب اله اسرائيل. هانذا ارد ادوات الحرب التي بيدكم التي انتم محاربون بها ملك بابل والكلدانيين الذين يحاصرونكم خارج السور واجمعهم في وسط هذه المدينة"));
arasuperobject.biblemap.put("b24_21_5", new arasuperobject(24,21,5,"وانا احاربكم بيد ممدودة وبذراع شديدة وبغضب وحمو وغيظ عظيم"));
arasuperobject.biblemap.put("b24_21_6", new arasuperobject(24,21,6,"واضرب سكان هذه المدينة الناس والبهائم معا. بوبإ عظيم يموتون"));
arasuperobject.biblemap.put("b24_21_7", new arasuperobject(24,21,7,"ثم بعد ذلك قال الرب ادفع صدقيا ملك يهوذا وعبيده والشعب والباقين في هذه المدينة من الوبإ والسيف والجوع ليد نبوخذراصر ملك بابل وليد اعدائهم وليد طالبي نفوسهم فيضربهم بحد السيف. لا يترأف عليهم ولا يشفق ولا يرحم"));
arasuperobject.biblemap.put("b24_21_8", new arasuperobject(24,21,8,"وتقول لهذا الشعب. هكذا قال الرب. هانذا اجعل امامكم طريق الحياة وطريق الموت"));
arasuperobject.biblemap.put("b24_21_9", new arasuperobject(24,21,9,"الذي يقيم في هذه المدينة يموت بالسيف والجوع والوبإ. والذي يخرج ويسقط الى الكلدانيين الذين يحاصرونكم يحيا وتصير نفسه له غنيمة"));
arasuperobject.biblemap.put("b24_21_10", new arasuperobject(24,21,10,"لاني قد جعلت وجهي على هذه المدينة للشر لا للخير يقول الرب. ليد ملك بابل تدفع فيحرقها بالنار"));
arasuperobject.biblemap.put("b24_21_11", new arasuperobject(24,21,11,"ولبيت ملك يهوذا تقول اسمعوا كلمة الرب"));
arasuperobject.biblemap.put("b24_21_12", new arasuperobject(24,21,12,"يا بيت داود هكذا قال الرب. اقضوا في الصباح عدلا وانقذوا المغصوب من يد الظالم لئلا يخرج كنار غضبي فيحرق وليس من يطفئ من اجل شر اعمالكم"));
arasuperobject.biblemap.put("b24_21_13", new arasuperobject(24,21,13,"هانذا ضدك يا ساكنة العمق صخرة السهل يقول الرب. الذين يقولون من ينزل علينا ومن يدخل الى منازلنا"));
arasuperobject.biblemap.put("b24_21_14", new arasuperobject(24,21,14,"ولكنني اعاقبكم حسب ثمر اعمالكم يقول الرب. واشعل نارا في وعره فتاكل ما حواليها"));
arasuperobject.biblemap.put("b24_22_1", new arasuperobject(24,22,1,"هكذا قال الرب. انزل الى بيت ملك يهوذا وتكلم هناك بهذه الكلمة"));
arasuperobject.biblemap.put("b24_22_2", new arasuperobject(24,22,2,"وقل. اسمع كلمة الرب يا ملك يهوذا الجالس على كرسي داود انت وعبيدك وشعبك الداخلين في هذه الابواب"));
arasuperobject.biblemap.put("b24_22_3", new arasuperobject(24,22,3,"هكذا قال الرب اجروا حقا وعدلا وانقذوا المغصوب من يد الظالم والغريب واليتيم والارملة لا تضطهدوا ولا تظلموا ولا تسفكوا دما زكيا في هذا الموضع"));
arasuperobject.biblemap.put("b24_22_4", new arasuperobject(24,22,4,"لانكم ان فعلتم هذا الامر يدخل في ابواب هذا البيت ملوك جالسون لداود على كرسيه راكبين في مركبات وعلى خيل. هو وعبيده وشعبه"));
arasuperobject.biblemap.put("b24_22_5", new arasuperobject(24,22,5,"وان لم تسمعوا لهذه الكلمات فقد اقسمت بنفسي يقول الرب ان هذا البيت يكون خرابا"));
arasuperobject.biblemap.put("b24_22_6", new arasuperobject(24,22,6,"لانه هكذا قال الرب عن بيت ملك يهوذا. جلعاد انت لي. راس من لبنان. اني اجعلك برية مدنا غير مسكونة"));
arasuperobject.biblemap.put("b24_22_7", new arasuperobject(24,22,7,"واقدّس عليك مهلكين كل واحد وآلاته فيقطعون خيار ارزك ويلقونه في النار"));
arasuperobject.biblemap.put("b24_22_8", new arasuperobject(24,22,8,"ويعبر امم كثيرة في هذه المدينة ويقولون الواحد لصاحبه لماذا فعل الرب مثل هذا لهذه المدينة العظيمة"));
arasuperobject.biblemap.put("b24_22_9", new arasuperobject(24,22,9,"فيقولون من اجل انهم تركوا عهد الرب الههم وسجدوا لآلهة اخرى وعبدوها"));
arasuperobject.biblemap.put("b24_22_10", new arasuperobject(24,22,10,"لا تبكوا ميتا ولا تندبوه. ابكوا ابكوا من يمضي لانه لا يرجع بعد فيرى ارض ميلاده"));
arasuperobject.biblemap.put("b24_22_11", new arasuperobject(24,22,11,"لانه هكذا قال الرب عن شلوم بن يوشيا ملك يهوذا المالك عوضا عن يوشيا ابيه الذي خرج من هذا الموضع لا يرجع اليه بعد"));
arasuperobject.biblemap.put("b24_22_12", new arasuperobject(24,22,12,"بل في الموضع الذي سبوه اليه يموت وهذه الارض لا يراها بعد"));
arasuperobject.biblemap.put("b24_22_13", new arasuperobject(24,22,13,"ويل لمن يبني بيته بغير عدل وعلاليه بغير حق الذي يستخدم صاحبه مجانا ولا يعطيه اجرته"));
arasuperobject.biblemap.put("b24_22_14", new arasuperobject(24,22,14,"القائل أبني لنفسي بيتا وسيعا وعلالي فسيحة ويشق لنفسه كوى ويسقف بارز ويدهن بمغرة"));
arasuperobject.biblemap.put("b24_22_15", new arasuperobject(24,22,15,"هل تملك لانك انت تحاذي الارز. أما اكل ابوك وشرب واجرى حقا وعدلا. حينئذ كان له خير"));
arasuperobject.biblemap.put("b24_22_16", new arasuperobject(24,22,16,"قضى قضاء الفقير والمسكين حينئذ كان خير. أليس ذلك معرفتي يقول الرب"));
arasuperobject.biblemap.put("b24_22_17", new arasuperobject(24,22,17,"لان عينيك وقلبك ليست الا على خطفك وعلى الدم الزكي لتسفكه وعلى الاغتصاب والظلم لتعملهما"));
arasuperobject.biblemap.put("b24_22_18", new arasuperobject(24,22,18,"لذلك هكذا قال الرب عن يهوياقيم بن يوشيا ملك يهوذا. لا يندبونه قائلين آه يا اخي او آه يا اخت. لا يندبونه قائلين آه يا سيد او آه يا جلاله"));
arasuperobject.biblemap.put("b24_22_19", new arasuperobject(24,22,19,"يدفن دفن حمار مسحوبا ومطروحا بعيدا عن ابواب اورشليم"));
arasuperobject.biblemap.put("b24_22_20", new arasuperobject(24,22,20,"اصعدي على لبنان واصرخي وفي باشان اطلقي صوتك واصرخي من عباريم لانه قد سحق كل محبيك"));
arasuperobject.biblemap.put("b24_22_21", new arasuperobject(24,22,21,"تكلمت اليك في راحتك. قلت لا اسمع. هذا طريقك منذ صباك انك لا تسمعين لصوتي"));
arasuperobject.biblemap.put("b24_22_22", new arasuperobject(24,22,22,"كل رعاتك ترعاهم الريح ومحبوك يذهبون الى السبي فحينئذ تخزين وتخجلين لاجل كل شرك"));
arasuperobject.biblemap.put("b24_22_23", new arasuperobject(24,22,23,"ايتها الساكنة في لبنان المعششة في الارز كم يشفق عليك عند أتيان المخاض عليك الوجع كوالدة"));
arasuperobject.biblemap.put("b24_22_24", new arasuperobject(24,22,24,"حيّ انا يقول الرب ولو كان كنياهو بن يهوياقيم ملك يهوذا خاتما على يدي اليمنى فاني من هناك انزعك"));
arasuperobject.biblemap.put("b24_22_25", new arasuperobject(24,22,25,"واسلمك ليد طالبي نفسك وليد الذين تخاف منهم وليد نبوخذراصر ملك بابل وليد الكلدانيين"));
arasuperobject.biblemap.put("b24_22_26", new arasuperobject(24,22,26,"واطرحك وامك التي ولدتك الى ارض اخرى لم تولدا فيها وهناك تموتان"));
arasuperobject.biblemap.put("b24_22_27", new arasuperobject(24,22,27,"اما الارض التي يشتاقان الى الرجوع اليها فلا يرجعان اليها"));
arasuperobject.biblemap.put("b24_22_28", new arasuperobject(24,22,28,"هل هذا الرجل كنياهو وعاء خزف مهان مكسور او اناء ليست فيه مسرة. لماذا طرح هو ونسله وألقوا الى ارض لم يعرفوها"));
arasuperobject.biblemap.put("b24_22_29", new arasuperobject(24,22,29,"يا ارض يا ارض يا ارض اسمعي كلمة الرب"));
arasuperobject.biblemap.put("b24_22_30", new arasuperobject(24,22,30,"هكذا قال الرب. اكتبوا هذا الرجل عقيما رجلا لا ينجح في ايامه لانه لا ينجح من نسله احد جالسا على كرسي داود وحاكما بعد في يهوذا"));
arasuperobject.biblemap.put("b24_23_1", new arasuperobject(24,23,1,"ويل للرعاة الذين يهلكون ويبددون غنم رعيتي يقول الرب"));
arasuperobject.biblemap.put("b24_23_2", new arasuperobject(24,23,2,"لذلك هكذا قال الرب اله اسرائيل عن الرعاة الذين يرعون شعبي. انتم بددتم غنمي وطردتموها ولم تتعهدوها. هانذا اعاقبكم على شر اعمالكم يقول الرب"));
arasuperobject.biblemap.put("b24_23_3", new arasuperobject(24,23,3,"وانا اجمع بقية غنمي من جميع الاراضي التي طردتها اليها واردها الى مرابضها فتثمر وتكثر"));
arasuperobject.biblemap.put("b24_23_4", new arasuperobject(24,23,4,"واقيم عليها رعاة يرعونها فلا تخاف بعد ولا ترتعد ولا تفقد يقول الرب"));
arasuperobject.biblemap.put("b24_23_5", new arasuperobject(24,23,5,"ها ايام تأتي يقول الرب واقيم لداود غصن بر فيملك ملك وينجح ويجري حقا وعدلا في الارض"));
arasuperobject.biblemap.put("b24_23_6", new arasuperobject(24,23,6,"في ايامه يخلص يهوذا ويسكن اسرائيل آمنا وهذا هو اسمه الذي يدعونه به الرب برنا"));
arasuperobject.biblemap.put("b24_23_7", new arasuperobject(24,23,7,"لذلك ها ايام تأتي يقول الرب ولا يقولون بعد حيّ هو الرب الذي اصعد بني اسرائيل من ارض مصر"));
arasuperobject.biblemap.put("b24_23_8", new arasuperobject(24,23,8,"بل حيّ هو الرب الذي اصعد وأتى بنسل بيت اسرائيل من ارض الشمال ومن جميع الاراضي التي طردتهم اليها فيسكنون في ارضهم"));
arasuperobject.biblemap.put("b24_23_9", new arasuperobject(24,23,9,"في الانبياء--انسحق قلبي في وسطي. ارتخت كل عظامي. صرت كانسان سكران ومثل رجل غلبته الخمر من اجل الرب ومن اجل كلام قدسه"));
arasuperobject.biblemap.put("b24_23_10", new arasuperobject(24,23,10,"لان الارض امتلأت من الفاسقين. لانه من اجل اللعن ناحت الارض جفت مراعي البرية وصار سعيهم للشر وجبروتهم للباطل"));
arasuperobject.biblemap.put("b24_23_11", new arasuperobject(24,23,11,"لان الانبياء والكهنة تنجسوا جميعا بل في بيتي وجدت شرهم يقول الرب"));
arasuperobject.biblemap.put("b24_23_12", new arasuperobject(24,23,12,"لذلك يكون طريقهم لهم كمزالق في ظلام دامس فيطردون ويسقطون فيها لاني اجلب عليهم شرا سنة عقابهم يقول الرب"));
arasuperobject.biblemap.put("b24_23_13", new arasuperobject(24,23,13,"وقد رأيت في انبياء السامرة حماقة. تنبأوا بالبعل واضلوا شعبي اسرائيل"));
arasuperobject.biblemap.put("b24_23_14", new arasuperobject(24,23,14,"وفي انبياء اورشليم رأيت ما يقشعر منه. يفسقون ويسلكون بالكذب ويشددون ايادي فاعلي الشر حتى لا يرجعوا الواحد عن شره. صاروا لي كلهم كسدوم وسكانها كعمورة"));
arasuperobject.biblemap.put("b24_23_15", new arasuperobject(24,23,15,"لذلك هكذا قال رب الجنود عن الانبياء. هانذا اطعمهم افسنتينا واسقيهم ماء العلقم لانه من عند انبياء اورشليم خرج نفاق في كل الارض"));
arasuperobject.biblemap.put("b24_23_16", new arasuperobject(24,23,16,"هكذا قال رب الجنود لا تسمعوا لكلام الانبياء الذين يتنبأون لكم. فانهم يجعلونكم باطلا. يتكلمون برؤيا قلبهم لا عن فم الرب"));
arasuperobject.biblemap.put("b24_23_17", new arasuperobject(24,23,17,"قائلين قولا لمحتقريّ قال الرب يكون لكم سلام. ويقولون لكل من يسير في عناد قلبه لا يأتي عليكم شر"));
arasuperobject.biblemap.put("b24_23_18", new arasuperobject(24,23,18,"لانه من وقف في مجلس الرب وراى وسمع كلمته. من اصغى لكلمته وسمع"));
arasuperobject.biblemap.put("b24_23_19", new arasuperobject(24,23,19,"ها زوبعة الرب. غيظ يخرج ونوء هائج. على رؤوس الاشرار يثور"));
arasuperobject.biblemap.put("b24_23_20", new arasuperobject(24,23,20,"لا يرتد غضب الرب حتى يجري ويقيم مقاصد قلبه. في آخر الايام تفهمون فهما"));
arasuperobject.biblemap.put("b24_23_21", new arasuperobject(24,23,21,"لم ارسل الانبياء بل هم جروا. لم اتكلم معهم بل هم تنبأوا"));
arasuperobject.biblemap.put("b24_23_22", new arasuperobject(24,23,22,"ولو وقفوا في مجلسي لأخبروا شعبي بكلامي وردّوهم عن طريقهم الرديء وعن شر اعمالهم"));
arasuperobject.biblemap.put("b24_23_23", new arasuperobject(24,23,23,"ألعلي اله من قريب يقول الرب ولست الها من بعيد"));
arasuperobject.biblemap.put("b24_23_24", new arasuperobject(24,23,24,"اذا اختبأ انسان في اماكن مستترة أفما اراه انا يقول الرب. أما املأ انا السموات والارض يقول الرب"));
arasuperobject.biblemap.put("b24_23_25", new arasuperobject(24,23,25,"قد سمعت ما قالته الانبياء الذين تنبأوا باسمي بالكذب قائلين حلمت حلمت"));
arasuperobject.biblemap.put("b24_23_26", new arasuperobject(24,23,26,"حتى متى يوجد في قلب الانبياء المتنبئين بالكذب بل هم انبياء خداع قلبهم"));
arasuperobject.biblemap.put("b24_23_27", new arasuperobject(24,23,27,"الذين يفكرون ان ينسّوا شعبي اسمي باحلامهم التي يقصونها الرجل على صاحبه كما نسي اباؤهم اسمي لاجل البعل"));
arasuperobject.biblemap.put("b24_23_28", new arasuperobject(24,23,28,"النبي الذي معه حلم فليقص حلما والذي معه كلمتي فليتكلم بكلمتي بالحق. ما للتبن مع الحنطة يقول الرب"));
arasuperobject.biblemap.put("b24_23_29", new arasuperobject(24,23,29,"أليست هكذا كلمتي كنار يقول الرب وكمطرقة تحطم الصخر"));
arasuperobject.biblemap.put("b24_23_30", new arasuperobject(24,23,30,"لذلك هانذا على الانبياء يقول الرب الذين يسرقون كلمتي بعضهم من بعض"));
arasuperobject.biblemap.put("b24_23_31", new arasuperobject(24,23,31,"هانذا على الانبياء يقول الرب الذين ياخذون لسانهم ويقولون قال"));
arasuperobject.biblemap.put("b24_23_32", new arasuperobject(24,23,32,"هانذا على الذين يتنبأون باحلام كاذبة يقول الرب الذين يقصونها ويضلون شعبي باكاذيبهم ومفاخراتهم وانا لم ارسلهم ولا أمرتهم. فلم يفيدوا هذا الشعب فائدة يقول الرب"));
arasuperobject.biblemap.put("b24_23_33", new arasuperobject(24,23,33,"واذا سألك هذا الشعب او نبي او كاهن قائلا ما وحي الرب فقل لهم اي وحي. اني ارفضكم هو قول الرب"));
arasuperobject.biblemap.put("b24_23_34", new arasuperobject(24,23,34,"فالنبي او الكاهن او الشعب الذي يقول وحي الرب اعاقب ذلك الرجل وبيته"));
arasuperobject.biblemap.put("b24_23_35", new arasuperobject(24,23,35,"هكذا تقولون الرجل لصاحبه والرجل لاخيه بماذا اجاب الرب وماذا تكلم به الرب"));
arasuperobject.biblemap.put("b24_23_36", new arasuperobject(24,23,36,"اما وحي الرب فلا تذكروه بعد لان كلمة كل انسان تكون وحيه اذ قد حرّفتم كلام الاله الحي رب الجنود الهنا"));
arasuperobject.biblemap.put("b24_23_37", new arasuperobject(24,23,37,"هكذا تقول للنبي بماذا اجابك الرب وماذا تكلم به الرب"));
arasuperobject.biblemap.put("b24_23_38", new arasuperobject(24,23,38,"واذا كنتم تقولون وحي الرب فلذلك هكذا قال الرب من اجل قولكم هذه الكلمة وحي الرب وقد ارسلت اليكم قائلا لا تقولوا وحي الرب"));
arasuperobject.biblemap.put("b24_23_39", new arasuperobject(24,23,39,"لذلك هانذا انساكم نسيانا وارفضكم من امام وجهي انتم والمدينة التي اعطيتكم وآباءكم اياها"));
arasuperobject.biblemap.put("b24_23_40", new arasuperobject(24,23,40,"واجعل عليكم عارا ابديا وخزيا ابديا لا ينسى"));
arasuperobject.biblemap.put("b24_24_1", new arasuperobject(24,24,1,"اراني الرب واذا سلّتا تين موضوعتان امام هيكل الرب بعدما سبى نبوخذراصر ملك بابل يكنيا بن يهوياقيم ملك يهوذا ورؤساء يهوذا والنجارين والحدادين من اورشليم وأتى بهم الى بابل"));
arasuperobject.biblemap.put("b24_24_2", new arasuperobject(24,24,2,"في السلة الواحدة تين جيد جدا مثل التين الباكوري وفي السلة الاخرى تين رديء جدا لا يؤكل من رداءته"));
arasuperobject.biblemap.put("b24_24_3", new arasuperobject(24,24,3,"فقال لي الرب ماذا انت راء يا ارميا. فقلت تينا. التين الجيد جيد جدا والتين الرديء رديء جدا لا يؤكل من رداءته"));
arasuperobject.biblemap.put("b24_24_4", new arasuperobject(24,24,4,"ثم صار كلام الرب اليّ قائلا"));
arasuperobject.biblemap.put("b24_24_5", new arasuperobject(24,24,5,"هكذا قال الرب اله اسرائيل كهذا التين الجيد هكذا انظر الى سبي يهوذا الذي ارسلته من هذا الموضع الى ارض الكلدانيين للخير"));
arasuperobject.biblemap.put("b24_24_6", new arasuperobject(24,24,6,"واجعل عيني عليهم للخير وارجعهم الى هذه الارض وابنيهم ولا اهدمهم واغرسهم ولا اقلعهم"));
arasuperobject.biblemap.put("b24_24_7", new arasuperobject(24,24,7,"واعطيهم قلبا ليعرفوني اني انا الرب فيكونوا لي شعبا وانا اكون لهم الها لانهم يرجعون اليّ بكل قلبهم"));
arasuperobject.biblemap.put("b24_24_8", new arasuperobject(24,24,8,"وكالتين الرديء الذي لا يؤكل من رداءته. هكذا قال الرب. هكذا اجعل صدقيا ملك يهوذا ورؤساءه وبقية اورشليم الباقية في هذه الارض والساكنة في ارض مصر"));
arasuperobject.biblemap.put("b24_24_9", new arasuperobject(24,24,9,"واسلمهم للقلق والشر في جميع ممالك الارض عارا ومثلا وهزاة ولعنة في جميع المواضع التي اطردهم اليها"));
arasuperobject.biblemap.put("b24_24_10", new arasuperobject(24,24,10,"وارسل عليهم السيف والجوع والوبأ حتى يفنوا عن وجه الارض التي اعطيتهم وآباءهم اياها"));
arasuperobject.biblemap.put("b24_25_1", new arasuperobject(24,25,1,"الكلام الذي صار الى ارميا عن كل شعب يهوذا في السنة الرابعة ليهوياقيم بن يوشيا ملك يهوذا. هي السنة الاولى لنبوخذراصر ملك بابل"));
arasuperobject.biblemap.put("b24_25_2", new arasuperobject(24,25,2,"الذي تكلم به ارميا النبي على كل شعب يهوذا وعلى كل سكان اورشليم قائلا"));
arasuperobject.biblemap.put("b24_25_3", new arasuperobject(24,25,3,"من السنة الثالثة عشر ليوشيا بن آمون ملك يهوذا الى هذا اليوم هذه الثلاث والعشرين سنة صارت كلمة الرب اليّ فكلمتكم مبكرا ومكلما فلم تسمعوا"));
arasuperobject.biblemap.put("b24_25_4", new arasuperobject(24,25,4,"وقد ارسل الرب اليكم كل عبيده الانبياء مبكرا ومرسلا فلم تسمعوا ولم تميلوا اذنكم للسمع"));
arasuperobject.biblemap.put("b24_25_5", new arasuperobject(24,25,5,"قائلين ارجعوا كل واحد عن طريقه الرديء وعن شر اعمالكم واسكنوا في الارض التي اعطاكم الرب اياها وآباءكم من الازل والى الابد"));
arasuperobject.biblemap.put("b24_25_6", new arasuperobject(24,25,6,"ولا تسلكوا وراء آلهة اخرى لتعبدوها وتسجدوا لها ولا تغيظوني بعمل ايديكم فلا اسيء اليكم"));
arasuperobject.biblemap.put("b24_25_7", new arasuperobject(24,25,7,"فلم تسمعوا لي يقول الرب لتغيظوني بعمل ايديكم شرا لكم"));
arasuperobject.biblemap.put("b24_25_8", new arasuperobject(24,25,8,"لذلك هكذا قال رب الجنود. من اجل انكم لم تسمعوا لكلامي"));
arasuperobject.biblemap.put("b24_25_9", new arasuperobject(24,25,9,"هانذا ارسل فآخذ كل عشائر الشمال يقول الرب والى نبوخذراصر عبدي ملك بابل وآتي بهم على هذه الارض وعلى كل سكانها وعلى كل هذه الشعوب حواليها فاحرمهم واجعلهم دهشا وصفيرا وخربا ابدية"));
arasuperobject.biblemap.put("b24_25_10", new arasuperobject(24,25,10,"وابيد منهم صوت الطرب وصوت الفرح صوت العريس وصوت العروس صوت الارحية ونور السراج"));
arasuperobject.biblemap.put("b24_25_11", new arasuperobject(24,25,11,"وتصير كل هذه الارض خرابا ودهشا وتخدم هذه الشعوب ملك بابل سبعين سنة"));
arasuperobject.biblemap.put("b24_25_12", new arasuperobject(24,25,12,"ويكون عند تمام السبعين سنة اني اعاقب ملك بابل وتلك الامة يقول الرب على اثمهم وارض الكلدانيين واجعلها خربا ابدية"));
arasuperobject.biblemap.put("b24_25_13", new arasuperobject(24,25,13,"واجلب على تلك الارض كل كلامي الذي تكلمت به عليها كل ما كتب في هذا السفر الذي تنبأ به ارميا على كل الشعوب"));
arasuperobject.biblemap.put("b24_25_14", new arasuperobject(24,25,14,"لانه قد استعبدهم ايضا امم كثيرة وملوك عظام فاجازيهم حسب اعمالهم وحسب عمل اياديهم"));
arasuperobject.biblemap.put("b24_25_15", new arasuperobject(24,25,15,"لانه هكذا قال لي الرب اله اسرائيل. خذ كاس خمر هذا السخط من يدي واسق جميع الشعوب الذين ارسلك انا اليهم اياها"));
arasuperobject.biblemap.put("b24_25_16", new arasuperobject(24,25,16,"فيشربوا ويترنحوا ويتجننوا من اجل السيف الذي ارسله انا بينهم"));
arasuperobject.biblemap.put("b24_25_17", new arasuperobject(24,25,17,"فأخذت الكاس من يد الرب وسقيت كل الشعوب الذين ارسلني الرب اليهم"));
arasuperobject.biblemap.put("b24_25_18", new arasuperobject(24,25,18,"اورشليم ومدن يهوذا وملوكها ورؤساءها لجعلها خرابا ودهشا وصفيرا ولعنة كهذا اليوم"));
arasuperobject.biblemap.put("b24_25_19", new arasuperobject(24,25,19,"وفرعون ملك مصر وعبيده ورؤساءه وكل شعبه"));
arasuperobject.biblemap.put("b24_25_20", new arasuperobject(24,25,20,"وكل اللفيف وكل ملوك ارض عوص وكل ملوك ارض فلسطين واشقلون وغزة وعقرون وبقية اشدود"));
arasuperobject.biblemap.put("b24_25_21", new arasuperobject(24,25,21,"وادوم وموآب وبني عمون"));
arasuperobject.biblemap.put("b24_25_22", new arasuperobject(24,25,22,"وكل ملوك صور وكل ملوك صيدون وملوك الجزائر التي في عبر البحر"));
arasuperobject.biblemap.put("b24_25_23", new arasuperobject(24,25,23,"وددان وتيماء وبوز وكل مقصوصي الشعر مستديرا"));
arasuperobject.biblemap.put("b24_25_24", new arasuperobject(24,25,24,"وكل ملوك العرب وكل ملوك اللفيف الساكنين في البرية"));
arasuperobject.biblemap.put("b24_25_25", new arasuperobject(24,25,25,"وكل ملوك زمري وكل ملوك عيلام وكل ملوك مادي"));
arasuperobject.biblemap.put("b24_25_26", new arasuperobject(24,25,26,"وكل ملوك الشمال القريبين والبعيدين كل واحد مع اخيه وكل ممالك الارض التي على وجه الارض. وملك شيشك يشرب بعدهم"));
arasuperobject.biblemap.put("b24_25_27", new arasuperobject(24,25,27,"وتقول لهم. هكذا قال رب الجنود اله اسرائيل. اشربوا واسكروا وتقيأوا واسقطوا ولا تقوموا من اجل السيف الذي ارسله انا بينكم"));
arasuperobject.biblemap.put("b24_25_28", new arasuperobject(24,25,28,"ويكون اذا أبوا ان يأخذوا الكاس من يدك ليشربوا انك تقول لهم. هكذا قال رب الجنود تشربون شربا"));
arasuperobject.biblemap.put("b24_25_29", new arasuperobject(24,25,29,"لاني هانذا ابتدئ اسيء الى المدينة التي دعي اسمي عليها فهل تتبرأون انتم. لا تتبرأون لاني انا ادعو السيف على كل سكان الارض يقول رب الجنود"));
arasuperobject.biblemap.put("b24_25_30", new arasuperobject(24,25,30,"وانت فتنبأ عليهم بكل هذا الكلام وقل لهم. الرب من العلاء يزمجر ومن مسكن قدسه يطلق صوته يزئر زئيرا على مسكنه بهتاف كالدائسين يصرخ ضد كل سكان الارض"));
arasuperobject.biblemap.put("b24_25_31", new arasuperobject(24,25,31,"بلغ الضجيج الى اطراف الارض لان للرب خصومة مع الشعوب هو يحاكم كل ذي جسد. يدفع الاشرار للسيف يقول الرب"));
arasuperobject.biblemap.put("b24_25_32", new arasuperobject(24,25,32,"هكذا قال رب الجنود. هوذا الشر يخرج من امة الى امة وينهض نوء عظيم من اطراف الارض"));
arasuperobject.biblemap.put("b24_25_33", new arasuperobject(24,25,33,"وتكون قتلى الرب في ذلك اليوم من اقصاء الارض الى اقصاء الارض. لا يندبون ولا يضمون ولا يدفنون. يكونون دمنة على وجه الارض"));
arasuperobject.biblemap.put("b24_25_34", new arasuperobject(24,25,34,"ولولوا ايها الرعاة واصرخوا وتمرغوا يا رؤساء الغنم لان ايامكم قد كملت للذبح وابددكم فتسقطون كاناء شهي"));
arasuperobject.biblemap.put("b24_25_35", new arasuperobject(24,25,35,"ويبيد المناص عن الرعاة والنجاة عن رؤساء الغنم"));
arasuperobject.biblemap.put("b24_25_36", new arasuperobject(24,25,36,"صوت صراخ الرعاة وولولة رؤساء الغنم. لان الرب قد اهلك مرعاهم"));
arasuperobject.biblemap.put("b24_25_37", new arasuperobject(24,25,37,"وبادت مراعي السلام من اجل حمو غضب الرب"));
arasuperobject.biblemap.put("b24_25_38", new arasuperobject(24,25,38,"ترك كشبل عيصه لان ارضهم صارت خرابا من اجل الظالم ومن اجل حمو غضبه"));
arasuperobject.biblemap.put("b24_26_1", new arasuperobject(24,26,1,"في ابتداء ملك يهوياقيم بن يوشيا ملك يهوذا صار هذا الكلام من قبل الرب قائلا"));
arasuperobject.biblemap.put("b24_26_2", new arasuperobject(24,26,2,"هكذا قال الرب. قف في دار بيت الرب وتكلم على كل مدن يهوذا القادمة للسجود في بيت الرب بكل الكلام الذي اوصيتك ان تتكلم به اليهم. لا تنقص كلمة"));
arasuperobject.biblemap.put("b24_26_3", new arasuperobject(24,26,3,"لعلهم يسمعون ويرجعون كل واحد عن طريقه الشرير فأندم عن الشر الذي قصدت ان اصنعه بهم من اجل شر اعمالهم"));
arasuperobject.biblemap.put("b24_26_4", new arasuperobject(24,26,4,"وتقول لهم هكذا قال الرب. ان لم تسمعوا لي لتسلكوا في شريعتي التي جعلتها امامكم"));
arasuperobject.biblemap.put("b24_26_5", new arasuperobject(24,26,5,"لتسمعوا لكلام عبيدي الانبياء الذين ارسلتهم انا اليكم مبكرا ومرسلا اياهم فلم تسمعوا"));
arasuperobject.biblemap.put("b24_26_6", new arasuperobject(24,26,6,"اجعل هذا البيت كشيلوه وهذه المدينة اجعلها لعنة لكل شعوب الارض"));
arasuperobject.biblemap.put("b24_26_7", new arasuperobject(24,26,7,"وسمع الكهنة والانبياء وكل الشعب ارميا يتكلم بهذا الكلام في بيت الرب"));
arasuperobject.biblemap.put("b24_26_8", new arasuperobject(24,26,8,"وكان لما فرغ ارميا من التكلم بكل ما اوصاه الرب ان يكلم كل الشعب به ان الكهنة والانبياء وكل الشعب امسكوه قائلين تموت موتا"));
arasuperobject.biblemap.put("b24_26_9", new arasuperobject(24,26,9,"لماذا تنبأت باسم الرب قائلا مثل شيلوه يكون هذا البيت وهذه المدينة تكون خربة بلا ساكن. واجتمع كل الشعب على ارميا في بيت الرب"));
arasuperobject.biblemap.put("b24_26_10", new arasuperobject(24,26,10,"فلما سمع رؤساء يهوذا بهذه الأمور صعدوا من بيت الملك الى بيت الرب وجلسوا في مدخل باب الرب الجديد"));
arasuperobject.biblemap.put("b24_26_11", new arasuperobject(24,26,11,"فتكلم الكهنة والانبياء مع الرؤساء وكل الشعب قائلين حق الموت على هذا الرجل لانه قد تنبأ على هذه المدينة كما سمعتم بآذانكم"));
arasuperobject.biblemap.put("b24_26_12", new arasuperobject(24,26,12,"فكلم ارميا كل الرؤساء وكل الشعب قائلا. الرب ارسلني لاتنبأ على هذا البيت وعلى هذه المدينة بكل الكلام الذي سمعتموه"));
arasuperobject.biblemap.put("b24_26_13", new arasuperobject(24,26,13,"فالآن اصلحوا طرقكم واعمالكم واسمعوا لصوت الرب الهكم فيندم الرب عن الشر الذي تكلم عليكم"));
arasuperobject.biblemap.put("b24_26_14", new arasuperobject(24,26,14,"اما انا فهانذا بيدكم. اصنعوا بي كما هو حسن ومستقيم في اعينكم"));
arasuperobject.biblemap.put("b24_26_15", new arasuperobject(24,26,15,"لكن اعلموا علما انكم ان قتلتموني تجعلون دما زكيا على انفسكم وعلى هذه المدينة وعلى سكانها لانه حقا ارسلني الرب اليكم لاتكلم في آذانكم بكل هذا الكلام"));
arasuperobject.biblemap.put("b24_26_16", new arasuperobject(24,26,16,"فقالت الرؤساء وكل الشعب للكهنة والانبياء ليس على هذا الرجل حق الموت لانه انما كلمنا باسم الرب الهنا"));
arasuperobject.biblemap.put("b24_26_17", new arasuperobject(24,26,17,"فقام اناس من شيوخ الارض وكلموا كل جماعة الشعب قائلين"));
arasuperobject.biblemap.put("b24_26_18", new arasuperobject(24,26,18,"ان ميخا المورشتي تنبأ في ايام حزقيا ملك يهوذا وكلم كل شعب يهوذا قائلا هكذا قال رب الجنود ان صهيون تفلح كحقل وتصير اورشليم خربا وجبل البيت شوامخ وعر"));
arasuperobject.biblemap.put("b24_26_19", new arasuperobject(24,26,19,"هل قتلا قتله حزقيا ملك يهوذا وكل يهوذا. ألم يخف الرب وطلب وجه الرب فندم الرب عن الشر الذي تكلم به عليهم. فنحن عاملون شرا عظيما ضد انفسنا"));
arasuperobject.biblemap.put("b24_26_20", new arasuperobject(24,26,20,"وقد كان رجل ايضا يتنبأ باسم الرب اوريا بن شمعيا من قرية يعاريم فتنبأ على هذه المدينة وعلى هذه الارض بكل كلام ارميا"));
arasuperobject.biblemap.put("b24_26_21", new arasuperobject(24,26,21,"ولما سمع الملك يهوياقيم وكل ابطاله وكل الرؤساء كلامه طلب الملك ان يقتله. فلما سمع اوريا خاف وهرب واتى الى مصر"));
arasuperobject.biblemap.put("b24_26_22", new arasuperobject(24,26,22,"فارسل الملك يهوياقيم اناسا الى مصر ألناثان بن عكبور ورجالا معه الى مصر"));
arasuperobject.biblemap.put("b24_26_23", new arasuperobject(24,26,23,"فاخرجوا اوريا من مصر واتوا به الى الملك يهوياقيم فضربه بالسيف وطرح جثته في قبور بني الشعب"));
arasuperobject.biblemap.put("b24_26_24", new arasuperobject(24,26,24,"ولكن يد اخيقام بن شافان كانت مع ارميا حتى لا يدفع ليد الشعب ليقتلوه"));
arasuperobject.biblemap.put("b24_27_1", new arasuperobject(24,27,1,"في ابتداء ملك يهوياقيم بن يوشيا ملك يهوذا صار هذا الكلام الى ارميا من قبل الرب قائلا"));
arasuperobject.biblemap.put("b24_27_2", new arasuperobject(24,27,2,"هكذا قال الرب لي. اصنع لنفسك ربطا وانيارا واجعلها على عنقك"));
arasuperobject.biblemap.put("b24_27_3", new arasuperobject(24,27,3,"وارسلها الى ملك ادوم والى ملك موآب والى ملك بني عمون والى ملك صور والى ملك صيدون بيد الرسل القادمين الى اورشليم الى صدقيا ملك يهوذا"));
arasuperobject.biblemap.put("b24_27_4", new arasuperobject(24,27,4,"واوصهم الى سادتهم قائلا. هكذا قال رب الجنود اله اسرائيل. هكذا تقولون لسادتكم"));
arasuperobject.biblemap.put("b24_27_5", new arasuperobject(24,27,5,"اني انا صنعت الارض والانسان والحيوان الذي على وجه الارض بقوتي العظيمة وبذراعي الممدودة واعطيتها لمن حسن في عينيّ"));
arasuperobject.biblemap.put("b24_27_6", new arasuperobject(24,27,6,"والآن قد دفعت كل هذه الاراضي ليد نبوخذناصّر ملك بابل عبدي واعطيته ايضا حيوان الحقل ليخدمه"));
arasuperobject.biblemap.put("b24_27_7", new arasuperobject(24,27,7,"فتخدمه كل الشعوب وابنه وابن ابنه حتى يأتي وقت ارضه ايضا فتستخدمه شعوب كثيرة وملوك عظام"));
arasuperobject.biblemap.put("b24_27_8", new arasuperobject(24,27,8,"ويكون ان الامة او المملكة التي لا تخدم نبوخذناصّر ملك بابل والتي لا تجعل عنقها تحت نير ملك بابل اني اعاقب تلك الامة بالسيف والجوع والوبإ يقول الرب حتى افنيها بيده"));
arasuperobject.biblemap.put("b24_27_9", new arasuperobject(24,27,9,"فلا تسمعوا انتم لانبيائكم وعرافيكم وحالميكم وعائفيكم وسحرتكم الذين يكلموكم قائلين لا تخدموا ملك بابل"));
arasuperobject.biblemap.put("b24_27_10", new arasuperobject(24,27,10,"لانهم انما يتنبأون لكم بالكذب لكي يبعدوكم من ارضكم ولأطردكم فتهلكوا"));
arasuperobject.biblemap.put("b24_27_11", new arasuperobject(24,27,11,"والامة التي تدخل عنقها تحت نير ملك بابل وتخدمه اجعلها تستقر في ارضها يقول الرب وتعملها وتسكن بها"));
arasuperobject.biblemap.put("b24_27_12", new arasuperobject(24,27,12,"وكلمت صدقيا ملك يهوذا بكل هذا الكلام قائلا ادخلوا اعناقكم تحت نير ملك بابل واخدموه وشعبه واحيوا"));
arasuperobject.biblemap.put("b24_27_13", new arasuperobject(24,27,13,"لماذا تموتون انت وشعبك بالسيف بالجوع والوبإ كما تكلم الرب عن الامة التي لا تخدم ملك بابل"));
arasuperobject.biblemap.put("b24_27_14", new arasuperobject(24,27,14,"فلا تسمعوا لكلام الانبياء الذين يكلمونكم قائلين لا تخدموا ملك بابل لانهم انما يتنبأون لكم بالكذب"));
arasuperobject.biblemap.put("b24_27_15", new arasuperobject(24,27,15,"لاني لم ارسلهم يقول الرب بل هم يتنبأون باسمي بالكذب لكي اطردكم فتهلكوا انتم والانبياء الذين يتنبأون لكم"));
arasuperobject.biblemap.put("b24_27_16", new arasuperobject(24,27,16,"وكلمت الكهنة وكل هذا الشعب قائلا هكذا قال الرب. لا تسمعوا لكلام انبيائكم الذين يتنبأون لكم قائلين ها آنية بيت الرب سترد سريعا من بابل. لانهم انما يتنبأون لكم بالكذب"));
arasuperobject.biblemap.put("b24_27_17", new arasuperobject(24,27,17,"لا تسمعوا لهم. اخدموا ملك بابل واحيوا. لماذا تصير هذه المدينة خربة"));
arasuperobject.biblemap.put("b24_27_18", new arasuperobject(24,27,18,"فان كانوا انبياء وان كانت كلمة الرب معهم فليتوسلوا الى رب الجنود لكي لا تذهب الى بابل الآنية الباقية في بيت الرب وبيت ملك يهوذا وفي اورشليم"));
arasuperobject.biblemap.put("b24_27_19", new arasuperobject(24,27,19,"لانه هكذا قال رب الجنود عن الاعمدة وعن البحر وعن القواعد وعن سائر الآنية الباقية في هذه المدينة"));
arasuperobject.biblemap.put("b24_27_20", new arasuperobject(24,27,20,"التي لم يأخذها نبوخذناصّر ملك بابل عند سبيه يكنيا بن يهوياقيم ملك يهوذا من اورشليم الى بابل وكل اشراف يهوذا واورشليم"));
arasuperobject.biblemap.put("b24_27_21", new arasuperobject(24,27,21,"انه هكذا قال رب الجنود اله اسرائيل عن الآنية الباقية في بيت الرب وبيت ملك يهوذا وفي اورشليم"));
arasuperobject.biblemap.put("b24_27_22", new arasuperobject(24,27,22,"يؤتى بها الى بابل وتكون هناك الى يوم افتقادي اياها يقول الرب فاصعدها واردها الى هذا الموضع"));
arasuperobject.biblemap.put("b24_28_1", new arasuperobject(24,28,1,"وحدث في تلك السنة في ابتداء ملك صدقيا ملك يهوذا في السنة الرابعة في الشهر الخامس ان حننيا بن عزور النبي الذي من جبعون كلمني في بيت الرب امام الكهنة وكل الشعب قائلا"));
arasuperobject.biblemap.put("b24_28_2", new arasuperobject(24,28,2,"هكذا تكلم رب الجنود اله اسرائيل قائلا. قد كسرت نير ملك بابل"));
arasuperobject.biblemap.put("b24_28_3", new arasuperobject(24,28,3,"في سنتين من الزمان ارد الى هذا الموضع كل آنية بيت الرب التي اخذها نبوخذناصّر ملك بابل من هذا الموضع وذهب بها الى بابل"));
arasuperobject.biblemap.put("b24_28_4", new arasuperobject(24,28,4,"وارد الى هذا الموضع يكنيا بن يهوياقيم ملك يهوذا وكل سبي يهوذا الذين ذهبوا الى بابل يقول الرب لاني اكسر نير ملك بابل"));
arasuperobject.biblemap.put("b24_28_5", new arasuperobject(24,28,5,"فكلم ارميا النبي حننيا النبي امام الكهنة وامام كل الشعب الواقفين في بيت الرب"));
arasuperobject.biblemap.put("b24_28_6", new arasuperobject(24,28,6,"وقال ارميا النبي آمين. هكذا ليصنع الرب. ليقم الرب كلامك الذي تنبأت به فيرد آنية بيت الرب وكل السبي من بابل الى هذا الموضع"));
arasuperobject.biblemap.put("b24_28_7", new arasuperobject(24,28,7,"ولكن اسمع هذه الكلمة التي اتكلم انا بها في اذنيك وفي آذان كل الشعب"));
arasuperobject.biblemap.put("b24_28_8", new arasuperobject(24,28,8,"ان الانبياء الذين كانوا قبلي وقبلك منذ القديم وتنبأوا على اراض كثيرة وعلى ممالك عظيمة بالحرب والشر والوبإ"));
arasuperobject.biblemap.put("b24_28_9", new arasuperobject(24,28,9,"النبي الذي تنبأ بالسلام فعند حصول كلمة النبي عرف ذلك النبي ان الرب قد ارسله حقا"));
arasuperobject.biblemap.put("b24_28_10", new arasuperobject(24,28,10,"ثم اخذ حننيا النبي النير عن عنق ارميا النبي وكسره"));
arasuperobject.biblemap.put("b24_28_11", new arasuperobject(24,28,11,"وتكلم حننيا امام كل الشعب قائلا. هكذا قال الرب. هكذا اكسر نير نبوخذناصّر ملك بابل في سنتين من الزمان عن عنق كل الشعوب. وانطلق ارميا النبي في سبيله"));
arasuperobject.biblemap.put("b24_28_12", new arasuperobject(24,28,12,"ثم صار كلام الرب الى ارميا بعدما كسر حننيا النبي النير عن عنق ارميا النبي قائلا"));
arasuperobject.biblemap.put("b24_28_13", new arasuperobject(24,28,13,"اذهب وكلم حننيا قائلا. هكذا قال الرب. قد كسرت انيار الخشب وعملت عوضا عنها انيارا من حديد"));
arasuperobject.biblemap.put("b24_28_14", new arasuperobject(24,28,14,"لانه هكذا قال رب الجنود اله اسرائيل. قد جعلت نيرا من حديد على عنق كل هؤلاء الشعوب ليخدموا نبوخذناصّر ملك بابل فيخدمونه وقد اعطيته ايضا حيوان الحقل"));
arasuperobject.biblemap.put("b24_28_15", new arasuperobject(24,28,15,"فقال ارميا النبي لحننيا النبي اسمع يا حننيا. ان الرب لم يرسلك وانت قد جعلت هذا الشعب يتكل على الكذب"));
arasuperobject.biblemap.put("b24_28_16", new arasuperobject(24,28,16,"لذلك هكذا قال الرب. هانذا طاردك عن وجه الارض. هذه السنة تموت لانك تكلمت بعصيان على الرب"));
arasuperobject.biblemap.put("b24_28_17", new arasuperobject(24,28,17,"فمات حننيا النبي في تلك السنة في الشهر السابع"));
arasuperobject.biblemap.put("b24_29_1", new arasuperobject(24,29,1,"هذا كلام الرسالة التي ارسلها ارميا النبي من اورشليم الى بقية شيوخ السبي والى الكهنة والانبياء والى كل الشعب الذين سباهم نبوخذناصّر من اورشليم الى بابل"));
arasuperobject.biblemap.put("b24_29_2", new arasuperobject(24,29,2,"بعد خروج يكنيا الملك والملكة والخصيان ورؤساء يهوذا واورشليم والنجارين والحدادين من اورشليم"));
arasuperobject.biblemap.put("b24_29_3", new arasuperobject(24,29,3,"بيد العاسة بن شافان وجمريا بن حلقيا اللذين ارسلهما صدقيا ملك يهوذا الى نبوخذناصّر ملك بابل الى بابل قائلا"));
arasuperobject.biblemap.put("b24_29_4", new arasuperobject(24,29,4,"هكذا قال رب الجنود اله اسرائيل لكل السبي الذي سبيته من اورشليم الى بابل"));
arasuperobject.biblemap.put("b24_29_5", new arasuperobject(24,29,5,"ابنوا بيوتا واسكنوا واغرسوا جنات وكلوا ثمرها"));
arasuperobject.biblemap.put("b24_29_6", new arasuperobject(24,29,6,"خذوا نساء ولدوا بنين وبنات وخذوا لبنيكم نساء واعطوا بناتكم لرجال فيلدن بنين وبنات واكثروا هناك ولا تقلوا"));
arasuperobject.biblemap.put("b24_29_7", new arasuperobject(24,29,7,"واطلبوا سلام المدينة التي سبيتكم اليها وصلّوا لاجلها الى الرب لانه بسلامها يكون لكم سلام"));
arasuperobject.biblemap.put("b24_29_8", new arasuperobject(24,29,8,"لانه هكذا قال رب الجنود اله اسرائيل. لا تغشكم انبياؤكم الذين في وسطكم وعرافوكم ولا تسمعوا لاحلامكم التي تتحلمونها"));
arasuperobject.biblemap.put("b24_29_9", new arasuperobject(24,29,9,"لانهم انما يتنبأون لكم باسمي بالكذب. انا لم ارسلهم يقول الرب"));
arasuperobject.biblemap.put("b24_29_10", new arasuperobject(24,29,10,"لانه هكذا قال الرب. اني عند تمام سبعين سنة لبابل اتعهدكم واقيم لكم كلامي الصالح بردكم الى هذا الموضع"));
arasuperobject.biblemap.put("b24_29_11", new arasuperobject(24,29,11,"لاني عرفت الافكار التي انا مفتكر بها عنكم يقول الرب افكار سلام لا شر لاعطيكم آخرة ورجاء"));
arasuperobject.biblemap.put("b24_29_12", new arasuperobject(24,29,12,"فتدعونني وتذهبون وتصلّون اليّ فاسمع لكم"));
arasuperobject.biblemap.put("b24_29_13", new arasuperobject(24,29,13,"وتطلبونني فتجدونني اذ تطلبونني بكل قلبكم"));
arasuperobject.biblemap.put("b24_29_14", new arasuperobject(24,29,14,"فأوجد لكم يقول الرب وارد سبيكم واجمعكم من كل الامم ومن كل المواضع التي طردتكم اليها يقول الرب واردكم الى الموضع الذي سبيتكم منه"));
arasuperobject.biblemap.put("b24_29_15", new arasuperobject(24,29,15,"لانكم قلتم قد اقام لنا الرب نبيين في بابل"));
arasuperobject.biblemap.put("b24_29_16", new arasuperobject(24,29,16,"فهكذا قال الرب للملك الجالس على كرسي داود ولكل الشعب الجالس في هذه المدينة اخوتكم الذين لم يخرجوا معكم في السبي"));
arasuperobject.biblemap.put("b24_29_17", new arasuperobject(24,29,17,"هكذا قال رب الجنود. هانذا ارسل عليهم السيف والجوع والوبأ واجعلهم كتين رديء لا يؤكل من الرداءة"));
arasuperobject.biblemap.put("b24_29_18", new arasuperobject(24,29,18,"والحقهم بالسيف والجوع والوبإ واجعلهم قلقا لكل ممالك الارض حلفا ودهشا وصفيرا وعارا في جميع الامم الذين طردتهم اليهم"));
arasuperobject.biblemap.put("b24_29_19", new arasuperobject(24,29,19,"من اجل انهم لم يسمعوا لكلامي يقول الرب اذ ارسلت اليهم عبيدي الانبياء مبكرا ومرسلا ولم تسمعوا يقول الرب"));
arasuperobject.biblemap.put("b24_29_20", new arasuperobject(24,29,20,"وانتم فاسمعوا كلمة الرب يا جميع السبي الذين ارسلتهم من اورشليم الى بابل"));
arasuperobject.biblemap.put("b24_29_21", new arasuperobject(24,29,21,"هكذا قال رب الجنود اله اسرائيل عن اخآب بن قولايا وعن صدقيا بن معسيا اللذين يتنبآن لكم باسمي بالكذب. هانذا ادفعهما ليد نبوخذراصر ملك بابل فيقتلهما امام عيونكم"));
arasuperobject.biblemap.put("b24_29_22", new arasuperobject(24,29,22,"وتؤخذ منهما لعنة لكل سبي يهوذا الذين في بابل فيقال يجعلك الرب مثل صدقيا ومثل اخآب اللذين قلاهما ملك بابل بالنار"));
arasuperobject.biblemap.put("b24_29_23", new arasuperobject(24,29,23,"من اجل انهما عملا قبيحا في اسرائيل وزنيا بنساء اصحابهما وتكلما باسمي كلاما كاذبا لم اوصهما به وانا العارف والشاهد يقول الرب"));
arasuperobject.biblemap.put("b24_29_24", new arasuperobject(24,29,24,"وكلم شمعيا النحلامي قائلا"));
arasuperobject.biblemap.put("b24_29_25", new arasuperobject(24,29,25,"هكذا تكلم رب الجنود اله اسرائيل قائلا. من اجل انك ارسلت رسائل باسمك الى كل الشعب الذي في اورشليم والى صفنيا بن معسيا الكاهن والى كل الكهنة قائلا"));
arasuperobject.biblemap.put("b24_29_26", new arasuperobject(24,29,26,"قد جعلك الرب كاهنا عوضا عن يهوياداع الكاهن لتكونوا وكلاء في بيت الرب لكل رجل مجنون ومتنبئ فتدفعه الى المقطرة والقيود"));
arasuperobject.biblemap.put("b24_29_27", new arasuperobject(24,29,27,"والآن لماذا لم تزجر ارميا العناثوثي المتنبّئ لكم"));
arasuperobject.biblemap.put("b24_29_28", new arasuperobject(24,29,28,"لانه لذلك ارسل الينا الى بابل قائلا انها مستطيلة. ابنوا بيوتا واسكنوا واغرسوا جنات وكلوا ثمرها"));
arasuperobject.biblemap.put("b24_29_29", new arasuperobject(24,29,29,"فقرأ صفنيا الكاهن هذه الرسالة في اذني ارميا النبي"));
arasuperobject.biblemap.put("b24_29_30", new arasuperobject(24,29,30,"ثم صار كلام الرب الى ارميا قائلا"));
arasuperobject.biblemap.put("b24_29_31", new arasuperobject(24,29,31,"ارسل الى كل السبي قائلا. هكذا قال الرب لشمعيا النحلامي. من اجل ان شمعيا قد تنبأ لكم وانا لم ارسله وجعلكم تتكلون على الكذب"));
arasuperobject.biblemap.put("b24_29_32", new arasuperobject(24,29,32,"لذلك هكذا قال الرب. هانذا اعاقب شمعيا النحلامي ونسله. لا يكون له انسان يجلس في وسط هذا الشعب ولا يرى الخير الذي ساصنعه لشعبي يقول الرب لانه تكلم بعصيان على الرب"));
arasuperobject.biblemap.put("b24_30_1", new arasuperobject(24,30,1,"الكلام الذي صار الى ارميا من قبل الرب قائلا"));
arasuperobject.biblemap.put("b24_30_2", new arasuperobject(24,30,2,"هكذا تكلم الرب اله اسرائيل قائلا. اكتب كل الكلام الذي تكلمت به اليك في سفر"));
arasuperobject.biblemap.put("b24_30_3", new arasuperobject(24,30,3,"لانه ها ايام تأتي يقول الرب وأرد سبي شعبي اسرائيل ويهوذا يقول الرب وارجعهم الى الارض التي اعطيت آباءهم اياها فيمتلكونها"));
arasuperobject.biblemap.put("b24_30_4", new arasuperobject(24,30,4,"فهذا هو الكلام الذي تكلم به الرب عن اسرائيل وعن يهوذا"));
arasuperobject.biblemap.put("b24_30_5", new arasuperobject(24,30,5,"لانه هكذا قال الرب. صوت ارتعاد سمعنا. خوف ولا سلام"));
arasuperobject.biblemap.put("b24_30_6", new arasuperobject(24,30,6,"اسألوا وانظروا ان كان ذكر يضع. لماذا ارى كل رجل ويداه على حقويه كماخض وتحول كل وجه الى صفرة"));
arasuperobject.biblemap.put("b24_30_7", new arasuperobject(24,30,7,"آه لان ذلك اليوم عظيم وليس مثله. وهو وقت ضيق على يعقوب ولكنه سيخلص منه"));
arasuperobject.biblemap.put("b24_30_8", new arasuperobject(24,30,8,"ويكون في ذلك اليوم يقول رب الجنود اني اكسر نيره عن عنقك واقطع ربطك ولا يستعبده بعد الغرباء"));
arasuperobject.biblemap.put("b24_30_9", new arasuperobject(24,30,9,"بل يخدمون الرب الههم وداود ملكهم الذي اقيمه لهم"));
arasuperobject.biblemap.put("b24_30_10", new arasuperobject(24,30,10,"اما انت يا عبدي يعقوب فلا تخف يقول الرب ولا ترتعب يا اسرائيل لاني هانذا اخلصك من بعيد ونسلك من ارض سبيه فيرجع يعقوب ويطمئن ويستريح ولا مزعج"));
arasuperobject.biblemap.put("b24_30_11", new arasuperobject(24,30,11,"لاني انا معك يقول الرب لاخلصك. وان افنيت جميع الامم الذين بددتك اليهم فانت لا افنيك بل اؤدبك بالحق ولا ابرئك تبرئة"));
arasuperobject.biblemap.put("b24_30_12", new arasuperobject(24,30,12,"لانه هكذا قال الرب. كسرك عديم الجبر وجرحك عضال"));
arasuperobject.biblemap.put("b24_30_13", new arasuperobject(24,30,13,"ليس من يقضي حاجتك للعصر ليس لك عقاقير رفادة"));
arasuperobject.biblemap.put("b24_30_14", new arasuperobject(24,30,14,"قد نسيك كل محبيك. اياك لم يطلبوا لاني ضربتك ضربة عدو تأديب قاس لان اثمك قد كثر وخطاياك تعاظمت"));
arasuperobject.biblemap.put("b24_30_15", new arasuperobject(24,30,15,"ما بالك تصرخين بسبب كسرك. جرحك عديم البرء لان اثمك قد كثر وخطاياك تعاظمت قد صنعت هذه بك"));
arasuperobject.biblemap.put("b24_30_16", new arasuperobject(24,30,16,"لذلك يؤكل كل آكليك ويذهب كل اعدائك قاطبة الى السبي ويكون كل سالبيك سلبا وادفع كل ناهبيك للنهب"));
arasuperobject.biblemap.put("b24_30_17", new arasuperobject(24,30,17,"لاني ارفدك واشفيك من جروحك يقول الرب. لانهم قد دعوك منفية صهيون التي لا سائل عنها"));
arasuperobject.biblemap.put("b24_30_18", new arasuperobject(24,30,18,"هكذا قال الرب. هانذا ارد سبي خيام يعقوب وارحم مساكنه وتبنى المدينة على تلّها والقصر يسكن على عادته"));
arasuperobject.biblemap.put("b24_30_19", new arasuperobject(24,30,19,"ويخرج منهم الحمد وصوت اللاعبين واكثرهم ولا يقلون واعظمهم ولا يصغرون"));
arasuperobject.biblemap.put("b24_30_20", new arasuperobject(24,30,20,"ويكون بنوهم كما في القديم وجماعتهم تثبت امامي واعاقب كل مضايقيهم"));
arasuperobject.biblemap.put("b24_30_21", new arasuperobject(24,30,21,"ويكون حاكمهم منهم ويخرج واليهم من وسطهم واقربه فيدنو اليّ لانه من هو هذا الذي ارهن قلبه ليدنو اليّ يقول الرب"));
arasuperobject.biblemap.put("b24_30_22", new arasuperobject(24,30,22,"وتكونون لي شعبا وانا اكون لكم الها"));
arasuperobject.biblemap.put("b24_30_23", new arasuperobject(24,30,23,"هوذا زوبعة الرب تخرج بغضب نوء جارف. على راس الاشرار يثور"));
arasuperobject.biblemap.put("b24_30_24", new arasuperobject(24,30,24,"لا يرتد حمو غضب الرب حتى يفعل وحتى يقيم مقاصد قلبه. في آخر الايام تفهمونها"));
arasuperobject.biblemap.put("b24_31_1", new arasuperobject(24,31,1,"في ذلك الزمان يقول الرب اكون الها لكل عشائر اسرائيل وهم يكونون لي شعبا"));
arasuperobject.biblemap.put("b24_31_2", new arasuperobject(24,31,2,"هكذا قال الرب. قد وجد نعمة في البرية الشعب الباقي عن السيف اسرائيل حين سرت لاريحه"));
arasuperobject.biblemap.put("b24_31_3", new arasuperobject(24,31,3,"تراءى لي الرب من بعيد. ومحبة ابدية احببتك من اجل ذلك ادمت لك الرحمة"));
arasuperobject.biblemap.put("b24_31_4", new arasuperobject(24,31,4,"سابنيك بعد فتبنين يا عذراء اسرائيل. تتزينين بعد بدفوفك وتخرجين في رقص اللاعبين"));
arasuperobject.biblemap.put("b24_31_5", new arasuperobject(24,31,5,"تغرسين بعد كروما في جبال السامرة. يغرس الغارسون ويبتكرون"));
arasuperobject.biblemap.put("b24_31_6", new arasuperobject(24,31,6,"لانه يكون يوم ينادي فيه النواطير في جبال افرايم قوموا فنصعد الى صهيون الى الرب الهنا"));
arasuperobject.biblemap.put("b24_31_7", new arasuperobject(24,31,7,"لانه هكذا قال الرب. رنموا ليعقوب فرحا واهتفوا براس الشعوب. سمعوا سبحوا وقولوا خلص يا رب شعبك بقية اسرائيل"));
arasuperobject.biblemap.put("b24_31_8", new arasuperobject(24,31,8,"هانذا آتي بهم من ارض الشمال واجمعهم من اطراف الارض. بينهم الاعمى والاعرج الحبلى والماخض معا. جمع عظيم يرجع الى هنا"));
arasuperobject.biblemap.put("b24_31_9", new arasuperobject(24,31,9,"بالبكاء يأتون وبالتضرعات اقودهم. اسيّرهم الى انهار ماء في طريق مستقيمة لا يعثرون فيها. لاني صرت لاسرائيل ابا وافرايم هو بكري"));
arasuperobject.biblemap.put("b24_31_10", new arasuperobject(24,31,10,"اسمعوا كلمة الرب ايها الامم واخبروا في الجزائر البعيدة وقولوا مبدد اسرائيل يجمعه ويحرسه كراع قطيعه"));
arasuperobject.biblemap.put("b24_31_11", new arasuperobject(24,31,11,"لان الرب فدى يعقوب وفكه من يد الذي هو اقوى منه"));
arasuperobject.biblemap.put("b24_31_12", new arasuperobject(24,31,12,"فياتون ويرنمون في مرتفع صهيون ويجرون الى جود الرب على الحنطة وعلى الخمر وعلى الزيت وعلى ابناء الغنم والبقر. وتكون نفسهم كجنة ريا ولا يعودون يذوبون بعد"));
arasuperobject.biblemap.put("b24_31_13", new arasuperobject(24,31,13,"حينئذ تفرح العذراء بالرقص والشبان والشيوخ معا واحول نوحهم الى طرب واعزيهم وافرحهم من حزنهم"));
arasuperobject.biblemap.put("b24_31_14", new arasuperobject(24,31,14,"واروي نفس الكهنة من الدسم ويشبع شعبي من جودي يقول الرب"));
arasuperobject.biblemap.put("b24_31_15", new arasuperobject(24,31,15,"هكذا قال الرب. صوت سمع في الرامة نوح بكاء مرّ. راحيل تبكي على اولادها وتأبى ان تتعزى عن اولادها لانهم ليسوا بموجودين"));
arasuperobject.biblemap.put("b24_31_16", new arasuperobject(24,31,16,"هكذا قال الرب. امنعي صوتك عن البكاء وعينيك عن الدموع لانه يوجد جزاء لعملك يقول الرب. فيرجعون من ارض العدو"));
arasuperobject.biblemap.put("b24_31_17", new arasuperobject(24,31,17,"ويوجد رجاء لآخرتك يقول الرب. فيرجع الابناء الى تخمهم"));
arasuperobject.biblemap.put("b24_31_18", new arasuperobject(24,31,18,"سمعا سمعت افرايم ينتحب. ادبتني فتأدبت كعجل غير مروض. توبني فأتوب لانك انت الرب الهي"));
arasuperobject.biblemap.put("b24_31_19", new arasuperobject(24,31,19,"لاني بعد رجوعي ندمت وبعد تعلمي صفقت على فخذي. خزيت وخجلت لاني قد حملت عار صباي"));
arasuperobject.biblemap.put("b24_31_20", new arasuperobject(24,31,20,"هل افرايم ابن عزيز لديّ او ولد مسرّ. لاني كلما تكلمت به اذكره بعد ذكرا. من اجل ذلك حنّت احشائي اليه. رحمة ارحمه يقول الرب"));
arasuperobject.biblemap.put("b24_31_21", new arasuperobject(24,31,21,"انصبي لنفسك صوى. اجعلي لنفسك انصابا. اجعلي قلبك نحو السكة الطريق التي ذهبت فيها. ارجعي يا عذراء اسرائيل ارجعي الى مدنك هذه"));
arasuperobject.biblemap.put("b24_31_22", new arasuperobject(24,31,22,"حتى متى تطوفين ايتها البنت المرتدة. لان الرب قد خلق شيئا حديثا في الارض. انثى تحيط برجل"));
arasuperobject.biblemap.put("b24_31_23", new arasuperobject(24,31,23,"هكذا قال رب الجنود اله اسرائيل. سيقولون بعد هذه الكلمة في ارض يهوذا وفي مدنها عندما ارد سبيهم. يباركك الرب يا مسكن البر يا ايها الجبل المقدس"));
arasuperobject.biblemap.put("b24_31_24", new arasuperobject(24,31,24,"فيسكن فيه يهوذا وكل مدنه معا الفلاحون والذين يسرحون القطعان"));
arasuperobject.biblemap.put("b24_31_25", new arasuperobject(24,31,25,"لاني ارويت النفس المعيية وملأت كل نفس ذائبة"));
arasuperobject.biblemap.put("b24_31_26", new arasuperobject(24,31,26,"على ذلك استيقظت ونظرت ولذّ لي نومي"));
arasuperobject.biblemap.put("b24_31_27", new arasuperobject(24,31,27,"ها ايام تأتي يقول الرب وازرع بيت اسرائيل وبيت يهوذا بزرع انسان وزرع حيوان"));
arasuperobject.biblemap.put("b24_31_28", new arasuperobject(24,31,28,"ويكون كما سهرت عليهم للاقتلاع والهدم والقرض والاهلاك والاذى كذلك اسهر عليهم للبناء والغرس يقول الرب"));
arasuperobject.biblemap.put("b24_31_29", new arasuperobject(24,31,29,"في تلك الايام لا يقولون بعد الآباء اكلوا حصرما واسنان الابناء ضرست"));
arasuperobject.biblemap.put("b24_31_30", new arasuperobject(24,31,30,"بل كل واحد يموت بذنبه كل انسان ياكل الحصرم تضرس اسنانه"));
arasuperobject.biblemap.put("b24_31_31", new arasuperobject(24,31,31,"ها ايام تأتي يقول الرب واقطع مع بيت اسرائيل ومع بيت يهوذا عهدا جديدا"));
arasuperobject.biblemap.put("b24_31_32", new arasuperobject(24,31,32,"ليس كالعهد الذي قطعته مع آبائهم يوم امسكتهم بيدهم لاخرجهم من ارض مصر حين نقضوا عهدي فرفضتهم يقول الرب"));
arasuperobject.biblemap.put("b24_31_33", new arasuperobject(24,31,33,"بل هذا هو العهد الذي اقطعه مع بيت اسرائيل بعد تلك الايام يقول الرب. اجعل شريعتي في داخلهم واكتبها على قلوبهم واكون لهم الها وهم يكونون لي شعبا"));
arasuperobject.biblemap.put("b24_31_34", new arasuperobject(24,31,34,"ولا يعلّمون بعد كل واحد صاحبه وكل واحد اخاه قائلين اعرفوا الرب لانهم كلهم سيعرفونني من صغيرهم الى كبيرهم يقول الرب. لاني اصفح عن اثمهم ولا اذكر خطيتهم بعد"));
arasuperobject.biblemap.put("b24_31_35", new arasuperobject(24,31,35,"هكذا قال الرب الجاعل الشمس للاضاءة نهارا وفرائض القمر والنجوم للاضاءة ليلا الزاجر البحر حين تعجّ امواجه رب الجنود اسمه"));
arasuperobject.biblemap.put("b24_31_36", new arasuperobject(24,31,36,"ان كانت هذه الفرائض تزول من امامي يقول الرب فان نسل اسرائيل ايضا يكف من ان يكون امة امامي كل الايام"));
arasuperobject.biblemap.put("b24_31_37", new arasuperobject(24,31,37,"هكذا قال الرب ان كانت السموات تقاس من فوق وتفحص اساسات الارض من اسفل فاني انا ايضا ارفض كل نسل اسرائيل من اجل كل ما عملوا يقول الرب"));
arasuperobject.biblemap.put("b24_31_38", new arasuperobject(24,31,38,"ها ايام تأتي يقول الرب وتبنى المدينة للرب من برج حننئيل الى باب الزاوية"));
arasuperobject.biblemap.put("b24_31_39", new arasuperobject(24,31,39,"ويخرج بعد خيط القياس مقابله على اكمة جارب ويستدير الى جوعة"));
arasuperobject.biblemap.put("b24_31_40", new arasuperobject(24,31,40,"ويكون كل وادي الجثث والرماد وكل الحقول الى وادي قدرون الى زاوية باب الخيل شرقا قدسا للرب. لا تقلع ولا تهدم الى الابد"));
arasuperobject.biblemap.put("b24_32_1", new arasuperobject(24,32,1,"الكلمة التي صارت الى ارميا من قبل الرب في السنة العاشرة لصدقيا ملك يهوذا. هي السنة الثامنة عشر لنبوخذراصر"));
arasuperobject.biblemap.put("b24_32_2", new arasuperobject(24,32,2,"وكان حينئذ جيش ملك بابل يحاصر اورشليم وكان ارميا النبي محبوسا في دار السجن الذي في بيت ملك يهوذا"));
arasuperobject.biblemap.put("b24_32_3", new arasuperobject(24,32,3,"لان صدقيا ملك يهوذا حبسه قائلا لماذا تنبأت قائلا هكذا قال الرب. هانذا ادفع هذه المدينة ليد ملك بابل فياخذها"));
arasuperobject.biblemap.put("b24_32_4", new arasuperobject(24,32,4,"وصدقيا ملك يهوذا لا يفلت من يد الكلدانيين بل انما يدفع ليد ملك بابل ويكلمه فما لفم وعيناه تريان عينيه"));
arasuperobject.biblemap.put("b24_32_5", new arasuperobject(24,32,5,"ويسير بصدقيا الى بابل فيكون هناك حتى افتقده يقول الرب. ان حاربتم الكلدانيين لا تنجحون"));
arasuperobject.biblemap.put("b24_32_6", new arasuperobject(24,32,6,"فقال ارميا. كلمة الرب صارت اليّ قائلة"));
arasuperobject.biblemap.put("b24_32_7", new arasuperobject(24,32,7,"هوذا حنمئيل بن شلوم عمك يأتي اليك قائلا اشتر لنفسك حقلي الذي في عناثوث لان لك حق الفكاك للشراء"));
arasuperobject.biblemap.put("b24_32_8", new arasuperobject(24,32,8,"فجاء اليّ حنمئيل ابن عمي حسب كلمة الرب الى دار السجن وقال لي اشتر حقلي الذي في عناثوث الذي في ارض بنيامين لان لك حق الارث ولك الفكاك. اشتره لنفسك. فعرفت انها كلمة الرب"));
arasuperobject.biblemap.put("b24_32_9", new arasuperobject(24,32,9,"فاشتريت من حنمئيل ابن عمي الحقل الذي في عناثوث ووزنت له الفضة سبعة عشر شاقلا من الفضة"));
arasuperobject.biblemap.put("b24_32_10", new arasuperobject(24,32,10,"وكتبته في صك وختمت واشهدت شهودا ووزنت الفضة بموازين"));
arasuperobject.biblemap.put("b24_32_11", new arasuperobject(24,32,11,"واخذت صك الشراء المختوم حسب الوصية والفريضة والمفتوح"));
arasuperobject.biblemap.put("b24_32_12", new arasuperobject(24,32,12,"وسلّمت صك الشراء لباروخ بن نيريا بن محسيا امام حنمئيل ابن عمي وامام الشهود الذين امضوا صك الشراء امام كل اليهود الجالسين في دار السجن"));
arasuperobject.biblemap.put("b24_32_13", new arasuperobject(24,32,13,"واوصيت باروخ امامهم قائلا"));
arasuperobject.biblemap.put("b24_32_14", new arasuperobject(24,32,14,"هكذا قال رب الجنود اله اسرائيل. خذ هذين الصكين صك الشراء هذا المختوم والصك المفتوح هذا واجعلهما في اناء من خزف لكي يبقيا اياما كثيرة"));
arasuperobject.biblemap.put("b24_32_15", new arasuperobject(24,32,15,"لانه هكذا قال رب الجنود اله اسرائيل سيشترون بعد بيوتا وحقولا وكروما في هذه الارض"));
arasuperobject.biblemap.put("b24_32_16", new arasuperobject(24,32,16,"ثم صليت الى الرب بعد تسليم صك الشراء لباروخ بن نيريا قائلا"));
arasuperobject.biblemap.put("b24_32_17", new arasuperobject(24,32,17,"آه ايها السيد الرب ها انك قد صنعت السموات والارض بقوتك العظيمة وبذراعك الممدودة. لا يعسر عليك شيء"));
arasuperobject.biblemap.put("b24_32_18", new arasuperobject(24,32,18,"صانع الاحسان لالوف ومجازي ذنب الآباء في حضن بنيهم بعدهم الاله العظيم الجبار رب الجنود اسمه"));
arasuperobject.biblemap.put("b24_32_19", new arasuperobject(24,32,19,"عظيم في المشورة وقادر في العمل الذي عيناك مفتوحتان على كل طرق بني آدم لتعطي كل واحد حسب طرقه وحسب ثمر اعماله"));
arasuperobject.biblemap.put("b24_32_20", new arasuperobject(24,32,20,"الذي جعلت آيات وعجائب في ارض مصر الى هذا اليوم وفي اسرائيل وفي الناس وجعلت لنفسك اسما كهذا اليوم"));
arasuperobject.biblemap.put("b24_32_21", new arasuperobject(24,32,21,"واخرجت شعبك اسرائيل من ارض مصر بآيات وعجائب وبيد شديدة وذراع ممدودة ومخافة عظيمة"));
arasuperobject.biblemap.put("b24_32_22", new arasuperobject(24,32,22,"واعطيتهم هذه الارض التي حلفت لآبائهم ان تعطيهم اياها ارضا تفيض لبنا وعسلا"));
arasuperobject.biblemap.put("b24_32_23", new arasuperobject(24,32,23,"فأتوا وامتلكوها ولم يسمعوا لصوتك ولا ساروا في شريعتك. كل ما اوصيتهم ان يعملوه لم يعملوه فاوقعت بهم كل هذا الشر"));
arasuperobject.biblemap.put("b24_32_24", new arasuperobject(24,32,24,"ها المتارس. قد أتوا الى المدينة ليأخذوها وقد دفعت المدينة ليد الكلدانيين الذين يحاربونها بسبب السيف والجوع والوبإ وما تكلمت به فقد حدث وها انت ناظر"));
arasuperobject.biblemap.put("b24_32_25", new arasuperobject(24,32,25,"وقد قلت انت لي ايها السيد الرب اشتر لنفسك الحقل بفضة واشهد شهودا وقد دفعت المدينة ليد الكلدانيين"));
arasuperobject.biblemap.put("b24_32_26", new arasuperobject(24,32,26,"ثم صارت كلمة الرب الى ارميا قائلة"));
arasuperobject.biblemap.put("b24_32_27", new arasuperobject(24,32,27,"هانذا الرب اله كل ذي جسد. هل يعسر عليّ أمر ما"));
arasuperobject.biblemap.put("b24_32_28", new arasuperobject(24,32,28,"لذلك هكذا قال الرب. هانذا ادفع هذه المدينة ليد الكلدانيين وليد نبوخذراصر ملك بابل فياخذها"));
arasuperobject.biblemap.put("b24_32_29", new arasuperobject(24,32,29,"فيأتي الكلدانيون الذين يحاربون هذه المدينة فيشعلون هذه المدينة بالنار ويحرقونها والبيوت التي بخروا على سطوحها للبعل وسكبوا سكائب لآلهة اخرى ليغيظوني"));
arasuperobject.biblemap.put("b24_32_30", new arasuperobject(24,32,30,"لان بني اسرائيل وبني يهوذا انما صنعوا الشر في عينيّ منذ صباهم. لان بني اسرائيل انما اغاظوني بعمل ايديهم يقول الرب"));
arasuperobject.biblemap.put("b24_32_31", new arasuperobject(24,32,31,"لان هذه المدينة قد صارت لي لغضبي ولغيظي من اليوم الذي فيه بنوها الى هذا اليوم لأنزعها من امام وجهي"));
arasuperobject.biblemap.put("b24_32_32", new arasuperobject(24,32,32,"من اجل كل شر بني اسرائيل وبني يهوذا الذي عملوه ليغيظوني به هم وملوكهم رؤساؤهم وكهنتهم وانبياؤهم ورجال يهوذا وسكان اورشليم"));
arasuperobject.biblemap.put("b24_32_33", new arasuperobject(24,32,33,"وقد حولوا لي القفا لا الوجه وقد علّمتهم مبكرا ومعلّما ولكنهم لم يسمعوا ليقبلوا ادبا"));
arasuperobject.biblemap.put("b24_32_34", new arasuperobject(24,32,34,"بل وضعوا مكرهاتهم في البيت الذي دعي باسمي لينجسوه"));
arasuperobject.biblemap.put("b24_32_35", new arasuperobject(24,32,35,"وبنوا المرتفعات للبعل التي في وادي ابن هنوم ليجيزوا بنيهم وبناتهم في النار لمولك الأمر الذي لم اوصهم به ولا صعد على قلبي ليعملوا هذا الرجس ليجعلوا يهوذا يخطئ"));
arasuperobject.biblemap.put("b24_32_36", new arasuperobject(24,32,36,"والآن لذلك هكذا قال الرب اله اسرائيل عن هذه المدينة التي تقولون انها قد دفعت ليد ملك بابل بالسيف والجوع والوبإ"));
arasuperobject.biblemap.put("b24_32_37", new arasuperobject(24,32,37,"هانذا اجمعهم من كل الاراضي التي طردتهم اليها بغضبي وغيظي وبسخط عظيم واردهم الى هذا الموضع واسكنهم آمنين"));
arasuperobject.biblemap.put("b24_32_38", new arasuperobject(24,32,38,"ويكونون لي شعبا وانا اكون لهم الها"));
arasuperobject.biblemap.put("b24_32_39", new arasuperobject(24,32,39,"واعطيهم قلبا واحدا وطريقا واحدا ليخافوني كل الايام لخيرهم وخير اولادهم بعدهم"));
arasuperobject.biblemap.put("b24_32_40", new arasuperobject(24,32,40,"واقطع لهم عهدا ابديا اني لا ارجع عنهم لاحسن اليهم واجعل مخافتي في قلوبهم فلا يحيدون عني"));
arasuperobject.biblemap.put("b24_32_41", new arasuperobject(24,32,41,"وافرح بهم لاحسن اليهم واغرسهم في هذه الارض بالامانة بكل قلبي وبكل نفسي"));
arasuperobject.biblemap.put("b24_32_42", new arasuperobject(24,32,42,"لانه هكذا قال الرب. كما جلبت على هذا الشعب كل هذا الشر العظيم هكذا اجلب انا عليهم كل الخير الذي تكلمت به اليهم"));
arasuperobject.biblemap.put("b24_32_43", new arasuperobject(24,32,43,"فتشترى الحقول في هذه الارض التي تقولون انها خربة بلا انسان وبلا حيوان وقد دفعت ليد الكلدانيين"));
arasuperobject.biblemap.put("b24_32_44", new arasuperobject(24,32,44,"يشترون الحقول بفضة ويكتبون ذلك في صكوك ويختمون ويشهدون شهودا في ارض بنيامين وحوالي اورشليم وفي مدن يهوذا ومدن الجبل ومدن السهل ومدن الجنوب لاني ارد سبيهم يقول الرب"));
arasuperobject.biblemap.put("b24_33_1", new arasuperobject(24,33,1,"ثم صارت كلمة الرب الى ارميا ثانية وهو محبوس بعد في دار السجن قائلة"));
arasuperobject.biblemap.put("b24_33_2", new arasuperobject(24,33,2,"هكذا قال الرب صانعها الرب مصورها ليثبتها يهوه اسمه"));
arasuperobject.biblemap.put("b24_33_3", new arasuperobject(24,33,3,"ادعني فاجيبك واخبرك بعظائم وعوائص لم تعرفها"));
arasuperobject.biblemap.put("b24_33_4", new arasuperobject(24,33,4,"لانه هكذا قال الرب اله اسرائيل عن بيوت هذه المدينة وعن بيوت ملوك يهوذا التي هدمت للمتاريس والمجانيق"));
arasuperobject.biblemap.put("b24_33_5", new arasuperobject(24,33,5,"يأتون ليحاربوا الكلدانيين ويملأوها من جيف الناس الذين ضربتهم بغضبي وغيظي والذين سترت وجهي عن هذه المدينة لاجل كل شرهم"));
arasuperobject.biblemap.put("b24_33_6", new arasuperobject(24,33,6,"هانذا اضع عليها رفادة وعلاجا واشفيهم واعلن لهم كثرة السلام والامانة"));
arasuperobject.biblemap.put("b24_33_7", new arasuperobject(24,33,7,"وارد سبي يهوذا وسبي اسرائيل وابنيهم كالاول"));
arasuperobject.biblemap.put("b24_33_8", new arasuperobject(24,33,8,"واطهرهم من كل اثمهم الذي اخطأوا به اليّ واغفر كل ذنوبهم التي اخطأوا بها اليّ والتي عصوا بها عليّ"));
arasuperobject.biblemap.put("b24_33_9", new arasuperobject(24,33,9,"فتكون لي اسم فرح للتسبيح وللزينة لدى كل امم الارض الذين يسمعون بكل الخير الذي اصنعه معهم فيخافون ويرتعدون من اجل كل الخير ومن اجل كل السلام الذي اصنعه لها"));
arasuperobject.biblemap.put("b24_33_10", new arasuperobject(24,33,10,"هكذا قال الرب. سيسمع بعد في هذا الموضع الذي تقولون انه خرب بلا انسان وبلا حيوان في مدن يهوذا وفي شوارع اورشليم الخربة بلا انسان ولا ساكن ولا بهيمة"));
arasuperobject.biblemap.put("b24_33_11", new arasuperobject(24,33,11,"صوت الطرب وصوت الفرح صوت العريس وصوت العروس صوت القائلين احمدوا رب الجنود لان الرب صالح لان الى الابد رحمته. صوت الذين ياتون بذبيحة الشكر الى بيت الرب لاني ارد سبي الارض كالاول يقول الرب"));
arasuperobject.biblemap.put("b24_33_12", new arasuperobject(24,33,12,"هكذا قال رب الجنود. سيكون بعد في هذا الموضع الخرب بلا انسان ولا بهيمة وفي كل مدنه مسكن الرعاة المربضين الغنم"));
arasuperobject.biblemap.put("b24_33_13", new arasuperobject(24,33,13,"في مدن الجبل ومدن السهل ومدن الجنوب وفي ارض بنيامين وحوالي اورشليم وفي مدن يهوذا تمر ايضا الغنم تحت يدي المحصي يقول الرب"));
arasuperobject.biblemap.put("b24_33_14", new arasuperobject(24,33,14,"ها ايام تأتي يقول الرب واقيم الكلمة الصالحة التي تكلمت بها الى بيت اسرائيل والى بيت يهوذا"));
arasuperobject.biblemap.put("b24_33_15", new arasuperobject(24,33,15,"في تلك الايام وفي ذلك الزمان انبت لداود غصن البر فيجري عدلا وبرا في الارض"));
arasuperobject.biblemap.put("b24_33_16", new arasuperobject(24,33,16,"في تلك الايام يخلص يهوذا وتسكن اورشليم آمنة وهذا ما تتسمى به الرب برنا"));
arasuperobject.biblemap.put("b24_33_17", new arasuperobject(24,33,17,"لانه هكذا قال الرب. لا ينقطع لداود انسان يجلس على كرسي بيت اسرائيل"));
arasuperobject.biblemap.put("b24_33_18", new arasuperobject(24,33,18,"ولا ينقطع للكهنة اللاويين انسان من امامي يصعد محرقة ويحرق تقدمة ويهيئ ذبيحة كل الايام"));
arasuperobject.biblemap.put("b24_33_19", new arasuperobject(24,33,19,"ثم صارت كلمة الرب الى ارميا قائلة"));
arasuperobject.biblemap.put("b24_33_20", new arasuperobject(24,33,20,"هكذا قال الرب. ان نقضتم عهدي مع النهار وعهدي مع الليل حتى لا يكون نهار ولا ليل في وقتهما"));
arasuperobject.biblemap.put("b24_33_21", new arasuperobject(24,33,21,"فان عهدي ايضا مع داود عبدي ينقض فلا يكون له ابن مالكا على كرسيه ومع اللاويين الكهنة خادمي"));
arasuperobject.biblemap.put("b24_33_22", new arasuperobject(24,33,22,"كما ان جند السموات لا يعد ورمل البحر لا يحصى هكذا اكثر نسل داود عبدي واللاويين خادميّ"));
arasuperobject.biblemap.put("b24_33_23", new arasuperobject(24,33,23,"ثم صارت كلمة الرب الى ارميا قائلة"));
arasuperobject.biblemap.put("b24_33_24", new arasuperobject(24,33,24,"اما ترى ما تكلم به هذا الشعب قائلا ان العشيرتين اللتين اختارهما الرب قد رفضهما. فقد احتقروا شعبي حتى لا يكونوا بعد امة امامهم"));
arasuperobject.biblemap.put("b24_33_25", new arasuperobject(24,33,25,"هكذا قال الرب ان كنت لم اجعل عهدي مع النهار والليل فرائض السموات والارض"));
arasuperobject.biblemap.put("b24_33_26", new arasuperobject(24,33,26,"فاني ايضا ارفض نسل يعقوب وداود عبدي فلا آخذ من نسله حكاما لنسل ابراهيم واسحق ويعقوب لاني ارد سبيهم وارحمهم"));
arasuperobject.biblemap.put("b24_34_1", new arasuperobject(24,34,1,"الكلمة التي صارت الى ارميا من قبل الرب حين كان نبوخذناصّر ملك بابل وكل جيشه وكل ممالك اراضي سلطان يده وكل الشعوب يحاربون اورشليم وكل مدنها قائلة"));
arasuperobject.biblemap.put("b24_34_2", new arasuperobject(24,34,2,"هكذا قال الرب اله اسرائيل. اذهب وكلم صدقيا ملك يهوذا وقل له. هكذا قال الرب. هانذا ادفع هذه المدينة ليد ملك بابل فيحرقها بالنار"));
arasuperobject.biblemap.put("b24_34_3", new arasuperobject(24,34,3,"وانت لا تفلت من يده بل تمسك امساكا وتدفع ليده وترى عيناك عيني ملك بابل وتكلمه فما لفم وتذهب الى بابل"));
arasuperobject.biblemap.put("b24_34_4", new arasuperobject(24,34,4,"ولكن اسمع كلمة الرب يا صدقيا ملك يهوذا. هكذا قال الرب من جهتك. لا تموت بالسيف"));
arasuperobject.biblemap.put("b24_34_5", new arasuperobject(24,34,5,"بسلام تموت وباحراق آبائك الملوك الاولين الذين كانوا قبلك هكذا يحرقون لك ويندبونك قائلين آه يا سيد. لاني انا تكلمت بالكلمة يقول الرب"));
arasuperobject.biblemap.put("b24_34_6", new arasuperobject(24,34,6,"فكلم ارميا النبي صدقيا ملك يهوذا بكل هذا الكلام في اورشليم"));
arasuperobject.biblemap.put("b24_34_7", new arasuperobject(24,34,7,"اذ كان جيش ملك بابل يحارب اورشليم وكل مدن يهوذا الباقية لخيش وعزيقة. لان هاتين بقيتا في مدن يهوذا مدينتين حصينتين"));
arasuperobject.biblemap.put("b24_34_8", new arasuperobject(24,34,8,"الكلمة التي صارت الى ارميا من قبل الرب بعد قطع الملك صدقيا عهدا مع كل الشعب الذي في اورشليم لينادوا بالعتق"));
arasuperobject.biblemap.put("b24_34_9", new arasuperobject(24,34,9,"ان يطلق كل واحد عبده وكل واحد امته العبراني والعبرانية حرّين حتى لا يستعبدهما اي اخويه اليهوديين احد"));
arasuperobject.biblemap.put("b24_34_10", new arasuperobject(24,34,10,"فلما سمع كل الرؤساء وكل الشعب الذين دخلوا في العهد ان يطلقوا كل واحد عبده وكل واحد امته حرّين ولا يستعبدوهما بعد اطاعوا واطلقوا"));
arasuperobject.biblemap.put("b24_34_11", new arasuperobject(24,34,11,"ولكنهم عادوا بعد ذلك فارجعوا العبيد والاماء الذين اطلقوهم احرارا واخضعوهم عبيدا واماء"));
arasuperobject.biblemap.put("b24_34_12", new arasuperobject(24,34,12,"فصارت كلمة الرب الى ارميا من قبل الرب قائلة"));
arasuperobject.biblemap.put("b24_34_13", new arasuperobject(24,34,13,"هكذا قال الرب اله اسرائيل. انا قطعت عهدا مع آبائكم يوم اخرجتهم من ارض مصر من بيت العبيد قائلا"));
arasuperobject.biblemap.put("b24_34_14", new arasuperobject(24,34,14,"في نهاية سبع سنين تطلقون كل واحد اخاه العبراني الذي بيع لك وخدمك ست سنين فتطلقه حرا من عندك. ولكن لم يسمع آباؤكم لي ولا امالوا اذنهم"));
arasuperobject.biblemap.put("b24_34_15", new arasuperobject(24,34,15,"وقد رجعتم انتم اليوم وفعلتم ما هو مستقيم في عينيّ منادين بالعتق كل واحد الى صاحبه وقطعتم عهدا امامي في البيت الذي دعي باسمي"));
arasuperobject.biblemap.put("b24_34_16", new arasuperobject(24,34,16,"ثم عدتم ودنستم اسمي وارجعتم كل واحد عبده وكل واحد امته الذين اطلقتموهم احرارا لانفسهم واخضعتموهم ليكونوا لكم عبيدا واماء"));
arasuperobject.biblemap.put("b24_34_17", new arasuperobject(24,34,17,"لذلك هكذا قال الرب. انتم لم تسمعوا لي لتنادوا بالعتق كل واحد الى اخيه وكل واحد الى صاحبه. هانذا انادي لكم بالعتق يقول الرب للسيف والوبإ والجوع واجعلكم قلقا لكل ممالك الارض"));
arasuperobject.biblemap.put("b24_34_18", new arasuperobject(24,34,18,"وادفع الناس الذين تعدوا عهدي الذين لم يقيموا كلام العهد الذي قطعوه امامي. العجل الذي قطعوه الى اثنين وجازوا بين قطعتيه"));
arasuperobject.biblemap.put("b24_34_19", new arasuperobject(24,34,19,"رؤساء يهوذا ورؤساء اورشليم الخصيان والكهنة وكل شعب الارض الذين جازوا بين قطعتي العجل"));
arasuperobject.biblemap.put("b24_34_20", new arasuperobject(24,34,20,"ادفعهم ليد اعدائهم وليد طالبي نفوسهم فتكون جثثهم أكلا لطيور السماء ووحوش الارض"));
arasuperobject.biblemap.put("b24_34_21", new arasuperobject(24,34,21,"وادفع صدقيا ملك يهوذا ورؤساءه ليد اعدائهم وليد طالبي نفوسهم وليد جيش ملك بابل الذين صعدوا عنكم"));
arasuperobject.biblemap.put("b24_34_22", new arasuperobject(24,34,22,"هانذا آمر يقول الرب واردهم الى هذه المدينة فيحاربونها وياخذونها ويحرقونها بالنار واجعل مدن يهوذا خربة بلا ساكن"));
arasuperobject.biblemap.put("b24_35_1", new arasuperobject(24,35,1,"الكلمة التي صارت الى ارميا من قبل الرب في ايام يهوياقيم بن يوشيا ملك يهوذا قائلة"));
arasuperobject.biblemap.put("b24_35_2", new arasuperobject(24,35,2,"اذهب الى بيت الركابيين وكلمهم وادخل بهم الى بيت الرب الى احد المخادع واسقهم خمرا"));
arasuperobject.biblemap.put("b24_35_3", new arasuperobject(24,35,3,"فأخذت يازنيا بن ارميا بن حبصينيا واخوته وكل بنيه وكل بيت الركابيين"));
arasuperobject.biblemap.put("b24_35_4", new arasuperobject(24,35,4,"ودخلت بهم الى بيت الرب الى مخدع بني حانان بن يجدليا رجل الله الذي بجانب مخدع الرؤساء الذي فوق مخدع معسيا بن شلوم حارس الباب"));
arasuperobject.biblemap.put("b24_35_5", new arasuperobject(24,35,5,"وجعلت امام بني الركابيين طاسات ملآنة خمرا واقداحا وقلت لهم اشربوا خمرا"));
arasuperobject.biblemap.put("b24_35_6", new arasuperobject(24,35,6,"فقالوا لا نشرب خمرا لان يوناداب بن ركاب ابانا اوصانا قائلا لا تشربوا خمرا انتم ولا بنوكم الى الابد"));
arasuperobject.biblemap.put("b24_35_7", new arasuperobject(24,35,7,"ولا تبنوا بيتا ولا تزرعوا زرعا ولا تغرسوا كرما ولا تكن لكم بل اسكنوا في الخيام كل ايامكم لكي تحيوا اياما كثيرة على وجه الارض التي انتم متغربون فيها"));
arasuperobject.biblemap.put("b24_35_8", new arasuperobject(24,35,8,"فسمعنا لصوت يوناداب بن ركاب ابينا في كل ما اوصانا به ان لا نشرب خمرا كل ايامنا نحن ونساؤنا وبنونا وبناتنا"));
arasuperobject.biblemap.put("b24_35_9", new arasuperobject(24,35,9,"وان لا نبني بيوتا لسكنانا وان لا يكون لنا كرم ولا حقل ولا زرع"));
arasuperobject.biblemap.put("b24_35_10", new arasuperobject(24,35,10,"فسكنا في الخيام وسمعنا وعملنا حسب كل ما اوصانا به يوناداب ابونا"));
arasuperobject.biblemap.put("b24_35_11", new arasuperobject(24,35,11,"ولكن كان لما صعد نبوخذراصر ملك بابل الى الارض اننا قلنا هلم فندخل الى اورشليم من وجه جيش الكلدانيين ومن وجه جيش الاراميين. فسكنا في اورشليم"));
arasuperobject.biblemap.put("b24_35_12", new arasuperobject(24,35,12,"ثم صارت كلمة الرب الى ارميا قائلة"));
arasuperobject.biblemap.put("b24_35_13", new arasuperobject(24,35,13,"هكذا قال رب الجنود اله اسرائيل. اذهب وقل لرجال يهوذا وسكان اورشليم. أما تقبلون تأديبا لتسمعوا كلامي يقول الرب"));
arasuperobject.biblemap.put("b24_35_14", new arasuperobject(24,35,14,"قد اقيم كلام يوناداب بن ركاب الذي اوصى بنيه ان لا يشربوا خمرا فلم يشربوا الى هذا اليوم لانهم سمعوا وصية ابيهم. وانا قد كلمتكم مبكرا ومكلما ولم تسمعوا لي"));
arasuperobject.biblemap.put("b24_35_15", new arasuperobject(24,35,15,"وقد ارسلت اليكم كل عبيدي الانبياء مبكرا ومرسلا قائلا ارجعوا كل واحد عن طريقه الرديئة واصلحوا اعمالكم ولا تذهبوا وراء آلهة اخرى لتعبدوها فتسكنوا في الارض التي اعطيتكم وآبائكم. فلم تميلوا اذنكم ولا سمعتم لي"));
arasuperobject.biblemap.put("b24_35_16", new arasuperobject(24,35,16,"لان بني يوناداب بن ركاب قد اقاموا وصية ابيهم التي اوصاهم بها. اما هذا الشعب فلم يسمع لي"));
arasuperobject.biblemap.put("b24_35_17", new arasuperobject(24,35,17,"لذلك هكذا قال الرب اله الجنود اله اسرائيل. هانذا اجلب على يهوذا وعلى كل سكان اورشليم كل الشر الذي تكلمت به عليهم لاني كلمتهم فلم يسمعوا ودعوتهم فلم يجيبوا"));
arasuperobject.biblemap.put("b24_35_18", new arasuperobject(24,35,18,"وقال ارميا لبيت الركابيين هكذا قال رب الجنود اله اسرائيل. من اجل انكم سمعتم لوصية يوناداب ابيكم وحفظتم كل وصاياه وعملتم حسب كل ما اوصاكم به"));
arasuperobject.biblemap.put("b24_35_19", new arasuperobject(24,35,19,"لذلك هكذا قال رب الجنود اله اسرائيل. لا ينقطع ليوناداب بن ركاب انسان يقف امامي كل الايام"));
arasuperobject.biblemap.put("b24_36_1", new arasuperobject(24,36,1,"وكان في السنة الرابعة ليهوياقيم بن يوشيا ملك يهوذا ان هذه الكلمة صارت الى ارميا من قبل الرب قائلة"));
arasuperobject.biblemap.put("b24_36_2", new arasuperobject(24,36,2,"خذ لنفسك درج سفر واكتب فيه كل الكلام الذي كلمتك به على اسرائيل وعلى يهوذا وعلى كل الشعوب من اليوم الذي كلمتك فيه من ايام يوشيا الى هذا اليوم"));
arasuperobject.biblemap.put("b24_36_3", new arasuperobject(24,36,3,"لعل بيت يهوذا يسمعون كل الشر الذي انا مفكر ان اصنعه بهم فيرجعوا كل واحد عن طريقه الرديء فاغفر ذنبهم وخطيتهم"));
arasuperobject.biblemap.put("b24_36_4", new arasuperobject(24,36,4,"فدعا ارميا باروخ بن نيريا فكتب باروخ عن فم ارميا كل كلام الرب الذي كلمه به في درج السفر"));
arasuperobject.biblemap.put("b24_36_5", new arasuperobject(24,36,5,"واوصى ارميا باروخ قائلا. انا محبوس لا اقدر ان ادخل بيت الرب"));
arasuperobject.biblemap.put("b24_36_6", new arasuperobject(24,36,6,"فادخل انت واقرأ في الدرج الذي كتبت عن فمي كل كلام الرب في آذان الشعب في بيت الرب في يوم الصوم واقرأه ايضا في آذان كل يهوذا القادمين من مدنهم"));
arasuperobject.biblemap.put("b24_36_7", new arasuperobject(24,36,7,"لعل تضرعهم يقع امام الرب فيرجعوا كل واحد عن طريقه الرديء لانه عظيم الغضب والغيظ اللذان تكلم بهما الرب على هذا الشعب"));
arasuperobject.biblemap.put("b24_36_8", new arasuperobject(24,36,8,"ففعل باروخ بن نيريا حسب كل ما اوصاه به ارميا النبي بقراءته في السفر كلام الرب في بيت الرب"));
arasuperobject.biblemap.put("b24_36_9", new arasuperobject(24,36,9,"وكان في السنة الخامسة ليهوياقيم بن يوشيا ملك يهوذا في الشهر التاسع انهم نادوا لصوم امام الرب كل الشعب في اورشليم وكل الشعب القادمين من مدن يهوذا الى اورشليم"));
arasuperobject.biblemap.put("b24_36_10", new arasuperobject(24,36,10,"فقرأ باروخ في السفر كلام ارميا في بيت الرب في مخدع جمريا بن شافان الكاتب في الدار العليا في مدخل باب بيت الرب الجديد في آذان كل الشعب"));
arasuperobject.biblemap.put("b24_36_11", new arasuperobject(24,36,11,"فلما سمع ميخايا بن جمريا بن شافان كل كلام الرب من السفر"));
arasuperobject.biblemap.put("b24_36_12", new arasuperobject(24,36,12,"نزل الى بيت الملك الى مخدع الكاتب واذا كل الرؤساء جلوس هناك. اليشاماع الكاتب ودلايا بن شمعيا والناثان بن عكبور وجمريا بن شافان وصدقيا بن حننيا وكل الرؤساء"));
arasuperobject.biblemap.put("b24_36_13", new arasuperobject(24,36,13,"واخبرهم ميخايا بكل الكلام الذي سمعه عندما قرأ باروخ السفر في آذان الشعب"));
arasuperobject.biblemap.put("b24_36_14", new arasuperobject(24,36,14,"فارسل كل الرؤساء الى باروخ يهودي بن نثنيا بن شلميا بن كوشي قائلين. الدرج الذي قرأت فيه في آذان الشعب خذه بيدك وتعال. فاخذ باروخ بن نيريا الدرج بيده واتى اليهم"));
arasuperobject.biblemap.put("b24_36_15", new arasuperobject(24,36,15,"فقالوا له اجلس واقرأه في آذاننا. فقرأ باروخ في آذانهم"));
arasuperobject.biblemap.put("b24_36_16", new arasuperobject(24,36,16,"فكان لما سمعوا كل الكلام انهم خافوا ناظرين بعضهم الى بعض وقالوا لباروخ إخبارا نخبر الملك بكل هذا الكلام"));
arasuperobject.biblemap.put("b24_36_17", new arasuperobject(24,36,17,"ثم سألوا باروخ قائلين اخبرنا كيف كتبت كل هذا الكلام عن فمه"));
arasuperobject.biblemap.put("b24_36_18", new arasuperobject(24,36,18,"فقال لهم باروخ بفمه كان يقرأ لي كل هذا الكلام وانا كنت اكتب في السفر بالحبر"));
arasuperobject.biblemap.put("b24_36_19", new arasuperobject(24,36,19,"فقال الرؤساء لباروخ اذهب واختبئ انت وارميا ولا يعلم انسان اين انتما"));
arasuperobject.biblemap.put("b24_36_20", new arasuperobject(24,36,20,"ثم دخلوا الى الملك الى الدار واودعوا الدرج في مخدع اليشاماع الكاتب واخبروا في اذني الملك بكل الكلام"));
arasuperobject.biblemap.put("b24_36_21", new arasuperobject(24,36,21,"فارسل الملك يهودي لياخذ الدرج فاخذه من مخدع اليشاماع الكاتب وقرأه يهودي في اذني الملك وفي آذان كل الرؤساء الواقفين لدى الملك"));
arasuperobject.biblemap.put("b24_36_22", new arasuperobject(24,36,22,"وكان الملك جالسا في بيت الشتاء في الشهر التاسع. والكانون قدامه متقد"));
arasuperobject.biblemap.put("b24_36_23", new arasuperobject(24,36,23,"وكان لما قرأ يهودي ثلاثة شطور او اربعة انه شقه بمبراة الكاتب والقاه الى النار التي في الكانون حتى فني كل الدرج في النار التي في الكانون"));
arasuperobject.biblemap.put("b24_36_24", new arasuperobject(24,36,24,"ولم يخف الملك ولا كل عبيده السامعين كل هذا الكلام ولا شققوا ثيابهم"));
arasuperobject.biblemap.put("b24_36_25", new arasuperobject(24,36,25,"ولكن الناثان ودلايا وجمريا ترجوا الملك ان لا يحرق الدرج فلم يسمع لهم"));
arasuperobject.biblemap.put("b24_36_26", new arasuperobject(24,36,26,"بل أمر الملك يرحمئيل ابن الملك وسرايا بن عزرئيل وشلميا بن عبدئيل ان يقبضوا على باروخ الكاتب وارميا النبي ولكن الرب خبأهما"));
arasuperobject.biblemap.put("b24_36_27", new arasuperobject(24,36,27,"ثم صارت كلمة الرب الى ارميا بعد احراق الملك الدرج والكلام الذي كتبه باروخ عن فم ارميا قائلة"));
arasuperobject.biblemap.put("b24_36_28", new arasuperobject(24,36,28,"عد فخذ لنفسك درجا آخر واكتب فيه كل الكلام الاول الذي كان في الدرج الاول الذي احرقه يهوياقيم ملك يهوذا"));
arasuperobject.biblemap.put("b24_36_29", new arasuperobject(24,36,29,"وقل ليهوياقيم ملك يهوذا. هكذا قال الرب. انت قد احرقت ذلك الدرج قائلا لماذا كتبت فيه قائلا مجيئا يجيء ملك بابل ويهلك هذه الارض ويلاشي منها الانسان والحيوان"));
arasuperobject.biblemap.put("b24_36_30", new arasuperobject(24,36,30,"لذلك هكذا قال الرب عن يهوياقيم ملك يهوذا. لا يكون له جالس على كرسي داود وتكون جثته مطروحة للحر نهارا وللبرد ليلا"));
arasuperobject.biblemap.put("b24_36_31", new arasuperobject(24,36,31,"واعاقبه ونسله وعبيده على اثمهم واجلب عليهم وعلى سكان اورشليم وعلى رجال يهوذا كل الشر الذي كلمتهم عنه ولم يسمعوا"));
arasuperobject.biblemap.put("b24_36_32", new arasuperobject(24,36,32,"فاخذ ارميا درجا آخر ودفعه لباروخ بن نيريا الكاتب فكتب فيه عن فم ارميا كل كلام السفر الذي احرقه يهوياقيم ملك يهوذا بالنار وزيد عليه ايضا كلام كثير مثله"));
arasuperobject.biblemap.put("b24_37_1", new arasuperobject(24,37,1,"وملك الملك صدقيا بن يوشيا مكان كنياهو بن يهوياقيم الذي ملكه نبوخذراصر ملك بابل في ارض يهوذا"));
arasuperobject.biblemap.put("b24_37_2", new arasuperobject(24,37,2,"ولم يسمع هو ولا عبيده ولا شعب الارض لكلام الرب الذي تكلم به عن يد ارميا النبي"));
arasuperobject.biblemap.put("b24_37_3", new arasuperobject(24,37,3,"وارسل الملك صدقيا يهوخل بن شلميا وصفنيا بن معسيا الكاهن الى ارميا النبي قائلا صلّ لاجلنا الى الرب الهنا"));
arasuperobject.biblemap.put("b24_37_4", new arasuperobject(24,37,4,"وكان ارميا يدخل ويخرج في وسط الشعب اذ لم يكونوا قد جعلوه في بيت السجن"));
arasuperobject.biblemap.put("b24_37_5", new arasuperobject(24,37,5,"وخرج جيش فرعون من مصر. فلما سمع الكلدانيون المحاصرون اورشليم بخبرهم صعدوا عن اورشليم"));
arasuperobject.biblemap.put("b24_37_6", new arasuperobject(24,37,6,"فصارت كلمة الرب الى ارميا النبي قائلة"));
arasuperobject.biblemap.put("b24_37_7", new arasuperobject(24,37,7,"هكذا قال الرب اله اسرائيل هكذا تقولون لملك يهوذا الذي ارسلكم اليّ لتستشيروني. ها ان جيش فرعون الخارج اليكم لمساعدتكم يرجع الى ارضه الى مصر"));
arasuperobject.biblemap.put("b24_37_8", new arasuperobject(24,37,8,"ويرجع الكلدانيون ويحاربون هذه المدينة وياخذونها ويحرقونها بالنار"));
arasuperobject.biblemap.put("b24_37_9", new arasuperobject(24,37,9,"هكذا قال الرب. لا تخدعوا انفسكم قائلين ان الكلدانيين سيذهبون عنا لانهم لا يذهبون"));
arasuperobject.biblemap.put("b24_37_10", new arasuperobject(24,37,10,"لانكم وان ضربتم كل جيش الكلدانيين الذين يحاربونكم وبقي منهم رجال قد طعنوا فانهم يقومون كل واحد في خيمته ويحرقون هذه المدينة بالنار"));
arasuperobject.biblemap.put("b24_37_11", new arasuperobject(24,37,11,"وكان لما أصعد جيش الكلدانيين عن اورشليم من وجه جيش فرعون"));
arasuperobject.biblemap.put("b24_37_12", new arasuperobject(24,37,12,"ان ارميا خرج من اورشليم لينطلق الى ارض بنيامين لينساب من هناك في وسط الشعب"));
arasuperobject.biblemap.put("b24_37_13", new arasuperobject(24,37,13,"وفيما هو في باب بنيامين اذا هناك ناظر الحراس اسمه يرئيا بن شلميا بن حننيا فقبض على ارميا النبي قائلا انك تقع للكلدانيين"));
arasuperobject.biblemap.put("b24_37_14", new arasuperobject(24,37,14,"فقال ارميا كذب. لا اقع للكلدانيين. ولم يسمع له فقبض يرئيا على ارميا وأتى به الى الرؤساء"));
arasuperobject.biblemap.put("b24_37_15", new arasuperobject(24,37,15,"فغضب الرؤساء على ارميا وضربوه وجعلوه في بيت السجن في بيت يوناثان الكاتب لانهم جعلوه بيت السجن"));
arasuperobject.biblemap.put("b24_37_16", new arasuperobject(24,37,16,"فلما دخل ارميا الى بيت الجب والى المقببات اقام ارميا هناك اياما كثيرة"));
arasuperobject.biblemap.put("b24_37_17", new arasuperobject(24,37,17,"ثم ارسل الملك صدقيا واخذه وسأله الملك في بيته سرا وقال هل توجد كلمة من قبل الرب. فقال ارميا توجد. فقال انك تدفع ليد ملك بابل"));
arasuperobject.biblemap.put("b24_37_18", new arasuperobject(24,37,18,"ثم قال ارميا للملك صدقيا ما هي خطيتي اليك والى عبيدك والى هذا الشعب حتى جعلتموني في بيت السجن"));
arasuperobject.biblemap.put("b24_37_19", new arasuperobject(24,37,19,"فاين انبياؤكم الذين تنبأوا لكم قائلين لا ياتي ملك بابل عليكم ولا على هذه الارض"));
arasuperobject.biblemap.put("b24_37_20", new arasuperobject(24,37,20,"فالآن اسمع يا سيدي الملك. ليقع تضرعي امامك ولا تردني الى بيت يوناثان الكاتب فلا اموت هناك"));
arasuperobject.biblemap.put("b24_37_21", new arasuperobject(24,37,21,"فامر الملك صدقيا ان يضعوا ارميا في دار السجن وان يعطى رغيف خبز كل يوم من سوق الخبازين حتى ينفد كل الخبز من المدينة. فاقام ارميا في دار السجن"));
arasuperobject.biblemap.put("b24_38_1", new arasuperobject(24,38,1,"وسمع شفطيا بن متان وجدليا بن فشحور ويوخل بن شلميا وفشحور بن ملكيا الكلام الذي كان ارميا يكلم به الشعب قائلا"));
arasuperobject.biblemap.put("b24_38_2", new arasuperobject(24,38,2,"هكذا قال الرب. الذي يقيم في هذه المدينة يموت بالسيف والجوع والوبإ. واما الذي يخرج الى الكلدانيين فانه يحيا وتكون له نفسه غنيمة فيحيا"));
arasuperobject.biblemap.put("b24_38_3", new arasuperobject(24,38,3,"هكذا قال الرب. هذه المدينة ستدفع دفعا ليد جيش ملك بابل فياخذها"));
arasuperobject.biblemap.put("b24_38_4", new arasuperobject(24,38,4,"فقال الرؤساء للملك ليقتل هذا الرجل لانه بذلك يضعف ايادي رجال الحرب الباقين في هذه المدينة وايادي كل الشعب اذ يكلمهم بمثل هذا الكلام. لان هذا الرجل لا يطلب السلام لهذا الشعب بل الشر"));
arasuperobject.biblemap.put("b24_38_5", new arasuperobject(24,38,5,"فقال الملك صدقيا ها هو بيدكم لان الملك لا يقدر عليكم في شيء"));
arasuperobject.biblemap.put("b24_38_6", new arasuperobject(24,38,6,"فاخذوا ارميا والقوه في جب ملكيا ابن الملك الذي في دار السجن ودلوا ارميا بحبال. ولم يكن في الجب ماء بل وحل فغاص ارميا في الوحل"));
arasuperobject.biblemap.put("b24_38_7", new arasuperobject(24,38,7,"فلما سمع عبد ملك الكوشي رجل خصي وهو في بيت الملك انهم جعلوا ارميا في الجب والملك جالس في باب بنيامين"));
arasuperobject.biblemap.put("b24_38_8", new arasuperobject(24,38,8,"خرج عبد ملك من بيت الملك وكلم الملك قائلا"));
arasuperobject.biblemap.put("b24_38_9", new arasuperobject(24,38,9,"يا سيدي الملك قد اساء هؤلاء الرجال في كل ما فعلوا بارميا النبي الذي طرحوه في الجب فانه يموت في مكانه بسبب الجوع لانه ليس بعد خبز في المدينة"));
arasuperobject.biblemap.put("b24_38_10", new arasuperobject(24,38,10,"فأمر الملك عبد ملك الكوشي قائلا خذ معك من هنا ثلاثين رجلا واطلع ارميا من الجب قبلما يموت"));
arasuperobject.biblemap.put("b24_38_11", new arasuperobject(24,38,11,"فاخذ عبد ملك الرجال معه ودخل الى بيت الملك الى اسفل المخزن واخذ من هناك ثيابا رثة وملابس بالية ودلاها الى ارميا الى الجب بحبال"));
arasuperobject.biblemap.put("b24_38_12", new arasuperobject(24,38,12,"وقال عبد ملك الكوشي لارميا ضع الثياب الرثة والملابس البالية تحت ابطيك تحت الحبال. ففعل ارميا كذلك"));
arasuperobject.biblemap.put("b24_38_13", new arasuperobject(24,38,13,"فجذبوا ارميا بالحبال واطلعوه من الجب. فاقام ارميا في دار السجن"));
arasuperobject.biblemap.put("b24_38_14", new arasuperobject(24,38,14,"فارسل الملك صدقيا واخذ ارميا النبي اليه الى المدخل الثالث الذي في بيت الرب وقال الملك لارميا انا اسألك عن أمر. لا تخف عني شيئا"));
arasuperobject.biblemap.put("b24_38_15", new arasuperobject(24,38,15,"فقال ارميا لصدقيا اذا اخبرتك أفما تقتلني قتلا. واذا اشرت عليك فلا تسمع لي"));
arasuperobject.biblemap.put("b24_38_16", new arasuperobject(24,38,16,"فحلف الملك صدقيا لارميا سرا قائلا حيّ هو الرب الذي صنع لنا هذه النفس اني لا اقتلك ولا ادفعك ليد هؤلاء الرجال الذين يطلبون نفسك"));
arasuperobject.biblemap.put("b24_38_17", new arasuperobject(24,38,17,"فقال ارميا لصدقيا هكذا قال الرب اله الجنود اله اسرائيل. ان كنت تخرج خروجا الى رؤساء ملك بابل تحيا نفسك ولا تحرق هذه المدينة بالنار بل تحيا انت وبيتك"));
arasuperobject.biblemap.put("b24_38_18", new arasuperobject(24,38,18,"ولكن ان كنت لا تخرج الى رؤساء ملك بابل تدفع هذه المدينة ليد الكلدانيين فيحرقونها بالنار وانت لا تفلت من يدهم"));
arasuperobject.biblemap.put("b24_38_19", new arasuperobject(24,38,19,"فقال صدقيا الملك لارميا اني اخاف من اليهود الذين قد سقطوا للكلدانيين لئلا يدفعوني ليدهم فيزدروا بي"));
arasuperobject.biblemap.put("b24_38_20", new arasuperobject(24,38,20,"فقال ارميا لا يدفعونك. اسمع لصوت الرب في ما اكلمك انا به فيحسن اليك وتحيا نفسك"));
arasuperobject.biblemap.put("b24_38_21", new arasuperobject(24,38,21,"وان كنت تأبى الخروج فهذه هي الكلمة التي اراني الرب اياها"));
arasuperobject.biblemap.put("b24_38_22", new arasuperobject(24,38,22,"ها كل النساء اللواتي بقين في بيت ملك يهوذا يخرجن الى رؤساء ملك بابل وهنّ يقلن قد خدعك وقدر عليك مسالموك. غاصت في الحمأة رجلاك وارتدتا الى الوراء"));
arasuperobject.biblemap.put("b24_38_23", new arasuperobject(24,38,23,"ويخرجون كل نسائك وبنيك الى الكلدانيين وانت لا تفلت من يدهم لانك انت تمسك بيد ملك بابل وهذه المدينة تحرق بالنار"));
arasuperobject.biblemap.put("b24_38_24", new arasuperobject(24,38,24,"فقال صدقيا لارميا لا يعلم احد بهذا الكلام فلا تموت"));
arasuperobject.biblemap.put("b24_38_25", new arasuperobject(24,38,25,"واذا سمع الرؤساء اني كلمتك واتوا اليك وقالوا لك اخبرنا بماذا كلمت الملك لا تخف عنا فلا نقتلك وماذا قال لك الملك"));
arasuperobject.biblemap.put("b24_38_26", new arasuperobject(24,38,26,"فقل لهم اني القيت تضرعي امام الملك حتى لا يردني الى بيت يوناثان لاموت هناك"));
arasuperobject.biblemap.put("b24_38_27", new arasuperobject(24,38,27,"فأتى كل الرؤساء الى ارميا وسألوه فاخبرهم حسب كل هذا الكلام الذي اوصاه به الملك فسكتوا عنه لان الأمر لم يسمع"));
arasuperobject.biblemap.put("b24_38_28", new arasuperobject(24,38,28,"فاقام ارميا في دار السجن الى اليوم الذي أخذت فيه اورشليم. ولما أخذت اورشليم"));
arasuperobject.biblemap.put("b24_39_1", new arasuperobject(24,39,1,"في السنة التاسعة لصدقيا ملك يهوذا في الشهر العاشر اتى نبوخذراصر ملك بابل وكل جيشه الى اورشليم وحاصروها"));
arasuperobject.biblemap.put("b24_39_2", new arasuperobject(24,39,2,"وفي السنة الحادية عشرة لصدقيا في الشهر الرابع في تاسع الشهر فتحت المدينة"));
arasuperobject.biblemap.put("b24_39_3", new arasuperobject(24,39,3,"ودخل كل رؤساء ملك بابل وجلسوا في الباب الاوسط نرجل شراصر وسمجرنبو وسرسخيم رئيس الخصيان ونرجل شراصر رئيس المجوس وكل بقية رؤساء ملك بابل"));
arasuperobject.biblemap.put("b24_39_4", new arasuperobject(24,39,4,"فلما رآهم صدقيا ملك يهوذا وكل رجال الحرب هربوا وخرجوا ليلا من المدينة في طريق جنة الملك من الباب بين السورين وخرج هو في طريق العربة"));
arasuperobject.biblemap.put("b24_39_5", new arasuperobject(24,39,5,"فسعى جيش الكلدانيين وراءهم فادركوا صدقيا في عربات اريحا فاخذوه واصعدوه الى نبوخذناصّر ملك بابل الى ربلة في ارض حماة فكلمه بالقضاء عليه"));
arasuperobject.biblemap.put("b24_39_6", new arasuperobject(24,39,6,"فقتل ملك بابل بني صدقيا في ربلة امام عينيه وقتل ملك بابل كل اشراف يهوذا"));
arasuperobject.biblemap.put("b24_39_7", new arasuperobject(24,39,7,"واعمى عيني صدقيا وقيده بسلاسل نحاس لياتى به الى بابل"));
arasuperobject.biblemap.put("b24_39_8", new arasuperobject(24,39,8,"اما بيت الملك وبيوت الشعب فاحرقها الكلدانيون بالنار ونقضوا اسوار اورشليم"));
arasuperobject.biblemap.put("b24_39_9", new arasuperobject(24,39,9,"وبقية الشعب الذين بقوا في المدينة والهاربون الذين سقطوا له وبقية الشعب الذين بقوا سباهم نبوزرادان رئيس الشرط الى بابل"));
arasuperobject.biblemap.put("b24_39_10", new arasuperobject(24,39,10,"ولكن بعض الشعب الفقراء الذين لم يكن لهم شيء تركهم نبوزرادان رئيس الشرط في ارض يهوذا واعطاهم كروما وحقولا في ذلك اليوم"));
arasuperobject.biblemap.put("b24_39_11", new arasuperobject(24,39,11,"واوصى نبوخذراصر ملك بابل على ارميا نبوزرادان رئيس الشرط قائلا"));
arasuperobject.biblemap.put("b24_39_12", new arasuperobject(24,39,12,"خذه وضع عينك عليه ولا تفعل به شيئا رديئا بل كما يكلمك هكذا افعل معه"));
arasuperobject.biblemap.put("b24_39_13", new arasuperobject(24,39,13,"فارسل نبوزرادان رئيس الشرط ونبوشزبان رئيس الخصيان ونرجل شراصر رئيس المجوس وكل رؤساء ملك بابل"));
arasuperobject.biblemap.put("b24_39_14", new arasuperobject(24,39,14,"ارسلوا فاخذوا ارميا من دار السجن واسلموه لجدليا بن اخيقام بن شافان ليخرج به الى البيت. فسكن بين الشعب"));
arasuperobject.biblemap.put("b24_39_15", new arasuperobject(24,39,15,"وصارت كلمة الرب الى ارميا اذ كان محبوسا في دار السجن قائلة"));
arasuperobject.biblemap.put("b24_39_16", new arasuperobject(24,39,16,"اذهب وكلم عبد ملك الكوشي قائلا. هكذا قال رب الجنود اله اسرائيل. هانذا جالب كلامي على هذه المدينة للشر لا للخير فيحدث امامك في ذلك اليوم"));
arasuperobject.biblemap.put("b24_39_17", new arasuperobject(24,39,17,"ولكنني انقذك في ذلك اليوم يقول الرب فلا تسلم ليد الناس الذين انت خائف منهم"));
arasuperobject.biblemap.put("b24_39_18", new arasuperobject(24,39,18,"بل انما انجيك نجاة فلا تسقط بالسيف بل تكون لك نفسك غنيمة لانك قد توكلت عليّ يقول الرب"));
arasuperobject.biblemap.put("b24_40_1", new arasuperobject(24,40,1,"الكلمة التي صارت الى ارميا من قبل الرب بعدما ارسله نبو زرادان رئيس الشرط من الرامة اذ اخذه وهو مقيد بالسلاسل في وسط كل سبي اورشليم ويهوذا الذين سبوا الى بابل"));
arasuperobject.biblemap.put("b24_40_2", new arasuperobject(24,40,2,"فاخذ رئيس الشرط ارميا وقال له ان الرب الهك قد تكلم بهذا الشر على هذا الموضع"));
arasuperobject.biblemap.put("b24_40_3", new arasuperobject(24,40,3,"فجلب الرب وفعل كما تكلم لانكم قد اخطأتم الى الرب ولم تسمعوا لصوته فحدث لكم هذا الامر"));
arasuperobject.biblemap.put("b24_40_4", new arasuperobject(24,40,4,"فالآن هانذا احلّك اليوم من القيود التي على يدك. فان حسن في عينيك ان تأتي معي الى بابل فتعال فاجعل عينيّ عليك. وان قبح في عينيك ان تاتي معي الى بابل فامتنع. انظر. كل الارض هي امامك فحيثما حسن وكان مستقيما في عينيك ان تنطلق فانطلق الى هناك"));
arasuperobject.biblemap.put("b24_40_5", new arasuperobject(24,40,5,"واذ كان لم يرجع بعد قال ارجع الى جدليا بن اخيقام بن شافان الذي اقامه ملك بابل على مدن يهوذا واقم عنده في وسط الشعب وانطلق الى حيث كان مستقيما في عينيك ان تنطلق. واعطاه رئيس الشرط زادا وهدية واطلقه"));
arasuperobject.biblemap.put("b24_40_6", new arasuperobject(24,40,6,"فجاء ارميا الى جدليا بن اخيقام الى المصفاة واقام عنده في وسط الشعب الباقين في الارض"));
arasuperobject.biblemap.put("b24_40_7", new arasuperobject(24,40,7,"فلما سمع كل رؤساء الجيوش الذين في الحقل هم ورجالهم ان ملك بابل قد اقام جدليا بن اخيقام على الارض وانه وكله على الرجال والنساء والاطفال وعلى فقراء الارض الذين لم يسبوا الى بابل"));
arasuperobject.biblemap.put("b24_40_8", new arasuperobject(24,40,8,"أتى الى جدليا الى المصفاة اسماعيل بن نثنيا ويوحانان ويوناثان ابنا قاريح وسرايا بن تنحومث وبنو عيفاي النطوفاتي ويزنيا ابن المعكي هم ورجالهم"));
arasuperobject.biblemap.put("b24_40_9", new arasuperobject(24,40,9,"فحلف لهم جدليا بن اخيقام بن شافان ولرجالهم قائلا لا تخافوا من ان تخدموا الكلدانيين. اسكنوا في الارض واخدموا ملك بابل فيحسن اليكم"));
arasuperobject.biblemap.put("b24_40_10", new arasuperobject(24,40,10,"اما انا فهانذا ساكن في المصفاة لاقف امام الكلدانيين الذين ياتون الينا. اما انتم فاجمعوا خمرا وتينا وزيتا وضعوا في اوعيتكم واسكنوا في مدنكم التي اخذتموها"));
arasuperobject.biblemap.put("b24_40_11", new arasuperobject(24,40,11,"وكذلك كل اليهود الذين في موآب وبين بني عمون وفي ادوم والذين في كل الاراضي سمعوا ان ملك بابل قد جعل بقية ليهوذا وقد اقام عليهم جدليا بن اخيقام بن شافان"));
arasuperobject.biblemap.put("b24_40_12", new arasuperobject(24,40,12,"فرجع كل اليهود من كل المواضع التي طوحوا اليها واتوا الى ارض يهوذا الى جدليا الى المصفاة وجمعوا خمرا وتينا كثيرا جدا"));
arasuperobject.biblemap.put("b24_40_13", new arasuperobject(24,40,13,"ثم ان يوحانان بن قاريح وكل رؤساء الجيوش الذين في الحقل أتوا الى جدليا الى المصفاة"));
arasuperobject.biblemap.put("b24_40_14", new arasuperobject(24,40,14,"وقالوا له. أتعلم علما ان بعليس ملك بني عمون قد ارسل اسماعيل بن نثنيا ليقتلك. فلم يصدقهم جدليا بن اخيقام"));
arasuperobject.biblemap.put("b24_40_15", new arasuperobject(24,40,15,"فكلم يوحانان بن قاريح جدليا سرا في المصفاة قائلا دعني انطلق واضرب اسماعيل بن نثنيا ولا يعلم انسان. لماذا يقتلك فيتبدد كل يهوذا المجتمع اليك وتهلك بقية يهوذا"));
arasuperobject.biblemap.put("b24_40_16", new arasuperobject(24,40,16,"فقال جدليا بن اخيقام ليوحانان بن قاريح لا تفعل هذا الامر لانك انما تتكلم بالكذب عن اسماعيل"));
arasuperobject.biblemap.put("b24_41_1", new arasuperobject(24,41,1,"وكان في الشهر السابع ان اسماعيل بن نثنيا بن اليشاماع من النسل الملوكي جاء هو وعظماء الملك وعشرة رجال معه الى جدليا بن اخيقام الى المصفاة واكلوا هناك خبزا معا في المصفاة"));
arasuperobject.biblemap.put("b24_41_2", new arasuperobject(24,41,2,"فقام اسماعيل بن نثنيا والعشرة الرجال الذين كانوا معه وضربوا جدليا بن اخيقام بن شافان بالسيف فقتلوه هذا الذي اقامه ملك بابل على الارض"));
arasuperobject.biblemap.put("b24_41_3", new arasuperobject(24,41,3,"وكل اليهود الذين كانوا معه اي مع جدليا في المصفاة والكلدانيون الذين وجدوا هناك ورجال الحرب ضربهم اسماعيل"));
arasuperobject.biblemap.put("b24_41_4", new arasuperobject(24,41,4,"وكان في اليوم الثاني بعد قتله جدليا ولم يعلم انسان"));
arasuperobject.biblemap.put("b24_41_5", new arasuperobject(24,41,5,"ان رجالا أتوا من شكيم ومن شيلو ومن السامرة ثمانين رجلا محلوقي اللحى ومشققي الثياب ومخمشين وبيدهم تقدمة ولبان ليدخلوهما الى بيت الرب"));
arasuperobject.biblemap.put("b24_41_6", new arasuperobject(24,41,6,"فخرج اسماعيل بن نثنيا للقائهم من المصفاة سائرا وباكيا. فكان لما لقيهم انه قال لهم هلم الى جدليا بن اخيقام"));
arasuperobject.biblemap.put("b24_41_7", new arasuperobject(24,41,7,"فكان لما اتوا الى وسط المدينة ان اسماعيل بن نثنيا قتلهم والقاهم الى وسط الجب هو والرجال الذين معه"));
arasuperobject.biblemap.put("b24_41_8", new arasuperobject(24,41,8,"ولكن وجد فيهم عشرة رجال قالوا لاسماعيل لا تقتلنا لانه يوجد لنا خزائن في الحقل قمح وشعير وزيت وعسل. فامتنع ولم يقتلهم بين اخوتهم"));
arasuperobject.biblemap.put("b24_41_9", new arasuperobject(24,41,9,"فالجب الذي طرح فيه اسماعيل كل جثث الرجال الذين قتلهم بسبب جدليا هو الذي صنعه الملك آسا من وجه بعشا ملك اسرائيل. فملأه اسماعيل بن نثنيا من القتلى"));
arasuperobject.biblemap.put("b24_41_10", new arasuperobject(24,41,10,"فسبى اسماعيل كل بقية الشعب الذين في المصفاة بنات الملك وكل الشعب الذي بقي في المصفاة الذين اقام عليهم نبوزرادان رئيس الشرط جدليا بن اخيقام سباهم اسماعيل بن نثنيا وذهب ليعبر الى بني عمون"));
arasuperobject.biblemap.put("b24_41_11", new arasuperobject(24,41,11,"فلما سمع يوحانان بن قاريح وكل رؤساء الجيوش الذين معه بكل الشر الذي فعله اسماعيل بن نثنيا"));
arasuperobject.biblemap.put("b24_41_12", new arasuperobject(24,41,12,"اخذوا كل الرجال وساروا ليحاربوا اسماعيل بن نثنيا فوجدوه عند المياه الكثيرة التي في جبعون"));
arasuperobject.biblemap.put("b24_41_13", new arasuperobject(24,41,13,"ولما رأى كل الشعب الذي مع اسماعيل يوحانان بن قاريح وكل رؤساء الجيوش الذين معهم فرحوا"));
arasuperobject.biblemap.put("b24_41_14", new arasuperobject(24,41,14,"فدار كل الشعب الذي سباه اسماعيل من المصفاة ورجعوا وساروا الى يوحانان بن قاريح"));
arasuperobject.biblemap.put("b24_41_15", new arasuperobject(24,41,15,"اما اسماعيل بن نثنيا فهرب بثمانية رجال من وجه يوحانان وسار الى بني عمون"));
arasuperobject.biblemap.put("b24_41_16", new arasuperobject(24,41,16,"فاخذ يوحانان بن قاريح وكل رؤساء الجيوش الذين معه كل بقية الشعب الذين استردهم من اسماعيل بن نثنيا من المصفاة بعد قتل جدليا بن اخيقام رجال الحرب المقتدرين والنساء والاطفال والخصيان الذين استردهم من جبعون"));
arasuperobject.biblemap.put("b24_41_17", new arasuperobject(24,41,17,"فساروا واقاموا في جيروت كمهام التي بجانب بيت لحم لكي يسيروا ويدخلوا مصر"));
arasuperobject.biblemap.put("b24_41_18", new arasuperobject(24,41,18,"من وجه الكلدانيين لانهم كانوا خائفين منهم لان اسماعيل بن نثنيا كان قد ضرب جدليا بن اخيقام الذي اقامه ملك بابل على الارض"));
arasuperobject.biblemap.put("b24_42_1", new arasuperobject(24,42,1,"فتقدم كل رؤساء الجيوش ويوحانان بن قاريح ويزنيا بن هوشعيا وكل الشعب من الصغير الى الكبير"));
arasuperobject.biblemap.put("b24_42_2", new arasuperobject(24,42,2,"وقالوا لارميا النبي ليت تضرعنا يقع امامك فتصلّي لاجلنا الى الرب الهك لاجل كل هذه البقية. لاننا قد بقينا قليلين من كثيرين كما ترانا عيناك"));
arasuperobject.biblemap.put("b24_42_3", new arasuperobject(24,42,3,"فيخبرنا الرب الهك عن الطريق الذي نسير فيه والأمر الذي نفعله"));
arasuperobject.biblemap.put("b24_42_4", new arasuperobject(24,42,4,"فقال لهم ارميا النبي قد سمعت. هانذا اصلي الى الرب الهكم كقولكم ويكون ان كل الكلام الذي يجيبكم الرب اخبركم به. لا امنع عنكم شيئا"));
arasuperobject.biblemap.put("b24_42_5", new arasuperobject(24,42,5,"فقالوا هم لارميا ليكن الرب بيننا شاهدا صادقا وامينا اننا نفعل حسب كل أمر يرسلك به الرب الهك الينا"));
arasuperobject.biblemap.put("b24_42_6", new arasuperobject(24,42,6,"ان خيرا وان شرا فاننا نسمع لصوت الرب الهك الذي نحن مرسلوك اليه ليحسن الينا اذا سمعنا لصوت الرب الهنا"));
arasuperobject.biblemap.put("b24_42_7", new arasuperobject(24,42,7,"وكان بعد عشرة ايام ان كلمة الرب صارت الى ارميا"));
arasuperobject.biblemap.put("b24_42_8", new arasuperobject(24,42,8,"فدعا يوحانان بن قاريح وكل رؤساء الجيوش الذين معه وكل الشعب من الصغير الى الكبير"));
arasuperobject.biblemap.put("b24_42_9", new arasuperobject(24,42,9,"وقال لهم. هكذا قال الرب اله اسرائيل الذي ارسلتموني اليه لكي القي تضرعكم امامه"));
arasuperobject.biblemap.put("b24_42_10", new arasuperobject(24,42,10,"ان كنتم تسكنون في هذه الارض فاني ابنيكم ولا انقضكم واغرسكم ولا اقتلعكم. لاني ندمت عن الشر الذي صنعته بكم"));
arasuperobject.biblemap.put("b24_42_11", new arasuperobject(24,42,11,"لا تخافوا ملك بابل الذي انتم خائفوه. لا تخافوه يقول الرب لاني انا معكم لاخلصكم وانقذكم من يده"));
arasuperobject.biblemap.put("b24_42_12", new arasuperobject(24,42,12,"واعطيكم نعمة فيرحمكم ويردكم الى ارضكم"));
arasuperobject.biblemap.put("b24_42_13", new arasuperobject(24,42,13,"وان قلتم لا نسكن في هذه الارض ولم تسمعوا لصوت الرب الهكم"));
arasuperobject.biblemap.put("b24_42_14", new arasuperobject(24,42,14,"قائلين لا بل الى ارض مصر نذهب حيث لا نرى حربا ولا نسمع صوت بوق ولا نجوع للخبز وهناك نسكن"));
arasuperobject.biblemap.put("b24_42_15", new arasuperobject(24,42,15,"فالآن لذلك اسمعوا كلمة الرب يا بقية يهوذا. هكذا قال رب الجنود اله اسرائيل. ان كنتم تجعلون وجوهكم للدخول الى مصر وتذهبون لتتغربوا هناك"));
arasuperobject.biblemap.put("b24_42_16", new arasuperobject(24,42,16,"يحدث ان السيف الذي انتم خائفون منه يدرككم هناك في ارض مصر والجوع الذي انتم خائفون منه يلحقكم هناك في مصر فتموتون هناك"));
arasuperobject.biblemap.put("b24_42_17", new arasuperobject(24,42,17,"ويكون ان كل الرجال الذين جعلوا وجوههم للدخول الى مصر ليتغربوا هناك يموتون بالسيف والجوع والوبإ ولا يكون منهم باق ولا ناج من الشر الذي اجلبه انا عليهم"));
arasuperobject.biblemap.put("b24_42_18", new arasuperobject(24,42,18,"لانه هكذا قال رب الجنود اله اسرائيل. كما انسكب غضبي وغيظي على سكان اورشليم هكذا ينسكب غيظي عليكم عند دخولكم الى مصر فتصيرون حلفا ودهشا ولعنة وعارا ولا ترون بعد هذا الموضع"));
arasuperobject.biblemap.put("b24_42_19", new arasuperobject(24,42,19,"قد تكلم الرب عليكم يا بقية يهوذا لا تدخلوا مصر. اعلموا علما اني قد انذرتكم اليوم"));
arasuperobject.biblemap.put("b24_42_20", new arasuperobject(24,42,20,"لانكم قد خدعتم انفسكم اذ ارسلتموني الى الرب الهكم قائلين صلّ لاجلنا الى الرب الهنا وحسب كل ما يقوله الرب الهنا هكذا اخبرنا فنفعل"));
arasuperobject.biblemap.put("b24_42_21", new arasuperobject(24,42,21,"فقد اخبرتكم اليوم فلم تسمعوا لصوت الرب الهكم ولا لشيء مما ارسلني به اليكم"));
arasuperobject.biblemap.put("b24_42_22", new arasuperobject(24,42,22,"فالآن اعلموا علما انكم تموتون بالسيف والجوع والوبإ في الموضع الذي ابتغيتم ان تدخلوه لتتغربوا فيه"));
arasuperobject.biblemap.put("b24_43_1", new arasuperobject(24,43,1,"وكان لما فرغ ارميا من ان كلم كل الشعب بكل كلام الرب الههم الذي ارسله الرب الههم اليهم بكل هذا الكلام"));
arasuperobject.biblemap.put("b24_43_2", new arasuperobject(24,43,2,"ان عزريا بن هوشعيا ويوحانان بن قاريح وكل الرجال المتكبرين كلموا ارميا قائلين. انت متكلم بالكذب. لم يرسلك الرب الهنا لتقول لا تذهبوا الى مصر لتتغربوا هناك"));
arasuperobject.biblemap.put("b24_43_3", new arasuperobject(24,43,3,"بل باروخ بن نيريا مهيجك علينا لتدفعنا ليد الكلدانيين ليقتلونا وليسبونا الى بابل"));
arasuperobject.biblemap.put("b24_43_4", new arasuperobject(24,43,4,"فلم يسمع يوحانان بن قاريح وكل رؤساء الجيوش وكل الشعب لصوت الرب بالاقامة في ارض يهوذا"));
arasuperobject.biblemap.put("b24_43_5", new arasuperobject(24,43,5,"بل اخذ يوحانان بن قاريح وكل رؤساء الجيوش كل بقية يهوذا الذين رجعوا من كل الامم الذين طوحوا اليهم ليتغربوا في ارض يهوذا"));
arasuperobject.biblemap.put("b24_43_6", new arasuperobject(24,43,6,"الرجال والنساء والاطفال وبنات الملك وكل الانفس الذين تركهم نبوزرادان رئيس الشرط مع جدليا بن اخيقام بن شافان وارميا النبي وباروخ بن نيريا"));
arasuperobject.biblemap.put("b24_43_7", new arasuperobject(24,43,7,"فجاءوا الى ارض مصر لانهم لم يسمعوا لصوت الرب وأتوا الى تحفنحيس"));
arasuperobject.biblemap.put("b24_43_8", new arasuperobject(24,43,8,"ثم صارت كلمة الرب الى ارميا في تحفنحيس قائلة"));
arasuperobject.biblemap.put("b24_43_9", new arasuperobject(24,43,9,"خذ بيدك حجارة كبيرة واطمرها في الملاط في الملبن الذي عند باب بيت فرعون في تحفنحيس امام رجال يهود"));
arasuperobject.biblemap.put("b24_43_10", new arasuperobject(24,43,10,"وقل لهم هكذا قال رب الجنود اله اسرائيل. هانذا ارسل وآخذ نبوخذراصر ملك بابل عبدي واضع كرسيه فوق هذه الحجارة التي طمرتها فيبسط ديباجه عليها"));
arasuperobject.biblemap.put("b24_43_11", new arasuperobject(24,43,11,"ويأتي ويضرب ارض مصر الذي للموت فللموت والذي للسبي فللسبي والذي للسيف فللسيف"));
arasuperobject.biblemap.put("b24_43_12", new arasuperobject(24,43,12,"واوقد نارا في بيوت آلهة مصر فيحرقها ويسبيها ويلبس ارض مصر كما يلبس الراعي رداءه ثم يخرج من هناك بسلام"));
arasuperobject.biblemap.put("b24_43_13", new arasuperobject(24,43,13,"ويكسر انصاب بيت شمس التي في ارض مصر ويحرق بيوت آلهة مصر بالنار"));
arasuperobject.biblemap.put("b24_44_1", new arasuperobject(24,44,1,"الكلمة التي صارت الى ارميا من جهة كل اليهود الساكنين في ارض مصر الساكنين في مجدل وفي تحفنحيس وفي نوف وفي ارض فتروس قائلة"));
arasuperobject.biblemap.put("b24_44_2", new arasuperobject(24,44,2,"هكذا قال رب الجنود اله اسرائيل. انتم رأيتم كل الشر الذي جلبته على اورشليم وعلى كل مدن يهوذا فها هي خربة هذا اليوم وليس فيها ساكن"));
arasuperobject.biblemap.put("b24_44_3", new arasuperobject(24,44,3,"من اجل شرهم الذي فعلوه ليغيظوني اذ ذهبوا ليبخروا ويعبدوا آلهة اخرى لم يعرفوها هم ولا انتم ولا آباؤكم"));
arasuperobject.biblemap.put("b24_44_4", new arasuperobject(24,44,4,"فارسلت اليكم كل عبيدي الانبياء مبكرا ومرسلا قائلا لا تفعلوا امر هذا الرجس الذي ابغضته"));
arasuperobject.biblemap.put("b24_44_5", new arasuperobject(24,44,5,"فلم يسمعوا ولا امالوا اذنهم ليرجعوا عن شرهم فلا يبخروا لآلهة اخرى"));
arasuperobject.biblemap.put("b24_44_6", new arasuperobject(24,44,6,"فانسكب غيظي وغضبي واشتعلا في مدن يهوذا وفي شوارع اورشليم فصارت خربة مقفرة كهذا اليوم"));
arasuperobject.biblemap.put("b24_44_7", new arasuperobject(24,44,7,"فالآن هكذا قال الرب اله الجنود اله اسرائيل. لماذا انتم فاعلون شرا عظيما ضد انفسكم لانقراضكم رجالا ونساء اطفالا ورضعا من وسط يهوذا ولا تبقى لكم بقية"));
arasuperobject.biblemap.put("b24_44_8", new arasuperobject(24,44,8,"لاغاظتي باعمال اياديكم اذ تبخرون لآلهة اخرى في ارض مصر التي اتيتم اليها لتتغربوا فيها لكي تنقرضوا ولكي تصيروا لعنة وعارا بين كل امم الارض"));
arasuperobject.biblemap.put("b24_44_9", new arasuperobject(24,44,9,"هل نسيتم شرور آبائكم وشرور ملوك يهوذا وشرور نسائهم وشروركم وشرور نسائكم التي فعلت في ارض يهوذا وفي شوارع اورشليم"));
arasuperobject.biblemap.put("b24_44_10", new arasuperobject(24,44,10,"لم يذلّوا الى هذا اليوم ولا خافوا ولا سلكوا في شريعتي وفرائضي التي جعلتها امامكم وامام آبائكم"));
arasuperobject.biblemap.put("b24_44_11", new arasuperobject(24,44,11,"لذلك هكذا قال رب الجنود اله اسرائيل. هانذا اجعل وجهي عليكم للشر ولأقرض كل يهوذا"));
arasuperobject.biblemap.put("b24_44_12", new arasuperobject(24,44,12,"وآخذ بقية يهوذا الذين جعلوا وجوههم للدخول الى ارض مصر ليتغربوا هناك فيفنون كلهم في ارض مصر. يسقطون بالسيف وبالجوع يفنون من الصغير الى الكبير بالسيف والجوع يموتون ويصيرون حلفا ودهشا ولعنة وعارا"));
arasuperobject.biblemap.put("b24_44_13", new arasuperobject(24,44,13,"واعاقب الذين يسكنون في ارض مصر كما عاقبت اورشليم بالسيف والجوع والوبإ"));
arasuperobject.biblemap.put("b24_44_14", new arasuperobject(24,44,14,"ولا يكون ناج ولا باق لبقية يهوذا الآتين ليتغربوا هناك في ارض مصر ليرجعوا الى ارض يهوذا التي يشتاقون الى الرجوع لاجل السكن فيها لانه لا يرجع منهم الا المنفلتون"));
arasuperobject.biblemap.put("b24_44_15", new arasuperobject(24,44,15,"فاجاب ارميا كل الرجال الذين عرفوا ان نساءهم يبخّرن لآلهة اخرى وكل النساء الواقفات محفل كبير وكل الشعب الساكن في ارض مصر في فتروس قائلين"));
arasuperobject.biblemap.put("b24_44_16", new arasuperobject(24,44,16,"اننا لا نسمع لك الكلمة التي كلمتنا بها باسم الرب"));
arasuperobject.biblemap.put("b24_44_17", new arasuperobject(24,44,17,"بل سنعمل كل امر خرج من فمنا فنبخر لملكة السموات ونسكب لها سكائب كما فعلنا نحن وآباؤنا وملوكنا ورؤساؤنا في ارض يهوذا وفي شوارع اورشليم فشبعنا خبزا وكنا بخير ولم نر شرا"));
arasuperobject.biblemap.put("b24_44_18", new arasuperobject(24,44,18,"ولكن من حين كففنا عن التبخير لملكة السموات وسكب سكائب لها احتجنا الى كل وفنينا بالسيف والجوع"));
arasuperobject.biblemap.put("b24_44_19", new arasuperobject(24,44,19,"واذ كنا نبخر لملكة السموات ونسكب لها سكائب فهل بدون رجالنا كنا نصنع لها كعكا لنعبدها ونسكب لها السكائب"));
arasuperobject.biblemap.put("b24_44_20", new arasuperobject(24,44,20,"فكلم ارميا كل الشعب الرجال والنساء وكل الشعب الذين جاوبوه بهذا الكلام قائلا"));
arasuperobject.biblemap.put("b24_44_21", new arasuperobject(24,44,21,"أليس البخور الذي بخرتموه في مدن يهوذا وفي شوارع اورشليم انتم وآباؤكم وملوككم ورؤساؤكم وشعب الارض هو الذي ذكره الرب وصعد على قلبه"));
arasuperobject.biblemap.put("b24_44_22", new arasuperobject(24,44,22,"ولم يستطع الرب ان يحتمل بعد من اجل شر اعمالكم من اجل الرجاسات التي فعلتم فصارت ارضكم خربة ودهشا ولعنة بلا ساكن كهذا اليوم"));
arasuperobject.biblemap.put("b24_44_23", new arasuperobject(24,44,23,"من اجل انكم قد بخرتم واخطأتم الى الرب ولم تسمعوا لصوت الرب ولم تسلكوا في شريعته وفرائضه وشهاداته من اجل ذلكم قد اصابكم هذا الشر كهذا اليوم"));
arasuperobject.biblemap.put("b24_44_24", new arasuperobject(24,44,24,"ثم قال ارميا لكل الشعب ولكل النساء اسمعوا كلمة الرب يا جميع يهوذا الذين في ارض مصر"));
arasuperobject.biblemap.put("b24_44_25", new arasuperobject(24,44,25,"هكذا تكلم رب الجنود اله اسرائيل قائلا. انتم ونساؤكم تكلمتم بفمكم واكملتم باياديكم قائلين اننا انما نتمم نذورنا التي نذرناها ان نبخر لملكة السموات ونسكب لها سكائب فانهنّ يقمن نذوركم ويتممن نذوركم"));
arasuperobject.biblemap.put("b24_44_26", new arasuperobject(24,44,26,"لذلك اسمعوا كلمة الرب يا جميع يهوذا الساكنين في ارض مصر. هانذا قد حلفت باسمي العظيم قال الرب ان اسمي لن يسمى بعد بفم انسان ما من يهوذا في كل ارض مصر قائلا حيّ السيد الرب"));
arasuperobject.biblemap.put("b24_44_27", new arasuperobject(24,44,27,"هانذا اسهر عليهم للشر لا للخير فيفنى كل رجال يهوذا الذين في ارض مصر بالسيف والجوع حتى يتلاشوا"));
arasuperobject.biblemap.put("b24_44_28", new arasuperobject(24,44,28,"والناجون من السيف يرجعون من ارض مصر الى ارض يهوذا نفرا قليلا فيعلم كل بقية يهوذا الذين أتوا الى ارض مصر ليتغربوا فيها كلمة ايّنا تقوم"));
arasuperobject.biblemap.put("b24_44_29", new arasuperobject(24,44,29,"وهذه هي العلامة لكم يقول الرب اني اعاقبكم في هذا الموضع لتعلموا انه لا بد ان يقوم كلامي عليكم للشر"));
arasuperobject.biblemap.put("b24_44_30", new arasuperobject(24,44,30,"هكذا قال الرب. هانذا ادفع فرعون حفرع ملك مصر ليد اعدائه وليد طالبي نفسه كما دفعت صدقيا ملك يهوذا ليد نبوخذراصر ملك بابل عدوه وطالب نفسه"));
arasuperobject.biblemap.put("b24_45_1", new arasuperobject(24,45,1,"الكلمة التي تكلم بها ارميا النبي الى باروخ بن نيريا عند كتابته هذا الكلام في سفر عن فم ارميا في السنة الرابعة ليهوياقيم بن يوشيا ملك يهوذا قائلا"));
arasuperobject.biblemap.put("b24_45_2", new arasuperobject(24,45,2,"هكذا قال الرب اله اسرائيل لك يا باروخ"));
arasuperobject.biblemap.put("b24_45_3", new arasuperobject(24,45,3,"قد قلت ويل لي لان الرب قد زاد حزنا على المي. قد غشي علي في تنهدي ولم اجد راحة"));
arasuperobject.biblemap.put("b24_45_4", new arasuperobject(24,45,4,"هكذا تقول له. هكذا قال الرب. هانذا اهدم ما بنيته واقتلع ما غرسته وكل هذه الارض"));
arasuperobject.biblemap.put("b24_45_5", new arasuperobject(24,45,5,"وانت فهل تطلب لنفسك امورا عظيمة. لا تطلب. لاني هانذا جالب شرا على كل ذي جسد يقول الرب واعطيك نفسك غنيمة في كل المواضع التي تسير اليها"));
arasuperobject.biblemap.put("b24_46_1", new arasuperobject(24,46,1,"كلمة الرب التي صارت الى ارميا النبي عن الامم"));
arasuperobject.biblemap.put("b24_46_2", new arasuperobject(24,46,2,"عن مصر عن جيش فرعون نخو ملك مصر الذي كان على نهر الفرات في كركميش الذي ضربه نبوخذراصر ملك بابل في السنة الرابعة ليهوياقيم بن يوشيا ملك يهوذا"));
arasuperobject.biblemap.put("b24_46_3", new arasuperobject(24,46,3,"اعدّوا المجن والترس وتقدموا للحرب"));
arasuperobject.biblemap.put("b24_46_4", new arasuperobject(24,46,4,"اسرجوا الخيل واصعدوا ايها الفرسان وانتصبوا بالخوذ. اصقلوا الرماح. البسوا الدروع"));
arasuperobject.biblemap.put("b24_46_5", new arasuperobject(24,46,5,"لماذا اراهم مرتعبين ومدبرين الى الوراء وقد تحطمت ابطالهم وفرّوا هاربين ولم يلتفتوا. الخوف حواليهم يقول الرب"));
arasuperobject.biblemap.put("b24_46_6", new arasuperobject(24,46,6,"الخفيف لا ينوص والبطل لا ينجو. في الشمال بجانب نهر الفرات عثروا وسقطوا"));
arasuperobject.biblemap.put("b24_46_7", new arasuperobject(24,46,7,"من هذا الصاعد كالنيل كانهار تتلاطم امواهها"));
arasuperobject.biblemap.put("b24_46_8", new arasuperobject(24,46,8,"تصعد مصر كالنيل وكانهار تتلاطم المياه. فيقول اصعد واغطي الارض. اهلك المدينة والساكنين فيها"));
arasuperobject.biblemap.put("b24_46_9", new arasuperobject(24,46,9,"اصعدي ايتها الخيل وهيجي ايتها المركبات ولتخرج الابطال. كوش وفوط القابضان المجن واللوديون القابضون والمادون القوس"));
arasuperobject.biblemap.put("b24_46_10", new arasuperobject(24,46,10,"فهذا اليوم للسيد رب الجنود يوم نقمة للانتقام من مبغضيه فياكل السيف ويشبع ويرتوي من دمهم. لان للسيد رب الجنود ذبيحة في ارض الشمال عند نهر الفرات"));
arasuperobject.biblemap.put("b24_46_11", new arasuperobject(24,46,11,"اصعدي الى جلعاد وخذي بلسانا يا عذراء بنت مصر. باطلا تكثرين العقاقير. لا رفادة لك"));
arasuperobject.biblemap.put("b24_46_12", new arasuperobject(24,46,12,"قد سمعت الامم بخزيك وقد ملأ الارض عويلك لان بطلا يصدم بطلا فيسقطان كلاهما معا"));
arasuperobject.biblemap.put("b24_46_13", new arasuperobject(24,46,13,"الكلمة التي تكلم بها الرب الى ارميا النبي في مجيء نبوخذراصر ملك بابل ليضرب ارض مصر"));
arasuperobject.biblemap.put("b24_46_14", new arasuperobject(24,46,14,"اخبروا في مصر واسمعوا في مجدل واسمعوا في نوف وفي تحفنحيس قولوا انتصب وتهيأ لان السيف ياكل حواليك"));
arasuperobject.biblemap.put("b24_46_15", new arasuperobject(24,46,15,"لماذا انطرح مقتدروك. لا يقفون لان الرب قد طرحهم"));
arasuperobject.biblemap.put("b24_46_16", new arasuperobject(24,46,16,"كثر العاثرين حتى يسقط الواحد على صاحبه ويقولوا قوموا فنرجع الى شعبنا والى ارض ميلادنا من وجه السيف الصارم"));
arasuperobject.biblemap.put("b24_46_17", new arasuperobject(24,46,17,"قد نادوا هناك فرعون ملك مصر هالك. قد فات الميعاد"));
arasuperobject.biblemap.put("b24_46_18", new arasuperobject(24,46,18,"حيّ انا يقول الملك رب الجنود اسمه كتابور بين الجبال وككرمل عند البحر يأتي"));
arasuperobject.biblemap.put("b24_46_19", new arasuperobject(24,46,19,"اصنعي لنفسك أهبة جلاء ايتها البنت الساكنة مصر لان نوف تصير خربة وتحرق فلا ساكن"));
arasuperobject.biblemap.put("b24_46_20", new arasuperobject(24,46,20,"مصر عجلة حسنة جدا. الهلاك من الشمال جاء جاء"));
arasuperobject.biblemap.put("b24_46_21", new arasuperobject(24,46,21,"ايضا متسأجروها في وسطها كعجول صيرة. لانهم هم ايضا يرتدون يهربون معا. لم يقفوا لان يوم هلاكهم اتى عليهم وقت عقابهم"));
arasuperobject.biblemap.put("b24_46_22", new arasuperobject(24,46,22,"صوتها يمشي كحية لانهم يسيرون بجيش وقد جاءوا اليها بالفؤوس كمحتطبي حطب"));
arasuperobject.biblemap.put("b24_46_23", new arasuperobject(24,46,23,"يقطعون وعرها يقول الرب وان يكن لا يحصى لانهم قد كثروا اكثر من الجراد ولا عدد لهم"));
arasuperobject.biblemap.put("b24_46_24", new arasuperobject(24,46,24,"قد أخزيت بنت مصر ودفعت ليد شعب الشمال"));
arasuperobject.biblemap.put("b24_46_25", new arasuperobject(24,46,25,"قال رب الجنود اله اسرائيل. هانذا اعاقب آمون نو وفرعون ومصر وآلهتها وملوكها فرعون والمتوكلين عليه"));
arasuperobject.biblemap.put("b24_46_26", new arasuperobject(24,46,26,"وادفعهم ليد طالبي نفوسهم وليد نبوخذراصر ملك بابل وليد عبيده. ثم بعد ذلك تسكن كالايام القديمة يقول الرب"));
arasuperobject.biblemap.put("b24_46_27", new arasuperobject(24,46,27,"وانت فلا تخف يا عبدي يعقوب ولا ترتعب يا اسرائيل لاني هانذا اخلصك من بعيد ونسلك من ارض سبيهم فيرجع يعقوب ويطمئن ويستريح ولا مخيف"));
arasuperobject.biblemap.put("b24_46_28", new arasuperobject(24,46,28,"اما انت يا عبدي يعقوب فلا تخف لاني انا معك لاني افني كل الامم الذين بددتك اليهم. اما انت فلا افنيك بل اؤدبك بالحق ولا ابرئك تبرئة"));
arasuperobject.biblemap.put("b24_47_1", new arasuperobject(24,47,1,"كلمة الرب التي صارت الى ارميا النبي عن الفلسطينيين قبل ضرب فرعون غزة"));
arasuperobject.biblemap.put("b24_47_2", new arasuperobject(24,47,2,"هكذا قال الرب. ها مياه تصعد من الشمال وتكون سيلا جارفا فتغشي الارض وملأها المدينة والساكنين فيها فيصرخ الناس ويولول كل سكان الارض"));
arasuperobject.biblemap.put("b24_47_3", new arasuperobject(24,47,3,"من صوت قرع حوافر اقويائه من صرير مركباته وصريف بكراته لا تلتفت الآباء الى البنين بسبب ارتخاء الايادي"));
arasuperobject.biblemap.put("b24_47_4", new arasuperobject(24,47,4,"بسبب اليوم الآتي لهلاك كل الفلسطينيين لينقرض من صور وصيدون كل بقية تعين لان الرب يهلك الفلسطينيين بقية جزيرة كفتور"));
arasuperobject.biblemap.put("b24_47_5", new arasuperobject(24,47,5,"أتى الصلع على غزة أهلكت اشقلون مع بقية وطائهم. حتى متى تخمشين نفسك"));
arasuperobject.biblemap.put("b24_47_6", new arasuperobject(24,47,6,"آه يا سيف الرب حتى متى لا تستريح. انضم الى غمدك اهدأ واسكن"));
arasuperobject.biblemap.put("b24_47_7", new arasuperobject(24,47,7,"كيف يستريح والرب قد اوصاه. على اشقلون وعلى ساحل البحر هناك واعده"));
arasuperobject.biblemap.put("b24_48_1", new arasuperobject(24,48,1,"عن موآب. هكذا قال رب الجنود اله اسرائيل. ويل لنبو لانها قد خربت. خزيت وأخذت قريتايم . ‎خزيت مسجاب وارتعبت"));
arasuperobject.biblemap.put("b24_48_2", new arasuperobject(24,48,2,"ليس موجودا بعد فخر موآب. في حشبون فكروا عليها شرا. هلم فنقرضها من ان تكون امة. وانت ايضا يا مدمين تصمّين ويذهب وراءك السيف"));
arasuperobject.biblemap.put("b24_48_3", new arasuperobject(24,48,3,"صوت صياح من حورونايم هلاك وسحق عظيم"));
arasuperobject.biblemap.put("b24_48_4", new arasuperobject(24,48,4,"قد حطمت موآب وأسمع صغارها صراخا"));
arasuperobject.biblemap.put("b24_48_5", new arasuperobject(24,48,5,"لانه في عقبة لوحيت يصعد بكاء على بكاء لانه في منحدر حورونايم سمع الاعداء صراخ انكسار"));
arasuperobject.biblemap.put("b24_48_6", new arasuperobject(24,48,6,"اهربوا نجّوا انفسكم وكونوا كعرعر في البرية"));
arasuperobject.biblemap.put("b24_48_7", new arasuperobject(24,48,7,"فمن اجل اتكالك على اعمالك وعلى خزائنك ستؤخذين انت ايضا ويخرج كموش الى السبي كهنته ورؤساؤه معا"));
arasuperobject.biblemap.put("b24_48_8", new arasuperobject(24,48,8,"ويأتي المهلك الى كل مدينة فلا تفلت مدينة فيبيد الوطاء ويهلك السهل كما قال الرب"));
arasuperobject.biblemap.put("b24_48_9", new arasuperobject(24,48,9,"اعطوا موآب جناحا لانها تخرج طائرة وتصير مدنها خربة بلا ساكن فيها"));
arasuperobject.biblemap.put("b24_48_10", new arasuperobject(24,48,10,"ملعون من يعمل عمل الرب برخاء وملعون من يمنع سيفه عن الدم"));
arasuperobject.biblemap.put("b24_48_11", new arasuperobject(24,48,11,"مستريح موآب منذ صباه وهو مستقر على درديه ولم يفرغ من اناء الى اناء ولم يذهب الى السبي. لذلك بقي طعمه فيه ورائحته لم تتغيّر"));
arasuperobject.biblemap.put("b24_48_12", new arasuperobject(24,48,12,"لذلك ها ايام تاتي يقول الرب وارسل اليه مصغين فيصغونه ويفرغون آنيته ويكسرون اوعيتهم"));
arasuperobject.biblemap.put("b24_48_13", new arasuperobject(24,48,13,"فيخجل موآب من كموش كما خجل بيت اسرائيل من بيت ايل متكلهم"));
arasuperobject.biblemap.put("b24_48_14", new arasuperobject(24,48,14,"كيف تقولون نحن جبابرة ورجال قوة للحرب"));
arasuperobject.biblemap.put("b24_48_15", new arasuperobject(24,48,15,"أهلكت موآب وصعدت مدنها وخيار منتخبيها نزلوا للقتل يقول الملك رب الجنود اسمه"));
arasuperobject.biblemap.put("b24_48_16", new arasuperobject(24,48,16,"قريب مجيء هلاك موآب وبليتها مسرعة جدا"));
arasuperobject.biblemap.put("b24_48_17", new arasuperobject(24,48,17,"اندبوها يا جميع الذين حواليها وكل العارفين اسمها قولوا كيف انكسر قضيب العزّ عصا الجلال"));
arasuperobject.biblemap.put("b24_48_18", new arasuperobject(24,48,18,"انزلي من المجد اجلسي في الظماء ايتها الساكنة بنت ديبون لان مهلك موآب قد صعد اليك واهلك حصونك"));
arasuperobject.biblemap.put("b24_48_19", new arasuperobject(24,48,19,"قفي على الطريق وتطلعي يا ساكنة عروعير. اسألي الهارب والناجية قولي ماذا حدث"));
arasuperobject.biblemap.put("b24_48_20", new arasuperobject(24,48,20,"قد خزي موآب لانه قد نقض. ولولوا واصرخوا اخبروا في ارنون ان موآب قد أهلك"));
arasuperobject.biblemap.put("b24_48_21", new arasuperobject(24,48,21,"وقد جاء القضاء على ارض السهل على حولون وعلى يهصة وعلى ميفعة"));
arasuperobject.biblemap.put("b24_48_22", new arasuperobject(24,48,22,"وعلى ديبون وعلى نبو وعلى بيت دبلاتايم"));
arasuperobject.biblemap.put("b24_48_23", new arasuperobject(24,48,23,"وعلى قريتايم وعلى بيت جامول وعلى بيت معون"));
arasuperobject.biblemap.put("b24_48_24", new arasuperobject(24,48,24,"وعلى قريوت وعلى بصرة وعلى كل مدن ارض موآب البعيدة والقريبة"));
arasuperobject.biblemap.put("b24_48_25", new arasuperobject(24,48,25,"عضب قرن موآب وتحطمت ذراعه يقول الرب"));
arasuperobject.biblemap.put("b24_48_26", new arasuperobject(24,48,26,"اسكروه لانه قد تعاظم على الرب فيتمرغ موآب في قيائه وهو ايضا يكون ضحكة"));
arasuperobject.biblemap.put("b24_48_27", new arasuperobject(24,48,27,"أفما كان اسرائيل ضحكة لك. هل وجد بين اللصوص حتى انك كلما كنت تتكلم به كنت تنغض الراس"));
arasuperobject.biblemap.put("b24_48_28", new arasuperobject(24,48,28,"خلوا المدن واسكنوا في الصخر يا سكان موآب وكونوا كحمامة تعشعش في جوانب فم الحفرة"));
arasuperobject.biblemap.put("b24_48_29", new arasuperobject(24,48,29,"قد سمعنا بكبرياء موآب. هو متكبر جدا. بعظمته وبكبريائه وجلاله وارتفاع قلبه"));
arasuperobject.biblemap.put("b24_48_30", new arasuperobject(24,48,30,"انا عرفت سخطه يقول الرب انه باطل. اكاذيبه فعلت باطلا"));
arasuperobject.biblemap.put("b24_48_31", new arasuperobject(24,48,31,"من اجل ذلك اولول على موآب وعلى موآب كله اصرخ. يؤنّ على رجال قير حارس"));
arasuperobject.biblemap.put("b24_48_32", new arasuperobject(24,48,32,"ابكي عليك بكاء يعزير يا جفنة سبمة. قد عبرت قضبانك البحر وصلت الى بحر يعزير. وقع المهلك على جناك وعلى قطافك"));
arasuperobject.biblemap.put("b24_48_33", new arasuperobject(24,48,33,"ونزع الفرح والطرب من البستان ومن ارض موآب وقد ابطلت الخمر من المعاصر. لا يداس بهتاف. جلبة لا هتاف"));
arasuperobject.biblemap.put("b24_48_34", new arasuperobject(24,48,34,"قد اطلقوا صوتهم من صراخ حشبون الى العالة الى ياهص من صوغر الى حورونايم كعجلة ثلاثية لان مياه نمريم ايضا تصير خربة"));
arasuperobject.biblemap.put("b24_48_35", new arasuperobject(24,48,35,"وابطل من موآب يقول الرب من يصعد في مرتفعة ومن يبخر لآلهته"));
arasuperobject.biblemap.put("b24_48_36", new arasuperobject(24,48,36,"من اجل ذلك يصوّت قلبي لموآب كناي ويصوت قلبي لرجال قير حارس كناي لان الثروة التي اكتسبوها قد بادت"));
arasuperobject.biblemap.put("b24_48_37", new arasuperobject(24,48,37,"لان كل راس اقرع وكل لحية مجزوزة وعلى كل الايادي خموش وعلى الاحقاء مسوح"));
arasuperobject.biblemap.put("b24_48_38", new arasuperobject(24,48,38,"على كل سطوح موآب وفي شوارعها كلها نوح لاني قد حطمت موآب كاناء لا مسرة به يقول الرب"));
arasuperobject.biblemap.put("b24_48_39", new arasuperobject(24,48,39,"يولولون قائلين كيف نقضت كيف حولت موآب قفاها بخزي فقد صارت موآب ضحكة ورعبا لكل من حواليها"));
arasuperobject.biblemap.put("b24_48_40", new arasuperobject(24,48,40,"لانه هكذا قال الرب ها هو يطير كنسر ويبسط جناحيه على موآب"));
arasuperobject.biblemap.put("b24_48_41", new arasuperobject(24,48,41,"قد أخذت قريوت وأمسكت الحصينات وسيكون قلب جبابرة موآب في ذلك اليوم كقلب امرأة ماخض"));
arasuperobject.biblemap.put("b24_48_42", new arasuperobject(24,48,42,"ويهلك موآب عن ان يكون شعبا لانه قد تعاظم على الرب"));
arasuperobject.biblemap.put("b24_48_43", new arasuperobject(24,48,43,"خوف وحفرة وفخ عليك يا ساكن موآب يقول الرب"));
arasuperobject.biblemap.put("b24_48_44", new arasuperobject(24,48,44,"الذي يهرب من وجه الخوف يسقط في الحفرة والذي يصعد من الحفرة يعلق في الفخ لاني اجلب عليها اي على موآب سنة عقابهم يقول الرب"));
arasuperobject.biblemap.put("b24_48_45", new arasuperobject(24,48,45,"في ظل حشبون وقف الهاربون بلا قوة. لانه قد خرجت نار من حشبون ولهيب من وسط سيحون فاكلت زاوية موآب وهامة بني الوغا"));
arasuperobject.biblemap.put("b24_48_46", new arasuperobject(24,48,46,"ويل لك يا موآب. باد شعب كموش لان بنيك قد أخذوا الى السبي وبناتك الى الجلاء"));
arasuperobject.biblemap.put("b24_48_47", new arasuperobject(24,48,47,"ولكنني ارد سبي موآب في آخر الايام يقول الرب الى هنا قضاء موآب"));
arasuperobject.biblemap.put("b24_49_1", new arasuperobject(24,49,1,"عن بني عمون. هكذا قال الرب. أليس لاسرائيل بنون او لا وارث له. لماذا يرث ملكهم جاد وشعبه يسكن في مدنه"));
arasuperobject.biblemap.put("b24_49_2", new arasuperobject(24,49,2,"لذلك ها ايام تأتي يقول الرب واسمع في ربة بني عمون جلبة حرب وتصير تلا خربا وتحرق بناتها بالنار فيرث اسرائيل الذين ورثوه يقول الرب"));
arasuperobject.biblemap.put("b24_49_3", new arasuperobject(24,49,3,"ولولي يا حشبون لان عاي قد خربت. اصرخن يا بنات ربة. تنطقن بمسوح. اندبن وطوّفن بين الجدران لان ملكهم يذهب الى السبي هو وكهنته ورؤساؤه معا"));
arasuperobject.biblemap.put("b24_49_4", new arasuperobject(24,49,4,"ما بالك تفتخرين بالاوطية. قد فاض وطاؤك دما ايتها البنت المرتدّة والمتوكلة على خزائنها قائلة من يأتي اليّ ‎"));
arasuperobject.biblemap.put("b24_49_5", new arasuperobject(24,49,5,"هانذا اجلب عليك خوفا يقول السيد رب الجنود من جميع الذين حواليك وتطردون كل واحد الى ما امامه وليس من يجمع التائهين"));
arasuperobject.biblemap.put("b24_49_6", new arasuperobject(24,49,6,"ثم بعد ذلك ارد سبي بني عمون يقول الرب"));
arasuperobject.biblemap.put("b24_49_7", new arasuperobject(24,49,7,"عن ادوم. هكذا قال رب الجنود. ألا حكمة بعد في تيمان. هل بادت المشورة من الفهماء هل فرغت حكمتهم"));
arasuperobject.biblemap.put("b24_49_8", new arasuperobject(24,49,8,"اهربوا التفتوا تعمقوا في السكن يا سكان ددان. لاني قد جلبت عليه بلية عيسو حين عاقبته"));
arasuperobject.biblemap.put("b24_49_9", new arasuperobject(24,49,9,"لو اتاك القاطفون أفما كانوا يتركون علالة او اللصوص ليلا أفما كانوا يهلكون ما يكفيهم"));
arasuperobject.biblemap.put("b24_49_10", new arasuperobject(24,49,10,"ولكنني جردت عيسو وكشفت مستتراته فلا يستطيع ان يختبئ. هلك نسله واخوته وجيرانه فلا يوجد"));
arasuperobject.biblemap.put("b24_49_11", new arasuperobject(24,49,11,"اترك ايتامك انا احييهم واراملك عليّ ليتوكلن"));
arasuperobject.biblemap.put("b24_49_12", new arasuperobject(24,49,12,"لانه هكذا قال الرب. ها ان الذين لا حق لهم ان يشربوا الكاس قد شربوا فهل انت تتبرأ تبرؤا. لا تتبرأ بل انما تشرب شربا"));
arasuperobject.biblemap.put("b24_49_13", new arasuperobject(24,49,13,"لاني بذاتي حلفت يقول الرب ان بصرة تكون دهشا وعارا وخرابا ولعنة وكل مدنها تكون خربا ابدية"));
arasuperobject.biblemap.put("b24_49_14", new arasuperobject(24,49,14,"قد سمعت خبرا من قبل الرب وأرسل رسول الى الامم قائلا تجمعوا وتعالوا عليها وقوموا للحرب"));
arasuperobject.biblemap.put("b24_49_15", new arasuperobject(24,49,15,"لاني ها قد جعلتك صغيرا بين الشعوب ومحتقرا بين الناس"));
arasuperobject.biblemap.put("b24_49_16", new arasuperobject(24,49,16,"قد غرّك تخويفك كبرياء قلبك يا ساكن في محاجيء الصخر الماسك مرتفع الاكمة. وان رفعت كنسر عشك فمن هناك احدرك يقول الرب"));
arasuperobject.biblemap.put("b24_49_17", new arasuperobject(24,49,17,"وتصير ادوم عجبا كل مار بها يتعجب ويصفر بسبب كل ضرباتها"));
arasuperobject.biblemap.put("b24_49_18", new arasuperobject(24,49,18,"كانقلاب سدوم وعمورة ومجاوراتها يقول الرب لا يسكن هناك انسان ولا يتغرب فيها ابن آدم"));
arasuperobject.biblemap.put("b24_49_19", new arasuperobject(24,49,19,"هوذا يصعد كاسد من كبرياء الاردن الى مرعى دائم. لاني اغمز واجعله يركض عنه. فمن هو منتخب فاقيمه عليه. لانه من مثلي ومن يحاكمني ومن هو الراعي الذي يقف امامي"));
arasuperobject.biblemap.put("b24_49_20", new arasuperobject(24,49,20,"لذلك اسمعوا مشورة الرب التي قضى بها على ادوم وافكاره التي افتكر بها على سكان تيمان. ان صغار الغنم تسحبهم. انه يخرب مسكنهم عليهم"));
arasuperobject.biblemap.put("b24_49_21", new arasuperobject(24,49,21,"من صوت سقوطهم رجفت الارض. صرخة سمع صوتها في بحر سوف"));
arasuperobject.biblemap.put("b24_49_22", new arasuperobject(24,49,22,"هوذا كنسر يرتفع ويطير ويبسط جناحيه على بصرة ويكون قلب جبابرة ادوم في ذلك اليوم كقلب امرأة ماخض"));
arasuperobject.biblemap.put("b24_49_23", new arasuperobject(24,49,23,"عن دمشق. خزيت حماة وارفاد. قد ذابوا لانهم قد سمعوا خبرا رديئا. في البحر اضطراب لا يستطيع الهدوء"));
arasuperobject.biblemap.put("b24_49_24", new arasuperobject(24,49,24,"ارتخت دمشق والتفتت للهرب. امسكتها الرعدة واخذها الضيق والاوجاع كماخض"));
arasuperobject.biblemap.put("b24_49_25", new arasuperobject(24,49,25,"كيف لم تترك المدينة الشهيرة قرية فرحي"));
arasuperobject.biblemap.put("b24_49_26", new arasuperobject(24,49,26,"لذلك تسقط شبانها في شوارعها وتهلك كل رجال الحرب في ذلك اليوم يقول رب الجنود"));
arasuperobject.biblemap.put("b24_49_27", new arasuperobject(24,49,27,"واشعل نارا في سور دمشق فتاكل قصور بنهدد"));
arasuperobject.biblemap.put("b24_49_28", new arasuperobject(24,49,28,"عن قيدار وعن ممالك حاصور التي ضربها نبوخذراصر ملك بابل. هكذا قال الرب. قوموا اصعدوا الى قيدار اخربوا بني المشرق"));
arasuperobject.biblemap.put("b24_49_29", new arasuperobject(24,49,29,"يأخذون خيامهم وغنمهم ويأخذون لانفسهم شققهم وكل آنيتهم وجمالهم وينادون اليهم الخوف من كل جانب"));
arasuperobject.biblemap.put("b24_49_30", new arasuperobject(24,49,30,"اهربوا انهزموا جدا تعمقوا في السكن يا سكان حاصور يقول الرب لان نبوخذراصر ملك بابل قد اشار عليكم مشورة وفكر عليكم فكرا"));
arasuperobject.biblemap.put("b24_49_31", new arasuperobject(24,49,31,"قوموا اصعدوا الى امة مطمئنة ساكنة آمنة يقول الرب لا مصاريع ولا عوارض لها. تسكن وحدها"));
arasuperobject.biblemap.put("b24_49_32", new arasuperobject(24,49,32,"وتكون جمالهم نهبا وكثرة ماشيتهم غنيمة واذري لكل ريح مقصوصي الشعر مستديرا وآتي بهلاكهم من كل جهاته يقول الرب"));
arasuperobject.biblemap.put("b24_49_33", new arasuperobject(24,49,33,"وتكون حاصور مسكن بنات آوى وخربة الى الابد. لا يسكن هناك انسان ولا يتغرب فيها ابن آدم"));
arasuperobject.biblemap.put("b24_49_34", new arasuperobject(24,49,34,"كلمة الرب التي صارت الى ارميا النبي على عيلام في ابتداء ملك صدقيا ملك يهوذا قائلة"));
arasuperobject.biblemap.put("b24_49_35", new arasuperobject(24,49,35,"هكذا قال رب الجنود. هانذا احطم قوس عيلام اول قوتهم"));
arasuperobject.biblemap.put("b24_49_36", new arasuperobject(24,49,36,"واجلب على عيلام اربع رياح من اربعة اطراف السماء واذريهم لكل هذه الرياح ولا تكون امة الا ويأتي اليها منفيو عيلام"));
arasuperobject.biblemap.put("b24_49_37", new arasuperobject(24,49,37,"واجعل العيلاميين يرتعبون امام اعدائهم وامام طالبي نفوسهم واجلب عليهم شرا حمو غضبي يقول الرب. وارسل وراءهم السيف حتى افنيهم"));
arasuperobject.biblemap.put("b24_49_38", new arasuperobject(24,49,38,"واضع كرسيي في عيلام وابيد من هناك الملك والرؤساء يقول الرب"));
arasuperobject.biblemap.put("b24_49_39", new arasuperobject(24,49,39,"ويكون في آخر الايام اني ارد سبي عيلام يقول الرب"));
arasuperobject.biblemap.put("b24_50_1", new arasuperobject(24,50,1,"الكلمة التي تكلم بها الرب عن بابل وعن ارض الكلدانيين على يد ارميا النبي"));
arasuperobject.biblemap.put("b24_50_2", new arasuperobject(24,50,2,"اخبروا في الشعوب وأسمعوا وارفعوا راية. أسمعوا لا تخفوا. قولوا أخذت بابل خزي بيل. انسحق مرودخ. خزيت اوثانها انسحقت اصنامها"));
arasuperobject.biblemap.put("b24_50_3", new arasuperobject(24,50,3,"لانه قد طلعت عليها امة من الشمال هي تجعل ارضها خربة فلا يكون فيها ساكن. من انسان الى حيوان هربوا وذهبوا"));
arasuperobject.biblemap.put("b24_50_4", new arasuperobject(24,50,4,"في تلك الايام وفي ذلك الزمان يقول الرب يأتي بنو اسرائيل هم وبنو يهوذا معا يسيرون سيرا ويبكون ويطلبون الرب الههم"));
arasuperobject.biblemap.put("b24_50_5", new arasuperobject(24,50,5,"يسألون عن طريق صهيون ووجوههم الى هناك قائلين هلم فنلصق بالرب بعهد ابدي لا ينسى"));
arasuperobject.biblemap.put("b24_50_6", new arasuperobject(24,50,6,"كان شعبي خرافا ضالة. قد اضلتهم رعاتهم. على الجبال اتاهوهم ساروا من جبل الى اكمة. نسوا مربضهم"));
arasuperobject.biblemap.put("b24_50_7", new arasuperobject(24,50,7,"كل الذين وجدوهم اكلوهم وقال مبغضوهم لا نذنب من اجل انهم اخطأوا الى الرب مسكن البر ورجاء آبائهم الرب"));
arasuperobject.biblemap.put("b24_50_8", new arasuperobject(24,50,8,"اهربوا من وسط بابل واخرجوا من ارض الكلدانيين وكونوا مثل كراريز امام الغنم"));
arasuperobject.biblemap.put("b24_50_9", new arasuperobject(24,50,9,"لاني هانذا اوقظ واصعد على بابل جمهور شعوب عظيمة من ارض الشمال فيصطفون عليها. من هناك تؤخذ. نبالهم كبطل مهلك لا يرجع فارغا"));
arasuperobject.biblemap.put("b24_50_10", new arasuperobject(24,50,10,"وتكون ارض الكلدانيين غنيمة. كل مغتنميها يشبعون يقول الرب"));
arasuperobject.biblemap.put("b24_50_11", new arasuperobject(24,50,11,"لانكم قد فرحتم وشمتم يا ناهبي ميراثي وقفزتم كعجلة في الكلإ وصهلتم كخيل"));
arasuperobject.biblemap.put("b24_50_12", new arasuperobject(24,50,12,"تخزى امكم جدا. تخجل التي ولدتكم. ها آخرة الشعوب برية وارض ناشفة وقفر"));
arasuperobject.biblemap.put("b24_50_13", new arasuperobject(24,50,13,"بسبب سخط الرب لا تسكن بل تصير خربة بالتمام. كل مار ببابل يتعجب ويصفر بسبب كل ضرباتها"));
arasuperobject.biblemap.put("b24_50_14", new arasuperobject(24,50,14,"اصطفوا على بابل حواليها يا جميع الذين ينزعون في القوس. ارموا عليها. لا توفروا السهام لانها قد اخطأت الى الرب"));
arasuperobject.biblemap.put("b24_50_15", new arasuperobject(24,50,15,"اهتفوا عليها حواليها. قد اعطت يدها. سقطت أسسها نقضت اسوارها. لانها نقمة الرب هي فانقموا منها. كما فعلت افعلوا بها"));
arasuperobject.biblemap.put("b24_50_16", new arasuperobject(24,50,16,"اقطعوا الزارع من بابل وماسك المنجل في وقت الحصاد. من وجه السيف القاسي يرجعون كل واحد الى شعبه ويهربون كل واحد الى ارضه"));
arasuperobject.biblemap.put("b24_50_17", new arasuperobject(24,50,17,"اسرائيل غنم متبددة. قد طردته السباع. اولا اكله ملك اشور ثم هذا الاخير نبوخذراصر ملك بابل هرس عظامه"));
arasuperobject.biblemap.put("b24_50_18", new arasuperobject(24,50,18,"لذلك هكذا قال رب الجنود اله اسرائيل. هانذا اعاقب ملك بابل وارضه كما عاقبت ملك اشور"));
arasuperobject.biblemap.put("b24_50_19", new arasuperobject(24,50,19,"وارد اسرائيل الى مسكنه فيرعى كرمل وباشان وفي جبل افرايم وجلعاد تشبع نفسه"));
arasuperobject.biblemap.put("b24_50_20", new arasuperobject(24,50,20,"في تلك الايام وفي ذلك الزمان يقول الرب يطلب اثم اسرائيل فلا يكون وخطية يهوذا فلا توجد لاني اغفر لمن ابقيه"));
arasuperobject.biblemap.put("b24_50_21", new arasuperobject(24,50,21,"اصعد على ارض مراثايم. عليها وعلى سكان فقود. اخرب وحرم وراءهم يقول الرب وافعل حسب كل ما امرتك به"));
arasuperobject.biblemap.put("b24_50_22", new arasuperobject(24,50,22,"صوت حرب في الارض وانحطام عظيم"));
arasuperobject.biblemap.put("b24_50_23", new arasuperobject(24,50,23,"كيف قطعت وتحطمت مطرقة كل الارض. كيف صارت بابل خربة بين الشعوب"));
arasuperobject.biblemap.put("b24_50_24", new arasuperobject(24,50,24,"قد نصبت لك شركا فعلقت يا بابل وانت لم تعرفي. قد وجدت وأمسكت لانك قد خاصمت الرب"));
arasuperobject.biblemap.put("b24_50_25", new arasuperobject(24,50,25,"فتح الرب خزانته واخرج آلات رجزه لان للسيد رب الجنود عملا في ارض الكلدانيين"));
arasuperobject.biblemap.put("b24_50_26", new arasuperobject(24,50,26,"هلم اليها من الاقصى. افتحوا اهرائها. كوّموها عراما وحرّموها ولا تكن لها بقية"));
arasuperobject.biblemap.put("b24_50_27", new arasuperobject(24,50,27,"اهلكوا كل عجولها. لتنزل للذبح. ويل لهم لانه قد اتى يومهم زمان عقابهم"));
arasuperobject.biblemap.put("b24_50_28", new arasuperobject(24,50,28,"صوت هاربين وناجين من ارض بابل ليخبروا في صهيون بنقمة الرب الهنا نقمة هيكله"));
arasuperobject.biblemap.put("b24_50_29", new arasuperobject(24,50,29,"ادعوا الى بابل اصحاب القسي. لينزل عليها كل من ينزع في القوس حواليها. لا يكن ناج كافئوها نظير عملها. افعلوا فيها حسب كل ما فعلت. لانها بغت على الرب على قدوس اسرائيل"));
arasuperobject.biblemap.put("b24_50_30", new arasuperobject(24,50,30,"لذلك يسقط شبانها في الشوارع وكل رجال حربها يهلكون في ذلك اليوم يقول الرب"));
arasuperobject.biblemap.put("b24_50_31", new arasuperobject(24,50,31,"هانذا عليك ايتها الباغية يقول السيد رب الجنود لانه قد أتى يومك حين عقابي اياك"));
arasuperobject.biblemap.put("b24_50_32", new arasuperobject(24,50,32,"فيعثر الباغي ويسقط ولا يكون له من يقيمه واشعل نارا في مدنه فتاكل كل ما حواليها"));
arasuperobject.biblemap.put("b24_50_33", new arasuperobject(24,50,33,"هكذا قال رب الجنود ان بني اسرائيل وبني يهوذا معا مظلومون وكل الذين سبوهم امسكوهم. ابوا ان يطلقوهم"));
arasuperobject.biblemap.put("b24_50_34", new arasuperobject(24,50,34,"وليهم قوي. رب الجنود اسمه. يقيم دعواهم لكي يريح الارض ويزعج سكان بابل"));
arasuperobject.biblemap.put("b24_50_35", new arasuperobject(24,50,35,"سيف على الكلدانيين يقول الرب وعلى سكان بابل وعلى رؤسائها وعلى حكمائها"));
arasuperobject.biblemap.put("b24_50_36", new arasuperobject(24,50,36,"سيف على المخادعين فيصيرون حمقا. سيف على ابطالها فيرتعبون"));
arasuperobject.biblemap.put("b24_50_37", new arasuperobject(24,50,37,"سيف على خيلها وعلى مركباتها وعلى كل اللفيف الذي في وسطها فيصيرون نساء. سيف على خزائنها فتنهب"));
arasuperobject.biblemap.put("b24_50_38", new arasuperobject(24,50,38,"حرّ على مياهها فتنشف لانها ارض منحوتات هي وبالاصنام تجنّ"));
arasuperobject.biblemap.put("b24_50_39", new arasuperobject(24,50,39,"لذلك تسكن وحوش القفر مع بنات آوى وتسكن فيها رعال النعام ولا تسكن بعد الى الابد ولا تعمر الى دور فدور"));
arasuperobject.biblemap.put("b24_50_40", new arasuperobject(24,50,40,"كقلب الله سدوم وعمورة ومجاوراتها يقول الرب لا يسكن هناك انسان ولا يتغرب فيها ابن آدم"));
arasuperobject.biblemap.put("b24_50_41", new arasuperobject(24,50,41,"هوذا شعب مقبل من الشمال وامة عظيمة ويوقظ ملوك كثيرون من اقاصي الارض"));
arasuperobject.biblemap.put("b24_50_42", new arasuperobject(24,50,42,"يمسكون القوس والرمح. هم قساة لا يرحمون. صوتهم يعج كبحر وعلى خيل يركبون مصطفين كرجل واحد لمحاربتك يا بنت بابل"));
arasuperobject.biblemap.put("b24_50_43", new arasuperobject(24,50,43,"سمع ملك بابل خبرهم فارتخت يداه. اخذته الضيقة والوجع كماخض"));
arasuperobject.biblemap.put("b24_50_44", new arasuperobject(24,50,44,"ها هو يصعد كأسد من كبرياء الاردن الى مرعى دائم. لاني اغمز واجعلهم يركضون عنه. فمن هو منتخب فاقيمه عليه. لانه من مثلي ومن يحاكمني ومن هو الراعي الذي يقف امامي"));
arasuperobject.biblemap.put("b24_50_45", new arasuperobject(24,50,45,"لذلك اسمعوا مشورة الرب التي قضى بها على بابل وافكاره التي افتكر بها على ارض الكلدانيين. ان صغار الغنم تسحبهم. انه يخرب مسكنهم عليهم"));
arasuperobject.biblemap.put("b24_50_46", new arasuperobject(24,50,46,"من القول أخذت بابل رجفت الارض وسمع صراخ في الشعوب"));
arasuperobject.biblemap.put("b24_51_1", new arasuperobject(24,51,1,"هكذا قال الرب. هانذا اوقظ على بابل وعلى الساكنين في وسط القائمين عليّ ريحا مهلكة"));
arasuperobject.biblemap.put("b24_51_2", new arasuperobject(24,51,2,"وارسل الى بابل مذرين فيذرونها ويفرغون ارضها لانهم يكونون عليها من كل جهة في يوم الشر"));
arasuperobject.biblemap.put("b24_51_3", new arasuperobject(24,51,3,"على النازع في قوسه فلينزع النازع وعلى المفتخر بدرعه فلا تشفقوا على منتخبيها بل حرموا كل جندها"));
arasuperobject.biblemap.put("b24_51_4", new arasuperobject(24,51,4,"فتسقط القتلى في ارض الكلدانيين والمطعونون في شوارعها"));
arasuperobject.biblemap.put("b24_51_5", new arasuperobject(24,51,5,"لان اسرائيل ويهوذا ليسا بمقطوعين عن الههما عن رب الجنود وان تكن ارضهما ملآنة اثما على قدوس اسرائيل"));
arasuperobject.biblemap.put("b24_51_6", new arasuperobject(24,51,6,"اهربوا من وسط بابل وانجوا كل واحد بنفسه. لا تهلكوا بذنبها لان هذا زمان انتقام الرب هو يؤدي لها جزاءها"));
arasuperobject.biblemap.put("b24_51_7", new arasuperobject(24,51,7,"بابل كاس ذهب بيد الرب تسكر كل الارض. من خمرها شربت الشعوب من اجل ذلك جنّت الشعوب"));
arasuperobject.biblemap.put("b24_51_8", new arasuperobject(24,51,8,"سقطت بابل بغتة وتحطمت. ولولوا عليها. خذوا بلسانا لجرحها لعلها تشفى"));
arasuperobject.biblemap.put("b24_51_9", new arasuperobject(24,51,9,"داوينا بابل فلم تشف. دعوها ولنذهب كل واحد الى ارضه لان قضاءها وصل الى السماء وارتفع الى السحاب"));
arasuperobject.biblemap.put("b24_51_10", new arasuperobject(24,51,10,"قد اخرج الرب برنا. هلم فنقص في صهيون عمل الرب الهنا"));
arasuperobject.biblemap.put("b24_51_11", new arasuperobject(24,51,11,"سنوا السهام. اعدوا الاتراس. قد ايقظ الرب روح ملوك مادي لان قصده على بابل ان يهلكها. لانه نقمة الرب نقمة هيكله"));
arasuperobject.biblemap.put("b24_51_12", new arasuperobject(24,51,12,"على اسوار بابل ارفعوا الراية. شددوا الحراسة. اقيموا الحراس. اعدوا الكمين لان الرب قد قصد وايضا فعل ما تكلم به على سكان بابل"));
arasuperobject.biblemap.put("b24_51_13", new arasuperobject(24,51,13,"ايتها الساكنة على مياه كثيرة الوافرة الخزائن قد اتت آخرتك كيل اغتصابك"));
arasuperobject.biblemap.put("b24_51_14", new arasuperobject(24,51,14,"قد حلف رب الجنود بنفسه اني لاملأنّك اناس كالغوغاء فيرفعون عليك جلبة"));
arasuperobject.biblemap.put("b24_51_15", new arasuperobject(24,51,15,"صانع الارض بقوته ومؤسس المسكونة بحكمته وبفهمه مدّ السموات"));
arasuperobject.biblemap.put("b24_51_16", new arasuperobject(24,51,16,"اذا اعطى قولا تكون كثرة مياه في السموات ويصعد السحاب من اقاصي الارض. صنع بروقا للمطر واخرج الريح من خزائنه"));
arasuperobject.biblemap.put("b24_51_17", new arasuperobject(24,51,17,"بلد كل انسان بمعرفته. خزي كل صائغ من التمثال. لان مسبوكه كذب ولا روح فيه"));
arasuperobject.biblemap.put("b24_51_18", new arasuperobject(24,51,18,"هي باطلة صنعة الاضاليل. في وقت عقابها تبيد"));
arasuperobject.biblemap.put("b24_51_19", new arasuperobject(24,51,19,"ليس كهذه نصيب يعقوب لانه مصوّر الجميع وقضيب ميراثه رب الجنود اسمه"));
arasuperobject.biblemap.put("b24_51_20", new arasuperobject(24,51,20,"انت لي فأس وادوات حرب فاسحق بك الامم واهلك بك الممالك"));
arasuperobject.biblemap.put("b24_51_21", new arasuperobject(24,51,21,"واكسر بك الفرس وراكبه واسحق بك المركبة وراكبها"));
arasuperobject.biblemap.put("b24_51_22", new arasuperobject(24,51,22,"واسحق بك الرجل والمرأة واسحق بك الشيخ والفتى واسحق بك الغلام والعذراء"));
arasuperobject.biblemap.put("b24_51_23", new arasuperobject(24,51,23,"واسحق بك الراعي وقطيعه واسحق بك الفلاح وفدانه واسحق بك الولاة والحكام"));
arasuperobject.biblemap.put("b24_51_24", new arasuperobject(24,51,24,"واكافئ بابل وكل سكان ارض الكلدانيين على كل شرهم الذي فعلوه في صهيون امام عيونكم يقول الرب"));
arasuperobject.biblemap.put("b24_51_25", new arasuperobject(24,51,25,"هانذا عليك ايها الجبل المهلك يقول الرب المهلك كل الارض فامدّ يدي عليك وادحرجك عن الصخور واجعلك جبلا محرقا"));
arasuperobject.biblemap.put("b24_51_26", new arasuperobject(24,51,26,"فلا يأخذون منك حجرا لزاوية ولا حجرا لأسس بل تكون خرابا الى الابد يقول الرب"));
arasuperobject.biblemap.put("b24_51_27", new arasuperobject(24,51,27,"ارفعوا الراية في الارض. اضربوا بالبوق في الشعوب قدسوا عليها الامم نادوا عليها ممالك اراراط ومنّي واشكناز. اقيموا عليها قائدا اصعدوا الخيل كغوغاء مقشعرّة"));
arasuperobject.biblemap.put("b24_51_28", new arasuperobject(24,51,28,"قدسوا عليها الشعوب ملوك مادي ولاتها وكل حكامها وكل ارض سلطانها"));
arasuperobject.biblemap.put("b24_51_29", new arasuperobject(24,51,29,"فترتجف الارض وتتوجع لان افكار الرب تقوم على بابل ليجعل ارض بابل خرابا بلا ساكن"));
arasuperobject.biblemap.put("b24_51_30", new arasuperobject(24,51,30,"كفّ جبابرة بابل عن الحرب وجلسوا في الحصون. نضبت شجاعتهم. صاروا نساء. حرقوا مساكنها. تحطمت عوارضها"));
arasuperobject.biblemap.put("b24_51_31", new arasuperobject(24,51,31,"يركض عدّاء للقاء عدّاء ومخبر للقاء مخبر ليخبر ملك بابل بان مدينته قد أخذت عن اقصى"));
arasuperobject.biblemap.put("b24_51_32", new arasuperobject(24,51,32,"وان المعابر قد أمسكت والقصب احرقوه بالنار ورجال الحرب اضطربت"));
arasuperobject.biblemap.put("b24_51_33", new arasuperobject(24,51,33,"لانه هكذا قال رب الجنود اله اسرائيل ان بنت بابل كبيدر وقت دوسه. بعد قليل يأتي عليها وقت الحصاد"));
arasuperobject.biblemap.put("b24_51_34", new arasuperobject(24,51,34,"اكلني افناني نبوخذراصر ملك بابل. جعلني اناء فارغا. ابتلعني كتنين وملأ جوفه من نعمي. طوّحني"));
arasuperobject.biblemap.put("b24_51_35", new arasuperobject(24,51,35,"ظلمي ولحمي على بابل تقول ساكنة صهيون ودمي على سكان ارض الكلدانيين تقول اورشليم"));
arasuperobject.biblemap.put("b24_51_36", new arasuperobject(24,51,36,"لذلك هكذا قال الرب. هانذا اخاصم خصومتك وانتقم نقمتك وانشف بحرها واجفف ينبوعها"));
arasuperobject.biblemap.put("b24_51_37", new arasuperobject(24,51,37,"وتكون بابل كوما ومأوى بنات آوى ودهشا وصفيرا بلا ساكن"));
arasuperobject.biblemap.put("b24_51_38", new arasuperobject(24,51,38,"يزمجرون معا كاشبال. يزئرون كجراء أسود"));
arasuperobject.biblemap.put("b24_51_39", new arasuperobject(24,51,39,"عند حرارتهم اعدّ لهم شرابا واسكرهم لكي يفرحوا ويناموا نوما ابديا ولا يستيقظوا يقول الرب"));
arasuperobject.biblemap.put("b24_51_40", new arasuperobject(24,51,40,"انزلهم كخراف للذبح وككباش مع اعتدة"));
arasuperobject.biblemap.put("b24_51_41", new arasuperobject(24,51,41,"كيف أخذت شيشك وأمسكت فخر كل الارض. كيف صارت بابل دهشا في الشعوب"));
arasuperobject.biblemap.put("b24_51_42", new arasuperobject(24,51,42,"طلع البحر على بابل فتغطت بكثرة امواجه"));
arasuperobject.biblemap.put("b24_51_43", new arasuperobject(24,51,43,"صارت مدنها خرابا ارضا ناشفة وقفرا ارضا لا يسكن فيها انسان ولا يعبر فيها ابن آدم"));
arasuperobject.biblemap.put("b24_51_44", new arasuperobject(24,51,44,"واعاقب بيل في بابل واخرج من فمه ما ابتلعه فلا تجري اليه الشعوب بعد ويسقط سور بابل ايضا"));
arasuperobject.biblemap.put("b24_51_45", new arasuperobject(24,51,45,"اخرجوا من وسطها يا شعبي ولينج كل واحد نفسه من حمو غضب الرب"));
arasuperobject.biblemap.put("b24_51_46", new arasuperobject(24,51,46,"ولا يضعف قلبكم فتخافوا من الخبر الذي سمع في الارض فانه يأتي خبر في هذه السنة ثم بعده في السنة الاخرى خبر وظلم في الارض متسلط على متسلط"));
arasuperobject.biblemap.put("b24_51_47", new arasuperobject(24,51,47,"لذلك ها ايام تاتي واعاقب منحوتات بابل فتخزى كل ارضها وتسقط كل قتلاها في وسطها"));
arasuperobject.biblemap.put("b24_51_48", new arasuperobject(24,51,48,"فتهتف على بابل السموات والارض وكل ما فيها لان الناهبين ياتون عليها من الشمال يقول الرب"));
arasuperobject.biblemap.put("b24_51_49", new arasuperobject(24,51,49,"كما اسقطت بابل قتلى اسرائيل تسقط ايضا قتلى بابل في كل الارض"));
arasuperobject.biblemap.put("b24_51_50", new arasuperobject(24,51,50,"ايها الناجون من السيف اذهبوا لا تقفوا اذكروا الرب من بعيد ولتخطر اورشليم ببالكم"));
arasuperobject.biblemap.put("b24_51_51", new arasuperobject(24,51,51,"قد خزينا لاننا قد سمعنا عارا غطى الخجل وجوهنا لان الغرباء دخلوا مقادس بيت الرب"));
arasuperobject.biblemap.put("b24_51_52", new arasuperobject(24,51,52,"لذلك ها ايام تأتي يقول الرب واعاقب منحوتاتها ويتنهد الجرحى في كل ارضها"));
arasuperobject.biblemap.put("b24_51_53", new arasuperobject(24,51,53,"فلو صعدت بابل الى السموات ولو حصّنت علياء عزها فمن عندي يأتي عليها الناهبون يقول الرب"));
arasuperobject.biblemap.put("b24_51_54", new arasuperobject(24,51,54,"صوت صراخ من بابل وانحطام عظيم من ارض الكلدانيين"));
arasuperobject.biblemap.put("b24_51_55", new arasuperobject(24,51,55,"لان الرب مخرب بابل وقد اباد منها الصوت العظيم وقد عجّت امواجهم كمياه كثيرة وأطلق ضجيج صوتهم"));
arasuperobject.biblemap.put("b24_51_56", new arasuperobject(24,51,56,"لانه جاء عليها على بابل المخرب وأخذ جبابرتها وتحطمت قسيهم لان الرب اله مجازاة يكافئ مكافأة"));
arasuperobject.biblemap.put("b24_51_57", new arasuperobject(24,51,57,"واسكر رؤساءها وحكماءها وولاتها وحكامها وابطالها فينامون نوما ابديا ولا يستيقظون يقول الملك رب الجنود اسمه"));
arasuperobject.biblemap.put("b24_51_58", new arasuperobject(24,51,58,"هكذا قال رب الجنود ان اسوار بابل العريضة تدمر تدميرا وابوابها الشامخة تحرق بالنار فتتعب الشعوب للباطل والقبائل للنار حتى تعيا"));
arasuperobject.biblemap.put("b24_51_59", new arasuperobject(24,51,59,"الأمر الذي اوصى به ارميا النبي سرايا بن نيريا بن محسيا عند ذهابه مع صدقيا ملك يهوذا الى بابل في السنة الرابعة لملكه. وكان سرايا رئيس المحلّة"));
arasuperobject.biblemap.put("b24_51_60", new arasuperobject(24,51,60,"فكتب ارميا كل الشر الآتي على بابل في سفر واحد كل هذا الكلام المكتوب على بابل"));
arasuperobject.biblemap.put("b24_51_61", new arasuperobject(24,51,61,"وقال ارميا لسرايا اذا دخلت الى بابل ونظرت وقرأت كل هذا الكلام"));
arasuperobject.biblemap.put("b24_51_62", new arasuperobject(24,51,62,"فقل انت يا رب قد تكلمت على هذا الموضع لتقرضه حتى لا يكون فيه ساكن من الناس الى البهائم بل يكون خربا ابدية"));
arasuperobject.biblemap.put("b24_51_63", new arasuperobject(24,51,63,"ويكون اذا فرغت من قراءة هذا السفر انك تربط فيه حجرا وتطرحه الى وسط الفرات"));
arasuperobject.biblemap.put("b24_51_64", new arasuperobject(24,51,64,"وتقول هكذا تغرق بابل ولا تقوم من الشر الذي انا جالبه عليها ويعيون. الى هنا كلام ارميا"));
arasuperobject.biblemap.put("b24_52_1", new arasuperobject(24,52,1,"كان صدقيا ابن احدى وعشرين سنة حين ملك وملك احدى عشرة سنة في اورشليم واسم امه حميطل بنت ارميا من لبنة"));
arasuperobject.biblemap.put("b24_52_2", new arasuperobject(24,52,2,"وعمل الشر في عيني الرب حسب كل ما عمل يهوياقيم"));
arasuperobject.biblemap.put("b24_52_3", new arasuperobject(24,52,3,"لانه لاجل غضب الرب على اورشليم ويهوذا حتى طرحهم من امام وجهه كان ان صدقيا تمرد على ملك بابل"));
arasuperobject.biblemap.put("b24_52_4", new arasuperobject(24,52,4,"وفي السنة التاسعة لملكه في الشهر العاشر في عاشر الشهر جاء نبوخذراصر ملك بابل هو وكل جيشه على اورشليم ونزلوا عليها وبنوا عليها ابراجا حواليها"));
arasuperobject.biblemap.put("b24_52_5", new arasuperobject(24,52,5,"فدخلت المدينة في الحصار الى السنة الحادية عشرة للملك صدقيا"));
arasuperobject.biblemap.put("b24_52_6", new arasuperobject(24,52,6,"في الشهر الرابع في تاسع الشهر اشتد الجوع في المدينة ولم يكن خبز لشعب الارض"));
arasuperobject.biblemap.put("b24_52_7", new arasuperobject(24,52,7,"فثغرت المدينة وهرب كل رجال القتال وخرجوا من المدينة ليلا في طريق الباب بين السورين اللذين عند جنة الملك والكلدانيون عند المدينة حواليها فذهبوا في طريق البرية"));
arasuperobject.biblemap.put("b24_52_8", new arasuperobject(24,52,8,"فتبعت جيوش الكلدانيين الملك فادركوا صدقيا في برية اريحا وتفرق كل جيشه عنه"));
arasuperobject.biblemap.put("b24_52_9", new arasuperobject(24,52,9,"فأخذوا الملك واصعدوه الى ملك بابل الى ربلة في ارض حماة فكلمه بالقضاء عليه"));
arasuperobject.biblemap.put("b24_52_10", new arasuperobject(24,52,10,"فقتل ملك بابل بني صدقيا امام عينيه وقتل ايضا كل رؤساء يهوذا في ربلة"));
arasuperobject.biblemap.put("b24_52_11", new arasuperobject(24,52,11,"واعمى عيني صدقيا وقيده بسلسلتين من نحاس وجاء به ملك بابل الى بابل وجعله في السجن الى يوم وفاته"));
arasuperobject.biblemap.put("b24_52_12", new arasuperobject(24,52,12,"وفي الشهر الخامس في عاشر الشهر وهي السنة التاسعة عشرة للملك نبوخذراصر ملك بابل جاء نبوزرادان رئيس الشرط الذي كان يقف امام ملك بابل الى اورشليم"));
arasuperobject.biblemap.put("b24_52_13", new arasuperobject(24,52,13,"واحرق بيت الرب وبيت الملك وكل بيوت اورشليم وكل بيوت العظماء احرقها بالنار"));
arasuperobject.biblemap.put("b24_52_14", new arasuperobject(24,52,14,"وكل اسوار اورشليم مستديرا هدمها كل جيش الكلدانيين الذي مع رئيس الشرط"));
arasuperobject.biblemap.put("b24_52_15", new arasuperobject(24,52,15,"وسبى نبوزرادان رئيس الشرط بعضا من فقراء الشعب وبقية الشعب الذين بقوا في المدينة والهاربين الذين سقطوا الى ملك بابل وبقية الجمهور"));
arasuperobject.biblemap.put("b24_52_16", new arasuperobject(24,52,16,"ولكن نبوزرادان رئيس الشرط ابقى من مساكين الارض كرّامين وفلاحين"));
arasuperobject.biblemap.put("b24_52_17", new arasuperobject(24,52,17,"وكسر الكلدانيون اعمدة النحاس التي لبيت الرب والقواعد وبحر النحاس الذي في بيت الرب وحملوا كل نحاسها الى بابل"));
arasuperobject.biblemap.put("b24_52_18", new arasuperobject(24,52,18,"وأخذوا القدور والرفوش والمقاص والمناضح والصحون وكل آنية النحاس التي كانوا يخدمون بها"));
arasuperobject.biblemap.put("b24_52_19", new arasuperobject(24,52,19,"واخذ رئيس الشرط الطسوس والمجامر والمناضح والقدور والمناير والصحون والاقداح ما كان من ذهب فالذهب وما كان من فضة فالفضة"));
arasuperobject.biblemap.put("b24_52_20", new arasuperobject(24,52,20,"والعمودين والبحر الواحد والاثني عشر ثورا من نحاس التي تحت القواعد التي عملها الملك سليمان لبيت الرب. لم يكن وزن لنحاس كل هذه الادوات"));
arasuperobject.biblemap.put("b24_52_21", new arasuperobject(24,52,21,"اما العمودان فكان طول العمود الواحد ثماني عشرة ذراعا وخيط اثنتا عشرة ذراعا يحيط به وغلظه اربع اصابع وهو اجوف"));
arasuperobject.biblemap.put("b24_52_22", new arasuperobject(24,52,22,"وعليه تاج من نحاس ارتفاع التاج الواحد خمس اذرع وعلى التاج حواليه شبكة ورمانات الكل من نحاس. ومثل ذلك للعمود الثاني والرمانات"));
arasuperobject.biblemap.put("b24_52_23", new arasuperobject(24,52,23,"وكانت الرمانات ستا وتسعين للجانب. كل الرمانات مئة على الشبكة حواليها"));
arasuperobject.biblemap.put("b24_52_24", new arasuperobject(24,52,24,"واخذ رئيس الشرط سرايا الكاهن الاول وصفنيا الكاهن الثاني وحارسي الباب الثلاثة"));
arasuperobject.biblemap.put("b24_52_25", new arasuperobject(24,52,25,"واخذ من المدينة خصيا واحدا كان وكيلا على رجال الحرب وسبعة رجال من الذين ينظرون وجه الملك الذين وجدوا في المدينة وكاتب رئيس الجند الذي كان يجمع شعب الارض للتجند وستين رجلا من شعب الارض الذين وجدوا في وسط المدينة"));
arasuperobject.biblemap.put("b24_52_26", new arasuperobject(24,52,26,"اخذهم نبوزرادان رئيس الشرط وسار بهم الى ملك بابل الى ربلة"));
arasuperobject.biblemap.put("b24_52_27", new arasuperobject(24,52,27,"فضربهم ملك بابل وقتلهم في ربلة في ارض حماة فسبي يهوذا من ارضه"));
arasuperobject.biblemap.put("b24_52_28", new arasuperobject(24,52,28,"هذا هو الشعب الذي سباه نبوخذراصر في السنة السابعة. من اليهود ثلاثة آلاف وثلاثة وعشرون"));
arasuperobject.biblemap.put("b24_52_29", new arasuperobject(24,52,29,"وفي السنة الثامنة عشرة لنبوخذراصر سبى من اورشليم ثمان مئة واثنتان وثلاثون نفسا"));
arasuperobject.biblemap.put("b24_52_30", new arasuperobject(24,52,30,"في السنة الثالثة والعشرين لنبوخذراصر سبى نبوزرادان رئيس الشرط من اليهود سبع مئة وخمسا واربعين نفسا. جملة النفوس اربعة آلاف وست مئة"));
arasuperobject.biblemap.put("b24_52_31", new arasuperobject(24,52,31,"وفي السنة السابعة والثلاثين لسبي يهوياكين في الشهر الثاني عشر في الخامس والعشرين من الشهر رفع اويل مرودخ ملك بابل في سنة تملكه راس يهوياكين ملك يهوذا وأخرجه من السجن"));
arasuperobject.biblemap.put("b24_52_32", new arasuperobject(24,52,32,"وكلمه بخير وجعل كرسيه فوق كراسي الملوك الذين معه في بابل"));
arasuperobject.biblemap.put("b24_52_33", new arasuperobject(24,52,33,"وغيّر ثياب سجنه وكان يأكل دائما الخبز امامه كل ايام حياته"));
arasuperobject.biblemap.put("b24_52_34", new arasuperobject(24,52,34,"ووظيفته وظيفة دائمة تعطى له من عند ملك بابل امر كل يوم بيومه الى يوم وفاته كل ايام حياته"));

