arasuperobject.biblemap.put("b26_1_20", new arasuperobject(26,1,20,"الى حيث تكون الروح لتسير يسيرون الى حيث الروح لتسير والبكرات ترتفع معها. لان روح الحيوانات كانت في البكرات"));
arasuperobject.biblemap.put("b26_1_21", new arasuperobject(26,1,21,"فاذا سارت تلك سارت هذه واذا وقفت تلك وقفت. واذا ارتفعت تلك عن الارض ارتفعت البكرات معها لان روح الحيوانات كانت في البكرات"));
arasuperobject.biblemap.put("b26_1_22", new arasuperobject(26,1,22,"وعلى رؤوس الحيوانات شبه مقبب كمنظر البلور الهائل منتشرا على رؤوسها من فوق"));
