arasuperobject.biblemap.put("b26_2_3", new arasuperobject(26,2,3,"وقال لي يا ابن آدم انا مرسلك الى بني اسرائيل الى امة متمردة قد تمردت عليّ. هم وآباؤهم عصوا عليّ الى ذات هذا اليوم"));
arasuperobject.biblemap.put("b26_2_4", new arasuperobject(26,2,4,"والبنون القساة الوجوه والصلاب القلوب انا مرسلك اليهم. فتقول لهم هكذا قال السيد الرب"));
arasuperobject.biblemap.put("b26_2_5", new arasuperobject(26,2,5,"وهم ان سمعوا وان امتنعوا. لانهم بيت متمرد. فانهم يعلمون ان نبيا كان بينهم"));
