arasuperobject.biblemap.put("b26_2_7", new arasuperobject(26,2,7,"وتتكلم معهم بكلامي ان سمعوا وان امتنعوا لانهم متمردون"));
arasuperobject.biblemap.put("b26_2_8", new arasuperobject(26,2,8,"وانت يا ابن آدم فاسمع ما انا مكلمك به. لا تكن متمردا كالبيت المتمرد. افتح فمك وكل ما انا معطيكه"));
