arasuperobject.biblemap.put("b26_2_10", new arasuperobject(26,2,10,"فنشره امامي وهو مكتوب من داخل ومن قفاه وكتب فيه مراث ونحيب وويل"));
arasuperobject.biblemap.put("b26_3_1", new arasuperobject(26,3,1,"وقال لي يا ابن آدم كل ما تجده. كل هذا الدرج واذهب كلم بيت اسرائيل"));
