arasuperobject.biblemap.put("b26_3_4", new arasuperobject(26,3,4,"فقال لي يا ابن آدم اذهب امض الى بيت اسرائيل وكلمهم بكلامي"));
arasuperobject.biblemap.put("b26_3_5", new arasuperobject(26,3,5,"لانك غير مرسل الى شعب غامض اللغة وثقيل اللسان بل الى بيت اسرائيل"));
arasuperobject.biblemap.put("b26_3_6", new arasuperobject(26,3,6,"لا الى شعوب كثيرة غامضة اللغة وثقيلة اللسان لست تفهم كلامهم. فلو ارسلتك الى هؤلاء لسمعوا لك"));
arasuperobject.biblemap.put("b26_3_7", new arasuperobject(26,3,7,"لكن بيت اسرائيل لا يشاء ان يسمع لك. لانهم لا يشاؤون ان يسمعوا لي. لان كل بيت اسرائيل صلاب الجباه وقساة القلوب"));
arasuperobject.biblemap.put("b26_3_8", new arasuperobject(26,3,8,"هانذا قد جعلت وجهك صلبا مثل وجوههم وجبهتك صلبة مثل جباههم"));
arasuperobject.biblemap.put("b26_3_9", new arasuperobject(26,3,9,"قد جعلت جبهتك كالماس اصلب من الصوان فلا تخفهم ولا ترتعب من وجوههم لانهم بيت متمرد"));
arasuperobject.biblemap.put("b26_3_10", new arasuperobject(26,3,10,"وقال لي يا ابن آدم كل الكلام الذي اكلمك به اوعه في قلبك واسمعه باذنيك"));
arasuperobject.biblemap.put("b26_3_11", new arasuperobject(26,3,11,"وامض اذهب الى المسبيين الى بني شعبك وكلمهم وقل لهم هكذا قال السيد الرب ان سمعوا وان امتنعوا"));
arasuperobject.biblemap.put("b26_3_12", new arasuperobject(26,3,12,"ثم حملني روح فسمعت خلفي صوت رعد عظيم مبارك مجد الرب من مكانه"));
arasuperobject.biblemap.put("b26_3_13", new arasuperobject(26,3,13,"وصوت اجنحة الحيوانات المتلاصقة الواحد باخيه وصوت البكرات معها وصوت رعد عظيم"));
arasuperobject.biblemap.put("b26_3_14", new arasuperobject(26,3,14,"فحملني الروح واخذني فذهبت مرّا في حرارة روحي ويد الرب كانت شديدة عليّ"));
arasuperobject.biblemap.put("b26_3_15", new arasuperobject(26,3,15,"فجئت الى المسبيين عند تل ابيب الساكنين عند نهر خابور وحيث سكنوا هناك سكنت سبعة ايام متحيرا في وسطهم"));
arasuperobject.biblemap.put("b26_3_16", new arasuperobject(26,3,16,"وكان عند تمام السبعة الايام ان كلمة الرب صارت اليّ قائلة"));
arasuperobject.biblemap.put("b26_3_17", new arasuperobject(26,3,17,"يا ابن آدم قد جعلتك رقيبا لبيت اسرائيل. فاسمع الكلمة من فمي وانذرهم من قبلي"));
arasuperobject.biblemap.put("b26_3_18", new arasuperobject(26,3,18,"اذا قلت للشرير موتا تموت وما انذرته انت ولا تكلمت انذارا للشرير من طريقه الرديئة لإحيائه فذلك الشرير يموت باثمه اما دمه فمن يدك اطلبه"));
arasuperobject.biblemap.put("b26_3_19", new arasuperobject(26,3,19,"وان انذرت انت الشرير ولم يرجع عن شره ولا عن طريقه الرديئة فانه يموت باثمه. اما انت فقد نجيت نفسك"));
arasuperobject.biblemap.put("b26_3_20", new arasuperobject(26,3,20,"والبار ان رجع عن بره وعمل اثما وجعلت معثرة امامه فانه يموت. لانك لم تنذره يموت في خطيته ولا يذكر بره الذي عمله. اما دمه فمن يدك اطلبه"));
arasuperobject.biblemap.put("b26_3_21", new arasuperobject(26,3,21,"وان انذرت انت البار من ان يخطئ البار وهو لم يخطئ فانه حياة يحيا لانه انذر وانت تكون قد نجيت نفسك"));
arasuperobject.biblemap.put("b26_3_22", new arasuperobject(26,3,22,"وكانت يد الرب عليّ هناك وقال لي قم اخرج الى البقعة وهناك اكلمك"));
arasuperobject.biblemap.put("b26_3_23", new arasuperobject(26,3,23,"فقمت وخرجت الى البقعة واذا بمجد الرب واقف هناك كالمجد الذي رايته عند نهر خابور. فخررت على وجهي"));
arasuperobject.biblemap.put("b26_3_24", new arasuperobject(26,3,24,"فدخل فيّ روح واقامني على قدميّ. ثم كلمني وقال لي. اذهب اغلق على نفسك في وسط بيتك"));
arasuperobject.biblemap.put("b26_3_25", new arasuperobject(26,3,25,"وانت يا ابن آدم فها هم يضعون عليك ربطا ويقيدونك بها فلا تخرج في وسطهم"));
arasuperobject.biblemap.put("b26_3_26", new arasuperobject(26,3,26,"وألصق لسانك بحنكك فتبكم ولا تكون لهم رجلا موبخا لانهم بيت متمرد"));
arasuperobject.biblemap.put("b26_3_27", new arasuperobject(26,3,27,"فاذا كلمتك افتح فمك فتقول لهم هكذا قال السيد الرب. من يسمع فليسمع ومن يمتنع فليمتنع. لانهم بيت متمرد"));
arasuperobject.biblemap.put("b26_4_1", new arasuperobject(26,4,1,"وانت يا ابن آدم فخذ لنفسك لبنة وضعها امامك وارسم عليها مدينة اورشليم"));
arasuperobject.biblemap.put("b26_4_2", new arasuperobject(26,4,2,"واجعل عليها حصار وابن عليها برجا واقم عليها مترسة واجعل عليها جيوشا واقم عليها مجانق حولها"));
arasuperobject.biblemap.put("b26_4_3", new arasuperobject(26,4,3,"وخذ انت لنفسك صاجا من حديد وانصبه سورا من حديد بينك وبين المدينة وثبت وجهك عليها فتكون في حصار وتحاصرها. تلك آية لبيت اسرائيل"));
arasuperobject.biblemap.put("b26_4_4", new arasuperobject(26,4,4,"واتكئ انت على جنبك اليسار وضع عليه اثم بيت اسرائيل. على عدد الايام التي فيها تتكئ عليه تحمل اثمهم"));
arasuperobject.biblemap.put("b26_4_5", new arasuperobject(26,4,5,"وانا قد جعلت لك سني اثمهم حسب عدد الايام ثلاث مئة يوم وتسعين يوما. فتحمل اثم بيت اسرائيل"));
arasuperobject.biblemap.put("b26_4_6", new arasuperobject(26,4,6,"فاذا اتممتها فاتكئ على جنبك اليمين ايضا فتحمل اثم بيت يهوذا اربعين يوما. فقد جعلت لك كل يوم عوضا عن سنة"));
arasuperobject.biblemap.put("b26_4_7", new arasuperobject(26,4,7,"فثبّت وجهك على حصار اورشليم وذراعك مكشوفة وتنبّا عليها"));
arasuperobject.biblemap.put("b26_4_8", new arasuperobject(26,4,8,"وهأنذا اجعل عليك ربطا فلا تقلب من جنب الى جنب حتى تتمم ايام حصارك"));
arasuperobject.biblemap.put("b26_4_9", new arasuperobject(26,4,9,"وخذ انت لنفسك قمحا وشعيرا وفولا وعدسا ودخنا وكرسنة وضعها في وعاء واحد واصنعها لنفسك خبزا كعدد الايام التي تتكئ فيها على جنبك. ثلاث مئة يوم وتسعين يوما تاكله"));
arasuperobject.biblemap.put("b26_4_10", new arasuperobject(26,4,10,"وطعامك الذي تأكله يكون بالوزن. كل يوم عشرين شاقلا. من وقت الى وقت تاكله"));
arasuperobject.biblemap.put("b26_4_11", new arasuperobject(26,4,11,"وتشرب الماء بالكيل. سدس الهين. من وقت الى وقت تشربه"));
arasuperobject.biblemap.put("b26_4_12", new arasuperobject(26,4,12,"وتأكل كعكا من الشعير. على الخرء الذي يخرج من الانسان تخبزه امام عيونهم"));
arasuperobject.biblemap.put("b26_4_13", new arasuperobject(26,4,13,"وقال الرب. هكذا ياكل بنو اسرائيل خبزهم النجس بين الامم الذين اطردهم اليهم"));
arasuperobject.biblemap.put("b26_4_14", new arasuperobject(26,4,14,"فقلت آه يا سيد الرب ها نفسي لم تتنجس ومن صباي الى الآن لم آكل ميتة او فريسة ولا دخل فمي لحم نجس"));
arasuperobject.biblemap.put("b26_4_15", new arasuperobject(26,4,15,"فقال لي انظر. قد جعلت لك خثي البقر بدل خرء الانسان فتصنع خبزك عليه"));
arasuperobject.biblemap.put("b26_4_16", new arasuperobject(26,4,16,"وقال لي يا ابن آدم هانذا اكسر قوام الخبز في اورشليم فياكلون الخبز بالوزن وبالغم ويشربون الماء بالكيل وبالحيرة"));
arasuperobject.biblemap.put("b26_4_17", new arasuperobject(26,4,17,"لكي يعوزهم الخبز والماء ويتحيروا الرجل واخوه ويفنوا باثمهم"));
arasuperobject.biblemap.put("b26_5_1", new arasuperobject(26,5,1,"وانت يا ابن آدم فخذ لنفسك سكينا حادا موسى الحلاق تأخذ لنفسك وامررها على راسك وعلى لحيتك. وخذ لنفسك ميزانا للوزن واقسمه"));
arasuperobject.biblemap.put("b26_5_2", new arasuperobject(26,5,2,"واحرق بالنار ثلثه في وسط المدينة اذا تمت ايام الحصار وخذ ثلثا واضربه بالسيف حواليه وذر ثلثا الى الريح. وانا استل سيفا وراءهم"));
arasuperobject.biblemap.put("b26_5_3", new arasuperobject(26,5,3,"وخذ منه قليلا بالعدد وصرّه في اذيالك"));
arasuperobject.biblemap.put("b26_5_4", new arasuperobject(26,5,4,"وخذ منه ايضا والقه في وسط النار واحرقه بالنار. منه تخرج نار على كل بيت اسرائيل"));
arasuperobject.biblemap.put("b26_5_5", new arasuperobject(26,5,5,"هكذا قال السيد الرب. هذه اورشليم في وسط الشعوب قد اقمتها وحواليها الاراضي"));
arasuperobject.biblemap.put("b26_5_6", new arasuperobject(26,5,6,"فخالفت احكامي باشرّ من الامم وفرائضي باشرّ من الاراضي التي حواليها لان احكامي رفضوها وفرائضي لم يسلكوا فيها"));
arasuperobject.biblemap.put("b26_5_7", new arasuperobject(26,5,7,"لاجل ذلك هكذا قال السيد الرب. من اجل انكم ضججتم اكثر من الامم التي حواليكم ولم تسلكوا في فرائضي ولم تعملوا حسب احكامي ولا عملتم حسب احكام الامم التي حواليكم"));
arasuperobject.biblemap.put("b26_5_8", new arasuperobject(26,5,8,"لذلك هكذا قال السيد الرب. ها اني انا ايضا عليك وسأجري في وسطك احكاما امام عيون الامم"));
arasuperobject.biblemap.put("b26_5_9", new arasuperobject(26,5,9,"وافعل بك ما لم افعل وما لن افعل مثله بعد بسبب كل ارجاسك"));
arasuperobject.biblemap.put("b26_5_10", new arasuperobject(26,5,10,"لاجل ذلك تاكل الآباء الابناء في وسطك والابناء يأكلون آباءهم واجري فيك احكاما واذري بقيتك كلها في كل ريح"));
arasuperobject.biblemap.put("b26_5_11", new arasuperobject(26,5,11,"من اجل ذلك حيّ انا يقول السيد الرب من اجل انك قد نجست مقدسي بكل مكرهاتك وبكل ارجاسك فانا ايضا اجزّ ولا تشفق عيني وانا ايضا لا اعفو"));
arasuperobject.biblemap.put("b26_5_12", new arasuperobject(26,5,12,"ثلثك يموت بالوبإ وبالجوع يفنون في وسطك وثلث يسقط بالسيف من حولك وثلث اذريه في كل ريح واستل سيفا ورائهم"));
arasuperobject.biblemap.put("b26_5_13", new arasuperobject(26,5,13,"واذا تم غضبي واحللت سخطي عليهم وتشفيت يعلمون اني انا الرب تكلمت في غيرتي اذا اتممت سخطي فيهم"));
arasuperobject.biblemap.put("b26_5_14", new arasuperobject(26,5,14,"واجعلك خرابا وعارا بين الامم التي حواليك امام عيني كل عابر"));
arasuperobject.biblemap.put("b26_5_15", new arasuperobject(26,5,15,"فتكونين عارا ولعنة وتأديبا ودهشا للامم التي حواليك اذا اجريت فيك احكاما بغضب وبسخط وبتوبيخات حامية. انا الرب تكلمت"));
arasuperobject.biblemap.put("b26_5_16", new arasuperobject(26,5,16,"اذا ارسلت عليهم سهام الجوع الشريرة التي تكون للخراب التي ارسلها لخرابكم وازيد الجوع عليكم واكسر لكم قوام الخبز"));
arasuperobject.biblemap.put("b26_5_17", new arasuperobject(26,5,17,"واذا ارسلت عليكم الجوع والوحوش الرديئة فتثكلك ويعبر فيك الوبأ والدم واجلب عليك سيفا. انا الرب تكلمت"));
arasuperobject.biblemap.put("b26_6_1", new arasuperobject(26,6,1,"وكان اليّ كلام الرب قائلا"));
arasuperobject.biblemap.put("b26_6_2", new arasuperobject(26,6,2,"يا ابن آدم اجعل وجهك نحو جبال اسرائيل وتنبأ عليها"));
arasuperobject.biblemap.put("b26_6_3", new arasuperobject(26,6,3,"وقل يا جبال اسرائيل اسمعي كلمة السيد الرب. هكذا قال السيد الرب للجبال وللآكام للاودية وللاوطئة هانذا انا جالب عليكم سيفا وابيد مرتفعاتكم"));
arasuperobject.biblemap.put("b26_6_4", new arasuperobject(26,6,4,"فتخرب مذابحكم وتتكسر شمساتكم واطرح قتلاكم قدام اصنامكم"));
arasuperobject.biblemap.put("b26_6_5", new arasuperobject(26,6,5,"واضع جثث بني اسرائيل قدام اصنامهم واذري عظامكم حول مذابحكم"));
arasuperobject.biblemap.put("b26_6_6", new arasuperobject(26,6,6,"في كل مساكنكم تقفر المدن وتخرب المرتفعات لكي تقفر وتخرب مذابحكم وتنكسر وتزول اصنامكم وتقطع شمساتكم وتمحى اعمالكم"));
arasuperobject.biblemap.put("b26_6_7", new arasuperobject(26,6,7,"وتسقط القتلى في وسطكم فتعلمون اني انا الرب"));
arasuperobject.biblemap.put("b26_6_8", new arasuperobject(26,6,8,"وابقي بقية اذ يكون لكم ناجون من السيف بين الامم عند تذرّيكم في الاراضي"));
arasuperobject.biblemap.put("b26_6_9", new arasuperobject(26,6,9,"والناجون منكم يذكرونني بين الامم الذين يسبون اليهم اذا كسرت قلبهم الزاني الذي حاد عني وعيونهم الزانية وراء اصنامهم ومقتوا انفسهم لاجل الشرور التي فعلوها في كل رجاساتهم"));
arasuperobject.biblemap.put("b26_6_10", new arasuperobject(26,6,10,"ويعلمون اني انا الرب. لم اقل باطلا اني افعل بهم هذا الشر"));
arasuperobject.biblemap.put("b26_6_11", new arasuperobject(26,6,11,"هكذا قال السيد الرب. اضرب بيدك واخبط برجلك وقل آه على كل رجاسات بيت اسرائيل الشريرة حتى يسقطوا بالسيف وبالجوع وبالوبإ"));
arasuperobject.biblemap.put("b26_6_12", new arasuperobject(26,6,12,"البعيد يموت باوبإ والقريب يسقط بالسيف والباقي والمنحصر يموت بالجوع فاتمم غضبي عليهم"));
arasuperobject.biblemap.put("b26_6_13", new arasuperobject(26,6,13,"فتعلمون اني انا الرب اذا كانت قتلاهم وسط اصنامهم حول مذابحهم على كل اكمة عالية وفي رؤوس كل الجبال وتحت كل شجرة خضراء وتحت كل بلوطة غبياء الموضع الذي قربوا فيه رائحة سرور لكل اصنامهم"));
arasuperobject.biblemap.put("b26_6_14", new arasuperobject(26,6,14,"وامد يدي عليهم واصيّر الارض مقفرة وخربة من القفر الى دبلة في كل مساكنهم فيعلمون اني انا الرب"));
arasuperobject.biblemap.put("b26_7_1", new arasuperobject(26,7,1,"وكان اليّ كلام الرب قائلا"));
arasuperobject.biblemap.put("b26_7_2", new arasuperobject(26,7,2,"وانت يا ابن آدم فهكذا قال السيد الرب لارض اسرائيل. نهاية. قد جاءت النهاية على زوايا الارض الاربع"));
arasuperobject.biblemap.put("b26_7_3", new arasuperobject(26,7,3,"الآن النهاية عليك وارسل غضبي عليك واحكم عليك كطرقك واجلب عليك كل رجاساتك"));
arasuperobject.biblemap.put("b26_7_4", new arasuperobject(26,7,4,"فلا تشفق عليك عيني ولا اعفو بل اجلب عليك طرقك وتكون رجاساتك في وسطك فتعلمون اني انا الرب"));
arasuperobject.biblemap.put("b26_7_5", new arasuperobject(26,7,5,"هكذا قال السيد الرب. شر شر وحيد هوذا قد أتى"));
arasuperobject.biblemap.put("b26_7_6", new arasuperobject(26,7,6,"نهاية قد جاءت. جاءت النهاية. انتبهت اليك. ها هي قد جاءت"));
arasuperobject.biblemap.put("b26_7_7", new arasuperobject(26,7,7,"انتهى الدور اليك ايها الساكن في الارض. بلغ الوقت. اقترب يوم اضطراب لا هتاف الجبال"));
arasuperobject.biblemap.put("b26_7_8", new arasuperobject(26,7,8,"الآن عن قريب اصب رجزي عليك واتمم سخطي عليك واحكم عليك كطرقك واجلب عليك كل رجاساتك"));
arasuperobject.biblemap.put("b26_7_9", new arasuperobject(26,7,9,"فلا تشفق عيني ولا اعفو بل اجلب عليك كطرقك ورجاساتك تكون في وسطك. فتعلمون اني انا الرب الضارب"));
arasuperobject.biblemap.put("b26_7_10", new arasuperobject(26,7,10,"ها هوذا اليوم ها هوذا قد جاء. دارت الدائرة. ازهرت العصا. افرخت الكبرياء"));
arasuperobject.biblemap.put("b26_7_11", new arasuperobject(26,7,11,"قام الظلم الى عصا الشر. لا يبقى منهم ولا من ثروتهم ولا من ضجيجهم ولا نوح عليهم"));
arasuperobject.biblemap.put("b26_7_12", new arasuperobject(26,7,12,"قد جاء الوقت. بلغ اليوم. فلا يفرحنّ الشاري ولا يحزننّ البائع لان الغضب على كل جمهورهم"));
arasuperobject.biblemap.put("b26_7_13", new arasuperobject(26,7,13,"لان البائع لن يعود الى المبيع وان كانوا بعد بين الاحياء. لان الرؤيا على كل جمهورها فلا يعود والانسان بإثمه لا يشدّد حياته"));
arasuperobject.biblemap.put("b26_7_14", new arasuperobject(26,7,14,"قد نفخوا في البوق واعدّوا الكل ولا ذاهب الى القتال. لان غضبي على كل جمهورهم"));
arasuperobject.biblemap.put("b26_7_15", new arasuperobject(26,7,15,"السيف من خارج والوبأ والجوع من داخل. الذي هو في الحقل يموت بالسيف والذي هو في المدينة يأكله الجوع والوبأ"));
arasuperobject.biblemap.put("b26_7_16", new arasuperobject(26,7,16,"وينفلت منهم منفلتون ويكونون على الجبال كحمام الاوطئة. كلهم يهدرون كل واحد على اثمه"));
arasuperobject.biblemap.put("b26_7_17", new arasuperobject(26,7,17,"كل الايدي ترتخي وكل الركب تصير ماء"));
arasuperobject.biblemap.put("b26_7_18", new arasuperobject(26,7,18,"ويتنطقون بالمسح ويغشاهم رعب وعلى جميع الوجوه خزي وعلى جميع رؤوسهم قرع"));
arasuperobject.biblemap.put("b26_7_19", new arasuperobject(26,7,19,"يلقون فضتهم في الشوارع وذهبهم يكون لنجاسة. لا تستطيع فضتهم وذهبهم انقاذهم في يوم غضب الرب. لا يشبعون منهما انفسهم ولا يملأون جوفهم لانهما صارا معثرة اثمهم"));
arasuperobject.biblemap.put("b26_7_20", new arasuperobject(26,7,20,"اما بهجة زينته فجعلها للكبرياء. جعلوا فيها اصنام مكرهاتهم رجاساتهم. لاجل ذلك جعلتها لهم نجاسة"));
arasuperobject.biblemap.put("b26_7_21", new arasuperobject(26,7,21,"اسلمها الى ايدي الغرباء للنهب والى اشرار الارض سلبا فينجسونها"));
arasuperobject.biblemap.put("b26_7_22", new arasuperobject(26,7,22,"وأحوّل وجهي عنهم فينجسون سرّي ويدخله المعتنقون وينجسونه"));
arasuperobject.biblemap.put("b26_7_23", new arasuperobject(26,7,23,"اصنع السلسلة لان الارض قد امتلأت من احكام الدم والمدينة امتلأت من الظلم"));
arasuperobject.biblemap.put("b26_7_24", new arasuperobject(26,7,24,"فآتي باشرّ الامم فيرثون بيوتهم وابيد كبرياء الاشدّاء فتتنجس مقادسهم"));
arasuperobject.biblemap.put("b26_7_25", new arasuperobject(26,7,25,"الرعب آت فيطلبون السلام ولا يكون"));
arasuperobject.biblemap.put("b26_7_26", new arasuperobject(26,7,26,"ستاتي مصيبة على مصيبة. ويكون خبر على خبر. فيطلبون رؤيا من النبي. والشريعة تباد عن الكاهن والمشورة عن الشيوخ"));
arasuperobject.biblemap.put("b26_7_27", new arasuperobject(26,7,27,"الملك ينوح والرئيس يلبس حيرة وايدي شعب الارض ترجف. كطريقهم اصنع بهم وكاحكامهم احكم عليهم فيعلمون اني انا الرب"));
arasuperobject.biblemap.put("b26_8_1", new arasuperobject(26,8,1,"وكان في السنة السادسة في الشهر السادس في الخامس من الشهر وانا جالس في بيتي ومشايخ يهوذا جالسون امامي ان يد السيد الرب وقعت عليّ هناك"));
arasuperobject.biblemap.put("b26_8_2", new arasuperobject(26,8,2,"فنظرت واذا شبه كمنظر نار من منظر حقويه الى تحت نار ومن حقويه الى فوق كمنظر لمعان كشبه النحاس اللامع"));
arasuperobject.biblemap.put("b26_8_3", new arasuperobject(26,8,3,"ومد شبه يد وأخذني بناصية راسي ورفعني روح بين الارض والسماء واتى بي في رؤى الله الى اورشليم الى مدخل الباب الداخلي المتجه نحو الشمال حيث مجلس تمثال الغيرة المهيج الغيرة"));
arasuperobject.biblemap.put("b26_8_4", new arasuperobject(26,8,4,"واذا مجد اله اسرائيل هناك مثل الرؤيا التي رأيتها في البقعة"));
arasuperobject.biblemap.put("b26_8_5", new arasuperobject(26,8,5,"ثم قال لي يا ابن آدم ارفع عينيك نحو طريق الشمال. فرفعت عينيّ نحو طريق الشمال واذا من شمالي باب المذبح تمثال الغيرة هذا في المدخل"));
arasuperobject.biblemap.put("b26_8_6", new arasuperobject(26,8,6,"وقال لي يا ابن آدم هل رأيت ما هم عاملون. الرجاسات العظيمة التي بيت اسرائيل عاملها هنا لابعادي عن مقدسي. وبعد تعود تنظر رجاسات اعظم"));
arasuperobject.biblemap.put("b26_8_7", new arasuperobject(26,8,7,"ثم جاء بي الى باب الدار فنظرت واذا ثقب في الحائط"));
arasuperobject.biblemap.put("b26_8_8", new arasuperobject(26,8,8,"ثم قال لي يا ابن آدم انقب في الحائط. فنقبت في الحائط فاذا باب"));
arasuperobject.biblemap.put("b26_8_9", new arasuperobject(26,8,9,"وقال لي ادخل وانظر الرجاسات الشريرة التي هم عاملوها هنا"));
arasuperobject.biblemap.put("b26_8_10", new arasuperobject(26,8,10,"فدخلت ونظرت واذا كل شكل دبابات وحيوان نجس وكل اصنام بيت اسرائيل مرسومة على الحائط على دائره"));
arasuperobject.biblemap.put("b26_8_11", new arasuperobject(26,8,11,"وواقف قدامها سبعون رجلا من شيوخ بيت اسرائيل ويازنيا بن شافان قائم في وسطهم وكل واحد مجمرته في يده وعطر عنان البخور صاعد"));
arasuperobject.biblemap.put("b26_8_12", new arasuperobject(26,8,12,"ثم قال لي أرأيت يا ابن آدم ما تفعله شيوخ بيت اسرائيل في الظلام كل واحد في مخادع تصاويره. لانهم يقولون الرب لا يرانا. الرب قد ترك الارض"));
arasuperobject.biblemap.put("b26_8_13", new arasuperobject(26,8,13,"وقال لي بعد تعود تنظر رجاسات اعظم هم عاملوها"));
arasuperobject.biblemap.put("b26_8_14", new arasuperobject(26,8,14,"فجاء بي الى مدخل باب بيت الرب الذي من جهة الشمال واذ هناك نسوة جالسات يبكين على تموز"));
arasuperobject.biblemap.put("b26_8_15", new arasuperobject(26,8,15,"فقال لي أرأيت هذا يا ابن آدم. بعد تعود تنظر رجاسات اعظم من هذه"));
arasuperobject.biblemap.put("b26_8_16", new arasuperobject(26,8,16,"فجاء بي الى دار بيت الرب الداخلية واذا عند باب هيكل الرب بين الرواق والمذبح نحو خمسة وعشرون رجلا ظهورهم نحو هيكل الرب ووجوههم نحو الشرق وهم ساجدون للشمس نحو الشرق"));
arasuperobject.biblemap.put("b26_8_17", new arasuperobject(26,8,17,"وقال لي أرأيت يا ابن آدم. أقليل لبيت يهوذا عمل الرجاسات التي عملوها هنا. لانهم قد ملأوا الارض ظلما ويعودون لاغاظتي وها هم يقرّبون الغصن الى انفهم"));
arasuperobject.biblemap.put("b26_8_18", new arasuperobject(26,8,18,"فانا ايضا اعامل بالغضب. لا تشفق عيني ولا اعفو. وان صرخوا في اذني بصوت عال لا اسمعهم"));
arasuperobject.biblemap.put("b26_9_1", new arasuperobject(26,9,1,"وصرخ في سمعي بصوت عال قائلا. قرّب وكلاء المدينة كل واحد وعدّته المهلكة بيده"));
arasuperobject.biblemap.put("b26_9_2", new arasuperobject(26,9,2,"واذا بستة رجال مقبلين من طريق الباب الاعلى الذي هو من جهة الشمال وكل واحد عدّته الساحقة بيده. وفي وسطهم رجل لابس الكتان وعلى جانبه دواة كاتب. فدخلوا ووقفوا جانب مذبح النحاس"));
arasuperobject.biblemap.put("b26_9_3", new arasuperobject(26,9,3,"ومجد اله اسرائيل صعد عن الكروب الذي كان عليه الى عتبة البيت. فدعا الرجل اللابس الكتان الذي دواة الكاتب على جانبه"));
arasuperobject.biblemap.put("b26_9_4", new arasuperobject(26,9,4,"وقال له الرب. اعبر في وسط المدينة في وسط اورشليم وسم سمة على جباه الرجال الذين يئنون ويتنهدون على كل الرجاسات المصنوعة في وسطها"));
arasuperobject.biblemap.put("b26_9_5", new arasuperobject(26,9,5,"وقال لاولئك في سمعي اعبروا في المدينة وراءه واضربوا. لا تشفق اعينكم ولا تعفوا"));
arasuperobject.biblemap.put("b26_9_6", new arasuperobject(26,9,6,"الشيخ والشاب والعذراء والطفل والنساء اقتلوا للهلاك. ولا تقربوا من انسان عليه السمة وابتدئوا من مقدسي. فابتدأوا بالرجال الشيوخ الذين امام البيت"));
arasuperobject.biblemap.put("b26_9_7", new arasuperobject(26,9,7,"وقال لهم نجسوا البيت واملأوا الدور قتلى. اخرجوا. فخرجوا وقتلوا في المدينة"));
arasuperobject.biblemap.put("b26_9_8", new arasuperobject(26,9,8,"وكان بينما هم يقتلون وأبقيت انا اني خررت على وجهي وصرخت وقلت آه يا سيد الرب. هل انت مهلك بقية اسرائيل كلها بصب رجزك على اورشليم"));
arasuperobject.biblemap.put("b26_9_9", new arasuperobject(26,9,9,"فقال لي ان اثم بيت اسرائيل ويهوذا عظيم جدا جدا وقد امتلأت الارض دماء وامتلأت المدينة جنفا. لانهم يقولون الرب قد ترك الارض والرب لا يرى"));
arasuperobject.biblemap.put("b26_9_10", new arasuperobject(26,9,10,"وانا ايضا عيني لا تشفق ولا اعفو. اجلب طريقهم على رؤوسهم"));
arasuperobject.biblemap.put("b26_9_11", new arasuperobject(26,9,11,"واذا بالرجل اللابس الكتان الذي الدواة على جانبه رد جوابا قائلا قد فعلت كما امرتني"));
arasuperobject.biblemap.put("b26_10_1", new arasuperobject(26,10,1,"ثم نظرت واذ على المقبب الذي على راس الكروبيم شيء كحجر العقيق الازرق كمنظر شبه عرش"));
arasuperobject.biblemap.put("b26_10_2", new arasuperobject(26,10,2,"وكلم الرجل اللابس الكتان وقال ادخل بين البكرات تحت الكروب واملأ حفنتيك جمر نار من بين الكروبيم وذرّها على المدينة. فدخل قدام عينيّ"));
arasuperobject.biblemap.put("b26_10_3", new arasuperobject(26,10,3,"والكروبيم واقفون عن يمين البيت حين دخل الرجل والسحابة ملأت الدار الداخلية"));
arasuperobject.biblemap.put("b26_10_4", new arasuperobject(26,10,4,"فارتفع مجد الرب عن الكروب الى عتبة البيت. فامتلأ البيت من السحابة وامتلأت الدار من لمعان مجد الرب"));
arasuperobject.biblemap.put("b26_10_5", new arasuperobject(26,10,5,"وسمع صوت اجنحة الكروبيم الى الدار الخارجية كصوت الله القدير اذا تكلم"));
arasuperobject.biblemap.put("b26_10_6", new arasuperobject(26,10,6,"وكان لما امر الرجل اللابس الكتان قائلا خذ نارا من بين البكرات من بين الكروبيم انه دخل ووقف بجانب البكرة"));
arasuperobject.biblemap.put("b26_10_7", new arasuperobject(26,10,7,"ومد كروب يده من بين الكروبيم الى النار التي بين الكروبيم فرفع منها ووضعها في حفنتي اللابس الكتان فأخذها وخرج"));
