arasuperobject.biblemap.put("b26_10_9", new arasuperobject(26,10,9,"ونظرت واذا اربع بكرات بجانب الكروبيم. بكرة واحدة بجانب الكروب الواحد وبكرة اخرى بجانب الكروب الآخر. ومنظر البكرات كشبه حجر الزبرجد"));
arasuperobject.biblemap.put("b26_10_10", new arasuperobject(26,10,10,"ومنظرهنّ شكل واحد للاربع. كانه كان بكرة وسط بكرة"));
arasuperobject.biblemap.put("b26_10_11", new arasuperobject(26,10,11,"لما سارت سارت على جوانبها الاربع. لم تدر عند سيرها. بل الى الموضع الذي توجّه اليه الرأس ذهبت وراءه. لم تدر عند سيرها"));
arasuperobject.biblemap.put("b26_10_12", new arasuperobject(26,10,12,"وكل جسمها وظهورها وايديها واجنحتها والبكرات ملآنة عيونا حواليها لبكراتها الاربع"));
arasuperobject.biblemap.put("b26_10_13", new arasuperobject(26,10,13,"اما البكرات فنودي اليها في سماعي يا بكرة"));
arasuperobject.biblemap.put("b26_10_14", new arasuperobject(26,10,14,"ولكل واحد اربعة اوجه. الوجه الاول وجه كروب والوجه الثاني وجه انسان والثالث وجه اسد والرابع وجه نمر"));
arasuperobject.biblemap.put("b26_10_15", new arasuperobject(26,10,15,"ثم صعد الكروبيم. هذا هو الحيوان الذي رأيته عند نهر خابور"));
arasuperobject.biblemap.put("b26_10_16", new arasuperobject(26,10,16,"وعند سير الكروبيم سارت البكرات بجانبها وعند رفع الكروبيم اجنحتها للارتفاع عن الارض لم تدر البكرات ايضا عن جانبها"));
arasuperobject.biblemap.put("b26_10_17", new arasuperobject(26,10,17,"عند وقوفها وقفت هذه وعند ارتفاعها ارتفعت معها لان فيها روح الحيوان"));
arasuperobject.biblemap.put("b26_10_18", new arasuperobject(26,10,18,"وخرج مجد الرب من على عتبة البيت ووقف على الكروبيم"));
arasuperobject.biblemap.put("b26_10_19", new arasuperobject(26,10,19,"فرفعت الكروبيم اجنحتها وصعدت عن الارض قدام عينيّ. عند خروجها كانت البكرات معها ووقفت عند مدخل باب بيت الرب الشرقي ومجد اله اسرائيل عليها من فوق"));
arasuperobject.biblemap.put("b26_10_20", new arasuperobject(26,10,20,"هذا هو الحيوان الذي رأيته تحت اله اسرائيل عند نهر خابور. وعلمت انها هي الكروبيم"));
arasuperobject.biblemap.put("b26_10_21", new arasuperobject(26,10,21,"لكل واحد اربعة اوجه ولكل واحد اربعة اجنحة وشبه ايدي انسان تحت اجنحتها"));
arasuperobject.biblemap.put("b26_10_22", new arasuperobject(26,10,22,"وشكل وجوهها هو شكل الوجوه التي رأيتها عند نهر خابور مناظرها وذواتها. كل واحد يسير الى جهة وجهه"));
arasuperobject.biblemap.put("b26_11_1", new arasuperobject(26,11,1,"ثم رفعني روح واتى بي الى باب بيت الرب الشرقي المتجه نحو الشرق واذا عند مدخل الباب خمسة وعشرون رجلا ورأيت بينهم يازنيا بن عزور وفلطيا بن بنايا رئيسي الشعب"));
arasuperobject.biblemap.put("b26_11_2", new arasuperobject(26,11,2,"فقال لي يا ابن آدم هؤلاء هم الرجال المفكرون بالاثم المشيرون مشورة رديئة في هذه المدينة"));
arasuperobject.biblemap.put("b26_11_3", new arasuperobject(26,11,3,"القائلون ما هو قريب بناء البيوت. هي القدر ونحن اللحم"));
arasuperobject.biblemap.put("b26_11_4", new arasuperobject(26,11,4,"لاجل ذلك تنبأ عليهم تنبأ يا ابن آدم"));
arasuperobject.biblemap.put("b26_11_5", new arasuperobject(26,11,5,"وحلّ عليّ روح الرب وقال لي قل. هكذا قال الرب. هكذا قلتم يا بيت اسرائيل وما يخطر ببالكم قد علمته"));
arasuperobject.biblemap.put("b26_11_6", new arasuperobject(26,11,6,"قد كثرتم قتلاكم في هذه المدينة وملأتم ازقتها بالقتلى"));
arasuperobject.biblemap.put("b26_11_7", new arasuperobject(26,11,7,"لذلك هكذا قال السيد الرب. قتلاكم الذين طرحتموهم في وسطها هم اللحم وهي القدر. واياكم اخرج من وسطها"));
arasuperobject.biblemap.put("b26_11_8", new arasuperobject(26,11,8,"قد فزعتم من السيف فالسيف اجلبه عليكم يقول السيد الرب"));
arasuperobject.biblemap.put("b26_11_9", new arasuperobject(26,11,9,"واخرجكم من وسطها واسلّمكم الى ايدي الغرباء واجري فيكم احكاما"));
arasuperobject.biblemap.put("b26_11_10", new arasuperobject(26,11,10,"بالسيف تسقطون. في تخم اسرائيل اقضي عليكم فتعلمون اني انا الرب"));
arasuperobject.biblemap.put("b26_11_11", new arasuperobject(26,11,11,"هذه لا تكون لكم قدرا ولا انتم تكونون اللحم في وسطها. في تخم اسرائيل اقضي عليكم"));
arasuperobject.biblemap.put("b26_11_12", new arasuperobject(26,11,12,"فتعلمون اني انا الرب الذي لم تسلكوا في فرائضه ولم تعملوا باحكامه بل عملتم حسب احكام الامم الذين حولكم"));
arasuperobject.biblemap.put("b26_11_13", new arasuperobject(26,11,13,"وكان لما تنبأت ان فلطيا بن بنايا مات. فخررت على وجهي وصرخت بصوت عظيم وقلت آه يا سيد الرب. هل تفني انت بقية اسرائيل"));
arasuperobject.biblemap.put("b26_11_14", new arasuperobject(26,11,14,"وكان اليّ كلام الرب قائلا"));
arasuperobject.biblemap.put("b26_11_15", new arasuperobject(26,11,15,"يا ابن آدم اخوتك اخوتك ذوو قرابتك وكل بيت اسرائيل باجمعه هم الذين قال لهم سكان اورشليم ابتعدوا عن الرب. لنا أعطيت هذه الارض ميراثا"));
arasuperobject.biblemap.put("b26_11_16", new arasuperobject(26,11,16,"لذلك قل. هكذا قال السيد الرب. وان كنت قد ابعدتهم بين الامم وان كنت قد بددتهم في الاراضي فاني اكون لهم مقدسا صغيرا في الاراضي التي يأتون اليها"));
arasuperobject.biblemap.put("b26_11_17", new arasuperobject(26,11,17,"لذلك قل. هكذا قال السيد الرب. اني اجمعكم من بين الشعوب واحشركم من الاراضي التي تبددتم فيها واعطيكم ارض اسرائيل"));
arasuperobject.biblemap.put("b26_11_18", new arasuperobject(26,11,18,"فيأتون الى هناك ويزيلون جميع مكرهاتها وجميع رجاساتها منها"));
arasuperobject.biblemap.put("b26_11_19", new arasuperobject(26,11,19,"واعطيهم قلبا واحدا واجعل في داخلكم روحا جديدا وانزع قلب الحجر من لحمهم واعطيهم قلب لحم"));
arasuperobject.biblemap.put("b26_11_20", new arasuperobject(26,11,20,"لكي يسلكوا في فرائضي ويحفظوا احكامي ويعملوا بها ويكونوا لي شعبا فانا اكون لهم الها"));
arasuperobject.biblemap.put("b26_11_21", new arasuperobject(26,11,21,"اما الذين قلبهم ذاهب وراء قلب مكرهاتهم ورجاساتهم فاني اجلب طريقهم على رؤوسهم يقول السيد الرب"));
arasuperobject.biblemap.put("b26_11_22", new arasuperobject(26,11,22,"ثم رفعت الكروبيم اجنحتها والبكرات معها ومجد اله اسرائيل عليها من فوق"));
arasuperobject.biblemap.put("b26_11_23", new arasuperobject(26,11,23,"وصعد مجد الرب من على وسط المدينة ووقف على الجبل الذي على شرقي المدينة"));
arasuperobject.biblemap.put("b26_11_24", new arasuperobject(26,11,24,"وحملني روح وجاء بي في الرؤيا بروح الله الى ارض الكلدانيين الى المسبيين. فصعدت عني الرؤيا التي رأيتها"));
