arasuperobject.biblemap.put("b26_12_1", new arasuperobject(26,12,1,"وكان اليّ كلام الرب قائلا"));
arasuperobject.biblemap.put("b26_12_2", new arasuperobject(26,12,2,"يا ابن آدم انت ساكن في وسط بيت متمرد الذين لهم اعين لينظروا ولا ينظرون. لهم آذان ليسمعوا ولا يسمعون لانهم بيت متمرد"));
arasuperobject.biblemap.put("b26_12_3", new arasuperobject(26,12,3,"وانت يا ابن آدم فهيّء لنفسك أهبة جلاء وارتحل قدام عيونهم نهارا وارتحل من مكانك الى مكان آخر قدام عيونهم لعلهم ينظرون انهم بيت متمرد"));
arasuperobject.biblemap.put("b26_12_4", new arasuperobject(26,12,4,"فتخرج اهبتك كأهبة الجلاء قدام عيونهم نهارا وانت تخرج مساء قدام عيونهم كالخارجين الى الجلاء"));
