arasuperobject.biblemap.put("b26_12_7", new arasuperobject(26,12,7,"ففعلت هكذا كما أمرت فأخرجت أهبتي كأهبة الجلاء نهارا وفي المساء نقبت لنفسي في الحائط بيدي واخرجت في العتمة وحملت على كتفي قدام عيونهم"));
arasuperobject.biblemap.put("b26_12_8", new arasuperobject(26,12,8,"وفي الصباح كانت اليّ كلمة الرب قائلة"));
arasuperobject.biblemap.put("b26_12_9", new arasuperobject(26,12,9,"يا ابن آدم ألم يقل لك بيت اسرائيل البيت المتمرد ماذا تصنع"));
arasuperobject.biblemap.put("b26_12_10", new arasuperobject(26,12,10,"قل لهم. هكذا قال السيد الرب. هذا الوحي هو الرئيس في اورشليم وكل بيت اسرائيل والذين هم في وسطهم"));
arasuperobject.biblemap.put("b26_12_11", new arasuperobject(26,12,11,"قل انا آية لكم. كما صنعت هكذا يصنع بهم. الى الجلاء الى السبي يذهبون"));
arasuperobject.biblemap.put("b26_12_12", new arasuperobject(26,12,12,"والرئيس الذي في وسطهم يحمل على الكتف في العتمة ويخرج. ينقبون في الحائط ليخرجوا منه. يغطي وجهه لكيلا ينظر الارض بعينيه"));
arasuperobject.biblemap.put("b26_12_13", new arasuperobject(26,12,13,"وابسط شبكتي عليه فيؤخذ في شركي وآتي به الى بابل الى ارض الكلدانيين ولكن لا يراها وهناك يموت"));
arasuperobject.biblemap.put("b26_12_14", new arasuperobject(26,12,14,"واذري في كل ريح جميع الذين حوله لنصره وكل جيوشه واستل السيف وراءهم"));
arasuperobject.biblemap.put("b26_12_15", new arasuperobject(26,12,15,"فيعلمون اني انا الرب حين ابددهم بين الامم واذريهم في الاراضي"));
arasuperobject.biblemap.put("b26_12_16", new arasuperobject(26,12,16,"وابقي منهم رجالا معدودين من السيف ومن الجوع ومن الوبإ لكي يحدّثوا بكل رجاساتهم بين الامم التي يأتون اليها فيعلمون اني انا الرب"));
arasuperobject.biblemap.put("b26_12_17", new arasuperobject(26,12,17,"وكانت اليّ كلمة الرب قائلة"));
arasuperobject.biblemap.put("b26_12_18", new arasuperobject(26,12,18,"يا ابن آدم كل خبزك بارتعاش واشرب ماءك بارتعاد وغمّ"));
arasuperobject.biblemap.put("b26_12_19", new arasuperobject(26,12,19,"وقل لشعب الارض. هكذا قال السيد الرب على سكان اورشليم في ارض اسرائيل يأكلون خبزهم بالغمّ ويشربون ماءهم بحيرة لكي تخرب ارضها عن ملئها من ظلم كل الساكنين فيها"));
arasuperobject.biblemap.put("b26_12_20", new arasuperobject(26,12,20,"والمدن المسكونة تخرب والارض تقفر فتعلمون اني انا الرب"));
arasuperobject.biblemap.put("b26_12_21", new arasuperobject(26,12,21,"وكان اليّ كلام الرب قائلا"));
arasuperobject.biblemap.put("b26_12_22", new arasuperobject(26,12,22,"يا ابن آدم ما هذا المثل الذي لكم على ارض اسرائيل القائل قد طالت الايام وخابت كل رؤيا"));
arasuperobject.biblemap.put("b26_12_23", new arasuperobject(26,12,23,"لذلك قل لهم. هكذا قال السيد الرب. ابطل هذا المثل فلا يمثّلون به بعد في اسرائيل. بل قل لهم قد اقتربت الايام وكلام كل رؤيا"));
arasuperobject.biblemap.put("b26_12_24", new arasuperobject(26,12,24,"لانه لا تكون بعد رؤيا باطلة ولا عرافة ملقة في وسط بيت اسرائيل"));
arasuperobject.biblemap.put("b26_12_25", new arasuperobject(26,12,25,"لاني انا الرب اتكلم والكلمة التي اتكلم بها تكون. لا تطول بعد. لاني في ايامكم ايها البيت المتمرد اقول الكلمة واجريها يقول السيد الرب"));
arasuperobject.biblemap.put("b26_12_26", new arasuperobject(26,12,26,"وكان اليّ كلام الرب قائلا"));
arasuperobject.biblemap.put("b26_12_27", new arasuperobject(26,12,27,"يا ابن آدم هوذا بيت اسرائيل قائلون الرؤيا التي هو رائيها هي الى ايام كثيرة وهو متنبئ لازمنة بعيدة"));
arasuperobject.biblemap.put("b26_12_28", new arasuperobject(26,12,28,"لذلك قل لهم. هكذا قال السيد الرب. لا يطول بعد شيء من كلامي. الكلمة التي تكلمت بها تكون يقول السيد الرب"));
arasuperobject.biblemap.put("b26_13_1", new arasuperobject(26,13,1,"وكان اليّ كلام الرب قائلا"));
arasuperobject.biblemap.put("b26_13_2", new arasuperobject(26,13,2,"يا ابن آدم تنبأ على انبياء اسرائيل الذين يتنبأون وقل للذين هم انبياء من تلقاء ذواتهم اسمعوا كلمة الرب"));
arasuperobject.biblemap.put("b26_13_3", new arasuperobject(26,13,3,"هكذا قال السيد الرب. ويل للانبياء الحمقى الذاهبين وراء روحهم ولم يروا شيئا"));
arasuperobject.biblemap.put("b26_13_4", new arasuperobject(26,13,4,"انبياؤك يا اسرائيل صاروا كالثعالب في الخرب"));
arasuperobject.biblemap.put("b26_13_5", new arasuperobject(26,13,5,"لم تصعدوا الى الثغر ولم تبنوا جدارا لبيت اسرائيل للوقوف في الحرب في يوم الرب"));
arasuperobject.biblemap.put("b26_13_6", new arasuperobject(26,13,6,"رأوا باطلا وعرافة كاذبة القائلون وحي الرب والرب لم يرسلهم وانتظروا اثبات الكلمة"));
arasuperobject.biblemap.put("b26_13_7", new arasuperobject(26,13,7,"ألم تروا رؤيا باطلة وتكلمتم بعرافة كاذبة قائلين وحي الرب وانا لم اتكلم"));
arasuperobject.biblemap.put("b26_13_8", new arasuperobject(26,13,8,"لذلك هكذا قال السيد الرب. لانكم تكلمتم بالباطل ورأيتم كذبا فلذلك ها انا عليكم يقول السيد الرب"));
arasuperobject.biblemap.put("b26_13_9", new arasuperobject(26,13,9,"وتكون يدي على الانبياء الذين يرون الباطل والذين يعرفون بالكذب. في مجلس شعبي لا يكونون وفي كتاب بيت اسرائيل لا يكتبون والى ارض اسرائيل لا يدخلون فتعلمون اني انا السيد الرب"));
arasuperobject.biblemap.put("b26_13_10", new arasuperobject(26,13,10,"من اجل انهم اضلوا شعبي قائلين سلام وليس سلام وواحد منهم يبني حائطا وها هم يملطونه بالطفال"));
arasuperobject.biblemap.put("b26_13_11", new arasuperobject(26,13,11,"فقل للذين يملطونه بالطفال انه يسقط. يكون مطر جارف وانتنّ يا حجارة البرد تسقطن وريح عاصفة تشققه"));
arasuperobject.biblemap.put("b26_13_12", new arasuperobject(26,13,12,"وهوذا اذا سقط الحائط أفلا يقال لكم اين الطين الذي طينتم به"));
arasuperobject.biblemap.put("b26_13_13", new arasuperobject(26,13,13,"لذلك هكذا قال السيد الرب. اني اشققه بريح عاصفة في غضبي ويكون مطر جارف في سخطي وحجارة برد في غيظي لافنائه"));
arasuperobject.biblemap.put("b26_13_14", new arasuperobject(26,13,14,"فاهدم الحائط الذي ملطتموه بالطفال وألصقه بالارض وينكشف اساسه فيسقط وتفنون انتم في وسطه فتعلمون اني انا الرب"));
arasuperobject.biblemap.put("b26_13_15", new arasuperobject(26,13,15,"فاتمّ غضبي على الحائط وعلى الذين ملطوه بالطفال واقول لكم ليس الحائط بموجود ولا الذين ملطوه"));
arasuperobject.biblemap.put("b26_13_16", new arasuperobject(26,13,16,"اي انبياء اسرائيل الذين يتنبأون لاورشليم ويرون لها رؤى سلام ولا سلام يقول السيد الرب"));
arasuperobject.biblemap.put("b26_13_17", new arasuperobject(26,13,17,"وانت يا ابن آدم فاجعل وجهك ضد بنات شعبك اللواتي يتنبأن من تلقاء ذواتهنّ وتنبأ عليهنّ"));
arasuperobject.biblemap.put("b26_13_18", new arasuperobject(26,13,18,"وقل هكذا قال السيد الرب. ويل للواتي يخطن وسائد لكل اوصال الايدي ويصنعن مخدات لراس كل قامة لاصطياد النفوس. أفتصطدن نفوس شعبي وتستحيين انفسكنّ"));
arasuperobject.biblemap.put("b26_13_19", new arasuperobject(26,13,19,"وتنجسنني عند شعبي لاجل حفنة شعير ولاجل فتات من الخبز لاماتة نفوس لا ينبغي ان تموت واستحياء نفوس لا ينبغي ان تحيا بكذبكنّ على شعبي السامعين للكذب"));
arasuperobject.biblemap.put("b26_13_20", new arasuperobject(26,13,20,"لذلك هكذا قال السيد الرب. ها انا ضد وسائدكنّ التي تصطدن بها النفوس كالفراخ وامزقها عن اذرعكنّ واطلق النفوس. النفوس التي تصطدنها كالفراخ"));
arasuperobject.biblemap.put("b26_13_21", new arasuperobject(26,13,21,"وامزّق مخداتكنّ وانقذ شعبي من ايديكنّ فلا يكونون بعد في ايديكنّ للصيد فتعلمن اني انا الرب"));
arasuperobject.biblemap.put("b26_13_22", new arasuperobject(26,13,22,"لانكنّ احزنتنّ قلب الصدّيق كذبا وانا لم احزنه وشددّتنّ ايدي الشرير حتى لا يرجع عن طريقه الرديئة فيحيا"));
arasuperobject.biblemap.put("b26_13_23", new arasuperobject(26,13,23,"فلذلك لن تعدن ترين الباطل ولا تعرفن عرافة بعد وانقذ شعبي من ايديكنّ فتعلمن اني انا الرب"));
arasuperobject.biblemap.put("b26_14_1", new arasuperobject(26,14,1,"فجاء اليّ رجال من شيوخ اسرائيل وجلسوا امامي"));
arasuperobject.biblemap.put("b26_14_2", new arasuperobject(26,14,2,"فصارت اليّ كلمة الرب قائلة"));
arasuperobject.biblemap.put("b26_14_3", new arasuperobject(26,14,3,"يا ابن آدم هؤلاء الرجال قد اصعدوا اصنامهم الى قلوبهم ووضعوا معثرة اثمهم تلقاء اوجههم. فهل أسأل منهم سؤالا"));
arasuperobject.biblemap.put("b26_14_4", new arasuperobject(26,14,4,"لاجل ذلك كلمهم وقل لهم. هكذا قال السيد الرب. كل انسان من بيت اسرائيل الذي يصعد اصنامه الى قلبه ويضع معثرة اثمه تلقاء وجهه ثم يأتي الى النبي فاني انا الرب اجيبه حسب كثرة اصنامه"));
arasuperobject.biblemap.put("b26_14_5", new arasuperobject(26,14,5,"لكي آخذ بيت اسرائيل بقلوبهم لانهم كلهم قد ارتدوا عني باصنامهم"));
arasuperobject.biblemap.put("b26_14_6", new arasuperobject(26,14,6,"لذلك قل لبيت اسرائيل هكذا قال السيد الرب. توبوا وارجعوا عن اصنامكم وعن كل رجاساتكم اصرفوا وجوهكم"));
arasuperobject.biblemap.put("b26_14_7", new arasuperobject(26,14,7,"لان كل انسان من بيت اسرائيل او من الغرباء المتغربين في اسرائيل اذا ارتد عني واصعد اصنامه الى قلبه ووضع معثرة اثمه تلقاء وجهه ثم جاء الى النبي ليسأله عني فاني انا الرب اجيبه بنفسي"));
arasuperobject.biblemap.put("b26_14_8", new arasuperobject(26,14,8,"واجعل وجهي ضد ذلك الانسان واجعله آية ومثلا واستأصله من وسط شعبي فتعلمون اني انا الرب"));
arasuperobject.biblemap.put("b26_14_9", new arasuperobject(26,14,9,"فاذا ضل النبي وتكلم كلاما فانا الرب قد اضللت ذلك النبي وسأمد يدي عليه وابيده من وسط شعبي اسرائيل"));
arasuperobject.biblemap.put("b26_14_10", new arasuperobject(26,14,10,"ويحملون اثمهم. كاثم السائل يكون اثم النبي"));
arasuperobject.biblemap.put("b26_14_11", new arasuperobject(26,14,11,"لكي لا يعود يضل عني بيت اسرائيل ولكي لا يعودوا يتنجسون بكل معاصيهم بل ليكونوا لي شعبا وانا اكون لهم الها يقول السيد الرب"));
arasuperobject.biblemap.put("b26_14_12", new arasuperobject(26,14,12,"وكانت اليّ كلمة الرب قائلة"));
arasuperobject.biblemap.put("b26_14_13", new arasuperobject(26,14,13,"يا ابن آدم ان اخطأت اليّ ارض وخانت خيانة فمددت يدي عليها وكسرت لها قوام الخبز وارسلت عليها الجوع وقطعت منها الانسان والحيوان"));
arasuperobject.biblemap.put("b26_14_14", new arasuperobject(26,14,14,"وكان فيها هؤلاء الرجال الثلاثة نوح ودانيال وايوب فانهم انما يخلصون انفسهم ببرهم يقول السيد الرب"));
arasuperobject.biblemap.put("b26_14_15", new arasuperobject(26,14,15,"ان عبّرت في الارض وحوشا رديئة فاثكلوها وصارت خرابا بلا عابر بسبب الوحوش"));
arasuperobject.biblemap.put("b26_14_16", new arasuperobject(26,14,16,"وفي وسطها هؤلاء الثلاثة الرجال فحيّ انا يقول السيد الرب انهم لا يخلصون بنين ولا بنات. هم وحدهم يخلصون والارض تصير خربة"));
arasuperobject.biblemap.put("b26_14_17", new arasuperobject(26,14,17,"او ان جلبت سيفا على تلك الارض وقلت يا سيف اعبر في الارض وقطعت منها الانسان والحيوان"));
arasuperobject.biblemap.put("b26_14_18", new arasuperobject(26,14,18,"وفي وسطها هؤلاء الرجال الثلاثة فحيّ انا يقول السيد الرب انهم لا يخلّصون بنين ولا بنات بل هم وحدهم يخلصون"));
arasuperobject.biblemap.put("b26_14_19", new arasuperobject(26,14,19,"او ان ارسلت وبأ على تلك الارض وسكبت غضبي عليها بالدم لاقطع منها الانسان والحيوان"));
arasuperobject.biblemap.put("b26_14_20", new arasuperobject(26,14,20,"وفي وسطها نوح ودانيال وايوب فحيّ انا يقول السيد الرب انهم لا يخلصون ابنا ولا ابنة. انما يخلصون انفسهم ببرهم"));
arasuperobject.biblemap.put("b26_14_21", new arasuperobject(26,14,21,"لانه هكذا قال السيد الرب. كم بالحري ان ارسلت احكامي الرديئة على اورشليم سيفا وجوعا ووحشا رديئا ووبأ لاقطع منها الانسان والحيوان"));
arasuperobject.biblemap.put("b26_14_22", new arasuperobject(26,14,22,"فهوذا بقية فيها ناجية تخرج بنون وبنات. هوذا يخرجون اليكم فتنظرون طريقهم واعمالهم وتتعزون عن الشر الذي جلبته على اورشليم عن كل ما جلبته عليها"));
arasuperobject.biblemap.put("b26_14_23", new arasuperobject(26,14,23,"ويعزونكم اذ ترون طريقهم واعمالهم فتعلمون اني لم اصنع بلا سبب كل ما صنعته فيها يقول السيد الرب"));
arasuperobject.biblemap.put("b26_15_1", new arasuperobject(26,15,1,"وكان اليّ كلام الرب قائلا"));
arasuperobject.biblemap.put("b26_15_2", new arasuperobject(26,15,2,"يا ابن آدم ماذا يكون عود الكرم فوق كل عود او فوق القضيب الذي من شجر الوعر"));
arasuperobject.biblemap.put("b26_15_3", new arasuperobject(26,15,3,"هل يؤخذ منه عود لاصطناع عمل ما او يأخذون منه وتدا ليعلّق عليه اناء ما"));
arasuperobject.biblemap.put("b26_15_4", new arasuperobject(26,15,4,"هوذا يطرح اكلا للنار. تاكل النار طرفيه ويحرق وسطه. فهل يصلح لعمل"));
arasuperobject.biblemap.put("b26_15_5", new arasuperobject(26,15,5,"هوذا حين كان صحيحا لم يكن يصلح لعمل ما. فكم بالحري لا يصلح بعد لعمل اذ اكلته النار فاحترق"));
arasuperobject.biblemap.put("b26_15_6", new arasuperobject(26,15,6,"لذلك هكذا قال السيد الرب مثل عود الكرم بين عيدان الوعر التي بذلتها اكلا للنار كذلك ابذل سكان اورشليم"));
arasuperobject.biblemap.put("b26_15_7", new arasuperobject(26,15,7,"واجعل وجهي ضدهم. يخرجون من نار فتأكلهم نار فتعلمون اني انا الرب حين اجعل وجهي ضدهم"));
arasuperobject.biblemap.put("b26_15_8", new arasuperobject(26,15,8,"واجعل الارض خرابا لانهم خانوا خيانة يقول السيد الرب"));
arasuperobject.biblemap.put("b26_16_1", new arasuperobject(26,16,1,"وكانت اليّ كلمة الرب قائلة"));
arasuperobject.biblemap.put("b26_16_2", new arasuperobject(26,16,2,"يا ابن آدم عرّف اورشليم برجاساتها"));
arasuperobject.biblemap.put("b26_16_3", new arasuperobject(26,16,3,"وقل. هكذا قال السيد الرب لاورشليم. مخرجك ومولدك من ارض كنعان. ابوك اموري وامك حثية"));
arasuperobject.biblemap.put("b26_16_4", new arasuperobject(26,16,4,"اما ميلادك يوم ولدت فلم تقطع سرتك ولم تغسلي بالماء للتنظّف ولم تملّحي تمليحا ولم تقمّطي تقميطا"));
arasuperobject.biblemap.put("b26_16_5", new arasuperobject(26,16,5,"لم تشفق عليك عين لتصنع لك واحدة من هذه لترق لك. بل طرحت على وجه الحقل بكراهة نفسك يوم ولدت"));
arasuperobject.biblemap.put("b26_16_6", new arasuperobject(26,16,6,"فمررت بك ورأيتك مدوسة بدمك فقلت لك بدمك عيشي. قلت لك بدمك عيشي"));
arasuperobject.biblemap.put("b26_16_7", new arasuperobject(26,16,7,"جعلتك ربوة كنبات الحقل فربوت وكبرت وبلغت زينة الازيان. نهد ثدياك ونبت شعرك وقد كنت عريانة وعارية"));
arasuperobject.biblemap.put("b26_16_8", new arasuperobject(26,16,8,"فمررت بك ورأيتك واذ زمنك زمن الحب. فبسطت ذيلي عليك وسترت عورتك وحلفت لك ودخلت معك في عهد يقول السيد الرب فصرت لي"));
arasuperobject.biblemap.put("b26_16_9", new arasuperobject(26,16,9,"فحمّمتك بالماء وغسلت عنك دماءك ومسحتك بالزيت"));
arasuperobject.biblemap.put("b26_16_10", new arasuperobject(26,16,10,"وألبستك مطرزة ونعلتك بالتّخس وازرتك بالكتان وكسوتك بزا"));
arasuperobject.biblemap.put("b26_16_11", new arasuperobject(26,16,11,"وحليتك بالحلي فوضعت اسورة في يديك وطوقا في عنقك"));
arasuperobject.biblemap.put("b26_16_12", new arasuperobject(26,16,12,"ووضعت خزامة في انفك واقراطا في اذنيك وتاج جمال على راسك"));
arasuperobject.biblemap.put("b26_16_13", new arasuperobject(26,16,13,"فتحليت بالذهب والفضة ولباسك الكتان والبز والمطرز. وأكلت السميذ والعسل والزيت وجملت جدا جدا فصلحت لمملكة"));
arasuperobject.biblemap.put("b26_16_14", new arasuperobject(26,16,14,"وخرج لك اسم في الامم لجمالك لانه كان كاملا ببهائي الذي جعلته عليك يقول السيد الرب"));
arasuperobject.biblemap.put("b26_16_15", new arasuperobject(26,16,15,"فاتكلت على جمالك وزنيت على اسمك وسكبت زناك على كل عابر فكان له"));
arasuperobject.biblemap.put("b26_16_16", new arasuperobject(26,16,16,"وأخذت من ثيابك وصنعت لنفسك مرتفعات موشّاة وزنيت عليها. أمر لم يأت ولم يكن"));
arasuperobject.biblemap.put("b26_16_17", new arasuperobject(26,16,17,"واخذت امتعة زينتك من ذهبي ومن فضتي التي اعطيتك وصنعت لنفسك صور ذكور وزنيت بها"));
arasuperobject.biblemap.put("b26_16_18", new arasuperobject(26,16,18,"واخذت ثيابك المطرزة وغطيتها بها ووضعت امامها زيتي وبخوري"));
arasuperobject.biblemap.put("b26_16_19", new arasuperobject(26,16,19,"وخبزي الذي اعطيتك السميذ والزيت والعسل الذي اطعمتك وضعتها امامها رائحة سرور وهكذا كان يقول السيد الرب"));
arasuperobject.biblemap.put("b26_16_20", new arasuperobject(26,16,20,"اخذت بنيك وبناتك الذين ولدتهم لي وذبحتهم لها طعاما. أهو قليل من زناك"));
arasuperobject.biblemap.put("b26_16_21", new arasuperobject(26,16,21,"انك ذبحت بنيّ وجعلتهم يجوزون في النار لها"));
arasuperobject.biblemap.put("b26_16_22", new arasuperobject(26,16,22,"وفي كل رجاساتك وزناك لم تذكري ايام صباك اذ كنت عريانة وعارية وكنت مدوسة بدمك"));
arasuperobject.biblemap.put("b26_16_23", new arasuperobject(26,16,23,"وكان بعد كل شرّك. ويل ويل لك يقول السيد الرب"));
arasuperobject.biblemap.put("b26_16_24", new arasuperobject(26,16,24,"انك بنيت لنفسك قبّة وصنعت لنفسك مرتفعة في كل شارع"));
arasuperobject.biblemap.put("b26_16_25", new arasuperobject(26,16,25,"في راس كل طريق بنيت مرتفعتك ورجّست جمالك وفرّجت رجليك لكل عابر واكثرت زناك"));
arasuperobject.biblemap.put("b26_16_26", new arasuperobject(26,16,26,"وزنيت مع جيرانك بني مصر الغلاظ اللحم وزدت في زناك لاغاظتي"));
arasuperobject.biblemap.put("b26_16_27", new arasuperobject(26,16,27,"فهانذا قد مددت يدي عليك ومنعت عنك فريضتك واسلمتك لمرام مبغضاتك بنات الفلسطينيين اللواتي يخجلن من طريقك الرذيلة"));
arasuperobject.biblemap.put("b26_16_28", new arasuperobject(26,16,28,"وزنيت مع بني اشور اذ كنت لم تشبعي فزنيت بهم ولم تشبعي ايضا"));
arasuperobject.biblemap.put("b26_16_29", new arasuperobject(26,16,29,"وكثرت زناك في ارض كنعان الى ارض الكلدانيين وبهذا ايضا لم تشبعي"));
arasuperobject.biblemap.put("b26_16_30", new arasuperobject(26,16,30,"ما امرض قلبك يقول السيد الرب اذ فعلت كل هذا فعل امرأة زانية سليطة"));
arasuperobject.biblemap.put("b26_16_31", new arasuperobject(26,16,31,"ببنائك قبّتك في راس كل طريق وصنعتك مرتفعتك في كل شارع. ولم تكوني كزانية بل محتقرة الأجرة"));
arasuperobject.biblemap.put("b26_16_32", new arasuperobject(26,16,32,"ايتها الزوجة الفاسقة تاخذ اجنبيين مكان زوجها"));
arasuperobject.biblemap.put("b26_16_33", new arasuperobject(26,16,33,"لكل الزواني يعطون هدية. اما انت فقد اعطيت كل محبيك هداياك ورشيتهم ليأتوك من كل جانب للزنا بك"));
arasuperobject.biblemap.put("b26_16_34", new arasuperobject(26,16,34,"وصار فيك عكس عادة النساء في زناك اذ لم يزن وراءك بل انت تعطين اجرة ولا اجرة تعطى لك فصرت بالعكس"));
arasuperobject.biblemap.put("b26_16_35", new arasuperobject(26,16,35,"فلذلك يا زانية اسمعي كلام الرب"));
arasuperobject.biblemap.put("b26_16_36", new arasuperobject(26,16,36,"هكذا قال السيد الرب. من اجل انه قد انفق نحاسك وانكشفت عورتك بزناك بمحبيك وبكل اصنام رجاساتك ولدماء بنيك الذين بذلتهم لها"));
arasuperobject.biblemap.put("b26_16_37", new arasuperobject(26,16,37,"لذلك هانذا اجمع جميع محبيك الذين لذذت لهم وكل الذين احببتهم مع كل الذين ابغضتهم فاجمعهم عليك من حولك واكشف عورتك لهم لينظروا كل عورتك"));
arasuperobject.biblemap.put("b26_16_38", new arasuperobject(26,16,38,"واحكم عليك احكام الفاسقات السافكات الدم واجعلك دم السخط والغيرة"));
arasuperobject.biblemap.put("b26_16_39", new arasuperobject(26,16,39,"واسلمك ليدهم فيهدمون قبتك ويهدمون مرتفعاتك وينزعون عنك ثيابك وياخذون ادوات زينتك ويتركونك عريانة وعارية . ً"));
arasuperobject.biblemap.put("b26_16_40", new arasuperobject(26,16,40,"ويصعدون عليك جماعة ويرجمونك بالحجارة ويقطعونك بسيوفهم"));
arasuperobject.biblemap.put("b26_16_41", new arasuperobject(26,16,41,"ويحرقون بيوتك بالنار ويجرون عليك احكاما قدام عيون نساء كثيرة. واكفّك عن الزنى وايضا لا تعطين اجرة بعد"));
arasuperobject.biblemap.put("b26_16_42", new arasuperobject(26,16,42,"وأحل غضبي بك فتنصرف غيرتي عنك فاسكن ولا اغضب بعد"));
arasuperobject.biblemap.put("b26_16_43", new arasuperobject(26,16,43,"من اجل انك لم تذكري ايام صباك بل اسخطتني في كل هذا فهانذا ايضا اجلب طريقك على راسك يقول السيد الرب فلا تفعلين هذه الرذيلة فوق رجاساتك كلها"));
arasuperobject.biblemap.put("b26_16_44", new arasuperobject(26,16,44,"هوذا كل ضارب مثل يضرب مثلا عليك قائلا مثل الام بنتها"));
arasuperobject.biblemap.put("b26_16_45", new arasuperobject(26,16,45,"ابنة امك انت الكارهة زوجها وبنيها. وانت اخت اخواتك اللواتي كرهن ازواجهنّ وابناءهنّ . ‎ امكنّ حثية وابوكنّ أموري"));
arasuperobject.biblemap.put("b26_16_46", new arasuperobject(26,16,46,"واختك الكبرى السامرة هي وبناتها الساكنة عن شمالك. واختك الصغرى الساكنة عن يمينك هي سدوم وبناتها"));
arasuperobject.biblemap.put("b26_16_47", new arasuperobject(26,16,47,"ولا في طريقهنّ سلكت ولا مثل رجاساتهنّ فعلت كأن ذلك قليل فقط ففسدت اكثر منهنّ في كل طرقك"));
arasuperobject.biblemap.put("b26_16_48", new arasuperobject(26,16,48,"حيّ انا يقول السيد الرب ان سدوم اختك لم تفعل هي ولا بناتها كما فعلت انت وبناتك"));
arasuperobject.biblemap.put("b26_16_49", new arasuperobject(26,16,49,"هذا كان اثم اختك سدوم الكبرياء والشبع من الخبز وسلام الاطمئنان كان لها ولبناتها ولم تشدد يد الفقير والمسكين"));
arasuperobject.biblemap.put("b26_16_50", new arasuperobject(26,16,50,"وتكبّرنّ وعملن الرجس امامي فنزعتهنّ كما رأيت"));
arasuperobject.biblemap.put("b26_16_51", new arasuperobject(26,16,51,"ولم تخطئ السامرة نصف خطاياك. بل زدت رجاساتك اكثر منهنّ وبرّرت اخواتك بكل رجاساتك التي فعلت"));
arasuperobject.biblemap.put("b26_16_52", new arasuperobject(26,16,52,"فاحملي ايضا خزيك انت القاضية على اخواتك. بخطاياك التي بها رجست اكثر منهنّ هنّ ابرّ منك. فاخجلي انت ايضا واحملي عارك بتبريرك اخواتك"));
arasuperobject.biblemap.put("b26_16_53", new arasuperobject(26,16,53,"وأرجّع سبيهنّ سبي سدوم وبناتها وسبي السامرة وبناتها وسبي مسبييك في وسطها"));
arasuperobject.biblemap.put("b26_16_54", new arasuperobject(26,16,54,"لكي تحملي عارك وتخزي من كل ما فعلت بتعزيتك اياهنّ"));
arasuperobject.biblemap.put("b26_16_55", new arasuperobject(26,16,55,"واخواتك سدوم وبناتها يرجعن الى حالتهنّ القديمة والسامرة وبناتها يرجعن الى حالتهنّ القديمة وانت وبناتك ترجعن الى حالتكنّ القديمة"));
arasuperobject.biblemap.put("b26_16_56", new arasuperobject(26,16,56,"واختك سدوم لم تكن تذكر في فمك يوم كبريائك"));
arasuperobject.biblemap.put("b26_16_57", new arasuperobject(26,16,57,"قبل ما انكشف شرّك كما في زمان تعيير بنات ارام وكل من حولها بنات الفلسطينيين اللواتي يحتقرنك من كل جهة"));
arasuperobject.biblemap.put("b26_16_58", new arasuperobject(26,16,58,"رذيلتك ورجاساتك انت تحملينها يقول الرب"));
arasuperobject.biblemap.put("b26_16_59", new arasuperobject(26,16,59,"لانه هكذا قال السيد الرب اني افعل بك كما فعلت اذ ازدريت بالقسم لنكث العهد"));
arasuperobject.biblemap.put("b26_16_60", new arasuperobject(26,16,60,"ولكني اذكر عهدي معك في ايام صباك واقيم لك عهدا ابديا"));
arasuperobject.biblemap.put("b26_16_61", new arasuperobject(26,16,61,"فتتذكرين طرقك وتخجلين اذ تقبلين اخواتك الكبر والصغر واجعلهنّ لك بنات ولكن لا بعهدك"));
arasuperobject.biblemap.put("b26_16_62", new arasuperobject(26,16,62,"وانا اقيم عهدي معك فتعلمين اني انا الرب"));
arasuperobject.biblemap.put("b26_16_63", new arasuperobject(26,16,63,"لكي تتذكري فتخزي ولا تفتحي فاك بعد بسبب خزيك حين اغفر لك كل ما فعلت يقول السيد الرب"));
arasuperobject.biblemap.put("b26_17_1", new arasuperobject(26,17,1,"وكان اليّ كلام الرب قائلا"));
arasuperobject.biblemap.put("b26_17_2", new arasuperobject(26,17,2,"يا ابن آدم حاج أحجية ومثّل مثلا لبيت اسرائيل"));
arasuperobject.biblemap.put("b26_17_3", new arasuperobject(26,17,3,"وقل. هكذا قال السيد الرب. نسر عظيم كبير الجناحين طويل القوادم واسع المناكب ذو تهاويل جاء الى لبنان واخذ فرع الارز"));
arasuperobject.biblemap.put("b26_17_4", new arasuperobject(26,17,4,"قصف راس خراعيبه وجاء به الى ارض كنعان وجعله في مدينة التجار"));
arasuperobject.biblemap.put("b26_17_5", new arasuperobject(26,17,5,"واخذ من زرع الارض والقاه في حقل الزرع. وجعله على مياه كثيرة. اقامه كالصفصاف"));
arasuperobject.biblemap.put("b26_17_6", new arasuperobject(26,17,6,"فنبت وصار كرمة منتشرة قصيرة الساق. انعطفت عليه زراجينها وكانت اصولها تحته فصارت كرمة وانبتت فروعا وافرخت اغصانا"));
arasuperobject.biblemap.put("b26_17_7", new arasuperobject(26,17,7,"وكان نسر آخر عظيم كبير الجناحين واسع المنكب فاذا بهذه الكرمة عطفت عليه اصولها وانبتت نحوه زراجينها ليسقيها في خمائل غرسها"));
arasuperobject.biblemap.put("b26_17_8", new arasuperobject(26,17,8,"في حقل جيد على مياه كثيرة هي مغروسة لتنبت اغصانا وتحمل ثمرا فتكون كرمة واسعة"));
arasuperobject.biblemap.put("b26_17_9", new arasuperobject(26,17,9,"قل. هكذا قال السيد الرب. هل تنجح. أفلا يقلع اصولها ويقطع ثمرها فتيبس. كل من اوراق اغصانها تيبس وليس بذراع عظيمة او بشعب كثير ليقلعوها من اصولها"));
arasuperobject.biblemap.put("b26_17_10", new arasuperobject(26,17,10,"ها هي المغروسة فهل تنجح. ألا تيبس يبسا كأن ريحا شرقية اصابتها. في خمائل نبتها تيبس"));
arasuperobject.biblemap.put("b26_17_11", new arasuperobject(26,17,11,"وكان اليّ كلام الرب قائلا"));
arasuperobject.biblemap.put("b26_17_12", new arasuperobject(26,17,12,"قل للبيت المتمرد أما علمتم ما هذه. قل هوذا ملك بابل قد جاء الى اورشليم واخذ ملكها ورؤساءها وجاء بهم اليه الى بابل"));
arasuperobject.biblemap.put("b26_17_13", new arasuperobject(26,17,13,"واخذ من الزرع الملكي وقطع معه عهدا وادخله في قسم واخذ اقوياء الارض"));
arasuperobject.biblemap.put("b26_17_14", new arasuperobject(26,17,14,"لتكون المملكة حقيرة ولا ترتفع. لتحفظ العهد فتثبت"));
arasuperobject.biblemap.put("b26_17_15", new arasuperobject(26,17,15,"فتمرد عليه بارساله رسله الى مصر ليعطوه خيلا وشعبا كثيرين. فهل ينجح هل يفلت فاعل هذا او ينقض عهدا ويفلت"));
arasuperobject.biblemap.put("b26_17_16", new arasuperobject(26,17,16,"حيّ انا يقول السيد الرب ان في موضع الملك الذي ملّكه الذي ازدرى قسمه ونقض عهده فعنده في وسط بابل يموت"));
arasuperobject.biblemap.put("b26_17_17", new arasuperobject(26,17,17,"ولا بجيش عظيم وجمع غفير يعينه فرعون في الحرب باقامة مترسة وبناء برج لقطع نفوس كثيرة"));
arasuperobject.biblemap.put("b26_17_18", new arasuperobject(26,17,18,"اذ ازدرى القسم لنقض العهد وهوذا قد اعطى يده وفعل هذا كله فلا يفلت"));
arasuperobject.biblemap.put("b26_17_19", new arasuperobject(26,17,19,"لاجل ذلك هكذا قال السيد الرب. حيّ انا ان قسمي الذي ازدراه وعهدي الذي نقضه اردهما على راسه"));
arasuperobject.biblemap.put("b26_17_20", new arasuperobject(26,17,20,"وابسط شبكتي عليه فيؤخذ في شركي وآتي به الى بابل واحاكمه هناك على خيانته التي خانني بها"));
arasuperobject.biblemap.put("b26_17_21", new arasuperobject(26,17,21,"وكل هاربيه وكل جيوشه يسقطون بالسيف والباقون يذرون في كل ريح فتعلمون اني انا الرب تكلمت"));
arasuperobject.biblemap.put("b26_17_22", new arasuperobject(26,17,22,"هكذا قال السيد الرب وآخذ انا من فرع الارز العالي واغرسه واقطف من راس خراعيبه غصنا واغرسه على جبل عال وشامخ"));
arasuperobject.biblemap.put("b26_17_23", new arasuperobject(26,17,23,"في جبل اسرائيل العالي اغرسه فينبت اغصانا ويحمل ثمرا ويكون ارزا واسعا فيسكن تحته كل طائر كل ذي جناح يسكن في ظل اغصانه"));
arasuperobject.biblemap.put("b26_17_24", new arasuperobject(26,17,24,"فتعلم جميع اشجار الحقل اني انا الرب وضعت الشجرة الرفيعة ورفعت الشجرة الوضيعة ويبّست الشجرة الخضراء وافرخت الشجرة اليابسة. انا الرب تكلمت وفعلت"));
arasuperobject.biblemap.put("b26_18_1", new arasuperobject(26,18,1,"وكان اليّ كلام الرب قائلا"));
arasuperobject.biblemap.put("b26_18_2", new arasuperobject(26,18,2,"ما لكم انتم تضربون هذا المثل على ارض اسرائيل قائلين الآباء اكلوا الحصرم واسنان الابناء ضرست"));
arasuperobject.biblemap.put("b26_18_3", new arasuperobject(26,18,3,"حيّ انا يقول السيد الرب لا يكون لكم من بعد ان تضربوا هذا المثل في اسرائيل"));
arasuperobject.biblemap.put("b26_18_4", new arasuperobject(26,18,4,"ها كل النفوس هي لي. نفس الاب كنفس الابن. كلاهما لي. النفس التي تخطئ هي تموت"));
arasuperobject.biblemap.put("b26_18_5", new arasuperobject(26,18,5,"والانسان الذي كان بارا وفعل حقا وعدلا"));
arasuperobject.biblemap.put("b26_18_6", new arasuperobject(26,18,6,"لم يأكل على الجبال ولم يرفع عينيه الى اصنام بيت اسرائيل ولم ينجّس امرأة قريبه ولم يقرب امرأة طامثا"));
arasuperobject.biblemap.put("b26_18_7", new arasuperobject(26,18,7,"ولم يظلم انسانا بل رد للمديون رهنه ولم يغتصب اغتصابا بل بذل خبزه للجوعان وكسا العريان ثوبا"));
arasuperobject.biblemap.put("b26_18_8", new arasuperobject(26,18,8,"ولم يعط بالربا ولم ياخذ مرابحة وكفّ يده عن الجور واجرى العدل والحق بين الانسان والانسان"));
arasuperobject.biblemap.put("b26_18_9", new arasuperobject(26,18,9,"وسلك في فرائضي وحفظ احكامي ليعمل بالحق فهو بار. حياة يحيا يقول السيد الرب"));
arasuperobject.biblemap.put("b26_18_10", new arasuperobject(26,18,10,"فان ولد ابنا معتنفا سفّاك دم ففعل شيئا من هذه"));
arasuperobject.biblemap.put("b26_18_11", new arasuperobject(26,18,11,"ولم يفعل كل تلك بل اكل على الجبال ونجّس امرأة قريبه"));
arasuperobject.biblemap.put("b26_18_12", new arasuperobject(26,18,12,"وظلم الفقير والمسكين واغتصب اغتصابا ولم يرد الرهن وقد رفع عينيه الى الاصنام وفعل الرجس"));
arasuperobject.biblemap.put("b26_18_13", new arasuperobject(26,18,13,"واعطى بالربا واخذ المرابحة أفيحيا. لا يحيا. قد عمل كل هذه الرجاسات فموتا يموت. دمه يكون على نفسه"));
arasuperobject.biblemap.put("b26_18_14", new arasuperobject(26,18,14,"وان ولد ابنا رأى جميع خطايا ابيه التي فعلها فرآها ولم يفعل مثلها"));
arasuperobject.biblemap.put("b26_18_15", new arasuperobject(26,18,15,"لم يأكل على الجبال ولم يرفع عينيه الى اصنام بيت اسرائيل ولا نجّس امرأة قريبه"));
arasuperobject.biblemap.put("b26_18_16", new arasuperobject(26,18,16,"ولا ظلم انسانا ولا ارتهن رهنا ولا اغتصب اغتصابا بل بذل خبزه للجوعان وكسى العريان ثوبا"));
arasuperobject.biblemap.put("b26_18_17", new arasuperobject(26,18,17,"ورفع يده عن الفقير ولم ياخذ ربا ولا مرابحة بل اجرى احكامي وسلك في فرائضي فانه لا يموت باثم ابيه. حياة يحيا"));
arasuperobject.biblemap.put("b26_18_18", new arasuperobject(26,18,18,"اما ابوه فلانه ظلم ظلما واغتصب اخاه اغتصابا وعمل غير الصالح بين شعبه فهوذا يموت باثمه"));
arasuperobject.biblemap.put("b26_18_19", new arasuperobject(26,18,19,"وانتم تقولون لماذا لا يحمل الابن من اثم الاب. اما الابن فقد فعل حقا وعدلا حفظ جميع فرائضي وعمل بها فحياة يحيا"));
arasuperobject.biblemap.put("b26_18_20", new arasuperobject(26,18,20,"النفس التي تخطئ هي تموت. الابن لا يحمل من اثم الاب والاب لا يحمل من اثم الابن. بر البار عليه يكون وشر الشرير عليه يكون"));
arasuperobject.biblemap.put("b26_18_21", new arasuperobject(26,18,21,"فاذا رجع الشرير عن جميع خطاياه التي فعلها وحفظ كل فرائضي وفعل حقا وعدلا فحياة يحيا. لا يموت"));
arasuperobject.biblemap.put("b26_18_22", new arasuperobject(26,18,22,"كل معاصيه التي فعلها لا تذكر عليه. في بره الذي عمل يحيا"));
arasuperobject.biblemap.put("b26_18_23", new arasuperobject(26,18,23,"هل مسرة أسر بموت الشرير يقول السيد الرب. ألا برجوعه عن طرقه فيحيا"));
arasuperobject.biblemap.put("b26_18_24", new arasuperobject(26,18,24,"واذا رجع البار عن بره وعمل اثما وفعل مثل كل الرجاسات التي يفعلها الشرير أفيحيا. كل بره الذي عمله لا يذكر. في خيانته التي خانها وفي خطيته التي اخطأ بها يموت"));
arasuperobject.biblemap.put("b26_18_25", new arasuperobject(26,18,25,"وانتم تقولون ليست طريق الرب مستوية. فاسمعوا الآن يا بيت اسرائيل. أطريقي هي غير مستوية أليست طرقكم غير مستوية"));
arasuperobject.biblemap.put("b26_18_26", new arasuperobject(26,18,26,"اذا رجع البار عن بره وعمل اثما ومات فيه فباثمه الذي عمله يموت"));
arasuperobject.biblemap.put("b26_18_27", new arasuperobject(26,18,27,"واذا رجع الشرير عن شره الذي فعل وعمل حقا وعدلا فهو يحيي نفسه"));
arasuperobject.biblemap.put("b26_18_28", new arasuperobject(26,18,28,"رأى فرجع عن كل معاصيه التي عملها فحياة يحيا. لا يموت"));
arasuperobject.biblemap.put("b26_18_29", new arasuperobject(26,18,29,"وبيت اسرائيل يقول ليست طريق الرب مستوية. أطرقي غير مستقيمة يا بيت اسرائيل. أليست طرقكم غير مستقيمة"));
arasuperobject.biblemap.put("b26_18_30", new arasuperobject(26,18,30,"من اجل ذلك اقضي عليكم يا بيت اسرائيل كل واحد كطرقه يقول السيد الرب. توبوا وارجعوا عن كل معاصيكم ولا يكون لكم الاثم مهلكة"));
arasuperobject.biblemap.put("b26_18_31", new arasuperobject(26,18,31,"اطرحوا عنكم كل معاصيكم التي عصيتم بها واعملوا لانفسكم قلبا جديدا وروحا جديدة. فلماذا تموتون يا بيت اسرائيل"));
arasuperobject.biblemap.put("b26_18_32", new arasuperobject(26,18,32,"لاني لا أسر بموت من يموت يقول السيد الرب. فارجعوا واحيوا"));
arasuperobject.biblemap.put("b26_19_1", new arasuperobject(26,19,1,"اما انت فارفع مرثاة على رؤساء اسرائيل"));
arasuperobject.biblemap.put("b26_19_2", new arasuperobject(26,19,2,"وقل. ما هي امك. لبوة ربضت بين الأسود وربّت جراءها بين الاشبال"));
arasuperobject.biblemap.put("b26_19_3", new arasuperobject(26,19,3,"ربّت واحدا من جرائها فصار شبلا وتعلم افتراس الفريسة. اكل الناس"));
arasuperobject.biblemap.put("b26_19_4", new arasuperobject(26,19,4,"فلما سمعت به الامم أخذ في حفرتهم فأتوا به بخزائم الى ارض مصر"));
arasuperobject.biblemap.put("b26_19_5", new arasuperobject(26,19,5,"فلما رأت انها قد انتظرت وهلك رجاؤها اخذت آخر من جرائها وصيرته شبلا"));
arasuperobject.biblemap.put("b26_19_6", new arasuperobject(26,19,6,"فتمشى بين الأسود. صار شبلا وتعلم افتراس الفريسة. اكل الناس"));
arasuperobject.biblemap.put("b26_19_7", new arasuperobject(26,19,7,"وعرف قصورهم وخرب مدنهم فاقفرت الارض وملؤها من صوت زمجرته"));
arasuperobject.biblemap.put("b26_19_8", new arasuperobject(26,19,8,"فاتفق عليه الامم من كل جهة من البلدان وبسطوا عليه شبكتهم فأخذ في حفرتهم"));
arasuperobject.biblemap.put("b26_19_9", new arasuperobject(26,19,9,"فوضعوه في قفص بخزائم واحضروه الى ملك بابل وأتوا به الى القلاع لكيلا يسمع صوته بعد على جبال اسرائيل"));
arasuperobject.biblemap.put("b26_19_10", new arasuperobject(26,19,10,"امك ككرمة مثلك غرست على المياه. كانت مثمرة مفرخة من كثرة المياه"));
