arasuperobject.biblemap.put("b26_21_1", new arasuperobject(26,21,1,"وكان اليّ كلام الرب قائلا"));
arasuperobject.biblemap.put("b26_21_2", new arasuperobject(26,21,2,"يا ابن آدم اجعل وجهك نحو اورشليم وتكلم على المقادس وتنبأ على ارض اسرائيل"));
arasuperobject.biblemap.put("b26_21_3", new arasuperobject(26,21,3,"وقل لارض اسرائيل. هكذا قال الرب. هانذا عليك واستل سيفي من غمده فاقطع منك الصدّيق والشرير"));
arasuperobject.biblemap.put("b26_21_4", new arasuperobject(26,21,4,"من حيث اني اقطع منك الصدّيق والشرير فلذلك يخرج سيفي من غمده على كل بشر من الجنوب الى الشمال"));
arasuperobject.biblemap.put("b26_21_5", new arasuperobject(26,21,5,"فيعلم كل بشر اني انا الرب سللت سيفي من غمده. لا يرجع ايضا"));
arasuperobject.biblemap.put("b26_21_6", new arasuperobject(26,21,6,"اما انت يا ابن آدم فتنهّد بانكسار الحقوين وبمرارة تنهّد امام عيونهم"));
arasuperobject.biblemap.put("b26_21_7", new arasuperobject(26,21,7,"ويكون اذا قالوا لك على م تتنهد انك تقول على الخبر لانه جاء فيذوب كل قلب وترتخي كل الايدي وتيئس كل روح وكل الركب تصير كالماء ها هي آتية وتكون يقول السيد الرب"));
