arasuperobject.biblemap.put("b26_21_10", new arasuperobject(26,21,10,"قد حدّد ليذبح ذبحا. قد صقل لكي يبرق فهل نبتهج. عصا ابني تزدري بكل عود"));
arasuperobject.biblemap.put("b26_21_11", new arasuperobject(26,21,11,"وقد اعطاه ليصقل لكي يمسك بالكف. هذا السيف قد حدّد وهو مصقول لكي يسلم ليد القاتل"));
