arasuperobject.biblemap.put("b26_21_13", new arasuperobject(26,21,13,"لانه امتحان. وماذا ان لم تكن ايضا العصا المزدرية يقول السيد الرب"));
arasuperobject.biblemap.put("b26_21_14", new arasuperobject(26,21,14,"فتنبأ انت يا ابن آدم واصفق كفا على كف وليعد السيف ثالثة. هو سيف القتلى سيف القتل العظيم المحيق بهم"));
arasuperobject.biblemap.put("b26_21_15", new arasuperobject(26,21,15,"لذوبان القلب وتكثير المهالك لذلك جعلت على كل الابواب سيفا متقلبا. آه قد جعل برّاقا هو مصقول للذبح"));
