arasuperobject.biblemap.put("b26_21_17", new arasuperobject(26,21,17,"وانا ايضا اصفّق كفّي على كفّي واسكن غضبي. انا الرب تكلمت"));
arasuperobject.biblemap.put("b26_21_18", new arasuperobject(26,21,18,"وكان اليّ كلام الرب قائلا"));
arasuperobject.biblemap.put("b26_21_19", new arasuperobject(26,21,19,"وانت يا ابن آدم عيّن لنفسك طريقين لمجيء سيف ملك بابل. من ارض واحدة تخرج الاثنتان. واصنع صوّة. على راس طريق المدينة اصنعها"));
arasuperobject.biblemap.put("b26_21_20", new arasuperobject(26,21,20,"عيّن طريقا لياتي السيف على ربّة بني عمون وعلى يهوذا في اورشليم المنيعة"));
