arasuperobject.biblemap.put("b26_22_10", new arasuperobject(26,22,10,"فيك كشف الانسان عورة ابيه. فيك أذلوا المتنجسة بطمثها"));
arasuperobject.biblemap.put("b26_22_11", new arasuperobject(26,22,11,"انسان فعل الرجس بامرأة قريبه. انسان نجس كنته برذيلة. انسان اذل فيك اخته بنت ابيه"));
