arasuperobject.biblemap.put("b26_22_13", new arasuperobject(26,22,13,"فهانذا قد صفقت بكفي بسبب خطفك الذي خطفت وبسبب دمك الذي كان في وسطك"));
arasuperobject.biblemap.put("b26_22_14", new arasuperobject(26,22,14,"فهل يثبت قلبك او تقوى يداك في الايام التي فيها أعاملك. انا الرب تكلمت وسافعل"));
