arasuperobject.biblemap.put("b26_22_16", new arasuperobject(26,22,16,"وتتدنسين بنفسك امام عيون الامم وتعلمين اني انا الرب"));
arasuperobject.biblemap.put("b26_22_17", new arasuperobject(26,22,17,"وكان اليّ كلام الرب قائلا"));
arasuperobject.biblemap.put("b26_22_18", new arasuperobject(26,22,18,"يا ابن آدم قد صار لي بيت اسرائيل زغلا كلهم نحاس وقصدير وحديد ورصاص في وسط كور. صاروا زغل فضة"));
