arasuperobject.biblemap.put("b26_22_27", new arasuperobject(26,22,27,"رؤساؤها في وسطها كذئاب خاطفة خطفا لسفك الدم لاهلاك النفوس لاكتساب كسب"));
arasuperobject.biblemap.put("b26_22_28", new arasuperobject(26,22,28,"وانبياؤها قد طينوا لهم بالطفال رائين باطلا وعارفين لهم كذبا قائلين هكذا قال السيد الرب والرب لم يتكلم"));
arasuperobject.biblemap.put("b26_22_29", new arasuperobject(26,22,29,"شعب الارض ظلموا ظلما وغصبوا غصبا واضطهدوا الفقير والمسكين وظلموا الغريب بغير الحق"));
arasuperobject.biblemap.put("b26_22_30", new arasuperobject(26,22,30,"وطلبت من بينهم رجلا يبني جدارا ويقف في الثغر امامي عن الارض لكي لا اخربها فلم اجد"));
