arasuperobject.biblemap.put("b26_23_1", new arasuperobject(26,23,1,"وكان اليّ كلام الرب قائلا"));
arasuperobject.biblemap.put("b26_23_2", new arasuperobject(26,23,2,"يا ابن آدم كان امرأتان ابنتا ام واحدة"));
