arasuperobject.biblemap.put("b26_23_23", new arasuperobject(26,23,23,"بني بابل وكل الكلدانيين فقود وشوع وقوع ومعهم كل بني اشور شبان شهوة ولاة وشحن كلهم رؤساء مركبات وشهراء. كلهم راكبون الخيل"));
arasuperobject.biblemap.put("b26_23_24", new arasuperobject(26,23,24,"فياتون عليك باسلحة مركبات وعجلات وبجماعة شعوب يقيمون عليك الترس والمجن والخوذة من حولك واسلم لهم الحكم فيحكمون عليك بأحكامهم"));
