arasuperobject.biblemap.put("b26_23_29", new arasuperobject(26,23,29,"فيعاملونك بالبغضاء وياخذون كل تعبك ويتركونك عريانة وعارية فتنكشف عورة زناك ورذيلتك وزناك"));
