arasuperobject.biblemap.put("b26_23_45", new arasuperobject(26,23,45,"والرجال الصدّيقون هم يحكمون عليهما حكم زانية وحكم سفاكة الدم لانهما زانيتان وفي ايديهما دم"));
arasuperobject.biblemap.put("b26_23_46", new arasuperobject(26,23,46,"لانه هكذا قال السيد الرب. اني أصعد عليهما جماعة واسلّمهما للجور والنهب"));
arasuperobject.biblemap.put("b26_23_47", new arasuperobject(26,23,47,"وترجمهما الجماعة بالحجارة ويقطّعونهما بسيوفهم ويذبحون ابناءهما وبناتهما ويحرقون بيوتهما بالنار"));
arasuperobject.biblemap.put("b26_23_48", new arasuperobject(26,23,48,"فابطل الرذيلة من الارض فتتأدب جميع النساء ولا يفعلن مثل رذيلتكما"));
