arasuperobject.biblemap.put("b26_24_1", new arasuperobject(26,24,1,"وكان كلام الرب اليّ في السنة التاسعة في الشهر العاشر في العاشر من الشهر قائلا"));
arasuperobject.biblemap.put("b26_24_2", new arasuperobject(26,24,2,"يا ابن آدم اكتب لنفسك اسم اليوم هذا اليوم بعينه فان ملك بابل قد اقترب الى اورشليم هذا اليوم بعينه"));
arasuperobject.biblemap.put("b26_24_3", new arasuperobject(26,24,3,"واضرب مثلا للبيت المتمرد وقل لهم. هكذا قال السيد الرب. ضع القدر. ضعها وايضا صبّ فيها ماء"));
arasuperobject.biblemap.put("b26_24_4", new arasuperobject(26,24,4,"اجمع اليها قطعها كل قطعة طيبة الفخذ والكتف. املأوها بخيار العظام"));
arasuperobject.biblemap.put("b26_24_5", new arasuperobject(26,24,5,"خذ من خيار الغنم وكومة العظام تحتها. إغلها اغلاء فتسلق ايضا عظامها في وسطها"));
arasuperobject.biblemap.put("b26_24_6", new arasuperobject(26,24,6,"لذلك هكذا قال السيد الرب. ويل لمدينة الدماء القدر التي فيها زنجارها وما خرج منها زنجارها. اخرجوها قطعة قطعة. لا تقع عليها قرعة"));
arasuperobject.biblemap.put("b26_24_7", new arasuperobject(26,24,7,"لان دمها في وسطها. قد وضعته على ضحّ الصخر لم ترقه على الارض لتواريه بالتراب"));
arasuperobject.biblemap.put("b26_24_8", new arasuperobject(26,24,8,"لصعود الغضب لتنقم نقمة وضعت دمها على ضحّ الصخر. لئلا يوارى"));
arasuperobject.biblemap.put("b26_24_9", new arasuperobject(26,24,9,"لذلك هكذا قال السيد الرب ويل لمدينة الدماء. اني انا اعظم كومتها"));
arasuperobject.biblemap.put("b26_24_10", new arasuperobject(26,24,10,"كثّر الحطب اضرم النار. انضج اللحم تبله تتبيلا ولتحرق العظام"));
arasuperobject.biblemap.put("b26_24_11", new arasuperobject(26,24,11,"ثم ضعها فارغة على الجمر ليحمى نحاسها ويحرق فيذوب قذرها فيها ويفنى زنجارها"));
arasuperobject.biblemap.put("b26_24_12", new arasuperobject(26,24,12,"بمشقات تعبت ولم تخرج منها كثرة زنجارها. في النار زنجارها"));
arasuperobject.biblemap.put("b26_24_13", new arasuperobject(26,24,13,"في نجاستك رذيلة لاني طهرتك فلم تطهري ولن تطهري بعد من نجاستك حتى احل غضبي عليك"));
arasuperobject.biblemap.put("b26_24_14", new arasuperobject(26,24,14,"انا الرب تكلمت. يأتي فافعله. لا اطلق ولا اشفق ولا اندم. حسب طرقك وحسب اعمالك يحكمون عليك يقول السيد الرب"));
arasuperobject.biblemap.put("b26_24_15", new arasuperobject(26,24,15,"وكان اليّ كلام الرب قائلا"));
arasuperobject.biblemap.put("b26_24_16", new arasuperobject(26,24,16,"يا ابن آدم هانذا آخذ عنك شهوة عينيك بضربة فلا تنح ولا تبك ولا تنزل دموعك"));
arasuperobject.biblemap.put("b26_24_17", new arasuperobject(26,24,17,"تنهّد ساكتا لا تعمل مناحة على اموات. لف عصابتك عليك واجعل نعليك في رجليك ولا تغط شاربيك ولا تاكل من خبز الناس"));
arasuperobject.biblemap.put("b26_24_18", new arasuperobject(26,24,18,"فكلمت الشعب صباحا وماتت زوجتي مساء. وفعلت في الغد كما أمرت"));
arasuperobject.biblemap.put("b26_24_19", new arasuperobject(26,24,19,"فقال لي الشعب ألا تخبرنا ما لنا وهذه التي انت صانعها"));
arasuperobject.biblemap.put("b26_24_20", new arasuperobject(26,24,20,"فاجبتهم قد كان اليّ كلام الرب قائلا"));
arasuperobject.biblemap.put("b26_24_21", new arasuperobject(26,24,21,"كلم بيت اسرائيل. هكذا قال السيد الرب. هانذا منجس مقدسي فخر عزكم شهوة اعينكم ولذّة نفوسكم. وابناؤكم وبناتكم الذين خلّفتم يسقطون بالسيف"));
arasuperobject.biblemap.put("b26_24_22", new arasuperobject(26,24,22,"وتفعلون كما فعلت لا تغطون شواربكم ولا تاكلون من خبز الناس"));
arasuperobject.biblemap.put("b26_24_23", new arasuperobject(26,24,23,"وتكون عصائبكم على رؤوسكم ونعالكم في ارجلكم. لا تنوحون ولا تبكون وتفنون بآثامكم تئنون بعضكم على بعض"));
arasuperobject.biblemap.put("b26_24_24", new arasuperobject(26,24,24,"ويكون حزقيال لكم آية. مثل كل ما صنع تصنعون. اذا جاء هذا تعلمون اني انا السيد الرب"));
arasuperobject.biblemap.put("b26_24_25", new arasuperobject(26,24,25,"وانت يا ابن آدم أفلا يكون في يوم آخذ عنهم عزّهم سرور فخرهم شهوة عيونهم ورفعة نفسهم ابناءهم وبناتهم"));
arasuperobject.biblemap.put("b26_24_26", new arasuperobject(26,24,26,"ان يأتي اليك في ذلك اليوم المنفلت ليسمع اذنيك"));
arasuperobject.biblemap.put("b26_24_27", new arasuperobject(26,24,27,"في ذلك اليوم ينفتح فمك للمنفلت وتتكلم ولا تكون من بعد ابكم وتكون لهم آية فيعلمون اني انا الرب"));
arasuperobject.biblemap.put("b26_25_1", new arasuperobject(26,25,1,"وكان اليّ كلام الرب قائلا"));
arasuperobject.biblemap.put("b26_25_2", new arasuperobject(26,25,2,"يا ابن آدم اجعل وجهك نحو بني عمون وتنبأ عليهم"));
arasuperobject.biblemap.put("b26_25_3", new arasuperobject(26,25,3,"وقل لبني عمون اسمعوا كلام السيد الرب. هكذا قال السيد الرب. من اجل انك قلت هه على مقدسي لانه تنجس وعلى ارض اسرائيل لانها خربت وعلى بيت يهوذا لانهم ذهبوا الى السبي"));
arasuperobject.biblemap.put("b26_25_4", new arasuperobject(26,25,4,"فلذلك هانذا اسلمك لبني المشرق ملكا فيقيمون صيرهم فيك ويجعلون مساكنهم فيك. هم ياكلون غلتك وهم يشربون لبنك"));
arasuperobject.biblemap.put("b26_25_5", new arasuperobject(26,25,5,"واجعل ربّة مناخا للابل وبني عمون مربضا للغنم فتعلمون اني انا الرب"));
arasuperobject.biblemap.put("b26_25_6", new arasuperobject(26,25,6,"لانه هكذا قال السيد الرب. من اجل انك صفقت بيديك وخبطت برجليك وفرحت بكل اهانتك للموت على ارض اسرائيل"));
arasuperobject.biblemap.put("b26_25_7", new arasuperobject(26,25,7,"فلذلك هانذا امد يدي عليك واسلمك غنيمة للامم واستأصلك من الشعوب وابيدك من الاراضي. اخربك فتعلم اني انا الرب"));
arasuperobject.biblemap.put("b26_25_8", new arasuperobject(26,25,8,"هكذا قال السيد الرب. من اجل ان موآب وسعير يقولون هوذا بيت يهوذا مثل كل الامم"));
arasuperobject.biblemap.put("b26_25_9", new arasuperobject(26,25,9,"لذلك هانذا افتح جانب موآب من المدن. من مدنه من اقصاها بهاء الارض بيت بشيموت وبعل معون وقريتايم"));
arasuperobject.biblemap.put("b26_25_10", new arasuperobject(26,25,10,"لبني المشرق على بني عمون واجعلهم ملكا لكيلا يذكر بنو عمون بين الامم"));
arasuperobject.biblemap.put("b26_25_11", new arasuperobject(26,25,11,"وبموآب اجري احكاما فيعلمون اني انا الرب"));
arasuperobject.biblemap.put("b26_25_12", new arasuperobject(26,25,12,"هكذا قال السيد الرب. من اجل ان ادوم قد عمل بالانتقام على بيت يهوذا واساء اساءة وانتقم منه"));
arasuperobject.biblemap.put("b26_25_13", new arasuperobject(26,25,13,"لذلك هكذا قال السيد الرب وامد يدي على ادوم واقطع منها الانسان والحيوان واصيرها خرابا من التيمن والى ددان يسقطون بالسيف"));
arasuperobject.biblemap.put("b26_25_14", new arasuperobject(26,25,14,"واجعل نقمتي في ادوم بيد شعبي اسرائيل فيفعلون بادوم كغضبي وكسخطي فيعرفون نقمتي يقول السيد الرب"));
arasuperobject.biblemap.put("b26_25_15", new arasuperobject(26,25,15,"هكذا قال السيد الرب. من اجل ان الفلسطينيين قد عملوا بالانتقام وانتقموا نقمة بالاهانة الى الموت للخراب من عداوة ابدية"));
arasuperobject.biblemap.put("b26_25_16", new arasuperobject(26,25,16,"فلذلك هكذا قال السيد الرب. هانذا امد يدي على الفلسطينيين واستأصل الكريتيين واهلك بقية ساحل البحر"));
arasuperobject.biblemap.put("b26_25_17", new arasuperobject(26,25,17,"واجري عليهم نقمات عظيمة بتأديب سخط فيعلمون اني انا الرب اذ اجعل نقمتي عليهم"));
arasuperobject.biblemap.put("b26_26_1", new arasuperobject(26,26,1,"وكان في السنة الحادية عشرة في اول الشهر ان كلام الرب كان اليّ قائلا"));
arasuperobject.biblemap.put("b26_26_2", new arasuperobject(26,26,2,"يا ابن آدم من اجل ان صور قالت على اورشليم هه قد انكسرت مصاريع الشعوب. قد تحولت اليّ. امتلئ اذ خربت"));
arasuperobject.biblemap.put("b26_26_3", new arasuperobject(26,26,3,"لذلك هكذا قال السيد الرب. هانذا عليك يا صور فأصعد عليك امما كثيرة كما يعلي البحر امواجه"));
arasuperobject.biblemap.put("b26_26_4", new arasuperobject(26,26,4,"فيخربون اسوار صور ويهدمون ابراجها واسحي ترابها عنها واصيرها ضحّ الصخر"));
arasuperobject.biblemap.put("b26_26_5", new arasuperobject(26,26,5,"فتصير مبسطا للشباك في وسط البحر لاني انا تكلمت يقول السيد الرب وتكون غنيمة للامم"));
arasuperobject.biblemap.put("b26_26_6", new arasuperobject(26,26,6,"وبناتها اللواتي في الحقل تقتل بالسيف فيعلمون اني انا الرب"));
arasuperobject.biblemap.put("b26_26_7", new arasuperobject(26,26,7,"لانه هكذا قال السيد الرب. هانذا اجلب على صور نبوخذراصر ملك بابل من الشمال ملك الملوك بخيل وبمركبات وبفرسان وجماعة وشعب كثير"));
arasuperobject.biblemap.put("b26_26_8", new arasuperobject(26,26,8,"فيقتل بناتك في الحقل بالسيف ويبني عليك معاقل ويبني عليك برجا ويقيم عليك مترسة ويرفع عليك ترسا"));
arasuperobject.biblemap.put("b26_26_9", new arasuperobject(26,26,9,"ويجعل مجانق على اسوارك ويهدم ابراجك بأدوات حربه"));
arasuperobject.biblemap.put("b26_26_10", new arasuperobject(26,26,10,"ولكثرة خيله يغطيك غبارها. من صوت الفرسان والعجلات والمركبات تتزلزل اسوارك عند دخوله ابوابك كما تدخل مدينة مثغورة"));
arasuperobject.biblemap.put("b26_26_11", new arasuperobject(26,26,11,"بحوافر خيله يدوس كل شوارعك. يقتل شعبك بالسيف فتسقط الى الارض انصاب عزك"));
arasuperobject.biblemap.put("b26_26_12", new arasuperobject(26,26,12,"وينهبون ثروتك ويغنمون تجارتك ويهدّون اسوارك ويهدمون بيوتك البهيجة ويضعون حجارتك وخشبك وترابك في وسط المياه"));
arasuperobject.biblemap.put("b26_26_13", new arasuperobject(26,26,13,"وابطّل قول اغانيك وصوت اعوادك لن يسمع بعد"));
arasuperobject.biblemap.put("b26_26_14", new arasuperobject(26,26,14,"واصيّرك كضحّ الصخر فتكونين مبسطا للشباك. لا تبنين بعد لاني انا الرب تكلمت يقول السيد الرب"));
arasuperobject.biblemap.put("b26_26_15", new arasuperobject(26,26,15,"هكذا قال السيد الرب لصور. أما تتزلزل الجزائر عند صوت سقوطك عند صراخ الجرحى عند وقوع القتل في وسطك"));
arasuperobject.biblemap.put("b26_26_16", new arasuperobject(26,26,16,"فتنزل جميع رؤساء البحر عن كراسيهم ويخلعون جببهم وينزعون ثيابهم المطرزة. يلبسون رعدات ويجلسون على الارض ويرتعدون كل لحظة ويتحيّرون منك"));
arasuperobject.biblemap.put("b26_26_17", new arasuperobject(26,26,17,"ويرفعون عليك مرثاة ويقولون لك كيف بدت يا معمورة من البحار المدينة الشهيرة التي كانت قوية في البحر هي وسكانها الذين اوقعوا رعبهم على جميع جيرانها"));
arasuperobject.biblemap.put("b26_26_18", new arasuperobject(26,26,18,"الآن ترتعد الجزائر يوم سقوطك وتضطرب الجزائر التي في البحر لزوالك"));
arasuperobject.biblemap.put("b26_26_19", new arasuperobject(26,26,19,"لانه هكذا قال السيد الرب. حين اصيّرك مدينة خربة كالمدن غير المسكونة حين اصعد عليك الغمر فتغشاك المياه الكثيرة"));
arasuperobject.biblemap.put("b26_26_20", new arasuperobject(26,26,20,"اهبطك مع الهابطين في الجب الى شعب القدم واجلسك في اسافل الارض في الخرب الابدية مع الهابطين في الجب لتكوني غير مسكونة واجعل فخرا في ارض الاحياء"));
arasuperobject.biblemap.put("b26_26_21", new arasuperobject(26,26,21,"اصيرك اهوالا ولا تكونين وتطلبين فلا توجدين بعد الى الابد يقول السيد الرب"));
arasuperobject.biblemap.put("b26_27_1", new arasuperobject(26,27,1,"وكان اليّ كلام الرب قائلا"));
arasuperobject.biblemap.put("b26_27_2", new arasuperobject(26,27,2,"وانت يا ابن آدم فارفع مرثاة على صور"));
arasuperobject.biblemap.put("b26_27_3", new arasuperobject(26,27,3,"وقل لصور ايتها الساكنة عند مداخل البحر تاجرة الشعوب الى جزائر كثيرة هكذا قال السيد الرب. يا صور انت قلت انا كاملة الجمال"));
arasuperobject.biblemap.put("b26_27_4", new arasuperobject(26,27,4,"تخومك في قلب البحور. بنّاؤوك تمموا جمالك"));
arasuperobject.biblemap.put("b26_27_5", new arasuperobject(26,27,5,"عملوا كل الواحك من سرو سنير. اخذوا ارزا من لبنان ليصنعوه لك سواري"));
arasuperobject.biblemap.put("b26_27_6", new arasuperobject(26,27,6,"صنعوا من بلوط باشان مجاديفك. صنعوا مقاعدك من عاج مطعّم في البقس من جزائر كتيم"));
arasuperobject.biblemap.put("b26_27_7", new arasuperobject(26,27,7,"كتان مطرز من مصر هو شراعك ليكون لك راية. الاسمانجوني والارجوان من جزائر اليشة كانا غطاءك"));
arasuperobject.biblemap.put("b26_27_8", new arasuperobject(26,27,8,"اهل صيدون وارواد كانوا ملاحيك. حكماؤك يا صور الذين كانوا فيك هم ربابينك"));
arasuperobject.biblemap.put("b26_27_9", new arasuperobject(26,27,9,"شيوخ جبيل وحكماؤها كانوا فيك قلافوك. جميع سفن البحر وملاحوها كانوا فيك ليتاجروا بتجارتك"));
arasuperobject.biblemap.put("b26_27_10", new arasuperobject(26,27,10,"فارس ولود وفوط كانوا في جيشك رجال حربك. علقوا فيك ترسا وخوذة. هم صيّروا بهاءك"));
arasuperobject.biblemap.put("b26_27_11", new arasuperobject(26,27,11,"بنو ارواد مع جيشك على الاسوار من حولك والابطال كانوا في بروجك. علقوا اتراسهم على اسوارك من حولك. هم تمموا جمالك"));
arasuperobject.biblemap.put("b26_27_12", new arasuperobject(26,27,12,"ترشيش تاجرتك بكثرة كل غنى بالفضة والحديد والقصدير والرصاص اقاموا اسواقك"));
arasuperobject.biblemap.put("b26_27_13", new arasuperobject(26,27,13,"ياوان وتوبال وماشك هم تجارك. بنفوس الناس وبآنية النحاس اقاموا تجارتك"));
arasuperobject.biblemap.put("b26_27_14", new arasuperobject(26,27,14,"ومن بيت توجرمة بالخيل والفرسان والبغال اقاموا اسواقك"));
arasuperobject.biblemap.put("b26_27_15", new arasuperobject(26,27,15,"بنو ددان تجارك. جزائر كثيرة تجار يدك. ادّوا هديتك قرونا من العاج والآبنوس"));
arasuperobject.biblemap.put("b26_27_16", new arasuperobject(26,27,16,"ارام تاجرتك بكثرة صنائعك تاجروا في اسواقك بالبهرمان والارجوان والمطرز والبوص والمرجان والياقوت"));
arasuperobject.biblemap.put("b26_27_17", new arasuperobject(26,27,17,"يهوذا وارض اسرائيل هم تجارك. تاجروا في سوقك بحنطة منّيت وحلاوى وعسل وزيت وبلسان"));
arasuperobject.biblemap.put("b26_27_18", new arasuperobject(26,27,18,"دمشق تاجرتك بكثرة صنائعك وكثرة كل غنى بخمر حلبون والصوف الابيض"));
arasuperobject.biblemap.put("b26_27_19", new arasuperobject(26,27,19,"ودان وياوان قدموا غزلا في اسواقك. حديد مشغول وسليخة وقصب الذريرة كانت في سوقك"));
arasuperobject.biblemap.put("b26_27_20", new arasuperobject(26,27,20,"ددان تاجرتك بطنافس للركوب"));
arasuperobject.biblemap.put("b26_27_21", new arasuperobject(26,27,21,"العرب وكل رؤساء قيدار هم تجار يدك بالخرفان والكباش والاعتدة. في هذه كانوا تجارك"));
arasuperobject.biblemap.put("b26_27_22", new arasuperobject(26,27,22,"تجار شبا ورعمة هم تجارك. بافخر كل انواع الطيب وبكل حجر كريم والذهب اقاموا اسواقك"));
arasuperobject.biblemap.put("b26_27_23", new arasuperobject(26,27,23,"حران وكنّة وعدن تجار شبا واشور وكلمد تجارك"));
arasuperobject.biblemap.put("b26_27_24", new arasuperobject(26,27,24,"هؤلاء تجارك بنفائس بأردية اسمانجونية ومطرزة واصونة مبرم معكومة بالحبال مصنوعة من الارز بين بضائعك"));
arasuperobject.biblemap.put("b26_27_25", new arasuperobject(26,27,25,"سفن ترشيش قوافلك لتجارتك فامتلأت وتمجدت جدا في قلب البحار"));
arasuperobject.biblemap.put("b26_27_26", new arasuperobject(26,27,26,"ملاحوك قد اتوا بك الى مياه كثيرة. كسرتك الريح الشرقية في قلب البحار"));
arasuperobject.biblemap.put("b26_27_27", new arasuperobject(26,27,27,"ثروتك واسواقك وبضاعتك وملاحوك وربابينك وقلافوك والمتاجرون بمتجرك وجميع رجال حربك الذين فيك وكل جمعك الذي في وسطك يسقطون في قلب البحار في يوم سقوطك"));
arasuperobject.biblemap.put("b26_27_28", new arasuperobject(26,27,28,"من صوت صراخ ربابينك تتزلزل المسارح"));
arasuperobject.biblemap.put("b26_27_29", new arasuperobject(26,27,29,"وكل ممسكي المجذاف والملاحون وكل ربابين البحر ينزلون من سفنهم ويقفون على البر"));
arasuperobject.biblemap.put("b26_27_30", new arasuperobject(26,27,30,"ويسمعون صوتهم عليك ويصرخون بمرارة ويذرّون ترابا فوق رؤوسهم ويتمرغون في الرماد"));
arasuperobject.biblemap.put("b26_27_31", new arasuperobject(26,27,31,"ويجعلون في انفسهم قرعة عليك ويتنطقون بالمسوح ويبكون عليك بمرارة نفس نحيبا مرّا"));
