arasuperobject.biblemap.put("b26_31_14", new arasuperobject(26,31,14,"لكيلا ترتفع شجرة ما وهي على المياه لقامتها ولا تجعل فرعها بين الغيوم ولا تقوم بلوطاتها في ارتفاعها كل شاربة ماء لانها قد أسلمت جميعا الى الموت الى الارض السفلى في وسط بني آدم مع الهابطين في الجب"));
arasuperobject.biblemap.put("b26_31_15", new arasuperobject(26,31,15,"هكذا قال السيد الرب. في يوم نزوله الى الهاوية اقمت نوحا. كسوت عليه الغمر ومنعت انهاره وفنيت المياه الكثيرة واحزنت لبنان عليه وكل اشجار الحقل ذبلت عليه"));
arasuperobject.biblemap.put("b26_31_16", new arasuperobject(26,31,16,"من صوت سقوطه ارجفت الامم عند انزالي اياه الى الهاوية مع الهابطين في الجب فتتعزى في الارض السفلى كل اشجار عدن مختار لبنان وخياره كل شاربة ماء"));
arasuperobject.biblemap.put("b26_31_17", new arasuperobject(26,31,17,"هم ايضا نزلوا الى الهاوية معه الى القتلى بالسيف وزرعه الساكنون تحت ظله في وسط الامم"));
arasuperobject.biblemap.put("b26_31_18", new arasuperobject(26,31,18,"من اشبهت في المجد والعظمة هكذا بين اشجار عدن. ستحدر مع اشجار عدن الى الارض السفلى وتضطجع بين الغلف مع المقتولين بالسيف. هذا فرعون وكل جمهوره يقول السيد الرب"));
arasuperobject.biblemap.put("b26_32_1", new arasuperobject(26,32,1,"وكان في السنة الثانية عشرة في الشهر الثاني عشر في اول الشهر ان كلام الرب صار اليّ قائلا"));
arasuperobject.biblemap.put("b26_32_2", new arasuperobject(26,32,2,"يا ابن آدم ارفع مرثاة على فرعون ملك مصر وقل له. اشبهت شبل الامم وانت نظير تمساح في البحار. اندفقت بانهارك وكدرت الماء برجليك وعكّرت انهارهم"));
arasuperobject.biblemap.put("b26_32_3", new arasuperobject(26,32,3,"هكذا قال السيد الرب. اني ابسط عليك شبكتي مع جماعة شعوب كثيرة وهم يصعدونك في مجزفتي"));
arasuperobject.biblemap.put("b26_32_4", new arasuperobject(26,32,4,"واتركك على الارض واطرحك على وجه الحقل وأقرّ عليك كل طيور السماء واشبع منك وحوش الارض كلها"));
arasuperobject.biblemap.put("b26_32_5", new arasuperobject(26,32,5,"والقي لحمك على الجبال واملأ الاودية من جيفك"));
arasuperobject.biblemap.put("b26_32_6", new arasuperobject(26,32,6,"واسقي ارض فيضانك من دمك الى الجبال وتمتلئ منك الآفاق"));
arasuperobject.biblemap.put("b26_32_7", new arasuperobject(26,32,7,"وعند اطفائي اياك احجب السموات واظلم نجومها واغشي الشمس بسحاب والقمر لا يضيء ضؤه"));
arasuperobject.biblemap.put("b26_32_8", new arasuperobject(26,32,8,"واظلم فوقك كل انوار السماء المنيرة واجعل الظلمة على ارضك يقول السيد الرب"));
arasuperobject.biblemap.put("b26_32_9", new arasuperobject(26,32,9,"واغم قلوب شعوب كثيرين عند اتياني بكسرك بين الامم في اراض لم تعرفها"));
arasuperobject.biblemap.put("b26_32_10", new arasuperobject(26,32,10,"واحيّر منك شعوبا كثيرين ملوكهم يقشعرّون عليك اقشعرارا عندما اخطر بسيفي قدام وجوههم فيرجفون كل لحظة كل واحد على نفسه في يوم سقوطك"));
arasuperobject.biblemap.put("b26_32_11", new arasuperobject(26,32,11,"لانه هكذا قال السيد الرب. سيف ملك بابل ياتي عليك"));
arasuperobject.biblemap.put("b26_32_12", new arasuperobject(26,32,12,"بسيوف الجبابرة أسقط جمهورك. كلهم عتاة الامم فيسلبون كبرياء مصر ويهلك كل جمهورها"));
arasuperobject.biblemap.put("b26_32_13", new arasuperobject(26,32,13,"وابيد جميع بهائمها عن المياه الكثيرة فلا تكدرها من بعد رجل انسان ولا تعكرها اضلاف بهيمة"));
arasuperobject.biblemap.put("b26_32_14", new arasuperobject(26,32,14,"حينئذ انضب مياههم واجري انهارهم كالزيت يقول السيد الرب"));
arasuperobject.biblemap.put("b26_32_15", new arasuperobject(26,32,15,"حين اجعل ارض مصر خرابا وتخلو الارض من ملئها عند ضربي جميع سكانها يعلمون اني انا الرب"));
arasuperobject.biblemap.put("b26_32_16", new arasuperobject(26,32,16,"هذه مرثاة يرثون بها. بنات الامم ترثو بها. على مصر وعلى كل جمهورها ترثو بها يقول السيد الرب"));
arasuperobject.biblemap.put("b26_32_17", new arasuperobject(26,32,17,"وكان في السنة الثانية عشرة في الخامس عشر من الشهر ان كلام الرب كان اليّ قائلا"));
arasuperobject.biblemap.put("b26_32_18", new arasuperobject(26,32,18,"يا ابن آدم ولول على جمهور مصر واحدره هو وبنات الامم العظيمة الى الارض السفلى مع الهابطين في الجب"));
arasuperobject.biblemap.put("b26_32_19", new arasuperobject(26,32,19,"ممن نعمت اكثر. انزل واضطجع مع الغلف"));
arasuperobject.biblemap.put("b26_32_20", new arasuperobject(26,32,20,"يسقطون في وسط القتلى بالسيف. قد أسلم السيف. امسكوها مع كل جمهورها"));
arasuperobject.biblemap.put("b26_32_21", new arasuperobject(26,32,21,"يكلمه اقوياء الجبابرة من وسط الهاوية مع اعوانه. قد نزلوا اضطجعوا غلفا قتلى بالسيف"));
arasuperobject.biblemap.put("b26_32_22", new arasuperobject(26,32,22,"هناك اشور وكل جماعتها. قبوره من حوله. كلهم قتلى ساقطون بالسيف"));
arasuperobject.biblemap.put("b26_32_23", new arasuperobject(26,32,23,"الذين جعلت قبورهم في اسافل الجب وجماعتها حول قبرها كلهم قتلى ساقطون بالسيف الذين جعلوا رعبا في ارض الاحياء"));
arasuperobject.biblemap.put("b26_32_24", new arasuperobject(26,32,24,"هناك عيلام وكل جمهورها حول قبرها كلهم قتلى ساقطون بالسيف الذين هبطوا غلفا الى الارض السفلى الذين جعلوا رعبهم في ارض الاحياء. فحملوا خزيهم مع الهابطين في الجب"));
arasuperobject.biblemap.put("b26_32_25", new arasuperobject(26,32,25,"قد جعلوا لها مضجعا بين القتلى مع كل جمهورها. حوله قبورهم كلهم غلف قتلى بالسيف مع انه قد جعل رعبهم في ارض الاحياء قد حملوا خزيهم مع الهابطين في الجب. قد جعل في وسط القتلى"));
arasuperobject.biblemap.put("b26_32_26", new arasuperobject(26,32,26,"هناك ماشك وتوبال وكل جمهورها حوله قبورها. كلهم غلف قتلى بالسيف مع انهم جعلوا رعبهم في ارض الاحياء"));
arasuperobject.biblemap.put("b26_32_27", new arasuperobject(26,32,27,"ولا يضطجعون مع الجبابرة الساقطين من الغلف النازلين الى الهاوية بادواة حربهم وقد وضعت سيوفهم تحت رؤوسهم فتكون آثامهم على عظامهم مع انهم رعب الجبابرة في ارض الاحياء"));
arasuperobject.biblemap.put("b26_32_28", new arasuperobject(26,32,28,"اما انت ففي وسط الغلف تنكسر وتضطجع مع القتلى بالسيف"));
arasuperobject.biblemap.put("b26_32_29", new arasuperobject(26,32,29,"هناك ادوم وملوكها وكل رؤسائها الذين مع جبروتهم قد ألقوا مع القتلى بالسيف فيضطجعون مع الغلف ومع الهابطين في الجب"));
arasuperobject.biblemap.put("b26_32_30", new arasuperobject(26,32,30,"هناك امراء الشمال كلهم وجميع الصيدونيين الهابطين مع القتلى برعبهم خزوا من جبروتهم واضطجعوا غلفا مع قتلى السيف وحملوا خزيهم مع الهابطين الى الجب"));
arasuperobject.biblemap.put("b26_32_31", new arasuperobject(26,32,31,"يراهم فرعون ويتعزى عن كل جمهوره. قتلى بالسيف فرعون وكل جمهوره يقول السيد الرب"));
arasuperobject.biblemap.put("b26_32_32", new arasuperobject(26,32,32,"لاني جعلت رعبه في ارض الاحياء فيضجع بين الغلف مع قتلى السيف فرعون وكل جمهوره يقول السيد الرب"));
arasuperobject.biblemap.put("b26_33_1", new arasuperobject(26,33,1,"وكان اليّ كلام الرب قائلا"));
arasuperobject.biblemap.put("b26_33_2", new arasuperobject(26,33,2,"يا ابن آدم كلم بني شعبك وقل لهم. اذا جلبت السيف على ارض فان اخذ شعب الارض رجلا من بينهم وجعلوه رقيبا لهم"));
arasuperobject.biblemap.put("b26_33_3", new arasuperobject(26,33,3,"فاذا رأى السيف مقبلا على الارض نفخ في البوق وحذّر الشعب"));
arasuperobject.biblemap.put("b26_33_4", new arasuperobject(26,33,4,"وسمع السامع صوت البوق ولم يتحذّر فجاء السيف واخذه فدمه يكون على راسه"));
arasuperobject.biblemap.put("b26_33_5", new arasuperobject(26,33,5,"سمع صوت البوق ولم يتحذّر فدمه يكون على نفسه. لو تحذّر لخلّص نفسه"));
arasuperobject.biblemap.put("b26_33_6", new arasuperobject(26,33,6,"فان رأى الرقيب السيف مقبلا ولم ينفخ في البوق ولم يتحذّر الشعب فجاء السيف واخذ نفسا منهم فهو قد أخذ بذنبه اما دمه فمن يد الرقيب اطلبه"));
arasuperobject.biblemap.put("b26_33_7", new arasuperobject(26,33,7,"وانت يا ابن آدم فقد جعلتك رقيبا لبيت اسرائيل فتسمع الكلام من فمي وتحذّرهم من قبلي"));
arasuperobject.biblemap.put("b26_33_8", new arasuperobject(26,33,8,"اذا قلت للشرير يا شرير موتا تموت. فان لم تتكلم لتحذّر الشرير من طريقه فذلك الشرير يموت بذنبه. اما دمه فمن يدك اطلبه"));
arasuperobject.biblemap.put("b26_33_9", new arasuperobject(26,33,9,"وان حذّرت الشرير من طريقه ليرجع عنه ولم يرجع عن طريقه فهو يموت بذنبه. اما انت فقد خلصت نفسك"));
arasuperobject.biblemap.put("b26_33_10", new arasuperobject(26,33,10,"وانت يا ابن آدم فكلم بيت اسرائيل وقل. انتم تتكلمون هكذا قائلين. ان معاصينا وخطايانا علينا وبها نحن فانون فكيف نحيا"));
arasuperobject.biblemap.put("b26_33_11", new arasuperobject(26,33,11,"قل لهم. حيّ انا يقول السيد الرب اني لا اسر بموت الشرير بل بان يرجع الشرير عن طريقه ويحيا. ارجعوا ارجعوا عن طرقكم الرديئة. فلماذا تموتون يا بيت اسرائيل"));
arasuperobject.biblemap.put("b26_33_12", new arasuperobject(26,33,12,"وانت يا ابن آدم فقل لبني شعبك. ان بر البار لا ينجيه في يوم معصيته والشرير لا يعثر بشره في يوم رجوعه عن شره ولا يستطيع البار ان يحيا ببره في يوم خطيئته"));
arasuperobject.biblemap.put("b26_33_13", new arasuperobject(26,33,13,"اذا قلت للبار حياة تحيا. فاتكل هو على بره وأثم فبره كله لا يذكر بل باثمه الذي فعله يموت"));
arasuperobject.biblemap.put("b26_33_14", new arasuperobject(26,33,14,"واذا قلت للشرير موتا تموت. فان رجع عن خطيته وعمل بالعدل والحق"));
arasuperobject.biblemap.put("b26_33_15", new arasuperobject(26,33,15,"ان رد الشرير الرهن وعوّض عن المغتصب وسلك في فرائض الحياة بلا عمل اثم فانه حياة يحيا. لا يموت"));
arasuperobject.biblemap.put("b26_33_16", new arasuperobject(26,33,16,"كل خطيته التي اخطأ بها لا تذكر عليه. عمل بالعدل والحق فيحيا حياة"));
arasuperobject.biblemap.put("b26_33_17", new arasuperobject(26,33,17,"وابناء شعبك يقولون ليست طريق الرب مستوية. بل هم طريقهم غير مستوية"));
arasuperobject.biblemap.put("b26_33_18", new arasuperobject(26,33,18,"عند رجوع البار عن بره وعند عمله اثما فانه يموت به"));
arasuperobject.biblemap.put("b26_33_19", new arasuperobject(26,33,19,"وعند رجوع الشرير عن شره وعند عمله بالعدل والحق فانه يحيا بهما"));
arasuperobject.biblemap.put("b26_33_20", new arasuperobject(26,33,20,"وانتم تقولون ان طريق الرب غير مستوية. اني احكم على كل واحد منكم كطرقه يا بيت اسرائيل"));
arasuperobject.biblemap.put("b26_33_21", new arasuperobject(26,33,21,"وكان في السنة الثانية عشرة من سبينا في الشهر العاشر في الخامس من الشهر انه جاء اليّ منفلت من اورشليم فقال قد ضربت المدينة"));
arasuperobject.biblemap.put("b26_33_22", new arasuperobject(26,33,22,"وكانت يد الرب عليّ مساء قبل مجيء المنفلت وفتحت فمي حتى جاء اليّ صباحا فانفتح فمي ولم اكن بعد ابكم"));
arasuperobject.biblemap.put("b26_33_23", new arasuperobject(26,33,23,"فكان اليّ كلام الرب قائلا"));
arasuperobject.biblemap.put("b26_33_24", new arasuperobject(26,33,24,"يا ابن آدم ان الساكنين في هذه الخرب في ارض اسرائيل يتكلمون قائلين ان ابراهيم كان واحدا وقد ورث الارض. ونحن كثيرون. لنا أعطيت الارض ميراثا"));
arasuperobject.biblemap.put("b26_33_25", new arasuperobject(26,33,25,"لذلك قل لهم. هكذا قال السيد الرب. تأكلون بالدم وترفعون اعينكم الى اصنامكم وتسفكون الدم. أفترثون الارض"));
arasuperobject.biblemap.put("b26_33_26", new arasuperobject(26,33,26,"وقفتم على سيفكم. فعلتم الرجس وكل منكم نجس امرأة صاحبه. أفترثون الارض"));
arasuperobject.biblemap.put("b26_33_27", new arasuperobject(26,33,27,"قل لهم. هكذا قال السيد الرب. حيّ انا ان الذين في الخرب يسقطون بالسيف والذي هو على وجه الحقل ابذله للوحش مأكلا والذين في الحصون وفي المغاير يموتون بالوبأ"));
arasuperobject.biblemap.put("b26_33_28", new arasuperobject(26,33,28,"فاجعل الارض خربة مقفرة وتبطل كبرياء عزتها وتخرب جبال اسرائيل بلا عابر"));
arasuperobject.biblemap.put("b26_33_29", new arasuperobject(26,33,29,"فيعلمون اني انا الرب حين اجعل الارض خربة مقفرة على كل رجاساتهم التي فعلوها"));
arasuperobject.biblemap.put("b26_33_30", new arasuperobject(26,33,30,"وانت يا ابن آدم فان بني شعبك يتكلمون عليك بجانب الجدران وفي ابواب البيوت ويتكلم الواحد مع الآخر الرجل مع اخيه قائلين هلم اسمعوا ما هو الكلام الخارج من عند الرب"));
arasuperobject.biblemap.put("b26_33_31", new arasuperobject(26,33,31,"وياتون اليك كما ياتي الشعب ويجلسون امامك كشعبي ويسمعون كلامك ولا يعملون به لانهم بافواههم يظهرون اشواقا وقلبهم ذاهب وراء كسبهم"));
arasuperobject.biblemap.put("b26_33_32", new arasuperobject(26,33,32,"وها انت لهم كشعر اشواق لجميل الصوت يحسن العزف فيسمعون كلامك ولا يعملون به"));
arasuperobject.biblemap.put("b26_33_33", new arasuperobject(26,33,33,"واذا جاء هذا. لانه ياتي. فيعلمون ان نبيا كان في وسطهم"));
arasuperobject.biblemap.put("b26_34_1", new arasuperobject(26,34,1,"وكان اليّ كلام الرب قائلا"));
arasuperobject.biblemap.put("b26_34_2", new arasuperobject(26,34,2,"يا ابن آدم تنبأ على رعاة اسرائيل تنبأ وقل لهم. هكذا قال السيد الرب للرعاة. ويل لرعاة اسرائيل الذين كانوا يرعون انفسهم. ألا يرعى الرعاة الغنم"));
arasuperobject.biblemap.put("b26_34_3", new arasuperobject(26,34,3,"تاكلون الشحم وتلبسون الصوف وتذبحون السمين ولا ترعون الغنم"));
arasuperobject.biblemap.put("b26_34_4", new arasuperobject(26,34,4,"المريض لم تقووه والمجروح لم تعصبوه والمكسور لم تجبروه والمطرود لم تستردوه والضال لم تطلبوه بل بشدة وبعنف تسلطتم عليهم"));
arasuperobject.biblemap.put("b26_34_5", new arasuperobject(26,34,5,"فتشتّتت بلا راعي وصارت مأكلا لجميع وحوش الحقل وتشتّتت"));
arasuperobject.biblemap.put("b26_34_6", new arasuperobject(26,34,6,"ضلّت غنمي في كل الجبال وعلى كل تل عال. وعلى كل وجه الارض تشتّتت غنمي ولم يكن من يسأل او يفتش"));
arasuperobject.biblemap.put("b26_34_7", new arasuperobject(26,34,7,"فلذلك ايها الرعاة اسمعوا كلام الرب"));
arasuperobject.biblemap.put("b26_34_8", new arasuperobject(26,34,8,"حيّ انا يقول السيد الرب من حيث ان غنمي صارت غنيمة وصارت غنمي مأكلا لكل وحش الحقل اذ لم يكن راع ولا سأل رعاتي عن غنمي ورعى الرعاة انفسهم ولم يرعوا غنمي"));
arasuperobject.biblemap.put("b26_34_9", new arasuperobject(26,34,9,"فلذلك ايها الرعاة اسمعوا كلام الرب"));
arasuperobject.biblemap.put("b26_34_10", new arasuperobject(26,34,10,"هكذا قال السيد الرب هانذا على الرعاة واطلب غنمي من يدهم واكفهم عن رعي الغنم ولا يرعى الرعاة انفسهم بعد فاخلص غنمي من افواههم فلا تكون لهم مأكلا"));
arasuperobject.biblemap.put("b26_34_11", new arasuperobject(26,34,11,"لانه هكذا قال السيد الرب. هانذا اسأل عن غنمي وافتقدها"));
arasuperobject.biblemap.put("b26_34_12", new arasuperobject(26,34,12,"كما يفتقد الراعي قطيعه يوم يكون في وسط غنمه المشتّتة هكذا افتقد غنمي واخلصها من جميع الاماكن التي تشتّتت اليها في يوم الغيم والضباب"));
arasuperobject.biblemap.put("b26_34_13", new arasuperobject(26,34,13,"واخرجها من الشعوب واجمعها من الاراضي وآتي بها الى ارضها وارعاها على جبال اسرائيل وفي الاودية وفي جميع مساكن الارض"));
arasuperobject.biblemap.put("b26_34_14", new arasuperobject(26,34,14,"ارعاها في مرعى جيد ويكون مراحها على جبال اسرائيل العالية هنالك تربض في مراح حسن وفي مرعى دسم يرعون على جبال اسرائيل"));
arasuperobject.biblemap.put("b26_34_15", new arasuperobject(26,34,15,"انا ارعى غنمي واربضها يقول السيد الرب"));
arasuperobject.biblemap.put("b26_34_16", new arasuperobject(26,34,16,"واطلب الضال واسترد المطرود واجبر الكسير واعصب الجريح وابيد السمين والقوي وارعاها بعدل"));
arasuperobject.biblemap.put("b26_34_17", new arasuperobject(26,34,17,"وانتم يا غنمي فهكذا قال السيد الرب. هانذا احكم بين شاة وشاة. بين كباش وتيوس"));
arasuperobject.biblemap.put("b26_34_18", new arasuperobject(26,34,18,"أهو صغير عندكم ان ترعوا المرعى الجيد وبقية مراعيكم تدوسونها بارجلكم وان تشربوا من المياه العميقة والبقية تكدرونها باقدامكم"));
arasuperobject.biblemap.put("b26_34_19", new arasuperobject(26,34,19,"وغنمي ترعى من دوس اقدامكم وتشرب من كدر ارجلكم"));
arasuperobject.biblemap.put("b26_34_20", new arasuperobject(26,34,20,"لذلك هكذا قال السيد الرب لهم. هانذا احكم بين الشاة السمينة والشاة المهزولة"));
arasuperobject.biblemap.put("b26_34_21", new arasuperobject(26,34,21,"لانكم بهزتم بالجنب والكتف ونطحتم المريضة بقرونكم حتى شتّتموها الى خارج"));
arasuperobject.biblemap.put("b26_34_22", new arasuperobject(26,34,22,"فاخلّص غنمي فلا تكون من بعد غنيمة واحكم بين شاة وشاة"));
arasuperobject.biblemap.put("b26_34_23", new arasuperobject(26,34,23,"واقيم عليها راعيا واحدا فيرعاها عبدي داود هو يرعاها وهو يكون لها راعيا"));
arasuperobject.biblemap.put("b26_34_24", new arasuperobject(26,34,24,"وانا الرب اكون لهم الها وعبدي داود رئيسا في وسطهم. انا الرب تكلمت"));
arasuperobject.biblemap.put("b26_34_25", new arasuperobject(26,34,25,"واقطع معهم عهد سلام وانزع الوحوش الرديئة من الارض فيسكنون في البرية مطمئنين وينامون في الوعور"));
arasuperobject.biblemap.put("b26_34_26", new arasuperobject(26,34,26,"واجعلهم وما حول اكمتي بركة وانزل عليهم المطر في وقته فتكون امطار بركة"));
arasuperobject.biblemap.put("b26_34_27", new arasuperobject(26,34,27,"وتعطي شجرة الحقل ثمرتها وتعطي الارض غلتها ويكونون آمنين في ارضهم ويعلمون اني انا الرب عند تكسيري ربط نيرهم واذا انقذتهم من يد الذين استعبدوهم"));
arasuperobject.biblemap.put("b26_34_28", new arasuperobject(26,34,28,"فلا يكونون بعد غنيمة للامم ولا ياكلهم وحش الارض بل يسكنون آمنين ولا مخيف"));
arasuperobject.biblemap.put("b26_34_29", new arasuperobject(26,34,29,"واقيم لهم غرسا لصيت فلا يكونون بعد منفيي الجوع في الارض ولا يحملون بعد تعيير الامم"));
arasuperobject.biblemap.put("b26_34_30", new arasuperobject(26,34,30,"فيعلمون اني انا الرب الههم معهم وهم شعبي بيت اسرائيل يقول السيد الرب"));
arasuperobject.biblemap.put("b26_34_31", new arasuperobject(26,34,31,"وانتم يا غنمي غنم مرعاي اناس انتم. انا الهكم يقول السيد الرب"));
arasuperobject.biblemap.put("b26_35_1", new arasuperobject(26,35,1,"وكان اليّ كلام الرب قائلا"));
arasuperobject.biblemap.put("b26_35_2", new arasuperobject(26,35,2,"يا ابن آدم اجعل وجهك نحو جبل سعير وتنبأ عليه"));
arasuperobject.biblemap.put("b26_35_3", new arasuperobject(26,35,3,"وقل له. هكذا قال السيد الرب. هانذا عليك يا جبل سعير وامد يدي عليك واجعلك خرابا مقفرا"));
arasuperobject.biblemap.put("b26_35_4", new arasuperobject(26,35,4,"اجعل مدنك خربة وتكون انت مقفرا وتعلم اني انا الرب"));
arasuperobject.biblemap.put("b26_35_5", new arasuperobject(26,35,5,"لانه كانت لك بغضة ابدية ودفعت بني اسرائيل الى يد السيف في وقت مصيبتهم وقت اثم النهاية"));
arasuperobject.biblemap.put("b26_35_6", new arasuperobject(26,35,6,"لذلك حيّ انا يقول السيد الرب اني اهيّئك للدم والدم يتبعك. اذ لم تكره الدم فالدم يتبعك"));
arasuperobject.biblemap.put("b26_35_7", new arasuperobject(26,35,7,"فاجعل جبل سعير خرابا ومقفرا واستأصل منه الذاهب والآئب"));
arasuperobject.biblemap.put("b26_35_8", new arasuperobject(26,35,8,"واملأ جباله من قتلاه. تلالك واوديتك وجميع انهارك يسقطون فيها قتلى بالسيف"));
arasuperobject.biblemap.put("b26_35_9", new arasuperobject(26,35,9,"واصيّرك خربا ابدية ومدنك لن تعود فتعلمون اني انا الرب"));
arasuperobject.biblemap.put("b26_35_10", new arasuperobject(26,35,10,"لانك قلت ان هاتين الامتين وهاتين الارضين تكونان لي فنمتلكهما والرب كان هناك"));
arasuperobject.biblemap.put("b26_35_11", new arasuperobject(26,35,11,"فلذلك حيّ انا يقول السيد الرب لافعلنّ كغضبك وكحسدك اللذين عاملت بهما من بغضتك لهم واعرّف بنفسي بينهم عندما احكم عليك"));
arasuperobject.biblemap.put("b26_35_12", new arasuperobject(26,35,12,"فتعلم اني انا الرب قد سمعت كل اهانتك التي تكلمت بها على جبال اسرائيل قائلا قد خربت قد أعطيناها ماكلا"));
arasuperobject.biblemap.put("b26_35_13", new arasuperobject(26,35,13,"قد تعظمتم عليّ بافواهكم وكثرتم كلامكم عليّ. انا سمعت"));
arasuperobject.biblemap.put("b26_35_14", new arasuperobject(26,35,14,"هكذا قال السيد الرب. عند فرح كل الارض اجعلك مقفرا"));
arasuperobject.biblemap.put("b26_35_15", new arasuperobject(26,35,15,"كما فرحت على ميراث بيت اسرائيل لانه خرب كذلك افعل بك. تكون خرابا يا جبل سعير انت وكل ادوم باجمعها فيعلمون اني انا الرب"));
arasuperobject.biblemap.put("b26_36_1", new arasuperobject(26,36,1,"وانت يا ابن آدم فتنبأ لجبال اسرائيل وقل. يا جبال اسرائيل اسمعي كلمة الرب"));
arasuperobject.biblemap.put("b26_36_2", new arasuperobject(26,36,2,"هكذا قال السيد الرب. من اجل ان العدو قال عليكم هه. ان المرتفعات القديمة صارت لنا ميراثا"));
arasuperobject.biblemap.put("b26_36_3", new arasuperobject(26,36,3,"فلذلك تنبأ وقل. هكذا قال السيد الرب. من اجل انهم قد اخربوكم وتهمّموكم من كل جانب لتكونوا ميراثا لبقية الامم وأصعدتم على شفاه اللسان وصرتم مذمة الشعب"));
arasuperobject.biblemap.put("b26_36_4", new arasuperobject(26,36,4,"لذلك فاسمعي يا جبال اسرائيل كلمة السيد الرب. هكذا قال السيد الرب للجبال وللآكام وللانهار وللاودية وللخرب المقفرة وللمدن المهجورة التي صارت للنهب والاستهزاء لبقية الامم الذين حولها"));
arasuperobject.biblemap.put("b26_36_5", new arasuperobject(26,36,5,"من اجل ذلك هكذا قال السيد الرب. اني في نار غيرتي تكلمت على بقية الامم وعلى ادوم كلها الذين جعلوا ارضي ميراثا لهم بفرح كل القلب وبغضة نفس لنهبها غنيمة"));
arasuperobject.biblemap.put("b26_36_6", new arasuperobject(26,36,6,"فتنبأ على ارض اسرائيل وقل للجبال وللتلال وللانهار وللاودية هكذا قال السيد الرب. هانذا في غيرتي وفي غضبي تكلمت من اجل انكم حملتم تعيير الامم"));
arasuperobject.biblemap.put("b26_36_7", new arasuperobject(26,36,7,"لذلك هكذا قال السيد الرب. اني رفعت يدي فالامم الذين حولكم هم يحملون تعييرهم"));
arasuperobject.biblemap.put("b26_36_8", new arasuperobject(26,36,8,"اما انتم يا جبال اسرائيل فانكم تنبتون فروعكم وتثمرون ثمركم لشعبي اسرائيل لانه قريب الاتيان"));
arasuperobject.biblemap.put("b26_36_9", new arasuperobject(26,36,9,"لاني انا لكم والتفت اليكم فتحرثون وتزرعون"));
arasuperobject.biblemap.put("b26_36_10", new arasuperobject(26,36,10,"واكثر الناس عليكم كل بيت اسرائيل باجمعه فتعمر المدن وتبنى الخرب"));
arasuperobject.biblemap.put("b26_36_11", new arasuperobject(26,36,11,"واكثر عليكم الانسان والبهيمة فيكثرون ويثمرون واسكنكم حسب حالتكم القديمة واحسن اليكم اكثر مما في اوائلكم فتعلمون اني انا الرب"));
arasuperobject.biblemap.put("b26_36_12", new arasuperobject(26,36,12,"وامشي الناس عليكم شعبي اسرائيل فيرثونك فتكون لهم ميراثا ولا تعود بعد تثكلهم"));
arasuperobject.biblemap.put("b26_36_13", new arasuperobject(26,36,13,"هكذا قال السيد الرب من اجل انهم قالوا لكم انت اكّالة الناس ومثكلة شعوبك"));
arasuperobject.biblemap.put("b26_36_14", new arasuperobject(26,36,14,"لذلك لن تأكلي الناس بعد ولا تثكلي شعوبك بعد يقول السيد الرب"));
arasuperobject.biblemap.put("b26_36_15", new arasuperobject(26,36,15,"ولا اسمّع فيك من بعد تعيير الامم ولا تحملين تعيير الشعوب بعد ولا تعثرين شعوبك بعد يقول السيد الرب"));
arasuperobject.biblemap.put("b26_36_16", new arasuperobject(26,36,16,"وكان اليّ كلام الرب قائلا"));
arasuperobject.biblemap.put("b26_36_17", new arasuperobject(26,36,17,"يا ابن آدم ان بيت اسرائيل لما سكنوا ارضهم نجسوها بطريقهم وبافعالهم. كانت طريقهم امامي كنجاسة الطامث"));
arasuperobject.biblemap.put("b26_36_18", new arasuperobject(26,36,18,"فسكبت غضبي عليهم لاجل الدم الذي سفكوه على الارض وباصنامهم نجسوها"));
arasuperobject.biblemap.put("b26_36_19", new arasuperobject(26,36,19,"فبددتهم في الامم فتذروا في الاراضي. كطريقهم وكافعالهم دنتهم"));
arasuperobject.biblemap.put("b26_36_20", new arasuperobject(26,36,20,"فلما جاءوا الى الامم حيث جاءوا نجسوا اسمي القدوس اذ قالوا لهم هؤلاء شعب الرب وقد خرجوا من ارضه"));
arasuperobject.biblemap.put("b26_36_21", new arasuperobject(26,36,21,"فتحننت على اسمي القدوس الذي نجسه بيت اسرائيل في الامم حيث جاءوا"));
arasuperobject.biblemap.put("b26_36_22", new arasuperobject(26,36,22,"لذلك فقل لبيت اسرائيل. هكذا قال السيد الرب. ليس لاجلكم انا صانع يا بيت اسرائيل بل لاجل اسمي القدوس الذي نجستموه في الامم حيث جئتم"));
arasuperobject.biblemap.put("b26_36_23", new arasuperobject(26,36,23,"فاقدس اسمي العظيم المنجس في الامم الذي نجستموه في وسطهم فتعلم كل الامم اني انا الرب يقول السيد الرب حين اتقدس فيكم قدام اعينهم"));
arasuperobject.biblemap.put("b26_36_24", new arasuperobject(26,36,24,"وآخذكم من بين الامم واجمعكم من جميع الاراضي وآتي بكم الى ارضكم"));
arasuperobject.biblemap.put("b26_36_25", new arasuperobject(26,36,25,"وارش عليكم ماء طاهرا فتطهرون من كل نجاستكم ومن كل اصنامكم اطهركم"));
arasuperobject.biblemap.put("b26_36_26", new arasuperobject(26,36,26,"واعطيكم قلبا جديدا واجعل روحا جديدة في داخلكم وانزع قلب الحجر من لحمكم واعطيكم قلب لحم"));
arasuperobject.biblemap.put("b26_36_27", new arasuperobject(26,36,27,"واجعل روحي في داخلكم واجعلكم تسلكون في فرائضي وتحفظون احكامي وتعملون بها"));
arasuperobject.biblemap.put("b26_36_28", new arasuperobject(26,36,28,"وتسكنون الارض التي اعطيت آباءكم ايّاها وتكونون لي شعبا وانا اكون لكم الها"));
arasuperobject.biblemap.put("b26_36_29", new arasuperobject(26,36,29,"واخلّصكم من كل نجاساتكم وادعو الحنطة واكثرها ولا اضع عليكم جوعا"));
arasuperobject.biblemap.put("b26_36_30", new arasuperobject(26,36,30,"واكثر ثمر الشجر وغلة الحقل لكيلا تنالوا بعد عار الجوع بين الامم"));
arasuperobject.biblemap.put("b26_36_31", new arasuperobject(26,36,31,"فتذكرون طرقكم الرديئة واعمالكم غير الصالحة وتمقتون انفسكم امام وجوهكم من اجل آثامكم وعلى رجاساتكم"));
arasuperobject.biblemap.put("b26_36_32", new arasuperobject(26,36,32,"لا من اجلكم انا صانع يقول السيد الرب فليكن معلوما لكم. فاخجلوا واخزوا من طرقكم يا بيت اسرائيل"));
arasuperobject.biblemap.put("b26_36_33", new arasuperobject(26,36,33,"هكذا قال السيد الرب في يوم تطهيري اياكم من كل آثامكم اسكنكم في المدن فتبنى الخرب"));
arasuperobject.biblemap.put("b26_36_34", new arasuperobject(26,36,34,"وتفلح الارض الخربة عوضا عن كونها خربة امام عيني كل عابر"));
arasuperobject.biblemap.put("b26_36_35", new arasuperobject(26,36,35,"فيقولون هذه الارض الخربة صارت كجنة عدن والمدن الخربة والمقفرة والمنهدمة محصنة معمورة"));
arasuperobject.biblemap.put("b26_36_36", new arasuperobject(26,36,36,"فتعلم الامم الذين تركوا حولكم اني انا الرب بنيت المنهدمة وغرست المقفرة. انا الرب تكلمت وسافعل ً"));
arasuperobject.biblemap.put("b26_36_37", new arasuperobject(26,36,37,"هكذا قال السيد الرب. بعد هذه اطلب من بيت اسرائيل لافعل لهم. اكثرهم كغنم اناس"));
arasuperobject.biblemap.put("b26_36_38", new arasuperobject(26,36,38,"كغنم مقدس كغنم اورشليم في مواسمها فتكون المدن الخربة ملآنة غنم اناس فيعلمون اني انا الرب"));
arasuperobject.biblemap.put("b26_37_1", new arasuperobject(26,37,1,"كانت عليّ يد الرب فاخرجني بروح الرب وانزلني في وسط البقعة وهي ملآنة عظاما"));
arasuperobject.biblemap.put("b26_37_2", new arasuperobject(26,37,2,"وأمرّني عليها من حولها واذا هي كثيرة جدا على وجه البقعة واذا هي يابسة جدا"));
arasuperobject.biblemap.put("b26_37_3", new arasuperobject(26,37,3,"فقال لي يا ابن آدم أتحيا هذه العظام. فقلت يا سيد الرب انت تعلم"));
arasuperobject.biblemap.put("b26_37_4", new arasuperobject(26,37,4,"فقال لي تنبأ على هذه العظام وقل لها. ايتها العظام اليابسة اسمعي كلمة الرب"));
arasuperobject.biblemap.put("b26_37_5", new arasuperobject(26,37,5,"هكذا قال السيد الرب لهذه العظام. هانذا أدخل فيكم روحا فتحيون"));
arasuperobject.biblemap.put("b26_37_6", new arasuperobject(26,37,6,"واضع عليكم عصبا واكسيكم لحما وابسط عليكم جلدا واجعل فيكم روحا فتحيون وتعلمون اني انا الرب"));
arasuperobject.biblemap.put("b26_37_7", new arasuperobject(26,37,7,"فتنبأت كما أمرت وبينما انا اتنبأ كان صوت واذا رعش فتقاربت العظام كل عظم الى عظمه"));
arasuperobject.biblemap.put("b26_37_8", new arasuperobject(26,37,8,"ونظرت واذا بالعصب واللحم كساها وبسط الجلد عليها من فوق وليس فيها روح"));
arasuperobject.biblemap.put("b26_37_9", new arasuperobject(26,37,9,"فقال لي تنبأ للروح تنبأ يا ابن آدم وقل للروح هكذا قال السيد الرب هلم يا روح من الرياح الاربع وهبّ على هؤلاء القتلى ليحيوا‎"));
arasuperobject.biblemap.put("b26_37_10", new arasuperobject(26,37,10,"فتنبأت كما امرني فدخل فيهم الروح فحيوا وقاموا على اقدامهم جيش عظيم جدا جدا"));
arasuperobject.biblemap.put("b26_37_11", new arasuperobject(26,37,11,"ثم قال لي يا بن آدم هذه العظام هي كل بيت اسرائيل. ها هم يقولون يبست عظامنا وهلك رجاؤنا. قد انقطعنا"));
arasuperobject.biblemap.put("b26_37_12", new arasuperobject(26,37,12,"لذلك تنبأ وقل لهم. هكذا قال السيد الرب. هانذا افتح قبوركم واصعدكم من قبوركم يا شعبي وآتي بكم الى ارض اسرائيل"));
arasuperobject.biblemap.put("b26_37_13", new arasuperobject(26,37,13,"فتعلمون اني انا الرب عند فتحي قبوركم واصعادي ايّاكم من قبوركم يا شعبي"));
arasuperobject.biblemap.put("b26_37_14", new arasuperobject(26,37,14,"واجعل روحي فيكم فتحيون واجعلكم في ارضكم فتعلمون اني انا الرب تكلمت وافعل يقول الرب"));
arasuperobject.biblemap.put("b26_37_15", new arasuperobject(26,37,15,"وكان اليّ كلام الرب قائلا"));
arasuperobject.biblemap.put("b26_37_16", new arasuperobject(26,37,16,"وانت يا ابن آدم خذ لنفسك عصا واحدة واكتب عليها ليهوذا ولبني اسرائيل رفقائه. وخذ عصا اخرى واكتب عليها ليوسف عصا افرايم وكل بيت اسرائيل رفقائه"));
arasuperobject.biblemap.put("b26_37_17", new arasuperobject(26,37,17,"واقرنهما الواحدة بالاخرى كعصا واحدة فتصيرا واحدة في يدك"));
arasuperobject.biblemap.put("b26_37_18", new arasuperobject(26,37,18,"فاذا كلمك ابناء شعبك قائلين أما تخبرنا ما لك وهذا"));
arasuperobject.biblemap.put("b26_37_19", new arasuperobject(26,37,19,"فقل لهم. هكذا قال السيد الرب. هانذا آخذ عصا يوسف التي في يد افرايم واسباط اسرائيل رفقاءه واضم اليها عصا يهوذا واجعلهم عصا واحدة فيصيرون واحدة في يدي"));
arasuperobject.biblemap.put("b26_37_20", new arasuperobject(26,37,20,"وتكون العصوان اللتان كتبت عليهما في يدك امام اعينهم"));
arasuperobject.biblemap.put("b26_37_21", new arasuperobject(26,37,21,"وقل لهم. هكذا قال السيد الرب. هانذا آخذ بني اسرائيل من بين الامم التي ذهبوا اليها واجمعهم من كل ناحية وآتي بهم الى ارضهم"));
arasuperobject.biblemap.put("b26_37_22", new arasuperobject(26,37,22,"واصيّرهم امة واحدة في الارض على جبال اسرائيل وملك واحد يكون ملكا عليهم كلهم ولا يكونون بعد امتين ولا ينقسمون بعد الى مملكتين"));
arasuperobject.biblemap.put("b26_37_23", new arasuperobject(26,37,23,"ولا يتنجسون بعد باصنامهم ولا برجاساتهم ولا بشيء من معاصيهم بل اخلصهم من كل مساكنهم التي فيها اخطأوا واطهّرهم فيكونون لي شعبا وانا اكون لهم الها"));
arasuperobject.biblemap.put("b26_37_24", new arasuperobject(26,37,24,"وداود عبدي يكون ملكا عليهم ويكون لجميعهم راع واحد فيسلكون في احكامي ويحفظون فرائضي ويعملون بها"));
arasuperobject.biblemap.put("b26_37_25", new arasuperobject(26,37,25,"ويسكنون في الارض التي اعطيت عبدي يعقوب اياها التي سكنها آباؤكم ويسكنون فيها هم وبنوهم وبنو بنيهم الى الابد وعبدي داود رئيس عليهم الى الابد"));
arasuperobject.biblemap.put("b26_37_26", new arasuperobject(26,37,26,"واقطع معهم عهد سلام فيكون معهم عهدا مؤبدا واقرّهم واكثرهم واجعل مقدسي في وسطهم الى الابد"));
arasuperobject.biblemap.put("b26_37_27", new arasuperobject(26,37,27,"ويكون مسكني فوقهم واكون لهم الها ويكونون لي شعبا"));
arasuperobject.biblemap.put("b26_37_28", new arasuperobject(26,37,28,"فتعلم الامم اني انا الرب مقدس اسرائيل اذ يكون مقدسي في وسطهم الى الابد"));
arasuperobject.biblemap.put("b26_38_1", new arasuperobject(26,38,1,"وكان اليّ كلام الرب قائلا"));
arasuperobject.biblemap.put("b26_38_2", new arasuperobject(26,38,2,"يا ابن آدم اجعل وجهك على جوج ارض ما جوج رئيس روش ماشك وتوبال وتنبأ عليه"));
arasuperobject.biblemap.put("b26_38_3", new arasuperobject(26,38,3,"وقل. هكذا قال السيد الرب. هانذا عليك يا جوج رئيس روش ماشك وتوبال"));
arasuperobject.biblemap.put("b26_38_4", new arasuperobject(26,38,4,"وارجعك واضع شكائم في فكيك واخرجك انت وكل جيشك خيلا وفرسانا كلهم لابسين افخر لباس جماعة عظيمة مع اتراس ومجان كلهم ممسكين السيوف"));
arasuperobject.biblemap.put("b26_38_5", new arasuperobject(26,38,5,"فارس وكوش وفوط معهم كلهم بمجن وخوذة"));
arasuperobject.biblemap.put("b26_38_6", new arasuperobject(26,38,6,"وجومر وكل جيوشه وبيت توجرمة من اقاصي الشمال مع كل جيشه شعوبا كثيرين معك"));
arasuperobject.biblemap.put("b26_38_7", new arasuperobject(26,38,7,"استعد وهيّئ لنفسك انت وكل جماعاتك المجتمعة اليك فصرت لهم موقرا"));
arasuperobject.biblemap.put("b26_38_8", new arasuperobject(26,38,8,"بعد ايام كثيرة تفتقد. في السنين الاخيرة تأتي الى الارض المستردة من السيف المجموعة من شعوب كثيرة على جبال اسرائيل التي كانت دائما خربة للذين أخرجوا من الشعوب وسكنوا آمنين كلهم"));
arasuperobject.biblemap.put("b26_38_9", new arasuperobject(26,38,9,"وتصعد وتأتي كزوبعة وتكون كسحابة تغشي الارض انت وكل جيوشك وشعوب كثيرون معك"));
arasuperobject.biblemap.put("b26_38_10", new arasuperobject(26,38,10,"هكذا قال السيد الرب. ويكون في ذلك اليوم ان أمورا تخطر ببالك فتفكر فكرا رديئا"));
arasuperobject.biblemap.put("b26_38_11", new arasuperobject(26,38,11,"وتقول اني اصعد على ارض اعراء. آتي الهادئين الساكنين في امن كلهم ساكنون بغير سور وليس لهم عارضة ولا مصاريع"));
arasuperobject.biblemap.put("b26_38_12", new arasuperobject(26,38,12,"لسلب السلب ولغنم الغنيمة لرد يدك على خرب معمورة وعلى شعب مجموع من الامم المقتني ماشية وقنية الساكن في اعالي الارض"));
arasuperobject.biblemap.put("b26_38_13", new arasuperobject(26,38,13,"شبا وددان وتجار ترشيش وكل اشبالها يقولون لك هل لسلب سلب انت جاء. هل لغنم غنيمة جمعت جماعتك لحمل الفضة والذهب لأخذ الماشية والقنية لنهب نهب عظيم"));
arasuperobject.biblemap.put("b26_38_14", new arasuperobject(26,38,14,"لذلك تنبأ يا ابن آدم وقل لجوج. هكذا قال السيد الرب. في ذلك اليوم عند سكنى شعبي اسرائيل آمنين أفلا تعلم"));
arasuperobject.biblemap.put("b26_38_15", new arasuperobject(26,38,15,"وتأتي من موضعك من اقاصي الشمال انت وشعوب كثيرون معك كلهم راكبون خيلا جماعة عظيمة وجيش كثير"));
arasuperobject.biblemap.put("b26_38_16", new arasuperobject(26,38,16,"وتصعد على شعبي اسرائيل كسحابة تغشي الارض. في الايام الاخيرة يكون. وآتي بك على ارضي لكي تعرفني الامم حين اتقدس فيك امام اعينهم يا جوج"));
arasuperobject.biblemap.put("b26_38_17", new arasuperobject(26,38,17,"هكذا قال السيد الرب. هل انت هو الذي تكلمت عنه في الايام القديمة عن يد عبيدي انبياء اسرائيل الذين تنبأوا في تلك الايام سنينا ان آتي بك عليهم"));
arasuperobject.biblemap.put("b26_38_18", new arasuperobject(26,38,18,"ويكون في ذلك اليوم يوم مجيء جوج على ارض اسرائيل يقول السيد الرب ان غضبي يصعد في انفي"));
arasuperobject.biblemap.put("b26_38_19", new arasuperobject(26,38,19,"وفي غيرتي في نار سخطي تكلمت انه في ذلك اليوم يكون رعش عظيم في ارض اسرائيل"));
arasuperobject.biblemap.put("b26_38_20", new arasuperobject(26,38,20,"فترعش امامي سمك البحر وطيور السماء ووحوش الحقل والدابّات التي تدب على الارض وكل الناس الذين على وجه الارض وتندك الجبال وتسقط المعاقل وتسقط كل الاسوار الى الارض"));
arasuperobject.biblemap.put("b26_38_21", new arasuperobject(26,38,21,"واستدعي السيف عليه في كل جبالي يقول السيد الرب. فيكون سيف كل واحد على اخيه"));
arasuperobject.biblemap.put("b26_38_22", new arasuperobject(26,38,22,"واعاقبه بالوبإ وبالدم وامطر عليه وعلى جيشه وعلى الشعوب الكثيرة الذين معه مطرا جارفا وحجارة برد عظيمة ونارا وكبريتا"));
arasuperobject.biblemap.put("b26_38_23", new arasuperobject(26,38,23,"فاتعظم واتقدس وأعرف في عيون امم كثيرة فيعلمون اني انا الرب"));
arasuperobject.biblemap.put("b26_39_1", new arasuperobject(26,39,1,"وانت يا ابن آدم تنبأ على جوج وقل. هكذا قال السيد الرب. هانذا عليك يا جوج رئيس روش ماشك وتوبال"));
arasuperobject.biblemap.put("b26_39_2", new arasuperobject(26,39,2,"واردك واقودك واصعدك من اقاصي الشمال وآتي بك على جبال اسرائيل"));
arasuperobject.biblemap.put("b26_39_3", new arasuperobject(26,39,3,"واضرب قوسك من يدك اليسرى واسقط سهامك من يدك اليمنى"));
arasuperobject.biblemap.put("b26_39_4", new arasuperobject(26,39,4,"فتسقط على جبال اسرائيل انت وكل جيشك والشعوب الذين معك. ابذلك مأكلا للطيور الكاسرة من كل نوع ولوحوش الحقل"));
arasuperobject.biblemap.put("b26_39_5", new arasuperobject(26,39,5,"على وجه الحقل تسقط لاني تكلمت يقول السيد الرب"));
arasuperobject.biblemap.put("b26_39_6", new arasuperobject(26,39,6,"وارسل نارا على ما جوج وعلى الساكنين في الجزائر آمنين فيعلمون اني انا الرب"));
arasuperobject.biblemap.put("b26_39_7", new arasuperobject(26,39,7,"واعرّف باسمي المقدس في وسط شعبي اسرائيل ولا ادع اسمي المقدس ينجّس بعد فتعلم الامم اني انا الرب قدوس اسرائيل"));
arasuperobject.biblemap.put("b26_39_8", new arasuperobject(26,39,8,"ها هو قد أتى وصار يقول السيد الرب. هذا هو اليوم الذي تكلمت عنه"));
arasuperobject.biblemap.put("b26_39_9", new arasuperobject(26,39,9,"ويخرج سكان مدن اسرائيل ويشعلون ويحرقون السلاح والمجان والاتراس والقسي والسهام والحراب والرماح ويوقدون بها النار سبع سنين"));
arasuperobject.biblemap.put("b26_39_10", new arasuperobject(26,39,10,"فلا يأخذون من الحقل عودا ولا يحتطبون من الوعور لانهم يحرقون السلاح بالنار وينهبون الذين نهبوهم ويسلبون الذين سلبوهم يقول السيد الرب"));
arasuperobject.biblemap.put("b26_39_11", new arasuperobject(26,39,11,"ويكون في ذلك اليوم اني اعطي جوجا موضعا هناك للقبر في اسرائيل ووادي عباريم بشرقي البحر فيسد نفس العابرين وهناك يدفنون جوجا وجمهوره كله ويسمونه وادي جمهور جوج"));
arasuperobject.biblemap.put("b26_39_12", new arasuperobject(26,39,12,"ويقبرهم بيت اسرائيل ليطهّروا الارض سبعة اشهر"));
arasuperobject.biblemap.put("b26_39_13", new arasuperobject(26,39,13,"كل شعب الارض يقبرون ويكون لهم يوم تمجيدي مشهورا يقول السيد الرب"));
arasuperobject.biblemap.put("b26_39_14", new arasuperobject(26,39,14,"ويفرزون اناسا مستديمين عابرين في الارض قابرين مع العابرين اولئك الذين بقوا على وجه الارض تطهيرا لها. بعد سبعة اشهر يفحصون"));
arasuperobject.biblemap.put("b26_39_15", new arasuperobject(26,39,15,"فيعبر العابرون في الارض واذا راى احد عظم انسان يبني بجانبه صوّة حتى يقبره القابرون في وادي جمهور جوج ــ"));
arasuperobject.biblemap.put("b26_39_16", new arasuperobject(26,39,16,"وايضا اسم المدينة همونة ــ فيطهّرون الارض"));
arasuperobject.biblemap.put("b26_39_17", new arasuperobject(26,39,17,"وانت يا ابن آدم فهكذا قال السيد الرب. قل لطائر كل جناح ولكل وحوش البر اجتمعوا وتعالوا احتشدوا من كل جهة الى ذبيحتي التي انا ذابحها لكم ذبيحة عظيمة على جبال اسرائيل لتاكلوا لحما وتشربوا دما"));
arasuperobject.biblemap.put("b26_39_18", new arasuperobject(26,39,18,"تأكلون لحم الجبابرة وتشربون دم رؤساء الارض كباش وحملان واعتدة وثيران كلها من مسمنات باشان"));
arasuperobject.biblemap.put("b26_39_19", new arasuperobject(26,39,19,"وتأكلون الشحم الى الشبع وتشربون الدم الى السكر من ذبيحتي التي ذبحتها لكم"));
arasuperobject.biblemap.put("b26_39_20", new arasuperobject(26,39,20,"فتشبعون على مائدتي من الخيل والمركبات والجبابرة وكل رجال الحرب يقول السيد الرب"));
arasuperobject.biblemap.put("b26_39_21", new arasuperobject(26,39,21,"واجعل مجدي في الامم وجميع الامم يرون حكمي الذي اجريته ويدي التي جعلتها عليهم"));
arasuperobject.biblemap.put("b26_39_22", new arasuperobject(26,39,22,"فيعلم بيت اسرائيل اني انا الرب الههم من ذلك اليوم فصاعدا"));
arasuperobject.biblemap.put("b26_39_23", new arasuperobject(26,39,23,"وتعلم الامم ان بيت اسرائيل قد أجلوا باثمهم لانهم خانوني فحجبت وجهي عنهم وسلمتهم ليد مضايقيهم فسقطوا كلهم بالسيف"));
arasuperobject.biblemap.put("b26_39_24", new arasuperobject(26,39,24,"كنجاستهم وكمعاصيهم فعلت معهم وحجبت وجهي عنهم"));
arasuperobject.biblemap.put("b26_39_25", new arasuperobject(26,39,25,"لذلك هكذا قال السيد الرب. الآن ارد سبي يعقوب وارحم كل بيت اسرائيل واغار على اسمي القدوس"));
arasuperobject.biblemap.put("b26_39_26", new arasuperobject(26,39,26,"فيحملون خزيهم وكل خيانتهم التي خانوني اياها عند سكنهم في ارضهم مطمئنين ولا مخيف"));
arasuperobject.biblemap.put("b26_39_27", new arasuperobject(26,39,27,"عند ارجاعي اياهم من الشعوب وجمعي اياهم من اراضي اعدائهم وتقديسي فيهم امام عيون امم كثيرين"));
arasuperobject.biblemap.put("b26_39_28", new arasuperobject(26,39,28,"يعلمون اني انا الرب الههم باجلائي اياهم الى الامم ثم جمعهم الى ارضهم. ولا اترك بعد هناك احدا منهم"));
arasuperobject.biblemap.put("b26_39_29", new arasuperobject(26,39,29,"ولا احجب وجهي عنهم بعد لاني سكبت روحي على بيت اسرائيل يقول السيد الرب"));
arasuperobject.biblemap.put("b26_40_1", new arasuperobject(26,40,1,"في السنة الخامسة والعشرين من سبينا في راس السنة في العاشر من الشهر في السنة الرابعة عشرة بعدما ضربت المدينة في نفس ذلك اليوم كانت عليّ يد الرب وأتى بي الى هناك"));
arasuperobject.biblemap.put("b26_40_2", new arasuperobject(26,40,2,"في رؤى الله اتى بي الى ارض اسرائيل ووضعني على جبل عال جدا عليه كبناء مدينة من جهة الجنوب"));
arasuperobject.biblemap.put("b26_40_3", new arasuperobject(26,40,3,"ولما اتى بي الى هناك اذا برجل منظره كمنظر النحاس وبيده خيط كتان وقصبة القياس وهو واقف بالباب"));
arasuperobject.biblemap.put("b26_40_4", new arasuperobject(26,40,4,"فقال لي الرجل يا ابن آدم انظر بعينيك واسمع باذنيك واجعل قلبك الى كل ما اريكه لانه لاجل اراءتك أتي بك الى هنا. اخبر بيت اسرائيل بكل ما ترى"));
arasuperobject.biblemap.put("b26_40_5", new arasuperobject(26,40,5,"واذا بسور خارج البيت محيط به وبيد الرجل قصبة القياس ست اذرع طولا بالذراع وشبر. فقاس عرض البناء قصبة واحدة وسمكه قصبة واحدة"));
arasuperobject.biblemap.put("b26_40_6", new arasuperobject(26,40,6,"ثم جاء الى الباب الذي وجهه نحو الشرق وصعد في درجه وقاس عتبة الباب قصبة واحدة عرضا والعتبة الاخرى قصبة واحدة عرضا"));
arasuperobject.biblemap.put("b26_40_7", new arasuperobject(26,40,7,"والغرفة قصبة واحدة طولا وقصبة واحدة عرضا وبين الغرفات خمس اذرع. وعتبة الباب بجانب رواق الباب من داخل قصبة واحدة"));
arasuperobject.biblemap.put("b26_40_8", new arasuperobject(26,40,8,"وقاس رواق الباب من داخل قصبة واحدة"));
arasuperobject.biblemap.put("b26_40_9", new arasuperobject(26,40,9,"وقاس رواق الباب ثماني اذرع وعضائده ذراعين ورواق الباب من داخل"));
arasuperobject.biblemap.put("b26_40_10", new arasuperobject(26,40,10,"وغرفات الباب نحو الشرق ثلاث من هنا وثلاث من هناك. للثلاث قياس واحد وللعضائد قياس واحد من هنا ومن هناك"));
arasuperobject.biblemap.put("b26_40_11", new arasuperobject(26,40,11,"وقاس عرض مدخل الباب عشر اذرع وطول الباب ثلاث عشرة ذراعا"));
arasuperobject.biblemap.put("b26_40_12", new arasuperobject(26,40,12,"والحافة امام الغرفات ذراع واحدة من هنا والحافة ذراع واحدة من هناك. والغرفة ست اذرع من هنا وست اذرع من هناك"));
arasuperobject.biblemap.put("b26_40_13", new arasuperobject(26,40,13,"ثم قاس الباب من سقف الغرفة الواحدة الى سقف الاخرى عرض خمس وعشرين ذراعا. الباب مقابل الباب"));
arasuperobject.biblemap.put("b26_40_14", new arasuperobject(26,40,14,"وعمل عضائد ستين ذراعا الى عضادة الدار حول الباب"));
