arasuperobject.biblemap.put("b26_47_23", new arasuperobject(26,47,23,"ويكون انه في السبط الذي فيه يتغرب غريب هناك تعطونه ميراثه يقول السيد الرب"));
arasuperobject.biblemap.put("b26_48_1", new arasuperobject(26,48,1,"وهذه اسماء الاسباط. من طرف الشمال الى جانب طريق حثلون الى مدخل حماة حصر عينان تخم دمشق شمالا الى جانب حماة لدان. فيكون له من الشرق الى البحر قسم واحد"));
arasuperobject.biblemap.put("b26_48_2", new arasuperobject(26,48,2,"وعلى تخم دان من جانب المشرق الى جانب البحر لاشير قسم واحد"));
arasuperobject.biblemap.put("b26_48_3", new arasuperobject(26,48,3,"وعلى تخم اشير من جانب الشرق الى جانب البحر لنفتالي قسم واحد"));
arasuperobject.biblemap.put("b26_48_4", new arasuperobject(26,48,4,"وعلى تخم نفتالي من جانب الشرق الى جانب البحر لمنسّى قسم واحد"));
arasuperobject.biblemap.put("b26_48_5", new arasuperobject(26,48,5,"وعلى تخم منسّى من جانب الشرق الى جانب البحر لافرايم قسم واحد"));
arasuperobject.biblemap.put("b26_48_6", new arasuperobject(26,48,6,"وعلى تخم افرايم من جانب الشرق الى جانب البحر لرأوبين قسم واحد"));
arasuperobject.biblemap.put("b26_48_7", new arasuperobject(26,48,7,"وعلى تخم رأوبين من جانب الشرق الى جانب البحر ليهوذا قسم واحد"));
arasuperobject.biblemap.put("b26_48_8", new arasuperobject(26,48,8,"وعلى تخم يهوذا من جانب الشرق الى جانب البحر تكون التقدمة التي تقدمونها خمسة وعشرين الفا عرضا والطول كاحد الاقسام من جانب الشرق الى جانب البحر ويكون المقدس في وسطها"));
arasuperobject.biblemap.put("b26_48_9", new arasuperobject(26,48,9,"التقدمة التي تقدمونها للرب تكون خمسة وعشرين الفا طولا وعشرة آلاف عرضا"));
arasuperobject.biblemap.put("b26_48_10", new arasuperobject(26,48,10,"ولهؤلاء تكون تقدمة القدس للكهنة. من جهة الشمال خمسة وعشرون الفا في الطول ومن جهة البحر عشرة آلاف في العرض ومن جهة الشرق عشرة آلاف في العرض ومن جهة الجنوب خمسة وعشرون الفا في الطول ويكون مقدس الرب في وسطها"));
arasuperobject.biblemap.put("b26_48_11", new arasuperobject(26,48,11,"اما المقدس فللكهنة من بني صادوق الذين حرسوا حراستي الذين لم يضلّوا حين ضل بنو اسرائيل كما ضل اللاويون"));
arasuperobject.biblemap.put("b26_48_12", new arasuperobject(26,48,12,"وتكون لهم تقدمة من تقدمة الارض قدس اقداس على تخم اللاويين"));
arasuperobject.biblemap.put("b26_48_13", new arasuperobject(26,48,13,"وللاويين على موازاة تخم الكهنة خمسة وعشرون الفا في الطول وعشرة آلاف في العرض الطول كله خمسة وعشرون الفا والعرض عشرة آلاف"));
arasuperobject.biblemap.put("b26_48_14", new arasuperobject(26,48,14,"ولا يبيعون منه ولا يبدلون ولايصرفون باكورات الارض لانها مقدسة للرب"));
arasuperobject.biblemap.put("b26_48_15", new arasuperobject(26,48,15,"والخمسة الآلاف الفاضلة من العرض قدام الخمسة والعشرين الفا هي محللة للمدينة للسكنى وللمسرح والمدينة تكون في وسطها"));
arasuperobject.biblemap.put("b26_48_16", new arasuperobject(26,48,16,"وهذه اقيستها. جانب الشمال اربعة آلاف وخمس مئة وجانب الجنوب اربعة آلاف وخمس مئة وجانب الشرق اربعة آلاف وخمس مئة وجانب الغرب اربعة آلاف وخمس مئة"));
arasuperobject.biblemap.put("b26_48_17", new arasuperobject(26,48,17,"ويكون مسرح للمدينة نحو الشمال مئتين وخمسين ونحو الجنوب مئتين وخمسين ونحو الشرق مئتين وخمسين ونحو الغرب مئتين وخمسين"));
arasuperobject.biblemap.put("b26_48_18", new arasuperobject(26,48,18,"والباقي من الطول موازيا تقدمة القدس عشرة آلاف نحو الشرق وعشرة آلاف نحو الغرب. ويكون موازيا تقدمة القدس وغلته تكون اكلا لخدمة المدينة"));
arasuperobject.biblemap.put("b26_48_19", new arasuperobject(26,48,19,"اما خدمة المدينة فيخدمونها من كل اسباط اسرائيل"));
arasuperobject.biblemap.put("b26_48_20", new arasuperobject(26,48,20,"كل التقدمة خمسة وعشرون الفا بخمسة وعشرين الفا. مربعة تقدمون تقدمة القدس مع ملك المدينة"));
arasuperobject.biblemap.put("b26_48_21", new arasuperobject(26,48,21,"والبقية للرئيس من هنا ومن هناك لتقدمة القدس ولملك المدينة قدام الخمسة والعشرين الفا للتقدمة الى تخم الشرق ومن جهة الغرب قدام الخمسة والعشرين الفا على تخم الغرب موازيا املاك الرئيس وتكون تقدمة القدس ومقدس البيت في وسطها"));
arasuperobject.biblemap.put("b26_48_22", new arasuperobject(26,48,22,"ومن ملك اللاويين من ملك المدينة في وسط الذي هو للرئيس ما بين تخم يهوذا وتخم بنيامين يكون للرئيس"));
arasuperobject.biblemap.put("b26_48_23", new arasuperobject(26,48,23,"وباقي الاسباط فمن جانب الشرق الى جانب البحر لبنيامين قسم واحد"));
arasuperobject.biblemap.put("b26_48_24", new arasuperobject(26,48,24,"وعلى تخم بنيامين من جانب الشرق الى جانب البحر لشمعون قسم واحد"));
arasuperobject.biblemap.put("b26_48_25", new arasuperobject(26,48,25,"وعلى تخم شمعون من جانب الشرق الى جانب البحر ليساكر قسم واحد"));
arasuperobject.biblemap.put("b26_48_26", new arasuperobject(26,48,26,"وعلى تخم يساكر من جانب الشرق الى جانب البحر لزبولون قسم واحد"));
arasuperobject.biblemap.put("b26_48_27", new arasuperobject(26,48,27,"وعلى تخم زبولون من جانب الشرق الى جانب البحر لجاد قسم واحد"));
arasuperobject.biblemap.put("b26_48_28", new arasuperobject(26,48,28,"وعلى تخم جاد من جانب الجنوب يمينا يكون التخم من ثامار الى مياه مريبة قادش النهر الى البحر الكبير"));
arasuperobject.biblemap.put("b26_48_29", new arasuperobject(26,48,29,"هذه هي الارض التي تقسمونها ملكا لاسباط اسرائيل وهذه حصصهم يقول السيد الرب"));
arasuperobject.biblemap.put("b26_48_30", new arasuperobject(26,48,30,"وهذه مخارج المدينة. من جانب الشمال اربعة آلاف وخمس مئة مقياس"));
arasuperobject.biblemap.put("b26_48_31", new arasuperobject(26,48,31,"وابواب المدينة على اسماء اسباط اسرائيل. ثلاثة ابواب نحو الشمال. باب رأوبين وباب يهوذا وباب لاوي"));
arasuperobject.biblemap.put("b26_48_32", new arasuperobject(26,48,32,"والى جانب الشرق اربعة آلاف وخمس مئة وثلاثة ابواب. باب يوسف وباب بنيامين وباب دان"));
arasuperobject.biblemap.put("b26_48_33", new arasuperobject(26,48,33,"وجانب الجنوب اربعة آلاف وخمس مئة مقياس وثلاثة ابواب. باب شمعون وباب يساكر وباب زبولون"));
arasuperobject.biblemap.put("b26_48_34", new arasuperobject(26,48,34,"وجانب الغرب اربعة آلاف وخمس مئة وثلاثة ابواب. باب جاد وباب اشير وباب نفتالي"));
