arasuperobject.biblemap.put("b3_5_7", new arasuperobject(3,5,7,"وان لم تنل يده كفاية لشاة فيأتي بذبيحة لاثمه الذي اخطأ به يمامتين او فرخي حمام الى الرب احدهما ذبيحة خطية والآخر محرقة"));
arasuperobject.biblemap.put("b3_5_8", new arasuperobject(3,5,8,"يأتي بهما الى الكاهن فيقرّب الذي للخطية اولا. يحزّ راسه من قفاه ولا يفصله"));
arasuperobject.biblemap.put("b3_5_9", new arasuperobject(3,5,9,"وينضح من دم ذبيحة الخطية على حائط المذبح. والباقي من الدم يعصر الى اسفل المذبح. انه ذبيحة خطية"));
arasuperobject.biblemap.put("b3_5_10", new arasuperobject(3,5,10,"واما الثاني فيعمله محرقة كالعادة فيكفّر عنه الكاهن من خطيته التي اخطأ فيصفح عنه"));
arasuperobject.biblemap.put("b3_5_11", new arasuperobject(3,5,11,"وان لم تنل يده يمامتين او فرخي حمام فيأتي بقربانه عما اخطأ به عشر الإيفة من دقيق قربان خطية. لا يضع عليه زيتا ولا يجعل عليه لبانا لانه قربان خطية"));
arasuperobject.biblemap.put("b3_5_12", new arasuperobject(3,5,12,"ياتي به الى الكاهن فيقبض الكاهن منه ملء قبضته تذكاره ويوقده على المذبح على وقائد الرب. انه قربان خطية"));
arasuperobject.biblemap.put("b3_5_13", new arasuperobject(3,5,13,"فيكفّر عنه الكاهن من خطيته التي اخطأ بها في واحدة من ذلك فيصفح عنه. ويكون للكاهن كالتقدمة"));
arasuperobject.biblemap.put("b3_5_14", new arasuperobject(3,5,14,"وكلم الرب موسى قائلا"));
arasuperobject.biblemap.put("b3_5_15", new arasuperobject(3,5,15,"اذا خان احد خيانة واخطأ سهوا في اقداس الرب يأتي الى الرب بذبيحة لاثمه كبشا صحيحا من الغنم بتقويمك من شواقل فضة على شاقل القدس ذبيحة اثم"));
arasuperobject.biblemap.put("b3_5_16", new arasuperobject(3,5,16,"ويعوّض عما اخطأ به من القدس ويزيد عليه خمسه ويدفعه الى الكاهن فيكفّر الكاهن عنه بكبش الاثم فيصفح عنه"));
arasuperobject.biblemap.put("b3_5_17", new arasuperobject(3,5,17,"واذا اخطأ احد وعمل واحدة من جميع مناهي الرب التي لا ينبغي عملها ولم يعلم كان مذنبا وحمل ذنبه"));
arasuperobject.biblemap.put("b3_5_18", new arasuperobject(3,5,18,"فياتي بكبش صحيح من الغنم بتقويمك ذبيحة اثم الى الكاهن فيكفّر عنه الكاهن من سهوه الذي سها وهو لا يعلم فيصفح عنه"));
arasuperobject.biblemap.put("b3_5_19", new arasuperobject(3,5,19,"انه ذبيحة اثم. قد اثم اثما الى الرب"));
arasuperobject.biblemap.put("b3_6_1", new arasuperobject(3,6,1,"وكلم الرب موسى قائلا"));
arasuperobject.biblemap.put("b3_6_2", new arasuperobject(3,6,2,"اذا اخطأ احد وخان خيانة بالرب وجحد صاحبه وديعة او امانة او مسلوبا او اغتصب من صاحبه"));
arasuperobject.biblemap.put("b3_6_3", new arasuperobject(3,6,3,"او وجد لقطة وجحدها وحلف كاذبا على شيء ما كل من يفعله الانسان مخطئا به"));
arasuperobject.biblemap.put("b3_6_4", new arasuperobject(3,6,4,"فاذا اخطأ واذنب يرد المسلوب الذي سلبه او المغتصب الذي اغتصبه او الوديعة التي أودعت عنده او اللقطة التي وجدها"));
arasuperobject.biblemap.put("b3_6_5", new arasuperobject(3,6,5,"او كل ما حلف عليه كاذبا. يعوّضه براسه ويزيد عليه خمسه. الى الذي هو له يدفعه يوم ذبيحة اثمه"));
arasuperobject.biblemap.put("b3_6_6", new arasuperobject(3,6,6,"ويأتي الى الرب بذبيحة لاثمه كبشا صحيحا من الغنم بتقويمك ذبيحة اثم الى الكاهن"));
arasuperobject.biblemap.put("b3_6_7", new arasuperobject(3,6,7,"فيكفّر عنه الكاهن امام الرب فيصفح عنه في الشيء من كل ما فعله مذنبا به"));
arasuperobject.biblemap.put("b3_6_8", new arasuperobject(3,6,8,"وكلم الرب موسى قائلا"));
arasuperobject.biblemap.put("b3_6_9", new arasuperobject(3,6,9,"أوصي هرون وبنيه قائلا. هذه شريعة المحرقة. هي المحرقة تكون على الموقدة فوق المذبح كل الليل حتى الصباح ونار المذبح تتقد عليه"));
arasuperobject.biblemap.put("b3_6_10", new arasuperobject(3,6,10,"ثم يلبس الكاهن ثوبه من كتان ويلبس سراويل من كتان على جسده ويرفع الرماد الذي صيّرت النار المحرقة اياه على المذبح ويضعه بجانب المذبح"));
arasuperobject.biblemap.put("b3_6_11", new arasuperobject(3,6,11,"ثم يخلع ثيابه ويلبس ثيابا اخرى ويخرج الرماد الى خارج المحلّة الى مكان طاهر"));
arasuperobject.biblemap.put("b3_6_12", new arasuperobject(3,6,12,"والنار على المذبح تتقد عليه. لا تطفأ. ويشعل عليها الكاهن حطبا كل صباح ويرتّب عليها المحرقة ويوقد عليها شحم ذبائح السلامة"));
arasuperobject.biblemap.put("b3_6_13", new arasuperobject(3,6,13,"نار دائمة تتقد على المذبح. لا تطفأ"));
arasuperobject.biblemap.put("b3_6_14", new arasuperobject(3,6,14,"وهذه شريعة التقدمة. يقدمها بنو هرون امام الرب الى قدام المذبح"));
arasuperobject.biblemap.put("b3_6_15", new arasuperobject(3,6,15,"ويأخذ منها بقبضته بعض دقيق التقدمة وزيتها وكل اللبان الذي على التقدمة ويوقد على المذبح رائحة سرور تذكارها للرب"));
arasuperobject.biblemap.put("b3_6_16", new arasuperobject(3,6,16,"والباقي منها ياكله هرون وبنوه. فطيرا يؤكل في مكان مقدس. في دار خيمة الاجتماع ياكلونه"));
arasuperobject.biblemap.put("b3_6_17", new arasuperobject(3,6,17,"لا يخبز خميرا. قد جعلته نصيبهم من وقائدي. انها قدس اقداس كذبيحة الخطية وذبيحة الاثم"));
arasuperobject.biblemap.put("b3_6_18", new arasuperobject(3,6,18,"كل ذكر من بني هرون ياكل منها. فريضة دهرية في اجيالكم من وقائد الرب. كل من مسّها يتقدس"));
arasuperobject.biblemap.put("b3_6_19", new arasuperobject(3,6,19,"وكلم الرب موسى قائلا"));
arasuperobject.biblemap.put("b3_6_20", new arasuperobject(3,6,20,"هذا قربان هرون وبنيه الذي يقرّبونه للرب يوم مسحته. عشر الإيفة من دقيق تقدمة دائمة نصفها صباحا ونصفها مساء"));
arasuperobject.biblemap.put("b3_6_21", new arasuperobject(3,6,21,"على صاج تعمل بزيت مربوكة تاتي بها. ثرائد تقدمة فتاتا تقربها رائحة سرور للرب"));
arasuperobject.biblemap.put("b3_6_22", new arasuperobject(3,6,22,"والكاهن الممسوح عوضا عنه من بنيه يعملها فريضة دهرية للرب توقد بكمالها"));
arasuperobject.biblemap.put("b3_6_23", new arasuperobject(3,6,23,"وكل تقدمة كاهن تحرق بكمالها. لا تؤكل"));
arasuperobject.biblemap.put("b3_6_24", new arasuperobject(3,6,24,"وكلم الرب موسى قائلا"));
arasuperobject.biblemap.put("b3_6_25", new arasuperobject(3,6,25,"كلم هرون وبنيه قائلا. هذه شريعة ذبيحة الخطية. في المكان الذي تذبح فيه المحرقة تذبح ذبيحة الخطية امام الرب. انها قدس اقداس"));
arasuperobject.biblemap.put("b3_6_26", new arasuperobject(3,6,26,"الكاهن الذي يعملها للخطية ياكلها. في مكان مقدس تؤكل. في دار خيمة الاجتماع"));
arasuperobject.biblemap.put("b3_6_27", new arasuperobject(3,6,27,"كل من مسّ لحمها يتقدس. واذا انتثر من دمها على ثوب تغسل ما انتثر عليه في مكان مقدس"));
arasuperobject.biblemap.put("b3_6_28", new arasuperobject(3,6,28,"واما اناء الخزف الذي تطبخ فيه فيكسر. وان طبخت في اناء نحاس يجلى ويشطف بماء"));
arasuperobject.biblemap.put("b3_6_29", new arasuperobject(3,6,29,"كل ذكر من الكهنة ياكل منها. انها قدس اقداس"));
arasuperobject.biblemap.put("b3_6_30", new arasuperobject(3,6,30,"وكل ذبيحة خطية يدخل من دمها الى خيمة الاجتماع للتكفير في القدس لا تؤكل. تحرق بنار"));
