arasuperobject.biblemap.put("b3_7_2", new arasuperobject(3,7,2,"في المكان الذي يذبحون فيه المحرقة يذبحون ذبيحة الاثم. ويرشّ دمها على المذبح مستديرا"));
arasuperobject.biblemap.put("b3_7_3", new arasuperobject(3,7,3,"ويقرب منها كل شحمها الألية والشحم الذي يغشّي الاحشاء"));
