arasuperobject.biblemap.put("b3_7_15", new arasuperobject(3,7,15,"ولحم ذبيحة شكر سلامته يؤكل يوم قربانه. لا يبقي منه شيئا الى الصباح"));
arasuperobject.biblemap.put("b3_7_16", new arasuperobject(3,7,16,"وان كانت ذبيحة قربانه نذرا او نافلة ففي يوم تقريبه ذبيحته تؤكل. وفي الغد يؤكل ما فضل منها"));
arasuperobject.biblemap.put("b3_7_17", new arasuperobject(3,7,17,"واما الفاضل من لحم الذبيحة في اليوم الثالث فيحرق بالنار"));
arasuperobject.biblemap.put("b3_7_18", new arasuperobject(3,7,18,"وان اكل من لحم ذبيحة سلامته في اليوم الثالث لا تقبل. الذي يقربها لا تحسب له. تكون نجاسة. والنفس التي تاكل منها تحمل ذنبها"));
arasuperobject.biblemap.put("b3_7_19", new arasuperobject(3,7,19,"واللحم الذي مسّ شيئا ما نجسا لا يؤكل. يحرق بالنار. واللحم ياكل كل طاهر منه"));
arasuperobject.biblemap.put("b3_7_20", new arasuperobject(3,7,20,"واما النفس التي تأكل لحما من ذبيحة السلامة التي للرب ونجاستها عليها فتقطع تلك النفس من شعبها"));
arasuperobject.biblemap.put("b3_7_21", new arasuperobject(3,7,21,"والنفس التي تمسّ شيئا ما نجسا نجاسة انسان او بهيمة نجسة او مكروها ما نجسا ثم تأكل من لحم ذبيحة السلامة التي للرب تقطع تلك النفس من شعبها"));
arasuperobject.biblemap.put("b3_7_22", new arasuperobject(3,7,22,"وكلم الرب موسى قائلا"));
arasuperobject.biblemap.put("b3_7_23", new arasuperobject(3,7,23,"كلم بني اسرائيل قائلا. كل شحم ثور او كبش او ماعز لا تأكلوا"));
arasuperobject.biblemap.put("b3_7_24", new arasuperobject(3,7,24,"واما شحم الميتة وشحم المفترسة فيستعمل لكل عمل لكن اكلا لا تأكلوه"));
arasuperobject.biblemap.put("b3_7_25", new arasuperobject(3,7,25,"ان كل من اكل شحما من البهائم التي يقرّب منها وقودا للرب تقطع من شعبها النفس التي تاكل"));
arasuperobject.biblemap.put("b3_7_26", new arasuperobject(3,7,26,"وكل دم لا تأكلوا في جميع مساكنكم من الطير ومن البهائم"));
arasuperobject.biblemap.put("b3_7_27", new arasuperobject(3,7,27,"كل نفس تاكل شيئا من الدم تقطع تلك النفس من شعبها"));
arasuperobject.biblemap.put("b3_7_28", new arasuperobject(3,7,28,"وكلم الرب موسى قائلا"));
arasuperobject.biblemap.put("b3_7_29", new arasuperobject(3,7,29,"كلم بني اسرائيل قائلا. الذي يقرّب ذبيحة سلامته للرب يأتي بقربانه الى الرب من ذبيحة سلامته"));
arasuperobject.biblemap.put("b3_7_30", new arasuperobject(3,7,30,"يداه تأتيان بوقائد الرب. الشحم يأتي به مع الصدر. اما الصدر فلكي يردده ترديدا امام الرب"));
arasuperobject.biblemap.put("b3_7_31", new arasuperobject(3,7,31,"فيوقد الكاهن الشحم على المذبح ويكون الصدر لهرون وبنيه"));
arasuperobject.biblemap.put("b3_7_32", new arasuperobject(3,7,32,"والساق اليمنى تعطونها رفيعة للكاهن من ذبائح سلامتكم"));
