arasuperobject.biblemap.put("b3_7_34", new arasuperobject(3,7,34,"لان صدر الترديد وساق الرفيعة قد اخذتهما من بني اسرائيل من ذبائح سلامتهم واعطيتهما لهرون الكاهن ولبنيه فريضة دهرية من بني اسرائيل"));
arasuperobject.biblemap.put("b3_7_35", new arasuperobject(3,7,35,"تلك مسحة هرون ومسحة بنيه من وقائد الرب يوم تقديمهم ليكهنوا للرب"));
arasuperobject.biblemap.put("b3_7_36", new arasuperobject(3,7,36,"التي امر الرب ان تعطى لهم يوم مسحه اياهم من بني اسرائيل فريضة دهرية في اجيالهم"));
arasuperobject.biblemap.put("b3_7_37", new arasuperobject(3,7,37,"تلك شريعة المحرقة والتقدمة وذبيحة الخطية وذبيحة الاثم وذبيحة الملء وذبيحة السلامة"));
arasuperobject.biblemap.put("b3_7_38", new arasuperobject(3,7,38,"التي امر الرب بها موسى في جبل سيناء يوم امره بني اسرائيل بتقيرب قرابينهم للرب في برية سيناء"));
arasuperobject.biblemap.put("b3_8_1", new arasuperobject(3,8,1,"وكلم الرب موسى قائلا"));
arasuperobject.biblemap.put("b3_8_2", new arasuperobject(3,8,2,"خذ هرون وبنيه معه والثياب ودهن المسحة وثور الخطية والكبشين وسل الفطير"));
arasuperobject.biblemap.put("b3_8_3", new arasuperobject(3,8,3,"واجمع كل الجماعة الى باب خيمة الاجتماع"));
