arasuperobject.biblemap.put("b3_8_10", new arasuperobject(3,8,10,"ثم اخذ موسى دهن المسحة ومسح المسكن وكل ما فيه وقدسه"));
arasuperobject.biblemap.put("b3_8_11", new arasuperobject(3,8,11,"ونضح منه على المذبح سبع مرات ومسح المذبح وجميع آنيته والمرحضة وقاعدتها لتقديسها"));
arasuperobject.biblemap.put("b3_8_12", new arasuperobject(3,8,12,"وصب من دهن المسحة على راس هرون ومسحه لتقديسه"));
arasuperobject.biblemap.put("b3_8_13", new arasuperobject(3,8,13,"ثم قدم موسى بني هرون والبسهم اقمصة ونطّقهم بمناطق وشدّ لهم قلانس كما امر الرب موسى"));
arasuperobject.biblemap.put("b3_8_14", new arasuperobject(3,8,14,"ثم قدّم ثور الخطية ووضع هرون وبنوه ايديهم على راس ثور الخطية"));
arasuperobject.biblemap.put("b3_8_15", new arasuperobject(3,8,15,"فذبحه واخذ موسى الدم وجعله على قرون المذبح مستديرا باصبعه وطهر المذبح ثم صبّ الدم الى اسفل المذبح وقدّسه تكفيرا عنه"));
arasuperobject.biblemap.put("b3_8_16", new arasuperobject(3,8,16,"واخذ كل الشحم الذي على الاحشاء وزيادة الكبد والكليتين وشحمهما واوقده موسى على المذبح"));
arasuperobject.biblemap.put("b3_8_17", new arasuperobject(3,8,17,"واما الثور جلده ولحمه وفرثه فاحرقه بنار خارج المحلّة كما امر الرب موسى"));
arasuperobject.biblemap.put("b3_8_18", new arasuperobject(3,8,18,"ثم قدم كبش المحرقة فوضع هرون وبنوه ايديهم على راس الكبش"));
arasuperobject.biblemap.put("b3_8_19", new arasuperobject(3,8,19,"فذبحه ورشّ موسى الدم على المذبح مستديرا"));
arasuperobject.biblemap.put("b3_8_20", new arasuperobject(3,8,20,"وقطع الكبش الى قطعه واوقد موسى الراس والقطع والشحم"));
arasuperobject.biblemap.put("b3_8_21", new arasuperobject(3,8,21,"واما الاحشاء والاكارع فغسلها بماء واوقد موسى كل الكبش على المذبح. انه محرقة لرائحة سرور. وقود هو للرب. كما امر الرب موسى"));
arasuperobject.biblemap.put("b3_8_22", new arasuperobject(3,8,22,"ثم قدّم الكبش الثاني كبش الملء فوضع هرون وبنوه ايديهم على راس الكبش"));
arasuperobject.biblemap.put("b3_8_23", new arasuperobject(3,8,23,"فذبحه واخذ موسى من دمه وجعل على شحمة اذن هرون اليمنى وعلى ابهام يده اليمنى وعلى ابهام رجله اليمنى"));
arasuperobject.biblemap.put("b3_8_24", new arasuperobject(3,8,24,"ثم قدّم موسى بني هرون وجعل من الدم على شحم آذانهم اليمنى وعلى اباهم ايديهم اليمنى وعلى اباهم ارجلهم اليمنى. ثم رشّ موسى الدم على المذبح مستديرا"));
arasuperobject.biblemap.put("b3_8_25", new arasuperobject(3,8,25,"ثم اخذ الشحم الألية وكل الشحم الذي على الاحشاء وزيادة الكبد والكليتين وشحمهما والساق اليمنى"));
arasuperobject.biblemap.put("b3_8_26", new arasuperobject(3,8,26,"ومن سل الفطير الذي امام الرب اخذ قرصا واحدا فطيرا وقرصا واحدا من الخبز بزيت ورقاقة واحدة ووضعها على الشحم وعلى الساق اليمنى"));
arasuperobject.biblemap.put("b3_8_27", new arasuperobject(3,8,27,"وجعل الجميع على كفّي هرون وكفوف بنيه ورددها ترديدا امام الرب"));
arasuperobject.biblemap.put("b3_8_28", new arasuperobject(3,8,28,"ثم اخذها موسى عن كفوفهم واوقدها على المذبح فوق المحرقة. انها قربان ملء لرائحة سرور. وقود هي للرب"));
arasuperobject.biblemap.put("b3_8_29", new arasuperobject(3,8,29,"ثم اخذ موسى الصدر وردده ترديدا امام الرب من كبش الملء لموسى كان نصيبا كما امر الرب موسى"));
arasuperobject.biblemap.put("b3_8_30", new arasuperobject(3,8,30,"ثم اخذ موسى من دهن المسحة ومن الدم الذي على المذبح ونضح على هرون وعلى ثيابه وعلى بنيه وعلى ثياب بنيه معه وقدّس هرون وثيابه وبنيه وثياب بنيه معه"));
arasuperobject.biblemap.put("b3_8_31", new arasuperobject(3,8,31,"ثم قال موسى لهرون وبنيه اطبخوا اللحم لدى باب خيمة الاجتماع وهناك تأكلونه والخبز الذي في سل قربان الملء كما امرت قائلا هرون وبنوه يأكلونه"));
arasuperobject.biblemap.put("b3_8_32", new arasuperobject(3,8,32,"والباقي من اللحم والخبز تحرقونه بالنار"));
arasuperobject.biblemap.put("b3_8_33", new arasuperobject(3,8,33,"ومن لدن باب خيمة الاجتماع لا تخرجون سبعة ايام الى يوم كمال ايام ملئكم لانه سبعة ايام يملأ ايديكم"));
arasuperobject.biblemap.put("b3_8_34", new arasuperobject(3,8,34,"كما فعل في هذا اليوم قد امر الرب ان يفعل للتكفير عنكم"));
arasuperobject.biblemap.put("b3_8_35", new arasuperobject(3,8,35,"ولدى باب خيمة الاجتماع تقيمون نهارا وليلا سبعة ايام وتحفظون شعائر الرب فلا تموتون لاني هكذا أمرت"));
arasuperobject.biblemap.put("b3_8_36", new arasuperobject(3,8,36,"فعمل هرون وبنوه كل ما امر به الرب على يد موسى"));
arasuperobject.biblemap.put("b3_9_1", new arasuperobject(3,9,1,"وفي اليوم الثامن دعا موسى هرون وبنيه وشيوخ اسرائيل"));
arasuperobject.biblemap.put("b3_9_2", new arasuperobject(3,9,2,"وقال لهرون خذ لك عجلا ابن بقر لذبيحة خطية وكبشا لمحرقة صحيحين وقدّمهما امام الرب"));
arasuperobject.biblemap.put("b3_9_3", new arasuperobject(3,9,3,"وكلم بني اسرائيل قائلا خذوا تيسا من المعز لذبيحة خطية وعجلا وخروفا حوليّين صحيحين لمحرقة"));
arasuperobject.biblemap.put("b3_9_4", new arasuperobject(3,9,4,"وثورا وكبشا لذبيحة سلامة للذبح امام الرب وتقدمة ملتوتة بزيت. لان الرب اليوم يتراءى لكم"));
arasuperobject.biblemap.put("b3_9_5", new arasuperobject(3,9,5,"فاخذوا ما امر به موسى الى قدام خيمة الاجتماع وتقدم كل الجماعة ووقفوا امام الرب"));
arasuperobject.biblemap.put("b3_9_6", new arasuperobject(3,9,6,"فقال موسى هذا ما امر به الرب. تعملونه فيتراءى لكم مجد الرب"));
arasuperobject.biblemap.put("b3_9_7", new arasuperobject(3,9,7,"ثم قال موسى لهرون تقدم الى المذبح واعمل ذبيحة خطيتك ومحرقتك وكفّر عن نفسك وعن الشعب واعمل قربان الشعب وكفّر عنهم كما امر الرب"));
arasuperobject.biblemap.put("b3_9_8", new arasuperobject(3,9,8,"فتقدم هرون الى المذبح وذبح عجل الخطية الذي له"));
arasuperobject.biblemap.put("b3_9_9", new arasuperobject(3,9,9,"وقدّم بنو هرون اليه الدم فغمس اصبعه في الدم وجعل على قرون المذبح ثم صبّ الدم الى اسفل المذبح"));
arasuperobject.biblemap.put("b3_9_10", new arasuperobject(3,9,10,"والشحم والكليتين وزيادة الكبد من ذبيحة الخطية اوقدها على المذبح كما امر الرب موسى"));
arasuperobject.biblemap.put("b3_9_11", new arasuperobject(3,9,11,"واما اللحم والجلد فاحرقهما بنار خارج المحلّة"));
arasuperobject.biblemap.put("b3_9_12", new arasuperobject(3,9,12,"ثم ذبح المحرقة فناوله بنو هرون الدم فرشّه على المذبح مستديرا"));
arasuperobject.biblemap.put("b3_9_13", new arasuperobject(3,9,13,"ثم ناولوه المحرقة بقطعها والراس. فاوقدها على المذبح"));
arasuperobject.biblemap.put("b3_9_14", new arasuperobject(3,9,14,"وغسل الاحشاء والاكارع واوقدها فوق المحرقة على المذبح"));
arasuperobject.biblemap.put("b3_9_15", new arasuperobject(3,9,15,"ثم قدم قربان الشعب واخذ تيس الخطية الذي للشعب وذبحه وعمله للخطية كالاول"));
arasuperobject.biblemap.put("b3_9_16", new arasuperobject(3,9,16,"ثم قدم المحرقة وعملها كالعادة"));
arasuperobject.biblemap.put("b3_9_17", new arasuperobject(3,9,17,"ثم قدم التقدمة وملأ كفّه منها واوقدها على المذبح عدا محرقة الصباح"));
arasuperobject.biblemap.put("b3_9_18", new arasuperobject(3,9,18,"ثم ذبح الثور والكبش ذبيحة السلامة التي للشعب وناوله بنو هرون الدم فرشّه على المذبح مستديرا"));
arasuperobject.biblemap.put("b3_9_19", new arasuperobject(3,9,19,"والشحم من الثور ومن الكبش الألية وما يغشّي والكليتين وزيادة الكبد"));
arasuperobject.biblemap.put("b3_9_20", new arasuperobject(3,9,20,"ووضعوا الشحم على الصدرين فاوقد الشحم على المذبح"));
arasuperobject.biblemap.put("b3_9_21", new arasuperobject(3,9,21,"واما الصدران والساق اليمنى فرددها هرون ترديدا امام الرب كما امر موسى"));
arasuperobject.biblemap.put("b3_9_22", new arasuperobject(3,9,22,"ثم رفع هرون يده نحو الشعب وباركهم وانحدر من عمل ذبيحة الخطية والمحرقة وذبيحة السلامة"));
arasuperobject.biblemap.put("b3_9_23", new arasuperobject(3,9,23,"ودخل موسى وهرون الى خيمة الاجتماع ثم خرجا وباركا الشعب. فتراءى مجد الرب لكل الشعب"));
arasuperobject.biblemap.put("b3_9_24", new arasuperobject(3,9,24,"وخرجت نار من عند الرب واحرقت على المذبح المحرقة والشحم. فرأى جميع الشعب وهتفوا وسقطوا على وجوههم"));
arasuperobject.biblemap.put("b3_10_1", new arasuperobject(3,10,1,"واخذ ابنا هرون ناداب وابيهو كل منهما مجمرته وجعلا فيهما نارا ووضعا عليها بخورا وقربا امام الرب نارا غريبة لم يامرهما بها"));
arasuperobject.biblemap.put("b3_10_2", new arasuperobject(3,10,2,"فخرجت نار من عند الرب واكلتهما فماتا امام الرب"));
arasuperobject.biblemap.put("b3_10_3", new arasuperobject(3,10,3,"فقال موسى لهرون هذا ما تكلم به الرب قائلا في القريبين مني اتقدس وامام جميع الشعب اتمجد. فصمت هرون"));
arasuperobject.biblemap.put("b3_10_4", new arasuperobject(3,10,4,"فدعا موسى ميشائيل والصافان ابني عزّيئيل عم هرون وقال لهما تقدما ارفعا اخويكما من قدام القدس الى خارج المحلّة"));
arasuperobject.biblemap.put("b3_10_5", new arasuperobject(3,10,5,"فتقدما ورفعاهما في قميصيهما الى خارج المحلّة كما قال موسى"));
arasuperobject.biblemap.put("b3_10_6", new arasuperobject(3,10,6,"وقال موسى لهرون وألعازار وإيثامار ابنيه لا تكشفوا رؤوسكم ولا تشقوا ثيابكم لئلا تموتوا ويسخط على كل الجماعة. واما اخوتكم كل بيت اسرائيل فيبكون على الحريق الذي احرقه الرب"));
arasuperobject.biblemap.put("b3_10_7", new arasuperobject(3,10,7,"ومن باب خيمة الاجتماع لا تخرجوا لئلا تموتوا. لان دهن مسحة الرب عليكم. ففعلوا حسب كلام موسى"));
arasuperobject.biblemap.put("b3_10_8", new arasuperobject(3,10,8,"وكلم الرب هرون قائلا"));
arasuperobject.biblemap.put("b3_10_9", new arasuperobject(3,10,9,"خمرا ومسكرا لا تشرب انت وبنوك معك عند دخولكم الى خيمة الاجتماع لكي لا تموتوا. فرضا دهريا في اجيالكم"));
arasuperobject.biblemap.put("b3_10_10", new arasuperobject(3,10,10,"وللتمييز بين المقدس والمحلل وبين النجس والطاهر"));
arasuperobject.biblemap.put("b3_10_11", new arasuperobject(3,10,11,"ولتعليم بني اسرائيل جميع الفرائض التي كلمهم الرب بها بيد موسى"));
arasuperobject.biblemap.put("b3_10_12", new arasuperobject(3,10,12,"وقال موسى لهرون وألعازار وايثامار ابنيه الباقيين خذوا التقدمة الباقية من وقائد الرب وكلوها فطيرا بجانب المذبح لانها قدس اقداس"));
arasuperobject.biblemap.put("b3_10_13", new arasuperobject(3,10,13,"كلوها في مكان مقدّس لانها فريضتك وفريضة بنيك من وقائد الرب. فانني هكذا أمرت"));
arasuperobject.biblemap.put("b3_10_14", new arasuperobject(3,10,14,"واما صدر الترديد وساق الرفيعة فتاكلونهما في مكان طاهر انت وبنوك وبناتك معك. لانهما جعلا فريضتك وفريضة بنيك من ذبائح سلامة بني اسرائيل"));
arasuperobject.biblemap.put("b3_10_15", new arasuperobject(3,10,15,"ساق الرفيعة وصدر الترديد يأتون بهما مع وقائد الشحم ليردّدا ترديدا امام الرب. فيكونان لك ولبنيك معك فريضة دهرية كما امر الرب"));
arasuperobject.biblemap.put("b3_10_16", new arasuperobject(3,10,16,"واما تيس الخطية فان موسى طلبه فاذا هو قد احترق. فسخط على ألعازار وايثامار ابني هرون الباقيين وقال"));
arasuperobject.biblemap.put("b3_10_17", new arasuperobject(3,10,17,"ما لكما لم تاكلا ذبيحة الخطية في المكان المقدس لانها قدس اقداس وقد اعطاكما اياها لتحملا اثم الجماعة تكفيرا عنهم امام الرب"));
arasuperobject.biblemap.put("b3_10_18", new arasuperobject(3,10,18,"انه لم يؤت بدمها الى القدس داخلا. اكلا تأكلانها في القدس كما امرت"));
arasuperobject.biblemap.put("b3_10_19", new arasuperobject(3,10,19,"فقال هرون لموسى. انهما اليوم قد قربا ذبيحة خطيتهما ومحرقتهما امام الرب وقد اصابني مثل هذه. فلو اكلت ذبيحة الخطية اليوم هل كان يحسن في عيني الرب"));
arasuperobject.biblemap.put("b3_10_20", new arasuperobject(3,10,20,"فلما سمع موسى حسن في عينيه"));
arasuperobject.biblemap.put("b3_11_1", new arasuperobject(3,11,1,"وكلم الرب موسى وهرون قائلا لهما"));
arasuperobject.biblemap.put("b3_11_2", new arasuperobject(3,11,2,"كلما بني اسرائيل قائلين. هذه هي الحيوانات التي تأكلونها من جميع البهائم التي على الارض"));
arasuperobject.biblemap.put("b3_11_3", new arasuperobject(3,11,3,"كل ما شق ظلفا وقسمه ظلفين ويجترّ من البهائم فايّاه تأكلون"));
arasuperobject.biblemap.put("b3_11_4", new arasuperobject(3,11,4,"الا هذه فلا تأكلوها مما يجترّ ومما يشق الظلف الجمل. لانه يجترّ لكنه لا يشق ظلفا. فهو نجس لكم"));
arasuperobject.biblemap.put("b3_11_5", new arasuperobject(3,11,5,"والوبر. لانه يجترّ لكنه لا يشق ظلفا فهو نجس لكم"));
arasuperobject.biblemap.put("b3_11_6", new arasuperobject(3,11,6,"والارنب. لانه يجترّ لكنه لا يشق ظلفا فهو نجس لكم"));
arasuperobject.biblemap.put("b3_11_7", new arasuperobject(3,11,7,"والخنزير. لانه يشق ظلفا ويقسمه ظلفين لكنه لا يجترّ. فهو نجس لكم"));
arasuperobject.biblemap.put("b3_11_8", new arasuperobject(3,11,8,"من لحمها لا تأكلوا وجثثها لا تلمسوا. انها نجسة لكم"));
arasuperobject.biblemap.put("b3_11_9", new arasuperobject(3,11,9,"وهذا تأكلونه من جميع ما في المياه. كل ما له زعانف وحرشف في المياه في البحار وفي الانهار فاياه تاكلون"));
arasuperobject.biblemap.put("b3_11_10", new arasuperobject(3,11,10,"لكن كل ما ليس له زعانف وحرشف في البحار وفي الانهار من كل دبيب في المياه ومن كل نفس حية في المياه فهو مكروه لكم"));
arasuperobject.biblemap.put("b3_11_11", new arasuperobject(3,11,11,"ومكروها يكون لكم. من لحمه لا تاكلوا وجثته تكرهون"));
arasuperobject.biblemap.put("b3_11_12", new arasuperobject(3,11,12,"كل ما ليس له زعانف وحرشف في المياه فهو مكروه لكم"));
arasuperobject.biblemap.put("b3_11_13", new arasuperobject(3,11,13,"وهذه تكرهونها من الطيور. لا تؤكل. انها مكروهة. النسر والانوق والعقاب"));
arasuperobject.biblemap.put("b3_11_14", new arasuperobject(3,11,14,"والحدأة والباشق على اجناسه"));
arasuperobject.biblemap.put("b3_11_15", new arasuperobject(3,11,15,"وكل غراب على اجناسه"));
arasuperobject.biblemap.put("b3_11_16", new arasuperobject(3,11,16,"والنعامة والظليم والسّأف والباز على اجناسه"));
arasuperobject.biblemap.put("b3_11_17", new arasuperobject(3,11,17,"والبوم والغوّاص والكركي"));
arasuperobject.biblemap.put("b3_11_18", new arasuperobject(3,11,18,"والبجع والقوق والرخم"));
arasuperobject.biblemap.put("b3_11_19", new arasuperobject(3,11,19,"واللقلق والببغا على اجناسه والهدهد والخفاش"));
arasuperobject.biblemap.put("b3_11_20", new arasuperobject(3,11,20,"وكل دبيب الطير الماشي على اربع فهو مكروه لكم"));
arasuperobject.biblemap.put("b3_11_21", new arasuperobject(3,11,21,"الا هذا تأكلونه من جميع دبيب الطير الماشي على اربع. ما له كراعان فوق رجليه يثب بهما على الارض"));
arasuperobject.biblemap.put("b3_11_22", new arasuperobject(3,11,22,"هذا منه تأكلون. الجراد على اجناسه والدبا على اجناسه والحرجوان على اجناسه والجندب على اجناسه"));
arasuperobject.biblemap.put("b3_11_23", new arasuperobject(3,11,23,"لكن سائر دبيب الطير الذي له اربع ارجل فهو مكروه لكم"));
arasuperobject.biblemap.put("b3_11_24", new arasuperobject(3,11,24,"من هذه تتنجسون. كل من مسّ جثثها يكون نجسا الى المساء"));
arasuperobject.biblemap.put("b3_11_25", new arasuperobject(3,11,25,"وكل من حمل من جثثها يغسل ثيابه ويكون نجسا الى المساء"));
arasuperobject.biblemap.put("b3_11_26", new arasuperobject(3,11,26,"وجميع البهائم التي لها ظلف ولكن لا تشقّه شقّا او لا تجترّ فهي نجسة لكم. كل من مسّها يكون نجسا"));
arasuperobject.biblemap.put("b3_11_27", new arasuperobject(3,11,27,"وكل ما يمشي على كفوفه من جميع الحيوانات الماشية على اربع فهو نجس لكم. كل من مسّ جثثها يكون نجسا الى المساء"));
arasuperobject.biblemap.put("b3_11_28", new arasuperobject(3,11,28,"ومن حمل جثثها يغسل ثيابه ويكون نجسا الى المساء. انها نجسة لكم"));
arasuperobject.biblemap.put("b3_11_29", new arasuperobject(3,11,29,"وهذا هو النجس لكم من الدبيب الذي يدبّ على الارض. ابن عرس والفار والضب على اجناسه"));
arasuperobject.biblemap.put("b3_11_30", new arasuperobject(3,11,30,"والحرذون والورل والوزغة والعظاية والحرباء"));
arasuperobject.biblemap.put("b3_11_31", new arasuperobject(3,11,31,"هذه هي النجسة لكم من كل الدبيب. كل من مسّها بعد موتها يكون نجسا الى المساء"));
arasuperobject.biblemap.put("b3_11_32", new arasuperobject(3,11,32,"وكل ما وقع عليه واحد منها بعد موتها يكون نجسا. من كل متاع خشب او ثوب او جلد او بلاس. كل متاع يعمل به عمل يلقى في الماء ويكون نجسا الى المساء ثم يطهر"));
arasuperobject.biblemap.put("b3_11_33", new arasuperobject(3,11,33,"وكل متاع خزف وقع فيه منها فكل ما فيه يتنجس واما هو فتكسرونه"));
arasuperobject.biblemap.put("b3_11_34", new arasuperobject(3,11,34,"ما ياتي عليه ماء من كل طعام يؤكل يكون نجسا. وكل شراب يشرب في كل متاع يكون نجسا"));
arasuperobject.biblemap.put("b3_11_35", new arasuperobject(3,11,35,"وكل ما وقع عليه واحدة من جثثها يكون نجسا. التنور والموقدة يهدمان. انها نجسة وتكون نجسة لكم"));
arasuperobject.biblemap.put("b3_11_36", new arasuperobject(3,11,36,"الا العين والبئر مجتمعي الماء تكونان طاهرتين. لكن ما مسّ جثثها يكون نجسا"));
arasuperobject.biblemap.put("b3_11_37", new arasuperobject(3,11,37,"واذا وقعت واحدة من جثثها على شيء من بزر زرع يزرع فهو طاهر"));
arasuperobject.biblemap.put("b3_11_38", new arasuperobject(3,11,38,"لكن اذا جعل ماء على بزر فوقع عليه واحدة من جثثها فانه نجس لكم"));
arasuperobject.biblemap.put("b3_11_39", new arasuperobject(3,11,39,"واذا مات واحد من البهائم التي هي طعام لكم فمن مسّ جثته يكون نجسا الى المساء"));
arasuperobject.biblemap.put("b3_11_40", new arasuperobject(3,11,40,"ومن اكل من جثته يغسل ثيابه ويكون نجسا الى المساء. ومن حمل جثته يغسل ثيابه ويكون نجسا الى المساء"));
arasuperobject.biblemap.put("b3_11_41", new arasuperobject(3,11,41,"وكل دبيب يدبّ على الارض فهو مكروه لا يؤكل"));
arasuperobject.biblemap.put("b3_11_42", new arasuperobject(3,11,42,"كل ما يمشي على بطنه وكل ما يمشي على اربع مع كل ما كثرت ارجله من كل دبيب يدبّ على الارض لا تأكلوه لانه مكروه"));
arasuperobject.biblemap.put("b3_11_43", new arasuperobject(3,11,43,"لا تدنسوا انفسكم بدبيب يدبّ ولا تتنجسوا به ولا تكونوا به نجسين"));
arasuperobject.biblemap.put("b3_11_44", new arasuperobject(3,11,44,"اني انا الرب الهكم فتتقدسون وتكونون قديسين لاني انا قدوس. ولا تنجسوا انفسكم بدبيب يدبّ على الارض"));
arasuperobject.biblemap.put("b3_11_45", new arasuperobject(3,11,45,"اني انا الرب الذي اصعدكم من ارض مصر ليكون لكم الها. فتكونون قديسين لاني انا قدوس"));
arasuperobject.biblemap.put("b3_11_46", new arasuperobject(3,11,46,"هذه شريعة البهائم والطيور وكل نفس حية تسعى في الماء وكل نفس تدب على الارض"));
arasuperobject.biblemap.put("b3_11_47", new arasuperobject(3,11,47,"للتمييز بين النجس والطاهر وبين الحيوانات التي تؤكل والحيوانات التي لا تؤكل"));
arasuperobject.biblemap.put("b3_12_1", new arasuperobject(3,12,1,"وكلم الرب موسى قائلا"));
arasuperobject.biblemap.put("b3_12_2", new arasuperobject(3,12,2,"كلم بني اسرائيل قائلا. اذا حبلت امرأة وولدت ذكرا تكون نجسة سبعة ايام. كما في ايام طمث علتها تكون نجسة"));
arasuperobject.biblemap.put("b3_12_3", new arasuperobject(3,12,3,"وفي اليوم الثامن يختن لحم غرلته"));
arasuperobject.biblemap.put("b3_12_4", new arasuperobject(3,12,4,"ثم تقيم ثلاثة وثلاثين يوما في دم تطهيرها. كل شيء مقدس لا تمسّ والى المقدس لا تجيء حتى تكمل ايام تطهيرها"));
arasuperobject.biblemap.put("b3_12_5", new arasuperobject(3,12,5,"وان ولدت انثى تكون نجسة اسبوعين كما في طمثها. ثم تقيم ستة وستين يوما في دم تطهيرها"));
arasuperobject.biblemap.put("b3_12_6", new arasuperobject(3,12,6,"ومتى كملت ايام تطهيرها لاجل ابن او ابنة تأتي بخروف حولي محرقة وفرخ حمامة او يمامة ذبيحة خطية الى باب خيمة الاجتماع الى الكاهن"));
arasuperobject.biblemap.put("b3_12_7", new arasuperobject(3,12,7,"فيقدمهما امام الرب ويكفّر عنها فتطهر من ينبوع دمها. هذه شريعة التي تلد ذكرا او انثى"));
arasuperobject.biblemap.put("b3_12_8", new arasuperobject(3,12,8,"وان لم تنل يدها كفاية لشاة تاخذ يمامتين او فرخي حمام الواحد محرقة والآخر ذبيحة خطية فيكفّر عنها الكاهن فتطهر"));
arasuperobject.biblemap.put("b3_13_1", new arasuperobject(3,13,1,"وكلم الرب موسى وهرون قائلا"));
arasuperobject.biblemap.put("b3_13_2", new arasuperobject(3,13,2,"اذا كان انسان في جلد جسده ناتئ او قوباء او لمعة تصير في جلد جسده ضربة برص يؤتى به الى هرون الكاهن او الى احد بنيه الكهنة"));
arasuperobject.biblemap.put("b3_13_3", new arasuperobject(3,13,3,"فان رأى الكاهن الضربة في جلد الجسد وفي الضربة شعر قد ابيضّ ومنظر الضربة اعمق من جلد جسده فهي ضربة برص. فمتى رآه الكاهن يحكم بنجاسته"));
arasuperobject.biblemap.put("b3_13_4", new arasuperobject(3,13,4,"لكن ان كانت الضربة لمعة بيضاء في جلد جسده ولم يكن منظرها اعمق من الجلد ولم يبيضّ شعرها يحجز الكاهن المضروب سبعة ايام"));
arasuperobject.biblemap.put("b3_13_5", new arasuperobject(3,13,5,"فان رآه الكاهن في اليوم السابع واذا في عينه الضربة قد وقفت ولم تمتد الضربة في الجلد يحجزه الكاهن سبعة ايام ثانية"));
arasuperobject.biblemap.put("b3_13_6", new arasuperobject(3,13,6,"فان رآه الكاهن في اليوم السابع ثانية واذا الضربة كامدة اللون ولم تمتد الضربة في الجلد يحكم الكاهن بطهارته انها حزاز. فيغسل ثيابه ويكون طاهرا"));
arasuperobject.biblemap.put("b3_13_7", new arasuperobject(3,13,7,"لكن ان كانت القوباء تمتد في الجلد بعد عرضه على الكاهن لتطهيره يعرض على الكاهن ثانية"));
arasuperobject.biblemap.put("b3_13_8", new arasuperobject(3,13,8,"فان رأى الكاهن واذا القوباء قد امتدّت في الجلد يحكم الكاهن بنجاسته. انها برص"));
arasuperobject.biblemap.put("b3_13_9", new arasuperobject(3,13,9,"ان كانت في انسان ضربة برص فيؤتى به الى الكاهن"));
arasuperobject.biblemap.put("b3_13_10", new arasuperobject(3,13,10,"فان رأى الكاهن واذا في الجلد ناتئ ابيض قد صيّر الشعر ابيض وفي الناتئ وضح من لحم حيّ"));
arasuperobject.biblemap.put("b3_13_11", new arasuperobject(3,13,11,"فهو برص مزمن في جلد جسده فيحكم الكاهن بنجاسته. لا يحجزه لانه نجس"));
arasuperobject.biblemap.put("b3_13_12", new arasuperobject(3,13,12,"لكن ان كان البرص قد افرخ في الجلد وغطى البرص كل جلد المضروب من راسه الى قدميه حسب كل ما تراه عينا الكاهن"));
arasuperobject.biblemap.put("b3_13_13", new arasuperobject(3,13,13,"ورأى الكاهن واذا البرص قد غطى كل جسمه يحكم بطهارة المضروب. كله قد ابيض. انه طاهر"));
arasuperobject.biblemap.put("b3_13_14", new arasuperobject(3,13,14,"لكن يوم يرى فيه لحم حيّ يكون نجسا"));
arasuperobject.biblemap.put("b3_13_15", new arasuperobject(3,13,15,"فمتى رأى الكاهن اللحم الحي يحكم بنجاسته. اللحم الحيّ نجس. انه برص"));
arasuperobject.biblemap.put("b3_13_16", new arasuperobject(3,13,16,"ثم ان عاد اللحم الحي وابيضّ ياتي الى الكاهن"));
arasuperobject.biblemap.put("b3_13_17", new arasuperobject(3,13,17,"فان رآه الكاهن واذا الضربة قد صارت بيضاء يحكم الكاهن بطهارة المضروب. انه طاهر"));
arasuperobject.biblemap.put("b3_13_18", new arasuperobject(3,13,18,"واذا كان الجسم في جلده دمّلة قد برئت"));
arasuperobject.biblemap.put("b3_13_19", new arasuperobject(3,13,19,"وصار في موضع الدمّلة ناتئ ابيض او لمعة بيضاء ضاربة الى الحمرة يعرض على الكاهن"));
arasuperobject.biblemap.put("b3_13_20", new arasuperobject(3,13,20,"فان رأى الكاهن واذا منظرها اعمق من الجلد وقد ابيضّ شعرها يحكم الكاهن بنجاسته. انها ضربة برص افرخت في الدمّلة"));
arasuperobject.biblemap.put("b3_13_21", new arasuperobject(3,13,21,"لكن ان رآها الكاهن واذا ليس فيها شعر ابيض وليست اعمق من الجلد وهي كامدة اللون يحجزه الكاهن سبعة ايام"));
arasuperobject.biblemap.put("b3_13_22", new arasuperobject(3,13,22,"فان كانت قد امتدّت في الجلد يحكم الكاهن بنجاسته. انها ضربة"));
arasuperobject.biblemap.put("b3_13_23", new arasuperobject(3,13,23,"لكن ان وقفت اللمعة مكانها ولم تمتد فهي اثر الدمّلة فيحكم الكاهن بطهارته"));
arasuperobject.biblemap.put("b3_13_24", new arasuperobject(3,13,24,"او اذا كان الجسم في جلده كيّ نار وكان حيّ الكيّ لمعة بيضاء ضاربة الى الحمرة او بيضاء"));
arasuperobject.biblemap.put("b3_13_25", new arasuperobject(3,13,25,"ورآها الكاهن واذا الشعر في اللمعة قد ابيضّ ومنظرها اعمق من الجلد فهي برص قد افرخ في الكيّ فيحكم الكاهن بنجاسته. انها ضربة برص"));
arasuperobject.biblemap.put("b3_13_26", new arasuperobject(3,13,26,"لكن ان رآها الكاهن واذا ليس في اللمعة شعر ابيض وليست اعمق من الجلد وهي كامدة اللون يحجزه الكاهن سبعة ايام"));
arasuperobject.biblemap.put("b3_13_27", new arasuperobject(3,13,27,"ثم يراه الكاهن في اليوم السابع فان كانت قد امتدّت في الجلد يحكم الكاهن بنجاسته. انها ضربة برص"));
arasuperobject.biblemap.put("b3_13_28", new arasuperobject(3,13,28,"لكن ان وقفت اللمعة مكانها لم تمتد في الجلد وكانت كامدة اللون فهي ناتئ الكي فالكاهن يحكم بطهارته لانها اثر الكي"));
arasuperobject.biblemap.put("b3_13_29", new arasuperobject(3,13,29,"واذا كان رجل او امرأة فيه ضربة في الراس او في الذقن"));
arasuperobject.biblemap.put("b3_13_30", new arasuperobject(3,13,30,"ورأى الكاهن الضربة واذا منظرها اعمق من الجلد وفيها شعر اشقر دقيق يحكم الكاهن بنجاسته. انها قرع. برص الراس او الذقن"));
arasuperobject.biblemap.put("b3_13_31", new arasuperobject(3,13,31,"لكن اذا رأى الكاهن ضربة القرع واذا منظرها ليس اعمق من الجلد لكن ليس فيها شعر اسود يحجز الكاهن المضروب بالقرع سبعة ايام"));
arasuperobject.biblemap.put("b3_13_32", new arasuperobject(3,13,32,"فان رأى الكاهن الضربة في اليوم السابع واذا القرع لم يمتد ولم يكن فيه شعر اشقر ولا منظر القرع اعمق من الجلد"));
arasuperobject.biblemap.put("b3_13_33", new arasuperobject(3,13,33,"فليحلق لكن لا يحلق القرع. ويحجز الكاهن الاقرع سبعة ايام ثانية"));
arasuperobject.biblemap.put("b3_13_34", new arasuperobject(3,13,34,"فان رأى الكاهن الاقرع في اليوم السابع واذا القرع لم يمتدّ في الجلد وليس منظره اعمق من الجلد يحكم الكاهن بطهارته فيغسل ثيابه ويكون طاهرا"));
arasuperobject.biblemap.put("b3_13_35", new arasuperobject(3,13,35,"لكن ان كان القرع يمتد في الجلد بعد الحكم بطهارته"));
arasuperobject.biblemap.put("b3_13_36", new arasuperobject(3,13,36,"ورآه الكاهن واذا القرع قد امتدّ في الجلد فلا يفتش الكاهن على الشعر الاشقر. انه نجس"));
arasuperobject.biblemap.put("b3_13_37", new arasuperobject(3,13,37,"لكن ان وقف في عينيه ونبت فيه شعر اسود فقد برئ القرع. انه طاهر فيحكم الكاهن بطهارته"));
arasuperobject.biblemap.put("b3_13_38", new arasuperobject(3,13,38,"واذا كان رجل او امرأة في جلد جسده لمع لمع بيض"));
arasuperobject.biblemap.put("b3_13_39", new arasuperobject(3,13,39,"ورأى الكاهن واذا في جلد جسده لمع كامدة اللون بيضاء فذلك بهق قد افرخ في الجلد. انه طاهر"));
arasuperobject.biblemap.put("b3_13_40", new arasuperobject(3,13,40,"واذا كان انسان قد ذهب شعر راسه فهو اقرع. انه طاهر"));
arasuperobject.biblemap.put("b3_13_41", new arasuperobject(3,13,41,"وان ذهب شعر راسه من جهة وجهه فهو اصلع. انه طاهر"));
arasuperobject.biblemap.put("b3_13_42", new arasuperobject(3,13,42,"لكن اذا كان في القرعة او في الصلعة ضربة بيضاء ضاربة الى الحمرة فهو برص مفرخ في قرعته او في صلعته"));
arasuperobject.biblemap.put("b3_13_43", new arasuperobject(3,13,43,"فان رآه الكاهن واذا ناتئ الضربة ابيض ضارب الى الحمرة في قرعته او في صلعته كمنظر البرص في جلد الجسد"));
arasuperobject.biblemap.put("b3_13_44", new arasuperobject(3,13,44,"فهو انسان ابرص. انه نجس فيحكم الكاهن بنجاسته. ان ضربته في راسه"));
arasuperobject.biblemap.put("b3_13_45", new arasuperobject(3,13,45,"والابرص الذي فيه الضربة تكون ثيابه مشقوقة وراسه يكون مكشوفا ويغطي شاربيه وينادي نجس نجس"));
arasuperobject.biblemap.put("b3_13_46", new arasuperobject(3,13,46,"كل الايام التي تكون الضربة فيه يكون نجسا. انه نجس. يقيم وحده. خارج المحلّة يكون مقامه"));
arasuperobject.biblemap.put("b3_13_47", new arasuperobject(3,13,47,"واما الثوب فاذا كان فيه ضربة برص ثوب صوف او ثوب كتان"));
arasuperobject.biblemap.put("b3_13_48", new arasuperobject(3,13,48,"في السدى او اللحمة من الصوف او الكتان او في جلد او في كل مصنوع من جلد"));
arasuperobject.biblemap.put("b3_13_49", new arasuperobject(3,13,49,"وكانت الضربة ضاربة الى الخضرة او الى الحمرة في الثوب او في الجلد في السدى او اللحمة او في متاع ما من جلد فانها ضربة برص فتعرض على الكاهن"));
arasuperobject.biblemap.put("b3_13_50", new arasuperobject(3,13,50,"فيرى الكاهن الضربة ويحجز المضروب سبعة ايام"));
arasuperobject.biblemap.put("b3_13_51", new arasuperobject(3,13,51,"فمتى رأى الضربة في اليوم السابع اذا كانت الضربة قد امتدّت في الثوب في السدى او اللحمة او في الجلد من كل ما يصنع من جلد للعمل فالضربة برص مفسد. انها نجسة"));
arasuperobject.biblemap.put("b3_13_52", new arasuperobject(3,13,52,"فيحرق الثوب او السدى او اللحمة من الصوف او الكتان او متاع الجلد الذي كانت فيه الضربة لانها برص مفسد. بالنار يحرق"));
arasuperobject.biblemap.put("b3_13_53", new arasuperobject(3,13,53,"لكن ان رأى الكاهن واذا الضربة لم تمتد في الثوب في السدى او اللحمة او في متاع الجلد"));
arasuperobject.biblemap.put("b3_13_54", new arasuperobject(3,13,54,"يأمر الكاهن ان يغسلوا ما فيه الضربة ويحجزه سبعة ايام ثانية"));
arasuperobject.biblemap.put("b3_13_55", new arasuperobject(3,13,55,"فان رأى الكاهن بعد غسل المضروب واذا الضربة لم تغيّر منظرها ولا امتدّت الضربة فهو نجس. بالنار تحرقه. انها نخروب في جردة باطنه او ظاهره"));
arasuperobject.biblemap.put("b3_13_56", new arasuperobject(3,13,56,"لكن ان رأى الكاهن واذا الضربة كامدة اللون بعد غسله يمزقها من الثوب او الجلد من السدى او اللحمة"));
arasuperobject.biblemap.put("b3_13_57", new arasuperobject(3,13,57,"ثم ان ظهرت ايضا في الثوب في السدى او اللحمة او في متاع الجلد فهي مفرخة. بالنار تحرق ما فيه الضربة"));
