arasuperobject.biblemap.put("b3_15_30", new arasuperobject(3,15,30,"فيعمل الكاهن الواحد ذبيحة خطية والآخر محرقة ويكفّر عنها الكاهن امام الرب من سيل نجاستها"));
arasuperobject.biblemap.put("b3_15_31", new arasuperobject(3,15,31,"فتعزلان بني اسرائيل عن نجاستهم لئلا يموتوا في نجاستهم بتنجيسهم مسكني الذي في وسطهم"));
arasuperobject.biblemap.put("b3_15_32", new arasuperobject(3,15,32,"هذه شريعة ذي السيل والذي يحدث منه اضطجاع زرع فيتنجس بها"));
arasuperobject.biblemap.put("b3_15_33", new arasuperobject(3,15,33,"والعليلة في طمثها والسائل سيله الذكر والانثى والرجل الذي يضطجع مع نجسة"));
arasuperobject.biblemap.put("b3_16_1", new arasuperobject(3,16,1,"وكلم الرب موسى بعد موت ابني هرون عندما اقتربا امام الرب وماتا"));
arasuperobject.biblemap.put("b3_16_2", new arasuperobject(3,16,2,"وقال الرب لموسى كلم هرون اخاك ان لا يدخل كل وقت الى القدس داخل الحجاب امام الغطاء الذي على التابوت لئلا يموت. لاني في السحاب اتراءى على الغطاء"));
arasuperobject.biblemap.put("b3_16_3", new arasuperobject(3,16,3,"بهذا يدخل هرون الى القدس. بثور ابن بقر لذبيحة خطية وكبش لمحرقة"));
arasuperobject.biblemap.put("b3_16_4", new arasuperobject(3,16,4,"يلبس قميص كتان مقدسا وتكون سراويل كتان على جسده ويتنطّق بمنطقة كتان ويتعمم بعمامة كتان. انها ثياب مقدسة. فيرحض جسده بماء ويلبسها"));
arasuperobject.biblemap.put("b3_16_5", new arasuperobject(3,16,5,"ومن جماعة بني اسرائيل ياخذ تيسين من المعز لذبيحة خطية وكبشا واحدا لمحرقة"));
arasuperobject.biblemap.put("b3_16_6", new arasuperobject(3,16,6,"ويقرّب هرون ثور الخطية الذي له ويكفّر عن نفسه وعن بيته"));
arasuperobject.biblemap.put("b3_16_7", new arasuperobject(3,16,7,"ويأخذ التيسين ويوقفهما امام الرب لدى باب خيمة الاجتماع"));
arasuperobject.biblemap.put("b3_16_8", new arasuperobject(3,16,8,"ويلقي هرون على التيسين قرعتين قرعة للرب وقرعة لعزازيل"));
arasuperobject.biblemap.put("b3_16_9", new arasuperobject(3,16,9,"ويقرّب هرون التيس الذي خرجت عليه القرعة للرب ويعمله ذبيحة خطية"));
arasuperobject.biblemap.put("b3_16_10", new arasuperobject(3,16,10,"واما التيس الذي خرجت عليه القرعة لعزازيل فيوقف حيّا امام الرب ليكفّر عنه ليرسله الى عزازيل الى البرية"));
arasuperobject.biblemap.put("b3_16_11", new arasuperobject(3,16,11,"ويقدم هرون ثور الخطية الذي له ويكفّر عن نفسه وعن بيته ويذبح ثور الخطية الذي له"));
arasuperobject.biblemap.put("b3_16_12", new arasuperobject(3,16,12,"ويأخذ ملء المجمرة جمر نار عن المذبح من امام الرب وملء راحتيه بخورا عطرا دقيقا ويدخل بهما الى داخل الحجاب"));
arasuperobject.biblemap.put("b3_16_13", new arasuperobject(3,16,13,"ويجعل البخور على النار امام الرب فتغشي سحابة البخور الغطاء الذي على الشهادة فلا يموت"));
arasuperobject.biblemap.put("b3_16_14", new arasuperobject(3,16,14,"ثم يأخذ من دم الثور وينضح باصبعه على وجه الغطاء الى الشرق. وقدام الغطاء ينضح سبع مرات من الدم باصبعه"));
arasuperobject.biblemap.put("b3_16_15", new arasuperobject(3,16,15,"ثم يذبح تيس الخطية الذي للشعب ويدخل بدمه الى داخل الحجاب ويفعل بدمه كما فعل بدم الثور ينضحه على الغطاء وقدام الغطاء"));
arasuperobject.biblemap.put("b3_16_16", new arasuperobject(3,16,16,"فيكفّر عن القدس من نجاسات بني اسرائيل ومن سيّآتهم مع كل خطاياهم. وهكذا يفعل لخيمة الاجتماع القائمة بينهم في وسط نجاساتهم"));
arasuperobject.biblemap.put("b3_16_17", new arasuperobject(3,16,17,"ولا يكن انسان في خيمة الاجتماع من دخوله للتكفير في القدس الى خروجه. فيكفّر عن نفسه وعن بيته وعن كل جماعة اسرائيل"));
arasuperobject.biblemap.put("b3_16_18", new arasuperobject(3,16,18,"ثم يخرج الى المذبح الذي امام الرب ويكفّر عنه. ياخذ من دم الثور ومن دم التيس ويجعل على قرون المذبح مستديرا"));
arasuperobject.biblemap.put("b3_16_19", new arasuperobject(3,16,19,"وينضح عليه من الدم باصبعه سبع مرات ويطهّره ويقدسه من نجاسات بني اسرائيل"));
arasuperobject.biblemap.put("b3_16_20", new arasuperobject(3,16,20,"ومتى فرغ من التكفير عن القدس وعن خيمة الاجتماع وعن المذبح يقدم التيس الحي"));
arasuperobject.biblemap.put("b3_16_21", new arasuperobject(3,16,21,"ويضع هرون يديه على راس التيس الحي ويقرّ عليه بكل ذنوب بني اسرائيل وكل سيّآتهم مع كل خطاياهم ويجعلها على راس التيس ويرسله بيد من يلاقيه الى البرية"));
arasuperobject.biblemap.put("b3_16_22", new arasuperobject(3,16,22,"ليحمل التيس عليه كل ذنوبهم الى ارض مقفرة فيطلق التيس في البرية"));
arasuperobject.biblemap.put("b3_16_23", new arasuperobject(3,16,23,"ثم يدخل هرون الى خيمة الاجتماع ويخلع ثياب الكتان التي لبسها عند دخوله الى القدس ويضعها هناك"));
arasuperobject.biblemap.put("b3_16_24", new arasuperobject(3,16,24,"ويرحض جسده بماء في مكان مقدس ثم يلبس ثيابه ويخرج ويعمل محرقته ومحرقة الشعب ويكفّر عن نفسه وعن الشعب"));
arasuperobject.biblemap.put("b3_16_25", new arasuperobject(3,16,25,"وشحم ذبيحة الخطية يوقده على المذبح"));
arasuperobject.biblemap.put("b3_16_26", new arasuperobject(3,16,26,"والذي اطلق التيس الى عزازيل يغسل ثيابه ويرحض جسده بماء وبعد ذلك يدخل الى المحلّة"));
arasuperobject.biblemap.put("b3_16_27", new arasuperobject(3,16,27,"وثور الخطية وتيس الخطية اللذان أتي بدمهما للتكفير في القدس يخرجهما الى خارج المحلّة ويحرقون بالنار جلديهما ولحمهما وفرثهما"));
arasuperobject.biblemap.put("b3_16_28", new arasuperobject(3,16,28,"والذي يحرقهما يغسل ثيابه ويرحض جسده بماء وبعد ذلك يدخل الى المحلّة"));
arasuperobject.biblemap.put("b3_16_29", new arasuperobject(3,16,29,"ويكون لكم فريضة دهرية انكم في الشهر السابع في عاشر الشهر تذللون نفوسكم وكل عمل لا تعملون الوطني والغريب النازل في وسطكم"));
arasuperobject.biblemap.put("b3_16_30", new arasuperobject(3,16,30,"لانه في هذا اليوم يكفّر عنكم لتطهيركم. من جميع خطاياكم امام الرب تطهرون"));
arasuperobject.biblemap.put("b3_16_31", new arasuperobject(3,16,31,"سبت عطلة هو لكم وتذلّلون نفوسكم فريضة دهرية"));
arasuperobject.biblemap.put("b3_16_32", new arasuperobject(3,16,32,"ويكفّر الكاهن الذي يمسحه والذي يملأ يده للكهانة عوضا عن ابيه. يلبس ثياب الكتان الثياب المقدسة"));
arasuperobject.biblemap.put("b3_16_33", new arasuperobject(3,16,33,"ويكفّر عن مقدس القدس. وعن خيمة الاجتماع والمذبح يكفّر. وعن الكهنة وكل شعب الجماعة يكفّر"));
arasuperobject.biblemap.put("b3_16_34", new arasuperobject(3,16,34,"وتكون هذه لكم فريضة دهرية للتكفير عن بني اسرائيل من جميع خطاياهم مرة في السنة. ففعل كما امر الرب موسى"));
arasuperobject.biblemap.put("b3_17_1", new arasuperobject(3,17,1,"وكلم الرب موسى قائلا"));
arasuperobject.biblemap.put("b3_17_2", new arasuperobject(3,17,2,"كلم هرون وبنيه وجميع بني اسرائيل وقل لهم. هذا هو الامر الذي يوصي به الرب قائلا"));
arasuperobject.biblemap.put("b3_17_3", new arasuperobject(3,17,3,"كل انسان من بيت اسرائيل يذبح بقرا او غنما او معزى في المحلّة او يذبح خارج المحلّة"));
arasuperobject.biblemap.put("b3_17_4", new arasuperobject(3,17,4,"والى باب خيمة الاجتماع لا يأتي به ليقرّب قربانا للرب امام مسكن الرب يحسب على ذلك الانسان دم. قد سفك دما فيقطع ذلك الانسان من شعبه"));
arasuperobject.biblemap.put("b3_17_5", new arasuperobject(3,17,5,"لكي يأتي بنو اسرائيل بذبائحهم التي يذبحونها على وجه الصحراء ويقدموها للرب الى باب خيمة الاجتماع الى الكاهن ويذبحوها ذبائح سلامة للرب"));
arasuperobject.biblemap.put("b3_17_6", new arasuperobject(3,17,6,"ويرشّ الكاهن الدم على مذبح الرب لدى باب خيمة الاجتماع ويوقد الشحم لرائحة سرور للرب"));
arasuperobject.biblemap.put("b3_17_7", new arasuperobject(3,17,7,"ولا يذبحوا بعد ذبائحهم للتيوس التي هم يزنون وراءها. فريضة دهرية تكون هذه لهم في اجيالهم"));
arasuperobject.biblemap.put("b3_17_8", new arasuperobject(3,17,8,"وتقول لهم. كل انسان من بيت اسرائيل ومن الغرباء الذين ينزلون في وسطكم يصعد محرقة او ذبيحة"));
arasuperobject.biblemap.put("b3_17_9", new arasuperobject(3,17,9,"ولا يأتي بها الى باب خيمة الاجتماع ليصنعها للرب يقطع ذلك الانسان من شعبه"));
arasuperobject.biblemap.put("b3_17_10", new arasuperobject(3,17,10,"وكل انسان من بيت اسرائيل ومن الغرباء النازلين في وسطكم يأكل دما اجعل وجهي ضد النفس الآكلة الدم واقطعها من شعبها"));
arasuperobject.biblemap.put("b3_17_11", new arasuperobject(3,17,11,"لان نفس الجسد هي في الدم فانا اعطيتكم اياه على المذبح للتكفير عن نفوسكم. لان الدم يكفّر عن النفس"));
arasuperobject.biblemap.put("b3_17_12", new arasuperobject(3,17,12,"لذلك قلت لبني اسرائيل لا تاكل نفس منكم دما ولا يأكل الغريب النازل في وسطكم دما"));
arasuperobject.biblemap.put("b3_17_13", new arasuperobject(3,17,13,"وكل انسان من بني اسرائيل ومن الغرباء النازلين في وسطكم يصطاد صيدا وحشا او طائرا يؤكل يسفك دمه ويغطيه بالتراب"));
arasuperobject.biblemap.put("b3_17_14", new arasuperobject(3,17,14,"لان نفس كل جسد دمه هو بنفسه. فقلت لبني اسرائيل لا تأكلوا دم جسد ما. لان نفس كل جسد هي دمه. كل من اكله يقطع"));
arasuperobject.biblemap.put("b3_17_15", new arasuperobject(3,17,15,"وكل انسان يأكل ميتة او فريسة وطنيا كان او غريبا يغسل ثيابه ويستحم بماء ويبقى نجسا الى المساء ثم يكون طاهرا"));
arasuperobject.biblemap.put("b3_17_16", new arasuperobject(3,17,16,"وان لم يغسل ولم يرحض جسده يحمل ذنبه"));
arasuperobject.biblemap.put("b3_18_1", new arasuperobject(3,18,1,"وكلم الرب موسى قائلا"));
arasuperobject.biblemap.put("b3_18_2", new arasuperobject(3,18,2,"كلّم بني اسرائيل وقل لهم. انا الرب الهكم"));
arasuperobject.biblemap.put("b3_18_3", new arasuperobject(3,18,3,"مثل عمل ارض مصر التي سكنتم فيها لا تعملوا ومثل عمل ارض كنعان التي انا آت بكم اليها لا تعملوا وحسب فرائضهم لا تسلكوا"));
arasuperobject.biblemap.put("b3_18_4", new arasuperobject(3,18,4,"احكامي تعملون وفرائضي تحفظون لتسلكوا فيها. انا الرب الهكم"));
arasuperobject.biblemap.put("b3_18_5", new arasuperobject(3,18,5,"فتحفظون فرائضي واحكامي التي اذا فعلها الانسان يحيا بها. انا الرب"));
arasuperobject.biblemap.put("b3_18_6", new arasuperobject(3,18,6,"لا يقترب انسان الى قريب جسده ليكشف العورة. انا الرب"));
arasuperobject.biblemap.put("b3_18_7", new arasuperobject(3,18,7,"عورة ابيك وعورة امك لا تكشف. انها امك لا تكشف عورتها"));
arasuperobject.biblemap.put("b3_18_8", new arasuperobject(3,18,8,"عورة امرأة ابيك لا تكشف. انها عورة ابيك"));
arasuperobject.biblemap.put("b3_18_9", new arasuperobject(3,18,9,"عورة اختك بنت ابيك او بنت امك المولودة في البيت او المولودة خارجا لا تكشف عورتها"));
arasuperobject.biblemap.put("b3_18_10", new arasuperobject(3,18,10,"عورة ابنة ابنك او ابنة بنتك لا تكشف عورتها. انها عورتك"));
arasuperobject.biblemap.put("b3_18_11", new arasuperobject(3,18,11,"عورة بنت امرأة ابيك المولودة من ابيك لا تكشف عورتها انها اختك"));
arasuperobject.biblemap.put("b3_18_12", new arasuperobject(3,18,12,"عورة اخت ابيك لا تكشف. انها قريبة ابيك"));
arasuperobject.biblemap.put("b3_18_13", new arasuperobject(3,18,13,"عورة اخت امك لا تكشف. انها قريبة امك"));
arasuperobject.biblemap.put("b3_18_14", new arasuperobject(3,18,14,"عورة اخي ابيك لا تكشف. الى امرأته لا تقترب. انها عمتك"));
arasuperobject.biblemap.put("b3_18_15", new arasuperobject(3,18,15,"عورة كنّتك لا تكشف. انها امرأة ابنك. لا تكشف عورتها"));
arasuperobject.biblemap.put("b3_18_16", new arasuperobject(3,18,16,"عورة امرأة اخيك لا تكشف. انها عورة اخيك"));
arasuperobject.biblemap.put("b3_18_17", new arasuperobject(3,18,17,"عورة امرأة وبنتها لا تكشف. ولا تاخذ ابنة ابنها او ابنة بنتها لتكشف عورتها. انهما قريبتاها. انه رذيلة"));
arasuperobject.biblemap.put("b3_18_18", new arasuperobject(3,18,18,"ولا تأخذ امرأة على اختها للضرّ لتكشف عورتها معها في حياتها"));
arasuperobject.biblemap.put("b3_18_19", new arasuperobject(3,18,19,"ولا تقترب الى امرأة في نجاسة طمثها لتكشف عورتها"));
arasuperobject.biblemap.put("b3_18_20", new arasuperobject(3,18,20,"ولا تجعل مع امرأة صاحبك مضجعك لزرع فتتنجس بها"));
arasuperobject.biblemap.put("b3_18_21", new arasuperobject(3,18,21,"ولا تعط من زرعك للاجازة لمولك لئلا تدنس اسم الهك. انا الرب"));
arasuperobject.biblemap.put("b3_18_22", new arasuperobject(3,18,22,"ولا تضاجع ذكرا مضاجعة امرأة. انه رجس"));
arasuperobject.biblemap.put("b3_18_23", new arasuperobject(3,18,23,"ولا تجعل مع بهيمة مضجعك فتتنجس بها ولا تقف امرأة امام بهيمة لنزائها. انه فاحشة"));
arasuperobject.biblemap.put("b3_18_24", new arasuperobject(3,18,24,"بكل هذه لا تتنجسوا لانه بكل هذه قد تنجس الشعوب الذين انا طاردهم من امامكم"));
arasuperobject.biblemap.put("b3_18_25", new arasuperobject(3,18,25,"فتنجست الارض. فاجتزي ذنبها منها فتقذف الارض سكانها"));
arasuperobject.biblemap.put("b3_18_26", new arasuperobject(3,18,26,"لكن تحفظون انتم فرائضي واحكامي ولا تعملون شيئا من جميع هذه الرجسات لا الوطني ولا الغريب النازل في وسطكم"));
arasuperobject.biblemap.put("b3_18_27", new arasuperobject(3,18,27,"لان جميع هذه الرجسات قد عملها اهل الارض الذين قبلكم فتنجست الارض"));
arasuperobject.biblemap.put("b3_18_28", new arasuperobject(3,18,28,"فلا تقذفكم الارض بتنجيسكم اياها كما قذفت الشعوب التي قبلكم"));
arasuperobject.biblemap.put("b3_18_29", new arasuperobject(3,18,29,"بل كل من عمل شيئا من جميع هذه الرجسات تقطع الانفس التي تعملها من شعبها"));
arasuperobject.biblemap.put("b3_18_30", new arasuperobject(3,18,30,"فتحفظون شعائري لكي لا تعملوا شيئا من الرسوم الرجسة التي عملت قبلكم ولا تتنجسوا بها. انا الرب الهكم"));
arasuperobject.biblemap.put("b3_19_1", new arasuperobject(3,19,1,"وكلم الرب موسى قائلا"));
arasuperobject.biblemap.put("b3_19_2", new arasuperobject(3,19,2,"كلم كل جماعة بني اسرائيل وقل لهم تكونون قديسين لاني قدوس الرب الهكم"));
arasuperobject.biblemap.put("b3_19_3", new arasuperobject(3,19,3,"تهابون كل انسان امه واباه وتحفظون سبوتي. انا الرب الهكم"));
arasuperobject.biblemap.put("b3_19_4", new arasuperobject(3,19,4,"لا تلتفتوا الى الاوثان وآلهة مسبوكة لا تصنعوا لانفسكم. انا الرب الهكم"));
arasuperobject.biblemap.put("b3_19_5", new arasuperobject(3,19,5,"ومتى ذبحتم ذبيحة سلامة للرب فللرضا عنكم تذبحونها"));
arasuperobject.biblemap.put("b3_19_6", new arasuperobject(3,19,6,"يوم تذبحونها تؤكل وفي الغد. والفاضل الى اليوم الثالث يحرق بالنار"));
arasuperobject.biblemap.put("b3_19_7", new arasuperobject(3,19,7,"واذا اكلت في اليوم الثالث فذلك نجاسة لا يرضى به"));
arasuperobject.biblemap.put("b3_19_8", new arasuperobject(3,19,8,"ومن اكل منها يحمل ذنبه لانه قد دنّس قدس الرب. فتقطع تلك النفس من شعبها"));
arasuperobject.biblemap.put("b3_19_9", new arasuperobject(3,19,9,"وعندما تحصدون حصيد ارضكم لا تكمل زوايا حقلك في الحصاد. ولقاط حصيدك لا تلتقط"));
arasuperobject.biblemap.put("b3_19_10", new arasuperobject(3,19,10,"وكرمك لا تعلّله ونثار كرمك لا تلتقط. للمسكين والغريب تتركه. انا الرب الهكم"));
arasuperobject.biblemap.put("b3_19_11", new arasuperobject(3,19,11,"لا تسرقوا ولا تكذبوا ولا تغدروا احدكم بصاحبه"));
arasuperobject.biblemap.put("b3_19_12", new arasuperobject(3,19,12,"ولا تحلفوا باسمي للكذب فتدنّس اسم الهك. انا الرب"));
arasuperobject.biblemap.put("b3_19_13", new arasuperobject(3,19,13,"لا تغصب قريبك ولا تسلب. ولا تبت اجرة اجير عندك الى الغد"));
arasuperobject.biblemap.put("b3_19_14", new arasuperobject(3,19,14,"لا تشتم الاصم وقدام الاعمى لا تجعل معثرة. بل اخش الهك. انا الرب"));
arasuperobject.biblemap.put("b3_19_15", new arasuperobject(3,19,15,"لا ترتكبوا جورا في القضاء. لا تأخذوا بوجه مسكين ولا تحترم وجه كبير. بالعدل تحكم لقريبك"));
arasuperobject.biblemap.put("b3_19_16", new arasuperobject(3,19,16,"لا تسع في الوشاية بين شعبك. لا تقف على دم قريبك. انا الرب"));
arasuperobject.biblemap.put("b3_19_17", new arasuperobject(3,19,17,"لا تبغض اخاك في قلبك. انذارا تنذر صاحبك ولا تحمل لاجله خطية"));
arasuperobject.biblemap.put("b3_19_18", new arasuperobject(3,19,18,"لا تنتقم ولا تحقد على ابناء شعبك بل تحب قريبك كنفسك. انا الرب"));
arasuperobject.biblemap.put("b3_19_19", new arasuperobject(3,19,19,"فرائضي تحفظون. لا تنزّ بهائمك جنسين وحقلك لا تزرع صنفين ولا يكن عليك ثوب مصنّف من صنفين"));
arasuperobject.biblemap.put("b3_19_20", new arasuperobject(3,19,20,"واذا اضطجع رجل مع امرأة اضطجاع زرع وهي امة مخطوبة لرجل ولم تفد فداء ولا أعطيت حريتها فليكن تاديب. لا يقتلا لانها لم تعتق"));
arasuperobject.biblemap.put("b3_19_21", new arasuperobject(3,19,21,"ويأتي الى الرب بذبيحة لاثمه الى باب خيمة الاجتماع كبشا ذبيحة اثم"));
arasuperobject.biblemap.put("b3_19_22", new arasuperobject(3,19,22,"فيكفّر عنه الكاهن بكبش الاثم امام الرب من خطيته التي اخطأ فيصفح له عن خطيته التي اخطأ"));
arasuperobject.biblemap.put("b3_19_23", new arasuperobject(3,19,23,"ومتى دخلتم الارض وغرستم كل شجرة للطعام تحسبون ثمرها غرلتها. ثلاث سنين تكون لكم غلفاء. لا يؤكل منها"));
arasuperobject.biblemap.put("b3_19_24", new arasuperobject(3,19,24,"وفي السنة الرابعة يكون كل ثمرها قدسا لتمجيد الرب"));
arasuperobject.biblemap.put("b3_19_25", new arasuperobject(3,19,25,"وفي السنة الخامسة تاكلون ثمرها. لتزيد لكم غلتها. انا الرب الهكم"));
arasuperobject.biblemap.put("b3_19_26", new arasuperobject(3,19,26,"لا تأكلوا بالدم. لا تتفاءلوا ولا تعيفوا"));
arasuperobject.biblemap.put("b3_19_27", new arasuperobject(3,19,27,"لا تقصّروا رؤوسكم مستديرا ولا تفسد عارضيك"));
arasuperobject.biblemap.put("b3_19_28", new arasuperobject(3,19,28,"ولا تجرحوا اجسادكم لميت. وكتابة وسم لا تجعلوا فيكم. انا الرب"));
arasuperobject.biblemap.put("b3_19_29", new arasuperobject(3,19,29,"لا تدنس ابنتك بتعريضها للزنى لئلا تزني الارض وتمتلئ الارض رذيلة"));
arasuperobject.biblemap.put("b3_19_30", new arasuperobject(3,19,30,"سبوتي تحفظون ومقدسي تهابون. انا الرب"));
arasuperobject.biblemap.put("b3_19_31", new arasuperobject(3,19,31,"لا تلتفتوا الى الجان ولا تطلبوا التوابع فتتنجّسوا بهم. انا الرب الهكم"));
arasuperobject.biblemap.put("b3_19_32", new arasuperobject(3,19,32,"من امام الاشيب تقوم وتحترم وجه الشيخ وتخشى الهك. انا الرب"));
arasuperobject.biblemap.put("b3_19_33", new arasuperobject(3,19,33,"واذا نزل عندك غريب في ارضكم فلا تظلموه"));
arasuperobject.biblemap.put("b3_19_34", new arasuperobject(3,19,34,"كالوطني منكم يكون لكم الغريب النازل عندكم وتحبه كنفسك لانكم كنتم غرباء في ارض مصر. انا الرب الهكم"));
arasuperobject.biblemap.put("b3_19_35", new arasuperobject(3,19,35,"لا ترتكبوا جورا في القضاء لا في القياس ولا في الوزن ولا في الكيل"));
arasuperobject.biblemap.put("b3_19_36", new arasuperobject(3,19,36,"ميزان حق ووزنات حق وإيفة حق وهين حق تكون لكم. انا الرب الهكم الذي اخرجكم من ارض مصر"));
arasuperobject.biblemap.put("b3_19_37", new arasuperobject(3,19,37,"فتحفظون كل فرائضي وكل احكامي وتعملونها. انا الرب"));
arasuperobject.biblemap.put("b3_20_1", new arasuperobject(3,20,1,"وكلم الرب موسى قائلا"));
arasuperobject.biblemap.put("b3_20_2", new arasuperobject(3,20,2,"وتقول لبني اسرائيل. كل انسان من بني اسرائيل ومن الغرباء النازلين في اسرائيل اعطى من زرعه لمولك فانه يقتل. يرجمه شعب الارض بالحجارة"));
arasuperobject.biblemap.put("b3_20_3", new arasuperobject(3,20,3,"واجعل انا وجهي ضد ذلك الانسان واقطعه من شعبه لانه اعطى من زرعه لمولك لكي ينجس مقدسي ويدنس اسمي القدوس"));
arasuperobject.biblemap.put("b3_20_4", new arasuperobject(3,20,4,"وان غمض شعب الارض اعينهم عن ذلك الانسان عندما يعطي من زرعه لمولك فلم يقتلوه"));
arasuperobject.biblemap.put("b3_20_5", new arasuperobject(3,20,5,"فاني اضع وجهي ضد ذلك الانسان وضد عشيرته واقطعه وجميع الفاجرين وراءه بالزنى وراء مولك من شعبهم"));
arasuperobject.biblemap.put("b3_20_6", new arasuperobject(3,20,6,"والنفس التي تلتفت الى الجان والى التوابع لتزني وراءهم اجعل وجهي ضد تلك النفس واقطعها من شعبها"));
arasuperobject.biblemap.put("b3_20_7", new arasuperobject(3,20,7,"فتتقدّسون وتكونون قديسين لاني انا الرب الهكم"));
arasuperobject.biblemap.put("b3_20_8", new arasuperobject(3,20,8,"وتحفظون فرائضي وتعملونها. انا الرب مقدّسكم"));
arasuperobject.biblemap.put("b3_20_9", new arasuperobject(3,20,9,"كل انسان سبّ اباه او امه فانه يقتل. قد سبّ اباه او امه. دمه عليه"));
arasuperobject.biblemap.put("b3_20_10", new arasuperobject(3,20,10,"واذا زنى رجل مع امرأة فاذا زنى مع امرأة قريبه فانه يقتل الزاني والزانية"));
arasuperobject.biblemap.put("b3_20_11", new arasuperobject(3,20,11,"واذا اضطجع رجل مع امرأة ابيه فقد كشف عورة ابيه. انهما يقتلان كلاهما. دمهما عليهما"));
arasuperobject.biblemap.put("b3_20_12", new arasuperobject(3,20,12,"واذا اضطجع رجل مع كنّته فانهما يقتلان كلاهما. قد فعلا فاحشة. دمهما عليهما"));
arasuperobject.biblemap.put("b3_20_13", new arasuperobject(3,20,13,"واذا اضطجع رجل مع ذكر اضطجاع امرأة فقد فعلا كلاهما رجسا. انهما يقتلان. دمهما عليهما"));
arasuperobject.biblemap.put("b3_20_14", new arasuperobject(3,20,14,"واذا اتخذ رجل امرأة وامها فذلك رذيلة. بالنار يحرقونه واياهما لكي لا يكون رذيلة بينكم"));
arasuperobject.biblemap.put("b3_20_15", new arasuperobject(3,20,15,"واذا جعل رجل مضجعه مع بهيمة فانه يقتل والبهيمة تميتونها"));
arasuperobject.biblemap.put("b3_20_16", new arasuperobject(3,20,16,"واذا اقتربت امرأة الى بهيمة لنزائها تميت المرأة والبهيمة. انهما يقتلان. دمهما عليهما"));
arasuperobject.biblemap.put("b3_20_17", new arasuperobject(3,20,17,"واذا اخذ رجل اخته بنت ابيه او بنت امه ورأى عورتها ورأت هي عورته فذلك عار. يقطعان امام اعين بني شعبهما. قد كشف عورة اخته. يحمل ذنبه"));
arasuperobject.biblemap.put("b3_20_18", new arasuperobject(3,20,18,"واذا اضطجع رجل مع امرأة طامث وكشف عورتها عرّى ينبوعها وكشفت هي ينبوع دمها يقطعان كلاهما من شعبهما"));
arasuperobject.biblemap.put("b3_20_19", new arasuperobject(3,20,19,"عورة اخت امك او اخت ابيك لا تكشف انه قد عرّى قريبته. يحملان ذنبهما"));
arasuperobject.biblemap.put("b3_20_20", new arasuperobject(3,20,20,"واذا اضطجع رجل مع امرأة عمه فقد كشف عورة عمه. يحملان ذنبهما. يموتان عقيمين"));
arasuperobject.biblemap.put("b3_20_21", new arasuperobject(3,20,21,"واذا اخذ رجل امرأة اخيه فذلك نجاسة. قد كشف عورة اخيه. يكونان عقيمين"));
arasuperobject.biblemap.put("b3_20_22", new arasuperobject(3,20,22,"فتحفظون جميع فرائضي وجميع احكامي وتعملونها لكي لا تقذفكم الارض التي انا آت بكم اليها لتسكنوا فيها"));
arasuperobject.biblemap.put("b3_20_23", new arasuperobject(3,20,23,"ولا تسلكون في رسوم الشعوب الذين انا طاردهم من امامكم. لانهم قد فعلوا كل هذه فكرهتهم"));
arasuperobject.biblemap.put("b3_20_24", new arasuperobject(3,20,24,"وقلت لكم ترثون انتم ارضهم وانا اعطيكم اياها لترثوها ارضا تفيض لبنا وعسلا. انا الرب الهكم الذي ميّزكم من الشعوب"));
arasuperobject.biblemap.put("b3_20_25", new arasuperobject(3,20,25,"فتميّزون بين البهائم الطاهرة والنجسة وبين الطيور النجسة والطاهرة. فلا تدنسوا نفوسكم بالبهائم والطيور ولا بكل ما يدبّ على الارض مّما ميّزته لكم ليكون نجسا"));
arasuperobject.biblemap.put("b3_20_26", new arasuperobject(3,20,26,"وتكونون لي قديسين لاني قدوس انا الرب. وقد ميّزتكم من الشعوب لتكونوا لي"));
arasuperobject.biblemap.put("b3_20_27", new arasuperobject(3,20,27,"واذا كان في رجل او امرأة جان او تابعة فانه يقتل بالحجارة يرجمونه. دمه عليه"));
arasuperobject.biblemap.put("b3_21_1", new arasuperobject(3,21,1,"وقال الرب لموسى كلم الكهنة بني هرون وقل لهم. لا يتنجس احد منكم لميت في قومه"));
arasuperobject.biblemap.put("b3_21_2", new arasuperobject(3,21,2,"الا لاقربائه الاقرب اليه امه وابيه وابنه وابنته واخيه"));
arasuperobject.biblemap.put("b3_21_3", new arasuperobject(3,21,3,"واخته العذراء القريبة اليه التي لم تصر لرجل. لاجلها يتنجس"));
arasuperobject.biblemap.put("b3_21_4", new arasuperobject(3,21,4,"كزوج لا يتنجس باهله لتدنيسه"));
arasuperobject.biblemap.put("b3_21_5", new arasuperobject(3,21,5,"لا يجعلوا قرعة في رؤوسهم ولا يحلقوا عوارض لحاهم ولا يجرحوا جراحة في اجسادهم"));
arasuperobject.biblemap.put("b3_21_6", new arasuperobject(3,21,6,"مقدسين يكونون لالههم ولا يدنسون اسم الههم لانهم يقرّبون وقائد الرب طعام الههم فيكونون قدسا"));
arasuperobject.biblemap.put("b3_21_7", new arasuperobject(3,21,7,"امرأة زانية او مدنّسة لا يأخذوا ولا يأخذوا امرأة مطلّقة من زوجها. لانه مقدس لالهه"));
arasuperobject.biblemap.put("b3_21_8", new arasuperobject(3,21,8,"فتحسبه مقدسا لانه يقرب خبز الهك. مقدسا يكون عندك لاني قدوس انا الرب مقدسكم"));
arasuperobject.biblemap.put("b3_21_9", new arasuperobject(3,21,9,"واذا تدنست ابنة كاهن بالزنى فقد دنست اباها. بالنار تحرق"));
arasuperobject.biblemap.put("b3_21_10", new arasuperobject(3,21,10,"والكاهن الاعظم بين اخوته الذي صبّ على راسه دهن المسحة وملئت يده ليلبس الثياب لا يكشف راسه ولا يشق ثيابه"));
arasuperobject.biblemap.put("b3_21_11", new arasuperobject(3,21,11,"ولا يأتي الى نفس ميتة ولا يتنجس لابيه او امه"));
arasuperobject.biblemap.put("b3_21_12", new arasuperobject(3,21,12,"ولا يخرج من المقدس لئلا يدنس مقدس الهه. لان اكليل دهن مسحة الهه عليه. انا الرب"));
arasuperobject.biblemap.put("b3_21_13", new arasuperobject(3,21,13,"هذا يأخذ امرأة عذراء"));
arasuperobject.biblemap.put("b3_21_14", new arasuperobject(3,21,14,"اما الارملة والمطلقة والمدنسة والزانية فمن هؤلاء لا يأخذ بل يتخذ عذراء من قومه امرأة"));
arasuperobject.biblemap.put("b3_21_15", new arasuperobject(3,21,15,"ولا يدنس زرعه بين شعبه لاني انا الرب مقدسه"));
arasuperobject.biblemap.put("b3_21_16", new arasuperobject(3,21,16,"وكلم الرب موسى قائلا"));
arasuperobject.biblemap.put("b3_21_17", new arasuperobject(3,21,17,"كلم هرون قائلا. اذا كان رجل من نسلك في اجيالهم فيه عيب فلا يتقدم ليقرّب خبز الهه"));
arasuperobject.biblemap.put("b3_21_18", new arasuperobject(3,21,18,"لان كل رجل فيه عيب لا يتقدم. لا رجل اعمى ولا اعرج ولا افطس ولا زوائدي"));
arasuperobject.biblemap.put("b3_21_19", new arasuperobject(3,21,19,"ولا رجل فيه كسر رجل او كسر يد"));
arasuperobject.biblemap.put("b3_21_20", new arasuperobject(3,21,20,"ولا احدب ولا اكشم ولا من في عينه بياض ولا اجرب ولا اكلف ولا مرضوض الخصى"));
arasuperobject.biblemap.put("b3_21_21", new arasuperobject(3,21,21,"كل رجل فيه عيب من نسل هرون الكاهن لا يتقدم ليقرّب وقائد الرب. فيه عيب لا يتقدم ليقرّب خبز الهه"));
arasuperobject.biblemap.put("b3_21_22", new arasuperobject(3,21,22,"خبز الهه من قدس الاقداس ومن القدس ياكل"));
arasuperobject.biblemap.put("b3_21_23", new arasuperobject(3,21,23,"لكن الى الحجاب لا يأتي والى المذبح لا يقترب لان فيه عيبا لئلا يدنّس مقدسي. لاني انا الرب مقدّسهم"));
arasuperobject.biblemap.put("b3_21_24", new arasuperobject(3,21,24,"فكلم موسى هرون وبنيه وكل بني اسرائيل"));
arasuperobject.biblemap.put("b3_22_1", new arasuperobject(3,22,1,"وكلم الرب موسى قائلا"));
arasuperobject.biblemap.put("b3_22_2", new arasuperobject(3,22,2,"كلم هرون وبنيه ان يتوقّوا اقداس بني اسرائيل التي يقدسونها لي ولا يدنّسوا اسمي القدوس. انا الرب"));
arasuperobject.biblemap.put("b3_22_3", new arasuperobject(3,22,3,"قل لهم. في اجيالكم كل انسان من جميع نسلكم اقترب الى الاقداس التي يقدسها بنو اسرائيل للرب ونجاسته عليه تقطع تلك النفس من امامي. انا الرب"));
arasuperobject.biblemap.put("b3_22_4", new arasuperobject(3,22,4,"كل انسان من نسل هرون وهو ابرص او ذو سيل لا يأكل من الاقداس حتى يطهر. ومن مسّ شيئا نجسا لميت او انسان حدث منه اضطجاع زرع"));
arasuperobject.biblemap.put("b3_22_5", new arasuperobject(3,22,5,"او انسان مسّ دبيبا يتنجس به او انسانا يتنجس به لنجاسة فيه"));
arasuperobject.biblemap.put("b3_22_6", new arasuperobject(3,22,6,"فالذي يمسّ ذلك يكون نجسا الى المساء ولا يأكل من الاقداس بل يرحض جسده بماء"));
arasuperobject.biblemap.put("b3_22_7", new arasuperobject(3,22,7,"فمتى غربت الشمس يكون طاهرا ثم يأكل من الاقداس لانها طعامه"));
arasuperobject.biblemap.put("b3_22_8", new arasuperobject(3,22,8,"ميتة او فريسة لا يأكل فيتنجس بها. انا الرب"));
arasuperobject.biblemap.put("b3_22_9", new arasuperobject(3,22,9,"فيحفظون شعائري لكي لا يحملوا لاجلها خطية يموتون بها لانهم يدنسونها. انا الرب مقدسهم"));
arasuperobject.biblemap.put("b3_22_10", new arasuperobject(3,22,10,"وكل اجنبي لا يأكل قدسا. نزيل كاهن واجيره لا ياكلون قدسا"));
arasuperobject.biblemap.put("b3_22_11", new arasuperobject(3,22,11,"لكن اذا اشترى كاهن احدا شراء فضة فهو يأكل منه والمولود في بيته. هما ياكلان من طعامه"));
arasuperobject.biblemap.put("b3_22_12", new arasuperobject(3,22,12,"واذا صارت ابنة كاهن لرجل اجنبي لا تأكل من رفيعة الاقداس"));
arasuperobject.biblemap.put("b3_22_13", new arasuperobject(3,22,13,"واما ابنة كاهن قد صارت ارملة او مطلّقة ولم يكن لها نسل ورجعت الى بيت ابيها كما في صباها فتأكل من طعام ابيها. لكن كل اجنبي لا ياكل منه"));
arasuperobject.biblemap.put("b3_22_14", new arasuperobject(3,22,14,"واذا اكل انسان قدسا سهوا يزيد عليه خمسه ويدفع القدس للكاهن"));
arasuperobject.biblemap.put("b3_22_15", new arasuperobject(3,22,15,"فلا يدنسون اقداس بني اسرائيل التي يرفعونها للرب"));
