arasuperobject.biblemap.put("b3_25_10", new arasuperobject(3,25,10,"وتقدسون السنة الخمسين وتنادون بالعتق في الارض لجميع سكانها. تكون لكم يوبيلا وترجعون كل الى ملكه وتعودون كل الى عشيرته"));
arasuperobject.biblemap.put("b3_25_11", new arasuperobject(3,25,11,"يوبيلا تكون لكم السنة الخمسون لا تزرعوا ولا تحصدوا زرّيعها ولا تقطفوا كرمها المحول"));
arasuperobject.biblemap.put("b3_25_12", new arasuperobject(3,25,12,"انها يوبيل. مقدسة تكون لكم. من الحقل تأكلون غلتها"));
arasuperobject.biblemap.put("b3_25_13", new arasuperobject(3,25,13,"في سنة اليوبيل هذه ترجعون كل الى ملكه"));
arasuperobject.biblemap.put("b3_25_14", new arasuperobject(3,25,14,"فمتى بعت صاحبك مبيعا او اشتريت من يد صاحبك فلا يغبن احدكم اخاه"));
arasuperobject.biblemap.put("b3_25_15", new arasuperobject(3,25,15,"حسب عدد السنين بعد اليوبيل تشتري من صاحبك وحسب سني الغلة يبيعك"));
arasuperobject.biblemap.put("b3_25_16", new arasuperobject(3,25,16,"على قدر كثرة السنين تكثر ثمنه وعلى قدر قلّة السنين تقلل ثمنه لانه عدد الغلات يبيعك"));
arasuperobject.biblemap.put("b3_25_17", new arasuperobject(3,25,17,"فلا يغبن احدكم صاحبه بل اخش الهك. اني انا الرب الهكم"));
arasuperobject.biblemap.put("b3_25_18", new arasuperobject(3,25,18,"فتعملون فرائضي وتحفظون احكامي وتعملونها لتسكنوا على الارض آمنين"));
arasuperobject.biblemap.put("b3_25_19", new arasuperobject(3,25,19,"وتعطي الارض ثمرها فتاكلون للشبع وتسكنون عليها آمنين"));
arasuperobject.biblemap.put("b3_25_20", new arasuperobject(3,25,20,"واذا قلتم ماذا ناكل في السنة السابعة ان لم نزرع ولم نجمع غلتنا"));
arasuperobject.biblemap.put("b3_25_21", new arasuperobject(3,25,21,"فاني آمر ببركتي لكم في السنة السادسة فتعمل غلة لثلاث سنين"));
arasuperobject.biblemap.put("b3_25_22", new arasuperobject(3,25,22,"فتزرعون السنة الثامنة وتاكلون من الغلة العتيقة الى السنة التاسعة. الى ان تاتي غلتها تاكلون عتيقا"));
arasuperobject.biblemap.put("b3_25_23", new arasuperobject(3,25,23,"والارض لا تباع بتّة. لان لي الارض وانتم غرباء ونزلاء عندي"));
arasuperobject.biblemap.put("b3_25_24", new arasuperobject(3,25,24,"بل في كل ارض ملككم تجعلون فكاكا للارض"));
arasuperobject.biblemap.put("b3_25_25", new arasuperobject(3,25,25,"اذا افتقر اخوك فباع من ملكه ياتي وليّه الاقرب اليه ويفك مبيع اخيه"));
arasuperobject.biblemap.put("b3_25_26", new arasuperobject(3,25,26,"ومن لم يكن له ولي فان نالت يده ووجد مقدار فكاكه"));
arasuperobject.biblemap.put("b3_25_27", new arasuperobject(3,25,27,"يحسب سني بيعه ويرد الفاضل للانسان الذي باع له فيرجع الى ملكه"));
arasuperobject.biblemap.put("b3_25_28", new arasuperobject(3,25,28,"وان لم تنل يده كفاية ليرد له يكون مبيعه في يد شاريه الى سنة اليوبيل ثم يخرج في اليوبيل فيرجع الى ملكه"));
arasuperobject.biblemap.put("b3_25_29", new arasuperobject(3,25,29,"واذا باع انسان بيت سكن في مدينة ذات سور فيكون فكاكه الى تمام سنة بيعه. سنة يكون فكاكه"));
arasuperobject.biblemap.put("b3_25_30", new arasuperobject(3,25,30,"وان لم يفك قبل ان تكمل له سنة تامة وجب البيت الذي في المدينة ذات السور بتّة لشاريه في اجياله. لا يخرج في اليوبيل"));
arasuperobject.biblemap.put("b3_25_31", new arasuperobject(3,25,31,"لكن بيوت القرى التي ليس لها سور حولها فمع حقول الارض تحسب يكون لها فكاك وفي اليوبيل تخرج"));
arasuperobject.biblemap.put("b3_25_32", new arasuperobject(3,25,32,"واما مدن اللاويين بيوت مدن ملكهم فيكون لها فكاك مؤبّد للاويين"));
arasuperobject.biblemap.put("b3_25_33", new arasuperobject(3,25,33,"والذي يفكه من اللاويين المبيع من بيت او من مدينة ملكه يخرج في اليوبيل لان بيوت مدن اللاويين هي ملكهم في وسط بني اسرائيل"));
arasuperobject.biblemap.put("b3_25_34", new arasuperobject(3,25,34,"واما حقول المسارح لمدنهم فلا تباع لانها ملك دهري لهم"));
arasuperobject.biblemap.put("b3_25_35", new arasuperobject(3,25,35,"واذا افتقر اخوك وقصرت يده عندك فاعضده غريبا او مستوطنا فيعيش معك"));
arasuperobject.biblemap.put("b3_25_36", new arasuperobject(3,25,36,"لا تأخذ منه ربا ولا مرابحة بل اخش الهك فيعيش اخوك معك"));
arasuperobject.biblemap.put("b3_25_37", new arasuperobject(3,25,37,"فضتك لا تعطه بالربا وطعامك لا تعط بالمرابحة"));
arasuperobject.biblemap.put("b3_25_38", new arasuperobject(3,25,38,"انا الرب الهكم الذي اخرجكم من ارض مصر ليعطيكم ارض كنعان فيكون لكم الها"));
arasuperobject.biblemap.put("b3_25_39", new arasuperobject(3,25,39,"واذا افتقر اخوك عندك وبيع لك فلا تستعبده استعباد عبد"));
arasuperobject.biblemap.put("b3_25_40", new arasuperobject(3,25,40,"كاجير كنزيل يكون عندك. الى سنة اليوبيل يخدم عندك"));
arasuperobject.biblemap.put("b3_25_41", new arasuperobject(3,25,41,"ثم يخرج من عندك هو وبنوه معه ويعود الى عشيرته. والى ملك آبائه يرجع"));
arasuperobject.biblemap.put("b3_25_42", new arasuperobject(3,25,42,"لانهم عبيدي الذين اخرجتهم من ارض مصر لا يباعون بيع العبيد"));
arasuperobject.biblemap.put("b3_25_43", new arasuperobject(3,25,43,"لا تتسلط عليه بعنف. بل اخش الهك"));
arasuperobject.biblemap.put("b3_25_44", new arasuperobject(3,25,44,"واما عبيدك واماؤك الذين يكونون لك فمن الشعوب الذين حولكم. منهم تقتنون عبيدا واماء"));
arasuperobject.biblemap.put("b3_25_45", new arasuperobject(3,25,45,"وايضا من ابناء المستوطنين النازلين عندكم منهم تقتنون ومن عشائرهم الذين عندكم الذين يلدونهم في ارضكم فيكونون ملكا لكم"));
arasuperobject.biblemap.put("b3_25_46", new arasuperobject(3,25,46,"وتستملكونهم لابنائكم من بعدكم ميراث ملك. تستعبدونهم الى الدهر. واما اخوتكم بنو اسرائيل فلا يتسلط انسان على اخيه بعنف"));
arasuperobject.biblemap.put("b3_25_47", new arasuperobject(3,25,47,"واذا طالت يد غريب او نزيل عندك وافتقر اخوك عنده وبيع للغريب المستوطن عندك او لنسل عشيرة الغريب"));
arasuperobject.biblemap.put("b3_25_48", new arasuperobject(3,25,48,"فبعد بيعه يكون له فكاك. يفكه واحد من اخوته"));
arasuperobject.biblemap.put("b3_25_49", new arasuperobject(3,25,49,"او يفكه عمه او ابن عمه او يفكه واحد من اقرباء جسده من عشيرته او اذا نالت يده يفك نفسه"));
arasuperobject.biblemap.put("b3_25_50", new arasuperobject(3,25,50,"فيحاسب شاريه من سنة بيعه له الى سنة اليوبيل ويكون ثمن بيعه حسب عدد السنين. كايام اجير يكون عنده"));
arasuperobject.biblemap.put("b3_25_51", new arasuperobject(3,25,51,"ان بقي كثير من السنين فعلى قدرها يرد فكاكه من ثمن شرائه"));
arasuperobject.biblemap.put("b3_25_52", new arasuperobject(3,25,52,"وان بقي قليل من السنين الى سنة اليوبيل يحسب له وعلى قدر سنيه يرد فكاكه"));
arasuperobject.biblemap.put("b3_25_53", new arasuperobject(3,25,53,"كاجير من سنة الى سنة يكون عنده. لا يتسلط عليه بعنف امام عينيك"));
arasuperobject.biblemap.put("b3_25_54", new arasuperobject(3,25,54,"وان لم يفك بهؤلاء يخرج في سنة اليوبيل هو وبنوه معه"));
arasuperobject.biblemap.put("b3_25_55", new arasuperobject(3,25,55,"لان بني اسرائيل لي عبيد هم عبيدي الذين اخرجتهم من ارض مصر. انا الرب الهكم"));
arasuperobject.biblemap.put("b3_26_1", new arasuperobject(3,26,1,"لا تصنعوا لكم اوثانا ولا تقيموا لكم تمثالا منحوتا او نصبا ولا تجعلوا في ارضكم حجرا مصوّرا لتسجدوا له. لاني انا الرب الهكم"));
arasuperobject.biblemap.put("b3_26_2", new arasuperobject(3,26,2,"سبوتي تحفظون ومقدسي تهابون. انا الرب"));
arasuperobject.biblemap.put("b3_26_3", new arasuperobject(3,26,3,"اذا سلكتم في فرائضي وحفظتم وصاياي وعملتم بها"));
arasuperobject.biblemap.put("b3_26_4", new arasuperobject(3,26,4,"اعطي مطركم في حينه وتعطي الارض غلتها وتعطي اشجار الحقل اثمارها"));
arasuperobject.biblemap.put("b3_26_5", new arasuperobject(3,26,5,"ويلحق دراسكم بالقطاف ويلحق القطاف بالزرع فتأكلون خبزكم للشبع وتسكنون في ارضكم آمنين"));
arasuperobject.biblemap.put("b3_26_6", new arasuperobject(3,26,6,"واجعل سلاما في الارض فتنامون وليس من يزعجكم. وابيد الوحوش الرديئة من الارض ولا يعبر سيف في ارضكم"));
arasuperobject.biblemap.put("b3_26_7", new arasuperobject(3,26,7,"وتطردون اعداءكم فيسقطون امامكم بالسيف"));
arasuperobject.biblemap.put("b3_26_8", new arasuperobject(3,26,8,"يطرد خمسة منكم مئة ومئة منكم يطردون ربوة ويسقط اعداؤكم امامكم بالسيف"));
arasuperobject.biblemap.put("b3_26_9", new arasuperobject(3,26,9,"والتفت اليكم وأثمركم واكثركم وافي ميثاقي معكم"));
arasuperobject.biblemap.put("b3_26_10", new arasuperobject(3,26,10,"فتأكلون العتيق المعتّق وتخرجون العتيق من وجه الجديد"));
arasuperobject.biblemap.put("b3_26_11", new arasuperobject(3,26,11,"واجعل مسكني في وسطكم ولا ترذلكم نفسي"));
arasuperobject.biblemap.put("b3_26_12", new arasuperobject(3,26,12,"واسير بينكم واكون لكم الها وانتم تكونون لي شعبا"));
arasuperobject.biblemap.put("b3_26_13", new arasuperobject(3,26,13,"انا الرب الهكم الذي اخرجكم من ارض مصر من كونكم لهم عبيدا وقطع قيود نيركم وسيّركم قياما"));
arasuperobject.biblemap.put("b3_26_14", new arasuperobject(3,26,14,"لكن ان لم تسمعوا لي ولم تعملوا كل هذه الوصايا"));
arasuperobject.biblemap.put("b3_26_15", new arasuperobject(3,26,15,"وان رفضتم فرائضي وكرهت انفسكم احكامي فما عملتم كل وصاياي بل نكثتم ميثاقي"));
arasuperobject.biblemap.put("b3_26_16", new arasuperobject(3,26,16,"فاني اعمل هذه بكم. اسلّط عليكم رعبا وسلا وحمى تفني العينين وتتلف النفس وتزرعون باطلا زرعكم فياكله اعداؤكم"));
arasuperobject.biblemap.put("b3_26_17", new arasuperobject(3,26,17,"واجعل وجهي ضدكم فتنهزمون امام اعدائكم ويتسلط عليكم مبغضوكم وتهربون وليس من يطردكم"));
arasuperobject.biblemap.put("b3_26_18", new arasuperobject(3,26,18,"وان كنتم مع ذلك لا تسمعون لي ازيد على تأديبكم سبعة اضعاف حسب خطاياكم"));
arasuperobject.biblemap.put("b3_26_19", new arasuperobject(3,26,19,"فاحطم فخار عزكم واصير سماءكم كالحديد وارضكم كالنحاس"));
arasuperobject.biblemap.put("b3_26_20", new arasuperobject(3,26,20,"فتفرغ باطلا قوتكم وارضكم لا تعطي غلتها واشجار الارض لا تعطي اثمارها"));
arasuperobject.biblemap.put("b3_26_21", new arasuperobject(3,26,21,"وان سلكتم معي بالخلاف ولم تشاءوا ان تسمعوا لي ازيد عليكم ضربات سبعة اضعاف حسب خطاياكم"));
arasuperobject.biblemap.put("b3_26_22", new arasuperobject(3,26,22,"اطلق عليكم وحوش البرية فتعدمكم الاولاد وتقرض بهائمكم وتقللكم فتوحش طرقكم"));
arasuperobject.biblemap.put("b3_26_23", new arasuperobject(3,26,23,"وان لم تتأدبوا مني بذلك بل سلكتم معي بالخلاف"));
arasuperobject.biblemap.put("b3_26_24", new arasuperobject(3,26,24,"فاني انا اسلك معكم بالخلاف واضربكم سبعة اضعاف حسب خطاياكم"));
arasuperobject.biblemap.put("b3_26_25", new arasuperobject(3,26,25,"اجلب عليكم سيفا ينتقم نقمة الميثاق فتجتمعون الى مدنكم وأرسل في وسطكم الوبأ فتدفعون بيد العدو"));
arasuperobject.biblemap.put("b3_26_26", new arasuperobject(3,26,26,"بكسري لكم عصا الخبز تخبز عشر نساء خبزكم في تنور واحد ويرددن خبزكم بالوزن فتأكلون ولا تشبعون"));
arasuperobject.biblemap.put("b3_26_27", new arasuperobject(3,26,27,"وان كنتم بذلك لا تسمعون لي بل سلكتم معي بالخلاف"));
arasuperobject.biblemap.put("b3_26_28", new arasuperobject(3,26,28,"فانا اسلك معكم بالخلاف ساخطا وأؤدّبكم سبعة اضعاف حسب خطاياكم"));
arasuperobject.biblemap.put("b3_26_29", new arasuperobject(3,26,29,"فتأكلون لحم بنيكم. ولحم بناتكم تأكلون"));
arasuperobject.biblemap.put("b3_26_30", new arasuperobject(3,26,30,"واخرب مرتفعاتكم واقطع شمساتكم والقي جثثكم على جثث اصنامكم وترذلكم نفسي"));
arasuperobject.biblemap.put("b3_26_31", new arasuperobject(3,26,31,"واصيّر مدنكم خربة ومقادسكم موحشة ولا اشتم رائحة سروركم"));
arasuperobject.biblemap.put("b3_26_32", new arasuperobject(3,26,32,"وأوحش الارض فيستوحش منها اعداؤكم الساكنون فيها"));
arasuperobject.biblemap.put("b3_26_33", new arasuperobject(3,26,33,"واذرّيكم بين الامم واجرّد وراءكم السيف فتصير ارضكم موحشة ومدنكم تصير خربة"));
arasuperobject.biblemap.put("b3_26_34", new arasuperobject(3,26,34,"حينئذ تستوفي الارض سبوتها كل ايام وحشتها وانتم في ارض اعدائكم. حينئذ تسبت الارض وتستوفي سبوتها"));
arasuperobject.biblemap.put("b3_26_35", new arasuperobject(3,26,35,"كل ايام وحشتها تسبت ما لم تسبته من سبوتكم في سكنكم عليها"));
arasuperobject.biblemap.put("b3_26_36", new arasuperobject(3,26,36,"والباقون منكم ألقي الجبانة في قلوبهم في اراضي اعدائهم فيهزمهم صوت ورقة مندفعة فيهربون كالهرب من السيف ويسقطون وليس طارد"));
arasuperobject.biblemap.put("b3_26_37", new arasuperobject(3,26,37,"ويعثر بعضهم ببعض كما من امام السيف وليس طارد ولا يكون لكم قيام امام اعدائكم"));
arasuperobject.biblemap.put("b3_26_38", new arasuperobject(3,26,38,"فتهلكون بين الشعوب وتاكلكم ارض اعدائكم"));
arasuperobject.biblemap.put("b3_26_39", new arasuperobject(3,26,39,"والباقون منكم يفنون بذنوبهم في اراضي اعدائكم. وايضا بذنوب آبائهم معهم يفنون"));
arasuperobject.biblemap.put("b3_26_40", new arasuperobject(3,26,40,"لكن ان اقرّوا بذنوبهم وذنوب آبائهم في خيانتهم التي خانوني بها وسلوكهم معي الذي سلكوا بالخلاف"));
arasuperobject.biblemap.put("b3_26_41", new arasuperobject(3,26,41,"واني ايضا سلكت معهم بالخلاف وأتيت بهم الى ارض اعدائهم الا ان تخضع حينئذ قلوبهم الغلف ويستوفوا حينئذ عن ذنوبهم"));
arasuperobject.biblemap.put("b3_26_42", new arasuperobject(3,26,42,"اذكر ميثاقي مع يعقوب واذكر ايضا ميثاقي مع اسحق وميثاقي مع ابراهيم واذكر الارض"));
arasuperobject.biblemap.put("b3_26_43", new arasuperobject(3,26,43,"والارض تترك منهم وتستوفي سبوتها في وحشتها منهم وهم يستوفون عن ذنوبهم لانهم قد أبوا احكامي وكرهت انفسهم فرائضي"));
arasuperobject.biblemap.put("b3_26_44", new arasuperobject(3,26,44,"ولكن مع ذلك ايضا متى كانوا في ارض اعدائهم ما أبيتهم ولا كرهتهم حتى ابيدهم وانكث ميثاقي معهم. لاني انا الرب الههم"));
arasuperobject.biblemap.put("b3_26_45", new arasuperobject(3,26,45,"بل اذكر لهم الميثاق مع الاولين الذين اخرجتهم من ارض مصر امام اعين الشعوب لاكون لهم الها. انا الرب"));
arasuperobject.biblemap.put("b3_26_46", new arasuperobject(3,26,46,"هذه هي الفرائض والاحكام والشرائع التي وضعها الرب بينه وبين بني اسرائيل في جبل سيناء بيد موسى"));
arasuperobject.biblemap.put("b3_27_1", new arasuperobject(3,27,1,"وكلم الرب موسى قائلا"));
arasuperobject.biblemap.put("b3_27_2", new arasuperobject(3,27,2,"كلم بني اسرائيل وقل لهم. اذا افرز انسان نذرا حسب تقويمك نفوسا للرب"));
arasuperobject.biblemap.put("b3_27_3", new arasuperobject(3,27,3,"فان كان تقويمك لذكر من ابن عشرين سنة الى ابن ستين سنة يكون تقويمك خمسين شاقل فضة على شاقل المقدس"));
arasuperobject.biblemap.put("b3_27_4", new arasuperobject(3,27,4,"وان كان انثى يكون تقويمك ثلاثين شاقلا"));
arasuperobject.biblemap.put("b3_27_5", new arasuperobject(3,27,5,"وان كان من ابن خمس سنين الى ابن عشرين سنة يكون تقويمك لذكر عشرين شاقلا ولانثى عشرة شواقل"));
arasuperobject.biblemap.put("b3_27_6", new arasuperobject(3,27,6,"وان كان من ابن شهر الى ابن خمس سنين يكون تقويمك لذكر خمسة شواقل فضة ولانثى يكون تقويمك ثلاثة شواقل فضة"));
arasuperobject.biblemap.put("b3_27_7", new arasuperobject(3,27,7,"وان كان من ابن ستين سنة فصاعدا فان كان ذكرا يكون تقويمك خمسة عشر شاقلا. واما للانثى فعشرة شواقل"));
arasuperobject.biblemap.put("b3_27_8", new arasuperobject(3,27,8,"وان كان فقيرا عن تقويمك يوقفه امام الكاهن فيقومه الكاهن. على قدر ما تنال يد الناذر يقوّمه الكاهن"));
arasuperobject.biblemap.put("b3_27_9", new arasuperobject(3,27,9,"وان كان بهيمة مّما يقرّبونه قربانا للرب فكل ما يعطي منه للرب يكون قدسا"));
arasuperobject.biblemap.put("b3_27_10", new arasuperobject(3,27,10,"لا يغيّره ولا يبدله جيدا برديء او رديئا بجيد. وان ابدل بهيمة ببهيمة تكون هي وبديلها قدسا"));
arasuperobject.biblemap.put("b3_27_11", new arasuperobject(3,27,11,"وان كان بهيمة نجسة مما لا يقربونه قربانا للرب يوقف البهيمة امام الكاهن"));
arasuperobject.biblemap.put("b3_27_12", new arasuperobject(3,27,12,"فيقومها الكاهن جيدة ام رديئة. فحسب تقويمك يا كاهن هكذا يكون"));
arasuperobject.biblemap.put("b3_27_13", new arasuperobject(3,27,13,"فان فكها يزيد خمسها على تقويمك"));
arasuperobject.biblemap.put("b3_27_14", new arasuperobject(3,27,14,"واذا قدس انسان بيته قدسا للرب يقومه الكاهن جيدا ام رديئا وكما يقوّمه الكاهن هكذا يقوم"));
arasuperobject.biblemap.put("b3_27_15", new arasuperobject(3,27,15,"فان كان المقدس يفك بيته يزيد خمس فضة تقويمك عليه فيكون له"));
arasuperobject.biblemap.put("b3_27_16", new arasuperobject(3,27,16,"وان قدّس انسان بعض حقل ملكه للرب يكون تقويمك على قدر بذاره. بذار حومر من الشعير بخمسين شاقل فضة"));
arasuperobject.biblemap.put("b3_27_17", new arasuperobject(3,27,17,"ان قدس حقله من سنة اليوبيل فحسب تقويمك يقوم"));
arasuperobject.biblemap.put("b3_27_18", new arasuperobject(3,27,18,"وان قدس حقله بعد سنة اليوبيل يحسب له الكاهن الفضة على قدر السنين الباقية الى سنة اليوبيل فينقّص من تقويمك"));
arasuperobject.biblemap.put("b3_27_19", new arasuperobject(3,27,19,"فان فك الحقل مقدّسه يزيد خمس فضة تقويمك عليه فيجب له"));
arasuperobject.biblemap.put("b3_27_20", new arasuperobject(3,27,20,"لكن ان لم يفك الحقل وبيع الحقل لانسان آخر لا يفك بعد"));
arasuperobject.biblemap.put("b3_27_21", new arasuperobject(3,27,21,"بل يكون الحقل عند خروجه في اليوبيل قدسا للرب كالحقل المحرم. للكاهن يكون ملكه"));
arasuperobject.biblemap.put("b3_27_22", new arasuperobject(3,27,22,"وان قدّس للرب حقلا من شرائه ليس من حقول ملكه"));
arasuperobject.biblemap.put("b3_27_23", new arasuperobject(3,27,23,"يحسب له الكاهن مبلغ تقويمك الى سنة اليوبيل فيعطي تقويمك في ذلك اليوم قدسا للرب"));
arasuperobject.biblemap.put("b3_27_24", new arasuperobject(3,27,24,"وفي سنة اليوبيل يرجع الحقل الى الذي اشتراه منه الى الذي له ملك الارض"));
arasuperobject.biblemap.put("b3_27_25", new arasuperobject(3,27,25,"وكل تقويمك يكون على شاقل المقدس عشرين جيرة يكون الشاقل"));
arasuperobject.biblemap.put("b3_27_26", new arasuperobject(3,27,26,"لكن البكر الذي يفرز بكرا للرب من البهائم فلا يقدسه احد. ثورا كان او شاة فهو للرب"));
arasuperobject.biblemap.put("b3_27_27", new arasuperobject(3,27,27,"وان كان من البهائم النجسة يفديه حسب تقويمك ويزيد خمسه عليه. وان لم يفك فيباع حسب تقويمك"));
arasuperobject.biblemap.put("b3_27_28", new arasuperobject(3,27,28,"اما كل محرّم يحرمه انسان للرب من كل ما له من الناس والبهائم ومن حقول ملكه فلا يباع ولا يفك. ان كل محرم هو قدس اقداس للرب"));
arasuperobject.biblemap.put("b3_27_29", new arasuperobject(3,27,29,"كل محرّم يحرّم من الناس لا يفدى. يقتل قتلا"));
arasuperobject.biblemap.put("b3_27_30", new arasuperobject(3,27,30,"وكل عشر الارض من حبوب الارض واثمار الشجر فهو للرب. قدس للرب"));
arasuperobject.biblemap.put("b3_27_31", new arasuperobject(3,27,31,"وان فك انسان بعض عشره يزيد خمسه عليه"));
arasuperobject.biblemap.put("b3_27_32", new arasuperobject(3,27,32,"واما كل عشر البقر والغنم فكل ما يعبر تحت العصا يكون العاشر قدسا للرب"));
arasuperobject.biblemap.put("b3_27_33", new arasuperobject(3,27,33,"لا يفحص أجيد هو ام رديء ولا يبدله. وان ابدله يكون هو وبديله قدسا. لا يفك"));
arasuperobject.biblemap.put("b3_27_34", new arasuperobject(3,27,34,"هذه هي الوصايا التي اوصى الرب بها موسى الى بني اسرائيل في جبل سيناء"));

