arasuperobject.biblemap.put("b30_1_5", new arasuperobject(30,1,5,"واكسر مغلاق دمشق واقطع الساكن من بقعة آون وماسك القضيب من بيت عدن ويسبى شعب ارام الى قير قال الرب"));
arasuperobject.biblemap.put("b30_1_6", new arasuperobject(30,1,6,"هكذا قال الرب. من اجل ذنوب غزّة الثلاثة والاربعة لا ارجع عنه لانهم سبوا سبيا كاملا لكي يسلّموه الى ادوم"));
arasuperobject.biblemap.put("b30_1_7", new arasuperobject(30,1,7,"فارسل نارا على سور غزّة فتاكل قصورها"));
