arasuperobject.biblemap.put("b30_1_9", new arasuperobject(30,1,9,"هكذا قال الرب من اجل ذنوب صور الثلاثة والاربعة لا ارجع عنه لانهم سلّموا سبيا كاملا الى ادوم ولم يذكروا عهد الاخوة"));
arasuperobject.biblemap.put("b30_1_10", new arasuperobject(30,1,10,"فارسل نارا على سور صور فتاكل قصورها"));
arasuperobject.biblemap.put("b30_1_11", new arasuperobject(30,1,11,"هكذا قال الرب من اجل ذنوب ادوم الثلاثة والاربعة لا ارجع عنه لانه تبع بالسيف اخاه وافسد مراحمه وغضبه الى الدهر يفترس وسخطه يحفظه الى الابد"));
arasuperobject.biblemap.put("b30_1_12", new arasuperobject(30,1,12,"فارسل نارا على تيمان فتأكل قصور بصرة"));
