arasuperobject.biblemap.put("b30_2_3", new arasuperobject(30,2,3,"واقطع القاضي من وسطها واقتل جميع رؤسائها معه قال الرب"));
arasuperobject.biblemap.put("b30_2_4", new arasuperobject(30,2,4,"هكذا قال الرب. من اجل ذنوب يهوذا الثلاثة والاربعة لا ارجع عنه لانهم رفضوا ناموس الله ولم يحفظوا فرائضه واضلتهم اكاذيبهم التي سار آباؤهم وراءها"));
