arasuperobject.biblemap.put("b30_2_7", new arasuperobject(30,2,7,"الذين يتهمّمون تراب الارض على رؤوس المساكين ويصدّون سبيل البائسين ويذهب رجل وابوه الى صبية واحدة حتى يدنسوا اسم قدسي"));
arasuperobject.biblemap.put("b30_2_8", new arasuperobject(30,2,8,"ويتمدّدون على ثياب مرهونة بجانب كل مذبح ويشربون خمر المغرّمين في بيت آلهتهم"));
arasuperobject.biblemap.put("b30_2_9", new arasuperobject(30,2,9,"وانا قد ابدت من امامهم الاموري الذي قامته مثل قامة الارز وهو قوي كالبلوط. ابدت ثمره من فوق واصوله من تحت"));
arasuperobject.biblemap.put("b30_2_10", new arasuperobject(30,2,10,"وانا اصعدتكم من ارض مصر وسرت بكم في البرية اربعين سنة لترثوا ارض الاموري"));
arasuperobject.biblemap.put("b30_2_11", new arasuperobject(30,2,11,"واقمت من بنيكم انبياء ومن فتيانكم نذيرين. أليس هكذا يا بني اسرائيل يقول الرب"));
arasuperobject.biblemap.put("b30_2_12", new arasuperobject(30,2,12,"لكنكم سقيتم النذيرين خمرا واوصيتم الانبياء قائلين لا تتنبأوا"));
arasuperobject.biblemap.put("b30_2_13", new arasuperobject(30,2,13,"هانذا اضغط ما تحتكم كما تضغط العجلة الملآنة حزما"));
arasuperobject.biblemap.put("b30_2_14", new arasuperobject(30,2,14,"ويبيد المناص عن السريع والقوي لا يشدّد قوّته والبطل لا ينجّي نفسه"));
arasuperobject.biblemap.put("b30_2_15", new arasuperobject(30,2,15,"وماسك القوس لا يثبت وسريع الرجلين لا ينجو وراكب الخيل لا ينجّي نفسه"));
arasuperobject.biblemap.put("b30_2_16", new arasuperobject(30,2,16,"والقوي القلب بين الابطال يهرب عريانا في ذلك اليوم يقول الرب"));
arasuperobject.biblemap.put("b30_3_1", new arasuperobject(30,3,1,"اسمعوا هذا القول الذي تكلم به الرب عليكم يا بني اسرائيل على كل القبيلة التي اصعدتها من ارض مصر قائلا"));
arasuperobject.biblemap.put("b30_3_2", new arasuperobject(30,3,2,"اياكم فقط عرفت من جميع قبائل الارض لذلك اعاقبكم على جميع ذنوبكم"));
