arasuperobject.biblemap.put("b30_3_5", new arasuperobject(30,3,5,"هل يسقط عصفور في فخ الارض وليس له شرك. هل يرفع فخ عن الارض وهو لم يمسك شيئا"));
arasuperobject.biblemap.put("b30_3_6", new arasuperobject(30,3,6,"ام يضرب بالبوق في مدينة والشعب لا يرتعد. هل تحدث بلية في مدينة والرب لم يصنعها"));
arasuperobject.biblemap.put("b30_3_7", new arasuperobject(30,3,7,"ان السيد الرب لا يصنع امرا الا وهو يعلن سرّه لعبيده الانبياء"));
arasuperobject.biblemap.put("b30_3_8", new arasuperobject(30,3,8,"الاسد قد زمجر فمن لا يخاف. السيد الرب قد تكلم فمن لا يتنبأ"));
arasuperobject.biblemap.put("b30_3_9", new arasuperobject(30,3,9,"نادوا على القصور في اشدود وعلى القصور في ارض مصر وقولوا اجتمعوا على جبال السامرة وانظروا شغبا عظيما في وسطها ومظالم في داخلها"));
arasuperobject.biblemap.put("b30_3_10", new arasuperobject(30,3,10,"فانهم لا يعرفون ان يصنعوا الاستقامة يقول الرب. اولئك الذين يخزنون الظلم والاغتصاب في قصورهم"));
arasuperobject.biblemap.put("b30_3_11", new arasuperobject(30,3,11,"لذلك هكذا قال السيد الرب. ضيق حتى في كل ناحية من الارض فينزل عنك عزك وتنهب قصورك"));
arasuperobject.biblemap.put("b30_3_12", new arasuperobject(30,3,12,"هكذا قال الرب. كما ينزع الراعي من فم الاسد كراعين او قطعة اذن هكذا ينتزع بنو اسرائيل الجالسون في السامرة في زاوية السرير وعلى دمقس الفراش"));
arasuperobject.biblemap.put("b30_3_13", new arasuperobject(30,3,13,"اسمعوا واشهدوا على بيت يعقوب يقول السيد الرب اله الجنود"));
arasuperobject.biblemap.put("b30_3_14", new arasuperobject(30,3,14,"اني يوم معاقبتي اسرائيل على ذنوبه اعاقب مذابح بيت ايل فتقطع قرون المذبح وتسقط الى الارض"));
arasuperobject.biblemap.put("b30_3_15", new arasuperobject(30,3,15,"واضرب بيت الشتاء مع بيت الصيف فتبيد بيوت العاج وتضمحل البيوت العظيمة يقول الرب"));
