arasuperobject.biblemap.put("b32_1_3", new arasuperobject(32,1,3,"فقام يونان ليهرب الى ترشيش من وجه الرب فنزل الى يافا ووجد سفينة ذاهبة الى ترشيش فدفع اجرتها ونزل فيها ليذهب معهم الى ترشيش من وجه الرب"));
arasuperobject.biblemap.put("b32_1_4", new arasuperobject(32,1,4,"فارسل الرب ريحا شديدة الى البحر فحدث نوء عظيم في البحر حتى كادت السفينة تنكسر"));
arasuperobject.biblemap.put("b32_1_5", new arasuperobject(32,1,5,"فخاف الملاحون وصرخوا كل واحد الى الهه وطرحوا الامتعة التي في السفينة الى البحر ليخفّفوا عنهم. واما يونان فكان قد نزل الى جوف السفينة واضطجع ونام نوما ثقيلا"));
arasuperobject.biblemap.put("b32_1_6", new arasuperobject(32,1,6,"فجاء اليه رئيس النوتية وقال له ما لك نائما. قم اصرخ الى الهك عسى ان يفتكر الاله فينا فلا نهلك"));
arasuperobject.biblemap.put("b32_1_7", new arasuperobject(32,1,7,"وقال بعضهم لبعض هلم نلقي قرعا لنعرف بسبب من هذه البلية. فالقوا قرعا فوقعت القرعة على يونان"));
arasuperobject.biblemap.put("b32_1_8", new arasuperobject(32,1,8,"فقالوا له اخبرنا بسبب من هذه المصيبة علينا. ما هو عملك ومن اين اتيت. ما هي ارضك ومن اي شعب انت"));
arasuperobject.biblemap.put("b32_1_9", new arasuperobject(32,1,9,"فقال لهم انا عبراني وانا خائف من الرب اله السماء الذي صنع البحر والبر"));
arasuperobject.biblemap.put("b32_1_10", new arasuperobject(32,1,10,"فخاف الرجال خوفا عظيما وقالوا له لماذا فعلت هذا. فان الرجال عرفوا انه هارب من وجه الرب لانه اخبرهم"));
arasuperobject.biblemap.put("b32_1_11", new arasuperobject(32,1,11,"فقالوا له ماذا نصنع بك ليسكن البحر عنا. لان البحر كان يزداد اضطرابا"));
arasuperobject.biblemap.put("b32_1_12", new arasuperobject(32,1,12,"فقال لهم خذوني واطرحوني في البحر فيسكن البحر عنكم لانني عالم انه بسببي هذا النوء العظيم عليكم"));
arasuperobject.biblemap.put("b32_1_13", new arasuperobject(32,1,13,"ولكن الرجال جذفوا ليرجّعوا السفينة الى البر فلم يستطيعوا لان البحر كان يزداد اضطرابا عليهم"));
arasuperobject.biblemap.put("b32_1_14", new arasuperobject(32,1,14,"فصرخوا الى الرب وقالوا آه يا رب لا نهلك من اجل نفس هذا الرجل ولا تجعل علينا دما بريئا لانك يا رب فعلت كما شئت"));
arasuperobject.biblemap.put("b32_1_15", new arasuperobject(32,1,15,"ثم اخذوا يونان وطرحوه في البحر فوقف البحر عن هيجانه"));
arasuperobject.biblemap.put("b32_1_16", new arasuperobject(32,1,16,"فخاف الرجال من الرب خوفا عظيما وذبحوا ذبيحة للرب ونذروا نذورا"));
arasuperobject.biblemap.put("b32_1_17", new arasuperobject(32,1,17,"واما الرب فاعدّ حوتا عظيما ليبتلع يونان. فكان يونان في جوف الحوت ثلاثة ايام وثلاث ليال"));
arasuperobject.biblemap.put("b32_2_1", new arasuperobject(32,2,1,"فصلى يونان الى الرب الهه من جوف الحوت"));
arasuperobject.biblemap.put("b32_2_2", new arasuperobject(32,2,2,"وقال. دعوت من ضيقي الرب فاستجابني . ‎ صرخت من جوف الهاوية فسمعت صوتي"));
arasuperobject.biblemap.put("b32_2_3", new arasuperobject(32,2,3,"لانك طرحتني في العمق في قلب البحار. فاحاط بي نهر. جازت فوقي جميع تيّاراتك ولججك"));
arasuperobject.biblemap.put("b32_2_4", new arasuperobject(32,2,4,"فقلت قد طردت من امام عينيك. ولكنني اعود انظر الى هيكل قدسك"));
arasuperobject.biblemap.put("b32_2_5", new arasuperobject(32,2,5,"قد اكتنفتني مياه الى النفس. احاط بي غمر. التفّ عشب البحر براسي"));
arasuperobject.biblemap.put("b32_2_6", new arasuperobject(32,2,6,"نزلت الى اسافل الجبال. مغاليق الارض عليّ الى الابد. ثم اصعدت من الوهدة حياتي ايها الرب الهي"));
arasuperobject.biblemap.put("b32_2_7", new arasuperobject(32,2,7,"حين اعيت فيّ نفسي ذكرت الرب فجاءت اليك صلاتي الى هيكل قدسك"));
arasuperobject.biblemap.put("b32_2_8", new arasuperobject(32,2,8,"الذين يراعون اباطيل كاذبة يتركون نعمتهم"));
arasuperobject.biblemap.put("b32_2_9", new arasuperobject(32,2,9,"اما انا فبصوت الحمد اذبح لك واوفي بما نذرته. للرب الخلاص"));
arasuperobject.biblemap.put("b32_2_10", new arasuperobject(32,2,10,"وأمر الرب الحوت فقذف يونان الى البر"));
arasuperobject.biblemap.put("b32_3_1", new arasuperobject(32,3,1,"ثم صار قول الرب الى يونان ثانية قائلا"));
arasuperobject.biblemap.put("b32_3_2", new arasuperobject(32,3,2,"قم اذهب الى نينوى المدينة العظيمة وناد لها المناداة التي انا مكلّمك بها"));
arasuperobject.biblemap.put("b32_3_3", new arasuperobject(32,3,3,"فقام يونان وذهب الى نينوى بحسب قول الرب. اما نينوى فكانت مدينة عظيمة للّه مسيرة ثلاثة ايام"));
arasuperobject.biblemap.put("b32_3_4", new arasuperobject(32,3,4,"فابتدأ يونان يدخل المدينة مسيرة يوم واحد ونادى وقال بعد اربعين يوما تنقلب نينوى"));
arasuperobject.biblemap.put("b32_3_5", new arasuperobject(32,3,5,"فآمن اهل نينوى بالله ونادوا بصوم ولبسوا مسوحا من كبيرهم الى صغيرهم"));
arasuperobject.biblemap.put("b32_3_6", new arasuperobject(32,3,6,"وبلغ الأمر ملك نينوى فقام عن كرسيه وخلع رداءه عنه وتغطى بمسح وجلس على الرماد"));
arasuperobject.biblemap.put("b32_3_7", new arasuperobject(32,3,7,"ونودي وقيل في نينوى عن امر الملك وعظمائه قائلا لا تذق الناس ولا البهائم ولا البقر ولا الغنم شيئا. لا ترع ولا تشرب ماء"));
arasuperobject.biblemap.put("b32_3_8", new arasuperobject(32,3,8,"وليتغط بمسوح الناس والبهائم ويصرخوا الى الله بشدة ويرجعوا كل واحد عن طريقه الرديئة وعن الظلم الذي في ايديهم"));
arasuperobject.biblemap.put("b32_3_9", new arasuperobject(32,3,9,"لعل الله يعود ويندم ويرجع عن حمو غضبه فلا نهلك"));
arasuperobject.biblemap.put("b32_3_10", new arasuperobject(32,3,10,"فلما رأى الله اعمالهم انهم رجعوا عن طريقهم الرديئة ندم الله على الشر الذي تكلم ان يصنعه بهم فلم يصنعه"));
arasuperobject.biblemap.put("b32_4_1", new arasuperobject(32,4,1,"فغمّ ذلك يونان غمّا شديدا فاغتاظ"));
arasuperobject.biblemap.put("b32_4_2", new arasuperobject(32,4,2,"وصلى الى الرب وقال آه يا رب أليس هذا كلامي اذ كنت بعد في ارضي. لذلك بادرت الى الهرب الى ترشيش لاني علمت انك اله رؤوف ورحيم بطيء الغضب وكثير الرحمة ونادم على الشر"));
arasuperobject.biblemap.put("b32_4_3", new arasuperobject(32,4,3,"فالآن يا رب خذ نفسي مني لان موتي خير من حياتي"));
arasuperobject.biblemap.put("b32_4_4", new arasuperobject(32,4,4,"فقال الرب هل اغتظت بالصواب"));
arasuperobject.biblemap.put("b32_4_5", new arasuperobject(32,4,5,"وخرج يونان من المدينة وجلس شرقي المدينة وصنع لنفسه هناك مظلّة وجلس تحتها في الظل حتى يرى ماذا يحدث في المدينة"));
arasuperobject.biblemap.put("b32_4_6", new arasuperobject(32,4,6,"فاعدّ الرب الاله يقطينة فارتفعت فوق يونان لتكون ظلا على راسه لكي يخلّصه من غمّه. ففرح يونان من اجل اليقطينة فرحا عظيما"));
arasuperobject.biblemap.put("b32_4_7", new arasuperobject(32,4,7,"ثم اعدّ الله دودة عند طلوع الفجر في الغد فضربت اليقطينة فيبست"));
arasuperobject.biblemap.put("b32_4_8", new arasuperobject(32,4,8,"وحدث عند طلوع الشمس ان الله اعدّ ريحا شرقية حارّة فضربت الشمس على راس يونان فذبل فطلب لنفسه الموت وقال موتي خير من حياتي"));
arasuperobject.biblemap.put("b32_4_9", new arasuperobject(32,4,9,"فقال الله ليونان هل اغتظت بالصواب من اجل اليقطينة. فقال اغتظت بالصواب حتى الموت"));
arasuperobject.biblemap.put("b32_4_10", new arasuperobject(32,4,10,"فقال الرب انت شفقت على اليقطينة التي لم تتعب فيها ولا ربّيتها التي بنت ليلة كانت وبنت ليلة هلكت"));
arasuperobject.biblemap.put("b32_4_11", new arasuperobject(32,4,11,"أفلا اشفق انا على نينوى المدينة العظيمة التي يوجد فيها اكثر من اثنتي عشرة ربوة من الناس الذين لا يعرفون يمينهم من شمالهم وبهائم كثيرة"));

