package ara.bible;

public class arabible39 {

public arabible39() {

putmapbible39();

}

private void putmapbible39() {

arasuperobject.biblemap.put("b39_1_1", new arasuperobject(39,1,1,"وحي كلمة الرب لاسرائيل عن يد ملاخي"));
arasuperobject.biblemap.put("b39_1_2", new arasuperobject(39,1,2,"احببتكم قال الرب. وقلتم بم احببتنا. أليس عيسو اخا ليعقوب يقول الرب واحببت يعقوب"));
arasuperobject.biblemap.put("b39_1_3", new arasuperobject(39,1,3,"وابغضت عيسو وجعلت جباله خرابا وميراثه لذئاب البرية"));
arasuperobject.biblemap.put("b39_1_4", new arasuperobject(39,1,4,"لان ادوم قال قد هدمنا فنعود ونبني الخرب. هكذا قال رب الجنود هم يبنون وانا اهدم ويدعونهم تخوم الشر والشعب الذي غضب عليه الرب الى الابد"));
arasuperobject.biblemap.put("b39_1_5", new arasuperobject(39,1,5,"فترى اعينكم وتقولون ليتعظم الرب من عند تخم اسرائيل"));
arasuperobject.biblemap.put("b39_1_6", new arasuperobject(39,1,6,"الابن يكرم اباه والعبد يكرم سيده. فان كنت انا ابا فاين كرامتي وان كنت سيدا فاين هيبتي قال لكم رب الجنود ايها الكهنة المحتقرون اسمي. وتقولون بما احتقرنا اسمك"));
arasuperobject.biblemap.put("b39_1_7", new arasuperobject(39,1,7,"تقربون خبزا نجسا على مذبحي. وتقولون بم نجّسناك. بقولكم ان مائدة الرب محتقرة"));
arasuperobject.biblemap.put("b39_1_8", new arasuperobject(39,1,8,"وان قربتم الاعمى ذبيحة أفليس ذلك شرا وان قربتم الاعرج والسقيم أفليس ذلك شرا. قرّبه لواليك أفيرضى عليك او يرفع وجهك قال رب الجنود"));
arasuperobject.biblemap.put("b39_1_9", new arasuperobject(39,1,9,"والآن ترضّوا وجه الله فيتراءف علينا. هذه كانت من يدكم. هل يرفع وجهكم قال رب الجنود"));
arasuperobject.biblemap.put("b39_1_10", new arasuperobject(39,1,10,"من فيكم يغلق الباب بل لا توقدون على مذبحي مجانا. ليست لي مسرّة بكم قال رب الجنود ولا اقبل تقدمة من يدكم"));
arasuperobject.biblemap.put("b39_1_11", new arasuperobject(39,1,11,"لانه من مشرق الشمس الى مغربها اسمي عظيم بين الامم وفي كل مكان يقرب لاسمي بخور وتقدمة طاهرة لان اسمي عظيم بين الامم قال رب الجنود"));
arasuperobject.biblemap.put("b39_1_12", new arasuperobject(39,1,12,"اما انتم فمنجسوه بقولكم ان مائدة الرب تنجّست وثمرتها محتقر طعامها"));
arasuperobject.biblemap.put("b39_1_13", new arasuperobject(39,1,13,"وقلتم ما هذه المشقّة وتأففتم عليه قال رب الجنود وجئتم بالمغتصب والاعرج والسقيم فاتيتم بالتقدمة. فهل اقبلها من يدكم قال الرب"));
arasuperobject.biblemap.put("b39_1_14", new arasuperobject(39,1,14,"وملعون الماكر الذي يوجد في قطيعه ذكر وينذر ويذبح للسيد عائبا. لاني انا ملك عظيم قال رب الجنود واسمي مهيب بين الامم"));
arasuperobject.biblemap.put("b39_2_1", new arasuperobject(39,2,1,"والآن اليكم هذه الوصية ايها الكهنة"));
arasuperobject.biblemap.put("b39_2_2", new arasuperobject(39,2,2,"ان كنتم لا تسمعون ولا تجعلون في القلب لتعطوا مجدا لاسمي قال رب الجنود فاني ارسل عليكم اللعن وألعن بركاتكم بل قد لعنتها لانكم لستم جاعلين في القلب"));
arasuperobject.biblemap.put("b39_2_3", new arasuperobject(39,2,3,"هانذا انتهر لكم الزرع وامدّ الفرث على وجوهكم فرث اعيادكم فتنزعون معه"));
arasuperobject.biblemap.put("b39_2_4", new arasuperobject(39,2,4,"فتعلمون اني ارسلت اليكم هذه الوصية لكون عهدي مع لاوي قال رب الجنود"));
arasuperobject.biblemap.put("b39_2_5", new arasuperobject(39,2,5,"كان عهدي معه للحياة والسلام واعطيته اياهما للتقوى فاتقاني ومن اسمي ارتاع هو"));
arasuperobject.biblemap.put("b39_2_6", new arasuperobject(39,2,6,"شريعة الحق كانت في فيه واثم لم يوجد في شفتيه. سلك معي في السلام والاستقامة وارجع كثيرين عن الاثم"));
arasuperobject.biblemap.put("b39_2_7", new arasuperobject(39,2,7,"لان شفتي الكاهن تحفظان معرفة ومن فمه يطلبون الشريعة لانه رسول رب الجنود"));
arasuperobject.biblemap.put("b39_2_8", new arasuperobject(39,2,8,"اما انتم فحدتم عن الطريق واعثرتم كثيرين بالشريعة. افسدتم عهد لاوي قال رب الجنود"));
arasuperobject.biblemap.put("b39_2_9", new arasuperobject(39,2,9,"فانا ايضا صيّرتكم محتقرين ودنيئين عند كل الشعب كما انكم لم تحفظوا طرقي بل حابيتم في الشريعة"));
arasuperobject.biblemap.put("b39_2_10", new arasuperobject(39,2,10,"أليس اب واحد لكلنا. أليس اله واحد خلقنا. فلم نغدر الرجل باخيه لتدنيس عهد آبائنا"));
arasuperobject.biblemap.put("b39_2_11", new arasuperobject(39,2,11,"غدر يهوذا وعمل الرجس في اسرائيل وفي اورشليم. لان يهوذا قد نجّس قدس الرب الذي احبه وتزوج بنت اله غريب"));
arasuperobject.biblemap.put("b39_2_12", new arasuperobject(39,2,12,"يقطع الرب الرجل الذي يفعل هذا الساهر والمجيب من خيام يعقوب ومن يقرّب تقدمة لرب الجنود"));
arasuperobject.biblemap.put("b39_2_13", new arasuperobject(39,2,13,"وقد فعلتم هذا ثانية مغطين مذبح الرب بالدموع بالبكاء والصراخ فلا تراعى التقدمة بعد ولا يقبل المرضي من يدكم"));
arasuperobject.biblemap.put("b39_2_14", new arasuperobject(39,2,14,"فقلتم لماذا. من اجل ان الرب هو الشاهد بينك وبين امرأة شبابك التي انت غدرت بها وهي قرينتك وامرأة عهدك"));
arasuperobject.biblemap.put("b39_2_15", new arasuperobject(39,2,15,"أفلم يفعل واحد وله بقية الروح. ولماذا الواحد. طالبا زرع الله. فاحذروا لروحكم ولا يغدر احد بامرأة شبابه"));
arasuperobject.biblemap.put("b39_2_16", new arasuperobject(39,2,16,"لانه يكره الطلاق قال الرب اله اسرائيل وان يغطي احد الظلم بثوبه قال رب الجنود. فاحذروا لروحكم لئلا تغدروا"));
arasuperobject.biblemap.put("b39_2_17", new arasuperobject(39,2,17,"لقد اتعبتم الرب بكلامكم. وقلتم بم اتعبناه. بقولكم كل من يفعل الشر فهو صالح في عيني الرب وهو يسرّ بهم. او اين اله العدل"));
arasuperobject.biblemap.put("b39_3_1", new arasuperobject(39,3,1,"هانذا ارسل ملاكي فيهيء الطريق امامي ويأتي بغتة الى هيكله السيد الذي تطلبونه وملاك العهد الذي تسرّون به هوذا يأتي قال رب الجنود"));
arasuperobject.biblemap.put("b39_3_2", new arasuperobject(39,3,2,"ومن يحتمل يوم مجيئه ومن يثبت عند ظهوره. لانه مثل نار الممحص ومثل اشنان القصّار"));
arasuperobject.biblemap.put("b39_3_3", new arasuperobject(39,3,3,"فيجلس ممحصا ومنقيا للفضة فينقي بني لاوي ويصفيهم كالذهب والفضة ليكونوا مقربين للرب تقدمة بالبر"));
arasuperobject.biblemap.put("b39_3_4", new arasuperobject(39,3,4,"فتكون تقدمة يهوذا واورشليم مرضية للرب كما في ايام القدم وكما في السنين القديمة"));
arasuperobject.biblemap.put("b39_3_5", new arasuperobject(39,3,5,"واقترب اليكم للحكم واكون شاهدا سريعا على السحرة وعلى الفاسقين وعلى الحالفين زورا وعلى السالبين اجرة الاجير والارملة واليتيم ومن يصدّ الغريب ولا يخشاني قال رب الجنود"));
arasuperobject.biblemap.put("b39_3_6", new arasuperobject(39,3,6,"لاني انا الرب لا اتغيّر فانتم يا بني يعقوب لم تفنوا"));
arasuperobject.biblemap.put("b39_3_7", new arasuperobject(39,3,7,"من ايام آبائكم حدتم عن فرائضي ولم تحفظوها. ارجعوا اليّ ارجع اليكم قال رب الجنود. فقلتم بماذا نرجع"));
arasuperobject.biblemap.put("b39_3_8", new arasuperobject(39,3,8,"أيسلب الانسان الله. فانكم سلبتموني. فقلتم بم سلبناك. في العشور والتقدمة"));
arasuperobject.biblemap.put("b39_3_9", new arasuperobject(39,3,9,"قد لعنتم لعنا واياي انتم سالبون هذه الامة كلها"));
arasuperobject.biblemap.put("b39_3_10", new arasuperobject(39,3,10,"هاتوا جميع العشور الى الخزنة ليكون في بيتي طعام وجربوني بهذا قال رب الجنود ان كنت لا افتح لكم كوى السموات وافيض عليكم بركة حتى لا توسع"));
arasuperobject.biblemap.put("b39_3_11", new arasuperobject(39,3,11,"وانتهر من اجلكم الآكل فلا يفسد لكم ثمر الارض ولا يعقر لكم الكرم في الحقل قال رب الجنود"));
arasuperobject.biblemap.put("b39_3_12", new arasuperobject(39,3,12,"ويطوّبكم كل الامم لانكم تكونون ارض مسرّة قال رب الجنود"));
arasuperobject.biblemap.put("b39_3_13", new arasuperobject(39,3,13,"اقوالكم اشتدّت عليّ قال الرب. وقلتم ماذا قلنا عليك"));
arasuperobject.biblemap.put("b39_3_14", new arasuperobject(39,3,14,"قلتم عبادة الله باطلة وما المنفعة من اننا حفظنا شعائره واننا سلكنا بالحزن قدام رب الجنود"));
arasuperobject.biblemap.put("b39_3_15", new arasuperobject(39,3,15,"والآن نحن مطوّبون المستكبرين وايضا فاعلو الشر يبنون بل جربوا الله ونجوا"));
arasuperobject.biblemap.put("b39_3_16", new arasuperobject(39,3,16,"حينئذ كلّم متّقو الرب كل واحد قريبه والرب اصغى وسمع وكتب امامه سفر تذكرة للذين اتقوا الرب وللمفكرين في اسمه"));
arasuperobject.biblemap.put("b39_3_17", new arasuperobject(39,3,17,"ويكونون لي قال رب الجنود في اليوم الذي انا صانع خاصة واشفق عليهم كما يشفق الانسان على ابنه الذي يخدمه"));
arasuperobject.biblemap.put("b39_3_18", new arasuperobject(39,3,18,"فتعودون وتميزون بين الصدّيق والشرير بين من يعبد الله ومن لا يعبده"));
arasuperobject.biblemap.put("b39_4_1", new arasuperobject(39,4,1,"فهوذا ياتي اليوم المتقد كالتنور وكل المستكبرين وكل فاعلي الشر يكونون قشا ويحرقهم اليوم الآتي قال رب الجنود فلا يبقي لهم اصلا ولا فرعا"));
arasuperobject.biblemap.put("b39_4_2", new arasuperobject(39,4,2,"ولكم ايها المتّقون اسمي تشرق شمس البرّ والشفاء في اجنحتها فتخرجون وتنشأون كعجول الصيرة"));
arasuperobject.biblemap.put("b39_4_3", new arasuperobject(39,4,3,"وتدوسون الاشرار لانهم يكونون رمادا تحت بطون اقدامكم يوم افعل هذا قال رب الجنود"));
arasuperobject.biblemap.put("b39_4_4", new arasuperobject(39,4,4,"اذكروا شريعة موسى عبدي التي أمرته بها في حوريب على كل اسرائيل الفرائض والاحكام"));
arasuperobject.biblemap.put("b39_4_5", new arasuperobject(39,4,5,"هانذا ارسل اليكم ايليا النبي قبل مجيء يوم الرب اليوم العظيم والمخوف"));
arasuperobject.biblemap.put("b39_4_6", new arasuperobject(39,4,6,"فيرد قلب الآباء على الابناء وقلب الابناء على آبائهم لئلا آتي واضرب الارض بلعن"));

}

}